insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (12, 'Cháo cá chạch', 'Cá chạch còn có tên trường xuân ngư, là món ăn bổ dưỡng cho người cao tuổi và người yếu sinh lý. Các món ăn bài thuốc chữa yếu sinh lý từ cá chạch được nhiều quý ông áp dụng bởi tính hiệu nghiệm thần kỳ.', 1, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (38, 'Kho chay thập cẩm', '<p>Kho chay thập cẩm là một trong những <u><strong><a href="https://www.cooky.vn/bo-suu-tap/mon-chay-bo-duong-va-de-lam-309078" target="_blank">món ngon dễ làm</a></strong></u>, thường có trên các mâm cơm ngày chay, rất gần gũi với mọi người. Vào những ngày lễ hay Rằm, Mồng một thì món chay thập cẩm hay được chọn với nhiều loại rau quả tươi ngon để nấu cùng như cà rốt, nấm hương, đậu tuyết, đậu que với <u><strong><a href="https://www.cooky.vn/cong-thuc/canh-rong-bien-dau-hu-chay-19042" target="_blank">đậu hũ</a></strong></u> chiên mềm bùi cực hấp dẫn. Thực đơn của những người ăn chay sẽ thêm phần phong phú và nhiều màu sắc hơn khi có món kho chay thập cẩm này đấy. Chị nội trợ nhanh tay lưu lại ngay cách làm kho chay thập cẩm vào sổ tay các món chay tuyệt ngon để dành chế biến nhé.</p>', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (597, 'Thịt kho tàu', '<p>Món Thịt Kho Tàu (thịt kho trứng) là món ăn truyền thống, phổ biển trong những <a href="https://www.cooky.vn/bo-suu-tap/mam-com-tet-thuong-co-gi-322391" target="_blank">mâm cơm Tết người Việt</a>. Cách làm món thịt kho tàu đơn giản, thịt kho chín mềm, trứng luộc ngấm vị đậm đà. Cùng Cooky tham khảo món ăn ngày Tết - thịt kho tàu cho mâm cơm những ngày đầu năm nhé. Chưa có món thịt kho tàu, là chưa thấy Tết đâu đấy.</p>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (981, 'Bò Bít Tết', '<p>Bò bít tết là một món ăn đến từ nước Pháp rất được nhiều bạn trẻ yêu thích. Đây cũng là món ăn đắt tiền trong các nhà hàng sang trọng đấy. Thịt bò được chế biến mềm mịn, ngọt từ bên trong với chút gia vị tẩm ướp đậm đà, ăn kèm salad tươi ngon, khoai tây chiên giòn tan và nước sốt hấp dẫn đến khó cưỡng. Cách làm bò bít tết này sẽ mang đến một dĩa bò bít tết ngon lành, hấp dẫn không kém gì hương vị trong các nhà hàng đắt đỏ đâu nhé. Cùng lưu lại ngay cách làm bò bít tết ngon này vào sổ tay nấu nướng nha.</p>', 1, '2022-03-22 00:00:00', 55, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (1765, 'Gà Chiên Hàn Quốc', '<p>Một món cánh gà chiên với vị chua, cay, mặn, ngọt đặc trưng của sứ xở Kim Chi. Bạn hãy chọn món này cho bữa cơm gia đình hôm nay nhé! Đảm bảo cả nhà sẽ chén hết cơm cho mà xem.</p>', 2, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (2261, 'Bún thịt ba chỉ nướng', '<p>Cuối tuần đãi cả nhà tô bún thịt nướng thơm ngát ngon hơn ra hàng gấp nhiều lần, lại sạch sẽ, đảm bảo vệ sinh. Nhà bạn đã được thưởng thức chưa nào?</p>', 3, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (2572, 'Khô Bò Xé Sợi', '<p>Khô bò xé sợi mà được vắt thêm chút chanh, ăn vặt hay làm <a href="https://www.cooky.vn/bo-suu-tap/mon-ngon-nham-nhi-tren-ban-nhau-185722" target="_blank"><strong>mồi nhậu</strong></a> thì ngon hết xảy bà bảy rồi. Ngặt nỗi đi mua thì toàn thấy tin khô bò bẩn. Thôi thì các mẹ các chị cứ tham khảo cách làm khô bò xé sợi của em thành công 100% này nhé, tự làm tự ăn cho mạnh miệng các chị ạ. Cực công một chút mà bù lại ngon không chê vào đâu được. </p>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (2787, 'Thịt Bò Bắp Kho Thơm', '<p>Chỉ cần vài bước đơn giản từ hai nguyên liệu chính là thịt bò bắp và trái thơm, bạn đã có món kho ngon cho bữa cơm nhanh gọn, ấm cúng.</p>', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (3143, 'Gỏi Rau Muống Tôm', '<p>Gỏi là món ăn được rất nhiều người ưa thích bởi vị chua ngọt hài hòa, dễ ăn. Rau muống giòn giòn kết hợp với vị chua, ngọt hài hòa và thịt tôm ăn không ngấy. Cùng làm nhé!</p>', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (3210, 'Thịt nướng ngũ vị thơm lừng', 'Thịt nướng ngũ vị thơm lừng là món mặn đơn giản, chế biến nhanh gọn nhưng hương vị lại đậm đà. Miếng thịt chín béo thơm, hấp dẫn. Thích hợp cho mọi bữa cơm của gia đình bạn.', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (3390, 'Tôm xào rau củ chua ngọt', '<p><a href="https://www.cooky.vn/cong-thuc/tom-xao-rau-cu-maggi-47731/"><span style="color:#B22222;">Món tôm xào rau củ</span></a> chua ngọt với các loại rau củ không chỉ tạo nên màu sắc bắt mắt, mùi vị lại còn rất hấp dẫn đưa cơm. Nếu còn loay hoay chưa biết nấu gì với những con tôm tươi ngon nhà bạn thì đây chính là một gợi ý tuyệt vời nhé!</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (3494, 'Chả Giò Tôm Gà', 'Chả giò (nem rán) là món ăn rất quen thuộc với nhiều người tuy nhiên để giữ được độ giòn sau khi rán thì cần có một bí quyết nho nhỏ.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (4134, 'Đậu Hũ Sốt Nấm Hương', '<p>Đậu phụ sốt cùng với thịt bò hoặc thịt lợn băm là món ăn quen thuộc của mỗi gia đình. Tuy nhiên, chỉ với một thay đổi nhỏ là nấm hương, bạn đã chế biến ra được một món ăn chay thanh đạm nhưng không kém phần hấp dẫn.</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (4436, 'Bánh flan trà xanh', '<p>Với <a href="https://www.cooky.vn/cong-thuc/banh-flan-caramel-ngot-min-7848">cách làm bánh flan</a> trà xanh này, bạn vừa có bánh ngon để ăn lại vừa có thể "chăm sóc" sắc đẹp một cách hiệu quả bởi công dụng chống lão hóa của trà xanh.</p>', 2, '2022-03-22 00:00:00', 35, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (4437, 'Chả Giò Bắp', '<p>Chả Giò Bắp là một <strong><a href="https://www.cooky.vn/bo-suu-tap/lam-mon-an-choi-voi-bap-631416" target="_blank">món ngon được làm từ bắp</a></strong>, có thể ăn chơi hoặc ăn chay đều ngon. Cách nấu chả giò bắp với nhân làm từ hạt bắp nếp và bắp mỹ được bào nhỏ, trộn với nấm mèo và tàu hũ ky non cắt nhỏ. Được cuộn lại vừa tay và chiên giòn lên trong thật hấp dẫn. Hãy lưu lại cách làm chả giò bắp ngon lành này, rảnh rỗi làm mời cả nhà nhé!</p>', 4, '2022-03-22 00:00:00', 55, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (5290, 'Lạp xưởng tươi', '<p><span style="font-family: Arial; white-space: pre-wrap; text-decoration-skip-ink: none;">Lạp xưởng tươi</span><span style="font-family: Arial; white-space: pre-wrap; text-decoration-skip-ink: none;"> có thể làm ngay tại nhà với công thức không mấy khó sẽ khiến chị em bất ngờ đấy. Trong </span><span style="font-family: Arial; white-space: pre-wrap; text-decoration-skip-ink: none;">cách làm lạp xưởng tại nhà</span><span style="font-family: Arial; white-space: pre-wrap; text-decoration-skip-ink: none;">, công đoạn khó nhất chính là nhồi nhân. Với </span><span style="font-family: Arial; white-space: pre-wrap; text-decoration-skip-ink: none;">cách làm lạp xưởng tươi </span><span style="font-family: Arial; white-space: pre-wrap; text-decoration-skip-ink: none;">này sẽ giúp bạn dễ thực hiện hơn đấy. Gần đến Tết, chị em có thể làm ngay vài mẻ</span><span style="font-family: Arial; white-space: pre-wrap; text-decoration-skip-ink: none;"> lạp xương tươi</span><span style="font-family: Arial; white-space: pre-wrap; text-decoration-skip-ink: none;"> để có dịp dùng dần nha.</span></p>', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (6011, 'Bánh muffin cherry siêu ngon', '<div><div data-google-query-id="CP3wyoWJ-NkCFUJLKwodblkMYA" id="div-gpt-ad-1513236594464-0"><div id="google_ads_iframe_/280985523/AdAsia_Outstream_0__container__">Bánh muffin cherry hấp dẫn có độ ngọt vừa phải, cỡ bánh cũng nho nhỏ vừa vừa, không to quá nên ăn không bị ngán. Làm muffin cherry theo cách dưới đây để  lấy lòng cả những người không yêu thích bánh ngọt.</div></div></div>', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (6096, 'Bún Bò Kiểu Huế', '<p style="text-align: justify;">Ngày nay bún bò kiểu Huế là<strong> <a href="https://www.cooky.vn/cach-lam/m%C3%B3n-ngon-t%E1%BB%AB-b%C3%B2" target="_blank">món ngon từ bò</a></strong> đã và đang được mọi người yêu thích, <span style="text-align: justify;">có nhiều giá trị dinh dưỡng, và tốt cho sức khỏe</span>. <span style="text-align: justify;">Tuy nhiên, không phải ai cũng có thể làm món này chuẩn vị, giữ được hương vị đặc trưng của nó. </span>Chính vì thế mà mình đã làm nên công thức này để ai cũng nấu được món<strong> <a href="https://www.cooky.vn/cach-lam/b%C3%BAn-b%C3%B2" target="_blank">bún bò</a></strong>. Dưới đây là cách làm bún bò Huế<strong> </strong>ngon và mang đậm hương vị Huế ngay tại ngôi nhà của mình. Bạn không phải ra hàng quán mà cũng thưởng thức được.</p>', 6, '2022-03-22 00:00:00', 80, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (6183, 'Salad trái cây trộn chua ngọt', '<p>Trái cây vẫn luôn là trợ thủ đắc lực giúp bạn giảm cân hoặc làm đẹp. Tô trái cây trộn mát lạnh, hương vị của các loại trái cây hòa quyện tạo nên hương vị rất ngon. Rất đơn giản và dễ làm nhé!</p>', 2, '2022-03-22 00:00:00', 25, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (7423, 'Tôm Rang Muối Tiêu', '<p style="text-align: justify;">Cuối tuần chị em nội trợ hãy thử làm món tôm rang muối tiêu này xem sao nhé! Đảm bảo cả nhà sẽ tấm tắc khen ngon đấy. Thịt tôm ngọt, đậm đà của muối rất đưa cơm.</p>', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (7861, 'Trứng cút nướng rong biển', '<p>Chỉ với 3 bước cực kì đơn giản và mất chưa đến 15 phút, bạn đã có thể hoàn thành món trứng cút nướng nóng hổi, thơm phức ngon ơi là ngon. Trứng cút ngon béo cùng rong biển mằn mặn đặc trưng ăn một lần là nhớ mãi. Bạn có thể ăn trứng cút nướng kèm với tương ớt để thêm phần đậm đà, cay nhẹ nhé!</p>', 4, '2022-03-22 00:00:00', 15, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (8063, 'Bánh Flan Đậu Hũ Non', '<p><a href="https://www.cooky.vn/cong-thuc/banh-flan-caramel-ngot-min-7848">Bánh flan</a> là món ngọt "vạn người mê", vị béo của trứng hòa cùng hương thơm của đường và cà phê, ăn hoài không ngán. Nhưng cũng vì thế mà nhiều người sợ ăn nhiều sẽ gây béo, nóng. Bạn hoàn toàn có thể biến tấu công thức bánh flan thông thường bằng một phần đậu hũ.</p>', 2, '2022-03-22 00:00:00', 35, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (8192, 'Tép rim nước cốt dừa', '<p>Tép rim nước cốt dừa là món ăn tạo nên nét đặc trưng của cư dân miền sông nước, đặc biệt phổ biến ở xứ dừa Bến Tre. Với cách làm đơn giản, nguyên liệu dễ tìm, nhưng hương vị mang lại thì trên cả tuyệt vời đã làm cho món ăn này đi theo tâm trí biết bao người con miền Tây xa quê. Cùng theo dõi cách làm món Tép rim nước cốt dừa dân dã thôn quê này nhé!</p>', 1, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (8200, 'Bánh khoai lang tím bọc phô mai chiên', '<p>Khoai lang mềm mềm, giòn giòn kết hợp với nhân phô mai beo béo, tan chảy bên trong khiến cả nhà thích mê. Thay vì chế biến theo cách luộc, nấu canh, chè, kem... thì bạn có thể áp dụng theo phương pháp này để đổi mới món ăn nhé! Sẵn sàng vào bếp cùng Cooky chưa nào.</p>', 3, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (8759, 'Cá nục kho cà chua và dứa', '<p>Thịt cá chiên vàng thơm, ngon được sốt cùng cà chua và dứa (Thơm), tạo vị ngọt ngọt, chua chua sẽ là món ăn mặn rất đưa cơm đấy! Thay vì suốt ngày ăn thịt, bạn hãy làm mới bữa cơm gia đình bằng món ăn này nhé.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (9582, 'Mực Xào Sốt Cay', '<p>Từng miếng mực tươi ngon, trắng ngần với lớp nước sốt óng ả, sền sệt, vừa nhìn là đã muốn ăn ngay! Món mực xào cay thơm ngon tròn vị, vừa ăn vừa hít hà, xuýt xoa thật mê li, nhất là khi ăn cùng cơm nóng dẻo thơm, bao nhiêu cũng hết veo! Chắc chắn mực xào cay sẽ trở thành món ngon mà ăn rồi là cả nhà lại “mè nheo” để bạn vào bếp trổ tài thêm lần nữa đấy!</p>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (9660, 'Nấm Kim Châm Hấp Nước Tương', '<p>Món nấm hấp nước tương làm rất nhanh, không đòi hỏi nhiều thao tác chế biến nhưng lại vô cùng ấn tượng, đậm đà, giòn giòn. Món ăn này rất thích hợp cho những ngày trời mát nhé!</p>', 2, '2022-03-22 00:00:00', 25, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (11146, 'Bánh cuốn bằng chảo chống dính', '<p style="text-align:justify">Bánh Cuốn Nóng - <strong><a href="https://www.cooky.vn/bo-suu-tap/banh-cuon-goi-cuon-81600" target="_blank">món ngon dễ làm</a></strong>, cực hấp dẫn cho bữa sáng gia đình. Cách làm bánh cuốn bằng chảo chống dính đơn giản, vỏ bánh cuốn dai, nhân nấm, thịt băm xào đậm vị, béo bùi, thật khó cưỡng lại. Tự làm bánh cuốn tại nhà không khó. Để ăn bánh cuốn nóng được ngon hơn thì bên cạnh chén nước mắm chua cay mặn ngon thì bạn cần chuẩn bị đầy đủ các loại rau thơm ưa thích để chống ngấy nữa nhé.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (11626, 'Mắm Ruốc Chấm Xoài Cóc', '<p><strong>Mắm ruốc chấm xoài</strong> cóc là món chấm ngon mà rất nhiều người tò mò. Đây là loại mắm ruốc thường thấy trên các xe đẩy cóc xoài ổi dọc vỉa hè một thời làm mình mê mẩn. Cái tên thân thương là mắm ruốc xào, mằn mặn ngọt ngọt lại cay cay, chấm với xoài cóc ổi thì không gì sánh bằng. Rảnh rỗi chị em nhớ thử cách làm <a href="https://www.cooky.vn/cong-thuc/mam-ruoc-cham-xoai-coc-11626"><strong>mắm ruốc chấm xoài</strong></a> ngon khó cưỡng này nhé.</p>', 2, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (14657, 'Trà trái cây thập cẩm', '<p>Trái cây được chọn để pha trà này phải là những trái cây tươi. Dứa (Thơm), táo, dâu tây, thêm túi trà lọc... là bạn đã có thể tự tay pha cho mọi người 1 ly trà trái cây để thưởng thức rồi. Rất ngon mà lại tốt cho sức khỏe nữa đấy!</p>', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (14731, 'Nghêu Hấp Sả Ớt Cay', '<p>Thay vì ra ngoài nhà hàng ăn vừa đắt vừa không hợp vệ sinh thì hôm nay, bạn hãy đi chợ mua nghêu về tự chế biến nhé! Món <a href="https://www.cooky.vn/cong-thuc/ngheu-hap-sa-ot-558">nghêu hấp sả</a> ớt cay này không quá khó đâu.</p>', 2, '2022-03-22 00:00:00', 35, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (14954, 'Đậu Hũ Sốt Tứ Xuyên', 'Đậu hũ sốt Tứ Xuyên là 1 trong 10 món ngon nổi danh của Trung Quốc. Ai thử một lần cũng phải nhớ! Với đậu hũ trắng mềm với nước sốt cay cay thật hấp dẫn.', 2, '2022-03-22 00:00:00', 35, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (15963, 'Mứt Hoa Atiso Đỏ', '<p>Hoa atiso đỏ hay còn gọi là hồng hoa có tác dụng giải độc, mát gan, chứa nhiều vitamin. Đặc biệt món mứt atiso ăn ngọt giòn chua thanh rất hấp dẫn.</p>', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (16626, 'Bắp Xào Trứng Vịt Muối', '<p><a href="https://www.cooky.vn/cong-thuc/bap-xao-tep-kho-20881">Bắp xào</a> trứng muối phiên bản mới toanh so với bắp xào ruốc, thêm lòng đỏ trứng muối bùi bùi, một chút vị nhấn nhá điểm xuyết mà nói về vị thì tuyệt vời hơn cả mong đợi. Nguyên liệu và cách thực hiện thì quá đơn giản.</p>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (16742, 'Trứng chiên thịt bằm', 'Trứng chiên thịt bằm đơn giản, ngon miệng, thích hợp với những lúc không có nhiều thời gian chế biến. Món này chắc hẳn bé và cả nhà thích lắm đây. Thêm ngay vào sổ tay nội trợ nhé!', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (17005, 'Tôm rim tỏi', '<p style="text-align:justify">Tôm Rim Tỏi là <strong><a href="https://www.cooky.vn/bo-suu-tap/nhung-mon-rim-bat-com-an-dan-trong-ngay-tet-346919" target="_blank"><span style="color:#B22222">món rim bắt cơm</span></a></strong>, giúp đổi vị và chống ngán cho bữa ăn của gia đình. Cách làm tôm rim kết hợp vị tỏi thơm lừng cùng tôm tươi ngọt thịt, thấm đều gia vị cay mặn, đậm đà. Nếu chưa biết làm tôm rim sao cho ngon thì Cooky sẽ mách bạn cách làm tôm rim ngon, đơn giản cho ngày bận rộn, đảm bảo thu hút cả nhà, giúp bữa cơm thêm ngon miệng.</p>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (17491, 'Đậu hũ non om nấm hương', 'Nấm hương không chỉ thơm ngọt mà còn rất bổ dưỡng với nhiều vitamin và khoáng chất. Đặc biệt, bạn có thể dễ dàng kết hợp, chế biến nấm hương cùng nhiều nguyên liệu khác để mang đến những món ngon bổ dưỡng cho cả nhà thưởng thức đấy.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (18496, 'Bánh Bông Lan Cuộn Chà Bông Trứng Muối', '<p style="text-align: justify;">Bánh bông lan cuộn chà bông trứng muối, lạp xưởng mềm xốp, thơm ngon, dùng ăn chơi hay ăn sáng đều rất tuyệt vời. Thay vì ăn cả ổ bánh nhưng chỉ có một ít sốt và trứng muối ở topping thì với <strong><a href="https://www.cooky.vn/bo-suu-tap/bo-su-tap-cac-loai-banh-bong-lan-xop-nhe-tan-chay-267160" target="_blank">cách làm bánh bông lan</a></strong> cuộn chà bông trứng muối tại nhà này, bạn có thể tha hồ ăn trọn vẹn sốt, chà bông, lạp xưởng và trứng muối hết sức ngon lành chỉ trong một khoanh bánh nướng nhé.</p>', 1, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (18828, 'Nem chua bò ngày Tết', 'Món nem chua bò vừa thơm ngon lại có chút giòn giòn, ăn cùng tương ớt cay cay thật là tuyệt. Học cách làm món ăn này dành tặng ông xã nhậu lai rai cùng bạn bè ngày Tết đi nào.', 3, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (18831, 'Mứt Dừa Viên', '<p style="text-align:justify">Mứt Dừa Viên là món mứt mới lạ, đổi mới khẩu vị cho <strong><a href="https://www.cooky.vn/bo-suu-tap/tu-lam-mut-dua-nhieu-huong-vi-chuan-bi-cho-tet-den-gan-322125" target="_blank"><span style="color:#B22222">món mứt dừa</span></a></strong> sợi truyền thống ngày Tết. Chỉ cần chút sáng tạo trong cách làm mứt dừa viên, và kết hợp của nhiều màu sắc tạo cảm giác vừa ngon, vừa bắt mắt để đãi khách. Đặc biệt, với cách làm mứt dừa viên này, vị ngọt sẽ không quá gắt, không bị kết tinh đường, màu sắc lại từ rau củ an toàn nữa nhé.</p>', 2, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (18838, 'Trứng vịt bắc thảo', '<p style="text-align:justify">Trứng Vịt Bắc Thảo là <strong><a href="https://www.cooky.vn/bo-suu-tap/them-gung-cho-mon-ngon-bo-duong-dam-vi-140489" target="_blank">món ngon bổ dưỡng</a></strong>, có nguồn gốc từ Trung Hoa. Thay vì mua trứng bắc thảo ngoài chợ thì bạn có thể tự làm hột vịt bắc thảo tại nhà bằng cách: Ngâm trứng vịt trong hỗn hợp bùn nhão được làm từ đất sét pha kiềm và nước, hoặc có thể dùng tro gỗ, vôi tôi, muối, nhằm duy trì thời gian ủ trứng lâu nhất.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (18935, 'Bạch Tuộc Ngâm Sả Tắc Chua Cay', 'Nếu như món chân gà ngâm sả tắc đã từng làm mưa, làm gió một thời thì giờ đây món bạch tuộc ngâm sả tắc cũng HOT không kém. Cách làm, nguyên liệu cũng tương tự nhau nhưng điểm nổi trội, hấp dẫn của món ăn này chính là phần nước sốt ăn cùng. Từng con bạch tuộc sau khi hấp xong mềm, dai dai, không có vị tanh nhờ có gừng. Sả, tắc, ớt kết hợp làm cho món ăn trở nên đẹp mắt, thu hút.', 4, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (19443, 'Bánh Tráng Mắm Ruốc Bằng Chảo Chống Dính', 'Bánh tráng mắm ruốc lại có thể dễ dàng làm dễ dàng trên chảo chống chính, bánh khi ăn giòn, mềm kết hợp với vị thơm của bơ, của trứng rồi thịt bằm và nhất là mắm ruốc ngon lành. Món bánh tráng mắm ruốc với những nguyên liệu đơn giản sẽ giúp bạn có món ăn vặt xế chiều chiêu đãi mọi người.', 5, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (20032, 'Cá Nục Kho Nước Dừa Sốt Cà Chua', 'Cá nục khi kho bằng nước dừa giúp cho xương cá mềm rục, thịt cá ngọt và thơm ngon hơn. Sốt cà chua giúp giảm béo dầu mỡ và kích thích vị giác. Đây cũng là món ăn khá phổ biến của người miền Nam Trung bộ và được chế biến với ba nguyên liệu chính là cá nục, cà chua và nước dừa.', 5, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (20133, 'Chè Khúc Bạch Hai Màu', '<p><strong>Cách nấu chè khúc bạch</strong> cực ngon với miếng khúc bạch béo ngậy, hạnh nhân bùi bùi ăn kèm miếng cùi nhãn giòn thật khó cưỡng. Cùng trổ tài làm món chè khúc bạch hai màu thơm mát béo ngậy giải nhiệt cho gia đình trong những ngày hè nóng bức theo công thức mình giới thiệu dưới đây nhé.</p>', 4, '2022-03-22 00:00:00', 150, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (20427, 'Cách làm Đậu hũ Tứ Xuyên ', 'Đậu hũ Tứ Xuyên có tên gốc là Mapo tòfu hoặc Đậu hũ ma bà, là một trong những món ăn nổi tiếng của tỉnh Tứ Xuyên. Món ăn có vị cay nóng đặc trưng rất kích thích vị giác, ai ăn qua một lần đều thấy nhớ hoài. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (22435, 'Bò sốt me', '<p style="text-align:justify">Bò sốt me là <strong><a href="https://www.cooky.vn/bo-suu-tap/cac-mon-an-bo-sung-canxi-385640" target="_blank">món ngon bổ dưỡng</a></strong>, cực thơm ngon, thích hợp cho bữa tiệc sum họp, liên hoan cùng gia đình, bạn bè. Những lát thịt bò sốt me vừa chín tới, vẫn giữ dược độ mềm, ăn kèm với bánh mì rất là ngon. Điểm đặc biệt nhất cách làm bò sốt me này là hương vị chua thanh hòa trộn với các loại rau nấm, thơm bơ mà vẫn giữ được độ giòn ngọt tự nhiên.</p>', 4, '2022-03-22 00:00:00', 10, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (23049, 'Bánh gối nhân thịt', '<p style="text-align:justify">Bánh gối nhân thịt chiên (Bánh xếp chiên, bánh quai vạc) có lớp vỏ vàng ươm, giòn rụm, bao lấy phần nhân thịt nấm thơm phức, chấm với tương ớt hay là ăn với nước mắm dưa góp, rau sống đều ngon. Cùng Cooky tham khảo ngay cách làm bánh gối nhân thịt thơm ngon, đơn giản tại nhà ngay sau đây, để thay đổi khẩu vị cho <a href="https://www.cooky.vn/bo-suu-tap/bua-sang-280707" target="_blank"><span style="color:#B22222"><strong>bữa sáng</strong></span></a> nhé.</p>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (23068, 'Bì cuốn bánh tráng', '<p>Bì cuốn là món ăn rất thích họp trong những dịp cuối tuần cùng đông đủ cách thành viên gai đình. 1 cây bì cuốn đơn giản nhưng đầy đủ dinh dưỡng từ rau, thịt, bánh tráng. Đặc biệt không thể thiếu đi vai trò quan trọng của nước chấm chua ngọt hấp dẫn.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (23079, 'Thịt Bò Sốt Vang Béo Thơm', '<p><a href="https://www.cooky.vn/cong-thuc/bo-xot-vang-22618">Thịt bò sốt vang</a> béo thơm với thịt bò thăn nhiều nạc, cắt thành những viên nhỏ, dễ dàng ngấm đều gia vị và hỗn hợp sốt rượu vang, tương cà, ... có thể chấm ăn kèm với bánh mì nướng rất ngon.</p>', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (23593, 'Cháo Hải Sản', 'Món cháo hải sản có màu sắc vô cùng hấp dẫn. Cháo nấu theo kiểu này có độ đặc vừa phải, bí kíp là nhờ cách dùng cơm đang lạnh để nấu thay vì dùng gạo như truyền thống. Kèm với đó là vị ngọt tự nhiên của tôm và ghẹ tươi tiết ra làm cho món ăn thêm đậm đà, hấp dẫn mọi người.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (23821, 'Thịt Bò Xào Lúc Lắc', '<p>Đĩa thịt bò xào lúc lắc nóng hổi, đầy màu sắc lại thơm nức mũi sẽ làm bữa cơm chiều thêm phần ngon miệng. Miếng bò thơm mềm ăn kèm với khoai tây và rau củ đậm đà hứa hẹn sẽ hao cơm phải biết. Thêm một chút nước sốt, bạn có thể thưởng thức món ăn với bánh mỳ cũng rất tuyệt!</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (23870, 'Bánh Phở Xào Gà', 'Món bánh phở xào gà là món ăn lý tưởng để khởi đầu ngày mới. Món ăn vừa có tinh bột, protein, lại đầy đủ các loại rau củ quả cung cấp chất xơ và vitamin cho cơ thể. Nguyên liệu dùng để chế biến món ăn lại rất phổ biến và cũng rất sẵn để trong tủ lạnh. Món ăn sau chế biến vô cùng đậm đà, từng sợi bánh phở dai mềm quyện với sốt thịt gà và rau củ ngọt ngậy ăn rất là ngon miệng.', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (24673, 'Trà vải tươi', 'Trà vải là thức uống thơm mát mà ai cũng ưa thích mỗi khi hè về. Thay vì pha trà vải theo cách thông thường, bạn hãy thử pha trà vải theo cách này nhé! Chắc chắn bạn sẽ cực kì hài lòng với ly trà vải vừa đẹp mắt lại vừa thơm ngon đấy! ', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (24777, 'Thịt Gà Hấp Nấm Hương', 'Thịt gà hấp nấm hương là một món ăn chế biến nhanh với nguyên liệu chủ đạo là gà và nấm hương. Bạn chỉ cần tẩm ướp gia vị theo cách thức trên và hấp chín là đã có một món tuyệt ngon cho bữa cơm gia đình mình.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (25119, 'Hủ Tiếu Bò Kho', '<p>Bò kho từ lâu đã là món ăn truyền thống của Việt Nam được nhà nhà, người người ưa thích! Chuẩn bị một nồi bò kho ngon đúng điệu cho cả nhà cùng thưởng thức vào bữa sáng hẳn là điều mà bà nội trợ nào cũng muốn làm. Với công thức này, bạn sẽ có được một tô hủ tiếu bò kho chuẩn ngon, hấp dẫn hệt như ngoài hàng, còn chờ gì mà không xắn tay vào bếp làm ngay món ăn hấp dẫn này?</p>', 4, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (26251, 'Bánh Pudding Trứng Sữa', '<p>Món bánh pudding trứng sữa là món bánh đơn giản mà ngon miệng. Món bánh thích hợp để làm món ăn sáng, đặc biệt với các bạn nhỏ từ 6 tháng tuổi trở lên bạn có thể thay sữa bột thông thường bằng sữa công thức để làm món bánh này cho bé ăn dặm sẽ rất tốt lắm đấy.</p>', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (27396, 'Bánh chưng', '<p style="text-align:justify"><strong>Bánh Chưng</strong> - <strong><a href="https://www.cooky.vn/bo-suu-tap/tong-hop-cac-mon-an-truyen-thong-khong-the-thieu-trong-dip-tet-65216" target="_blank">món ngon truyền thống</a></strong> không thể thiếu trong ngày Tết ở miền Bắc. <strong>Cách làm bánh chưng xanh</strong> đơn giản, có phần nếp bánh dẻo ngon, màu xanh nhạt đẹp mắt, nhân bánh chưng ngon với đậu xanh bùi, thịt ba chỉ béo ngậy, cực hấp dẫn. Để học <strong>cách gói bánh chưng</strong> đẹp mắt thì cần có những bí quyết từ khâu chọn lá dong đến ngâm nếp, đậu xanh... tất cả đều có trong hướng dẫn từ Cooky ngay sau đây nhé.</p>', 5, '2022-03-22 00:00:00', 480, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (27915, 'Súp Gà Bắp Bổ Dưỡng', '<p>Súp gà bắp bổ dưỡng với nhiều nguyên liệu quen thuộc như bắp hột, thịt ức gà, nấm hương, trứng, ... Món súp béo ngọt, ấm nóng thích hợp tẩm bổ cho người suy dinh dướng nhé!</p>', 5, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (28248, 'Cơm chiên tôm cay', '<p style="text-align: justify;">Vào những ngày quá chán với những món ăn thường ngày, hãy thử chiêu đãi bản thân món Cơm chiên tôm cay cực kì bắt mắt và hấp dẫn mà Cooky giới thiệu dưới đây. Cách làm chỉ với vài bước đơn giản, nguyên liệu thường là những thứ có sẵn trong tủ lạnh thôi mà thành phẩm mang lại không chê vào đâu được.</p>', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (28628, 'Bánh Yến Mạch Chuối', '<p style="text-align:justify">Bánh chuối yến mạch là loại bánh rất hot trong <strong><a href="https://www.cooky.vn/bo-suu-tap/diet-giam-can-450897" target="_blank"><span style="color:#B22222">thực đơn giảm cân</span></a></strong> cho nữ và những ai quan tâm đến thức ăn tốt cho sức khỏe. Miếng bánh chuối yến mạch thơm ngon giòn xốp mang hương vị rất riêng của loại ngũ cốc này. Thích hợp với thực đơn giảm cân, muốn giữ da giữ dáng nè. Cách làm bánh chuối yến mạch cũng chẳng hề khó nữa, rảnh rỗi làm thử nhé.</p><p style="text-align:justify"> </p>', 5, '2022-03-22 00:00:00', 15, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (28772, 'Củ kiệu ngâm chanh dây', '<p>Củ kiệu là món ăn kèm nhưng lại là "linh hồn" của mâm cỗ ngày Tết. Thông thường củ kiệu ngâm với giấm sẽ có vị chua hắc nhưng với cách dùng chanh dây này bạn sẽ cảm nhận vị ngọt và chua thanh nhẹ, giúp kích thích vị giác đồng thời hỗ trợ tiêu hóa. Mùa Tết năm nay đừng ngần ngại thử món củ kiệu ngâm chanh dây cho gia đình và người thân thưởng thức nhé.</p>', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (28830, 'Khổ qua muối chua ngọt', '<p>Khổ qua muối chua ngọt rất ngon miệng, vị giòn giòn chua ngọt giúp bữa cơm gia đình bạn trở nên bớt ngán, ngon miệng hơn bao giờ hết.</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (29184, 'Lẩu Mì Hàn Quốc', '<p>Đổi gió cuối tuần cùng công thức chế biến lẩu mì Hàn Quốc vừa ngon vừa rẻ mà ai cũng có thể nấu được ngay tại nhà chỉ với các nguyên liệu vô cùng đơn giản.</p>', 4, '2022-03-22 00:00:00', 35, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (29869, 'Nui Sốt Tôm', '<p style="text-align: justify;">Nếu bạn đã quá quen thuộc với món mì bò băm của Italy, Hãy thay đổi khẩu vị với nui sốt tôm rất đơn giản và nhanh gọn. Phần sốt cà chua, phô mai thơm lừng, mới lạ sẽ làm mới món nui của bạn đấy! Cùng chế biến món nui sốt tôm này nhé.</p>', 4, '2022-03-22 00:00:00', 25, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (29965, 'Chân Gà Xào Cà Ri', '<p style="text-align: justify;">Xin giới thiệu với các bạn một món ăn từ nguyên liệu chân gà, đó là chân gà xài cari, một món ăn vẫn còn rất mới. Chân gà không có thịt lại có gân giòn sần sật nên làm món nhắm hay món ăn vặt đều rất ngon. Thử đổi bữa cho cả nhà với chân gà xào cari siêu hấp dẫn này nhé!</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (30080, 'Miến xào Singapore', '<p style="text-align: justify;">Cuối tuần đổi món với miến xào Singapore vừa ngon vừa dễ làm nhé! Làm mới món miến dân dã, quen thuộc với cách làm miến xào Singapore, chắc hẳn sẽ mang đến bạn những trải nghiệm ẩm thực bất ngờ. Món miến xào kiểu Singapore này có màu sắc rất bắt mắt, lại thêm nhiều rau ăn vừa giòn vừa ngọt. Cách làm miến cũng đơn giản mà lại dinh dưỡng. Thích hợp cho cả nhà đổi bữa vào dịp cuối tuần đấy!</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (30784, 'Trà Dâu Bụp Giấm - Hibiscus Strawberry Tea', '<p style="text-align: justify;">Không cần tốn nhiều thời gian và công sức, bạn cũng sẽ có được những ly trà dâu mát lạnh để giải nhiệt. Với vị chua dịu và mùi thơm của bụp giấm sẽ làm cho món trà dâu của bạn thêm phần đặc biệt.</p>', 6, '2022-03-22 00:00:00', 15, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (32045, 'Gỏi Cuốn Kiểu Nhật', '<p>Gỏi cuốn kiểu Nhật thanh mát được chế biến khá khác do với các loại gỏi cuốn của Việt Nam vì đó là sự kết hợp giữa rau củ tươi với thịt bò mềm dai ngon và hấp dẫn. Bạn hãy thử thực hiện món này để thay đổi thực đơn bữa cơm gia đình xem nào.</p>', 10, '2022-03-22 00:00:00', 34, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (32073, 'Kem chanh cherry', '<p style="text-align: justify;">Bạn đã từng thưởng thức món Kem chanh cherry bao giờ chưa? Đây là một loại kem rất thơm ngon, được nhiều người yêu thích đặc biệt là các bạn gái. Cách làm kem chanh cherry cũng không hề khó lắm, nguyên liệu để làm kem cũng rất dễ tìm. Cùng vào bếp để làm món kem ngon tuyệt cú mèo này nhé.</p>', 8, '2022-03-22 00:00:00', 15, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (32217, 'Bánh trứng cherry - Cherry custard', '<p style="text-align: justify;">Bánh trứng vốn đã hấp dẫn nhưng sẽ còn quyến rũ hơn nếu bạn thêm trái cherry thơm ngon, hứa hẹn sẽ mang đến cảm giác mới cực kỳ mới lạ và thư giãn. Vậy còn chần chờ gì nữa mà bạn không bắt tay vào làm và tận hưởng vị thơm ngon của món ăn này!</p>', 4, '2022-03-22 00:00:00', 35, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (32226, 'Trứng trà Trung Hoa', '<p style="text-align: justify;">Ở Trung Quốc, người ta dùng trứng ngâm trà như một món ăn nhẹ, đôi khi họ cũng dùng với cơm hoặc cháo. Trứng ngâm trà được bán tại các quầy hàng rong hoặc các cửa hàng lớn. Họ ăn quanh năm, nhưng nhiều nhất là những ngày Tết Nguyên đán bởi họ coi đây  là một món ăn mang lại sự thịnh vượng, may mắn.</p>', 5, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (32763, 'Salad Cơm Gạo Lứt', '<p style="text-align: justify;">Salad cơm gạo lứt là món ăn được chế biến với nhiều màu sắc trông rất bắt mắt và ngon miệng. Đặc biệt đây là món ăn đầy đủ dưỡng chất thích hợp cho sự phát triển của các bé nhà bạn.</p>', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (33169, 'Chân Gà Ngâm Sả Tắc Sa Tế Cay', '<p style="text-align: justify;">Chân gà ngâm sả tắc là món ăn vặt quá đỗi quen thuộc trong giới trẻ hiện nay, vị chua ngọt thơm của sả tắc giúp kích thích vị giác và mang lại cảm giác thèm ăn ngay từ cái nhìn đầu tiên. Chân gà ngâm giòn dai, thấm vị của nước ngâm. Rất phù hợp cho các bữa tụ tập vui chơi ăn uống cùng bạn bè và người thân cuối tuần, hoặc làm quà cũng khá thú vị nhé.</p>', 5, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (33870, 'Bánh cuốn Pad Thái', '<p style="text-align: justify;">Bánh tráng cuốn Pad Thái kiểu này vừa đẹp mắt lại vừa lạ miệng, cách làm thì không hề khó tí nào. Đây là món ăn lành tính, thanh lọc cơ thể, không chứa nhiều dầu mỡ, cực kì thích hợp để thưởng thức vào những ngày hè oi nóng đấy. Ngoài ra, nếu bạn đã có ý định giảm cân thì món ăn này cũng là một gợi ý không tồi phải không?</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (33921, 'Pate gan gà béo', '<p>Pate gan gà béo cách làm đơn giản. Pate mịn béo, thơm ngon. Có thể dùng phết lên bánh mì nướng ăn ngay hoặc phết vào trong lòng ở bánh mì, dồi thêm nhân bánh ưa thích vào là được. Nguồn: Bếp Nhà Chim - <a href="https://bepnhachim.wordpress.com/2014/01/28/pa-te-gan-ga/" target="_blank" rel="noopener noreferrer" data-auth="NotApplicable" data-linkindex="0" segoe="" ui="" web="" west="" european="" -apple-system="" blinkmacsystemfont="" roboto="" helvetica="" neue="" sans-serif="" vertical-align:="" baseline="">https://bepnhachim.wordpress.com/2014/01/28/pa-te-gan-ga/</a></p>', 5, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (34615, 'Cơm chiên Kimchi Hàn Quốc', '<p style="text-align: justify;">Cơm chiên Kimchi - Nghe tên thì các bạn cũng đã biết món ăn này bắt nguồn từ đâu rồi đúng không? Với màu sắc bắt mắt, nổi bật, hương vị thơm ngon đặc trưng, món cơm chiên này dần được người Việt chúng ta yêu thích. Nào cùng tham khảo công thức sau để có một món cơm chuẩn vị nhé!</p>', 2, '2022-03-22 00:00:00', 25, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (34716, 'Hàu Nướng Sốt Tỏi', '<p>Hàu là thực phẩm được người dân trên toàn thế giới yêu thích bởi chứa rất nhiều kẽm, nhiều loại vitamin và các khoáng chất tốt cho sức khỏe. Ngoài món hàu nướng hành, hàu nướng phô mai, bạn có thể làm hàu sốt tỏi - ngon ngọt, đậm đà và khó quên!</p>', 3, '2022-03-22 00:00:00', 25, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (35181, 'Cà tím sấy giòn', '<p>Một buổi tối đẹp trời mình muốn ăn snack nhưng sợ béo. Thế là món cà tím sấy giòn này ra đời. Cà tím là món ăn dễ tìm ở tất cả mọi nơi, tinh bột trong cà tím dường như bằng 0 và chất sơ thì không cần bàn. Ăn snack thỏa thích mà không cần sợ béo thì còn gì tuyệt bằng.</p>', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (35360, 'Há Cảo Chiên Tôm Thịt', '<p>Há cảo với phần nhân gồm tôm và thịt hòa quyện cùng gia vị đậm đà được bọc bên ngoài là lớp vỏ hoành thánh đẹp mắt. Khi được chiên lên vỏ bánh giòn nóng và nhân mềm ngon cắn ngập một cái thật thích phải không nào. Còn chần chờ gì mà chúng ta không thử ngay cách làm há cảo chiên tôm thịt cực dễ này nào.</p>', 4, '2022-03-22 00:00:00', 18, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (35480, 'Ba chỉ rim tôm khô', '<p>Thịt ba chỉ rim tôm khô là <a href="http://cooky.vn/bo-suu-tap/50-cong-thuc-thit-ba-chi-cho-com-nha-them-sung-tuc-589551" target="_blank">món ngon từ thịt ba chỉ</a> và tôm khô, đặc trưng hương vị của món Việt. Tôm khô rang thịt với vị béo ngậy của thịt ba chỉ kết hợp với vị ngọt thơm của tôm khô tạo nên món ăn hấp dẫn khó cưỡng. Cách chế biến tôm khô trong thịt kho tôm khô này, sẽ làm nổi bật hương vị món ăn, không hề tanh đâu, bắt tay làm ngay nhé!</p>', 4, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (35570, 'Bánh Mochi Khoai Tím', '<p>Bánh mochj khoai tím hấp dẫn người ăn với sắc tím đẹp mắt, lớp dừa bao phủ bên ngoài vừa nhìn đã thích mắt không thôi. Không những vậy, bánh còn có hương vị thơm ngon, dẻo ngọt quyện cùng phần nhân mứt đậu đỏ bùi bùi mà chỉ cần thử một miếng đã lưu luyến không thôi rồi đó. Bạn hãy thử làm ngay món này cho cả nhà ăn nhé.</p>', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (36174, 'Bánh quẩy giòn tan', '<p>Bánh quẩy giòn tan là món bánh quen thuộc đối với nhiều người. Bánh có thể cắt nhỏ ăn kèm cùng cháo hoặc bánh canh, hay các món bún nước đều tuyệt. Để làm được bánh quẩy thì đòi hỏi bạn phải nắm một chút mẹo nhỏ nhé!</p>', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (37837, 'Rong biển cuộn rau củ', '<p style="text-align: justify;">Rau biển là món ăn không còn xa lạ và được chế biến với nhiều nguyên liệu khác nhau, và món ăn này chủ yếu được cuộn cơm để làm món ăn no trong những buổi picnic gia đình. Tuy nhiên, sự biến tấu nhẹ nhàng dành cho những bạn không thích ăn cơm, hoặc các bạn ăn Eat Clean thì món này chắc chắn là sự lựa chọn hoàn hảo. Nguyên liệu rất tươi ngon lại chế biến rất đơn giản chắc chắn sẽ là công thức mới hay ho cho các nàng muốn có một hình thể đẹp.</p>', 4, '2022-03-22 00:00:00', 35, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (37990, 'Cocktail cherry Amaretto', '<p>Cocktail với vị cherry chua chua, ngọt ngọt hòa cùng rượu Amaretto thơm lừng cực ngon và hấp dẫn rất dễ làm nên bạn hãy thử làm ngay nhé. Công thức làm cocktail cherry Amaretto này dùng vào buổi tối sẽ rất tuyệt đấy.</p>', 2, '2022-03-22 00:00:00', 10, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (39881, 'Thịt băm bọc trứng cút sốt cà chua phô mai', '<p style="text-align: justify;">Một đứa cuồng thịt như mình thì chắc chắn phải bổ sung món này ngay vào "tủ công thức" rồi. Ôi cắn một viên thịt tròn tròn, bên trong là trứng cút bùi bùi cộng thêm chút sốt cà chua phô mai béo ngậy làm con tim một đứa thích ăn thịt như mình cứ thổn thức mãi thôi. Chia sẻ ngay công thức cho mọi người nè.</p>', 2, '2022-03-22 00:00:00', 35, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (40146, 'Cá hồi măng tây sốt cam', '<p>Cá hồi là thực phẩm thơm ngon, bổ dưỡng rất được các bếp trưởng phương Tây ưa chuộng. Một trong những món ngon từ cá hồi được nhiều gia đình yêu thích là cá hồi măng tây sốt cam. Món ăn lạ miệng này chinh phục tín đồ ẩm thực nhờ cá hồi mềm ngọt đậm đà hương vị, vỏ ngoài được chiên vàng ăn kèm nước sốt cam thơm phức và độ giòn rụm của măng tây . Vị béo ngậy của cá hồi kết hợp với cái thanh mát, chua chua ngọt ngọt của cam tươi giúp món ăn hấp dẫn mà không ngấy, thích hợp với cả trẻ nhỏ và người lớn. Cùng tham khảo cách làm cá hồi sốt cam tươi đơn giản mà thơm ngon chẳng kém ăn ở nhà hàng hạng sang.</p>', 1, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (40453, 'Cà ri thái xanh tôm và củ hủ dừa', '<p>Cà ri thái xanh là một món ăn rất nổi tiếng của ẩm thực Thái Lan. Hôm nay Cooky chia sẻ với các bạn tự nấu tại nhà mà không cần sử dụng gói gia vị có sẵn nhưng mùi vị vẫn thơm ngon hấp dẫn, đặc biệt món cà ri thái xanh được nấu từ tôm và củ hủ dừa, một nguyên liệu rất ngon và quen thuộc với các gia đình miền Nam. Cùng theo dõi nhé!</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (40837, 'Chả ram tôm đất', '<p style="text-align: justify;">Chả ram tôm đất Bình Định là <strong><a href="https://www.cooky.vn/bo-suu-tap/mon-ngon-dac-trung-cua-mien-trung-114896" target="_blank">món ăn đặc sản miền Trung</a></strong>, rất nổi tiếng. Cách làm chả ram Bình Định ngon đúng chuẩn cần có bánh tráng phơi sương, tôm đất tươi rang cùng thịt ba chỉ,... ăn kèm rau sống cùng nước mắm đậu phộng thơm béo, thật đậm đà. Lưu ngay hướng dẫn cách làm chả ram tôm đơn giản từ Cooky để triển ngay cho cả nhà dùng thử nào.</p>', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (42258, 'Salad Nui Rau Củ', '<p style="text-align: justify;"><style type="text/css"><!--td {border: 1px solid #ccc;}br {mso-data-placement:same-cell;}--></style>Salad nui rau củ rất giàu rau củ và chất xơ, món này sẽ là món ăn tuyệt vời cho bạn đang muốn giảm cân đấy! Ngoài ra, món này có thể thay thế mì Ý hay nui xào bò, mùi vị vẫn thơm ngon, thỏa cơn thèm ăn vặt của bạn mà không lo tăng cân!</p>', 3, '2022-03-22 00:00:00', 35, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (43089, 'Sandwich thịt nguội và phô mai nướng giòn - Croque Monsieur', '<p style="text-align: justify;">Một món sandwich kẹp thịt và phô mai, trắng và mềm cực kỳ nhàm chán sẽ được biến tấu khi phần bánh được áp chảo giòn rụm. Không những thơm lừng mùi bơ mà còn có thêm 1 lớp sốt phô mai được nướng rám vàng xinh xắn.</p>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (43168, 'Ghẹ Nấu Cà Ri', '<p style="text-align: justify;">Ghẹ nấu cà ri với ghẹ rang chín đỏ au, sau đó nấu cùng gừng tỏi, hành tây, nước cốt dừa, bột cà ri và hỗn hợp gia vị cho vừa miệng. Món này thích hợp cho những bữa tiệc hải sản của gia đình.</p>', 1, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (43398, 'Galaxy-ade - Soda ngân hà', '<p>Để phá tan những buổi trưa nóng bức của Sài Gòn, bạn thường làm loại nước uống nào? Chắc chắn là đa phần các bạn chọn trà sữa hoặc nước ngọt đúng không? Nhưng với mình mình chọn nước chanh. Để thêm một chút màu sắc cho xinh xắn, mình biến ly nước chanh của mình thành cả dải ngân hà luôn đây. Nếu đang thắc mắc tên gọi của nó vì sao là galaxy-ade thì thật ra nãy giờ mình đã giải thích rồi đấy, đuôi -ade xuất phát từ chữ "Lemonade" (nước chanh). Và ly nước chanh của mình mang màu sắc của dãy ngân hà vì vậy nó mới có tên là Galaxy-ade đó, nước chanh ngân hà của mình xinh và dễ thương y như tên gọi của nó đúng không?</p>', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (44117, 'Sợi bánh canh bột lọc', '<p>Tự làm sợi bánh canh bột lọc tại nhà cực đơn giản cho chị em khéo tay đây nhé! Nguyên liệu đơn giản chỉ cần bột năng và nước nóng, kếp hợp theo tỉ lệ nhất định là được.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (44604, 'Tokbokki Hàn Quốc', '<p>Tokbokki hay còn gọi là bánh gạo cay là một món ăn truyền thống của Hàn Quốc. Với công thức dưới đây, chỉ 30 phút thôi là các bạn có thể thưởng thức món Tokbokki thơm ngon đặc biệt của người Hàn Quốc rồi đó.</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (44818, 'Sườn cây Thái Lan', '<p>Sườn heo ớt xanh hay sườn cây Thái Lan (Laeng Saeb) là món ăn không chỉ được người dân bản địa Thái Lan yêu thích mà còn “hớp hồn” khách thập phương. Điểm thu hút nằm ở sự hùng vĩ của "ngọn núi" với những phần sườn heo được luộc chín phủ đầy nước hầm xương ngọt thịt và ớt xanh bắt mắt. Cách làm cũng không quá khó, bạn có thể hoàn toàn làm được món ăn này với công thức từ Cooky. Cùng vào bếp làm thử ngay món ăn hấp dẫn này cho cả gia đình mình nhé!</p>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (45657, 'Chả Giò Nghêu Khoai Môn', '<p>Bạn đã từng nghe tên nhiều loại chả giò nhưng Chả giò nghêu khoai môn thì chưa hoặc hiếm khi nghe đúng không? Với cách làm kết hợp những nguyên tắc cơ bản, nhấn nhá thêm một chút thay đổi khác lạ, món chả giò nghêu khoai môn thật sự xuất sắc về hương vị khi kết hợp được cái bùi của khoai môn, độ dai thơm đặc trưng của thịt nghêu, và sự giòn rụm của vỏ bánh tráng bía. Món chả chấm cùng nước mắm mỡ hành thơm dậy, ăn thử một cuốn đảm bảo bạn sẽ nhớ mãi không quên.</p>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (46063, 'Mì Udon Xào Tôm Bông Cải', '<p>Mì udon xào tôm bông cải là một món ăn ngon mà mỗi khi nhắc đến Nhật Bản, bạn sẽ tự động biết đến udon ngay. Mì udon không chỉ làm món nước, bạn còn thể xào cùng thịt, rau và các nguyên liệu khác.Trong công thức mì udon xào tôm bông cải này, từng sợi mì dai mềm, thơm ngon hòa quyện cùng thịt tơm mềm ngọt, tươi ngon và bông cải giòn sựt, giàu vitamin thấm đều nước sốt gia vị đậm đà. Để có một dĩa mì udon xào dinh dưỡng cho cả nhà, bạn chỉ cần lưu lại ngay cách làm mì udon xào tôm bông cải này và vào bếp trổ tài nấu nướng thôi nè.</p>', 1, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (46262, 'Đậu hũ chiên xào nước tương', '<p style="text-align: justify;">Lâu lâu thử thay đổi khẩu vị cho bữa cơm gia đình với <strong><u><a href="https://www.cooky.vn/bo-suu-tap/cac-mon-dau-hu-ngon-bo-re-va-de-lam-66874" target="_blank">món đậu hũ</a></u></strong> chiên xào nước tương này thử xem sao nhé. Những miếng đậu hũ mỏng, vuông vức, chiên chín giòn béo, bùi bùi thơm ngon, ngấm đều gia vị nước tương đậm đà, cực hấp dẫn. Đậu hũ chiên xào nước tương là món ăn ngon thanh đạm, rất được ưa chuộng. Loai bỏ nguyên liệu tỏi, bạn có thể dùng làm <strong><u><a href="https://www.cooky.vn/bo-suu-tap/nhung-mon-an-chay-de-lam-68949" target="_blank">món ăn chay</a></u></strong> cho cả nhà được luôn đấy. Nguyên liệu món ăn dễ tìm và rẻ tiền, thích hợp cho những bạn lần đầu xuống bếp chuẩn bị bữa cơm ngon cho cả nhà.</p>', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (46426, 'Lẩu bò nhúng mắm ruốc', '<p>Lẩu bò mắm ruốc - <u><strong><a href="https://www.cooky.vn/bo-suu-tap/30-mon-lau-thich-hop-cho-party-tai-nha-khi-troi-chuyen-lanh-300403" target="_blank">món ngon đặc sản</a></strong></u> Bình Dương thơm ngon ngất ngây, thích hợp cho cả nhà thưởng thức vào những ngày cuối tuần quây quần cùng nhau. Hương vị mới lạ với sự kết hợp giữa vị ngọt của <u><strong><a href="https://www.cooky.vn/cong-thuc/lau-bo-sa-te-vianco-45786" target="_blank">thịt bò</a></strong></u> tươi, cùng với vị đậm đà đặc trưng riêng của mắm ruốc trong nước lẩu sẽ khiến cả nhà trầm trồ cho mà xem. Cách nấu lẩu bò mắm ruốc cũng khá đơn giản, chị em nội trợ có thể tự làm ngay tại nhà với công thức mà mình chia sẻ dưới đây nhé!</p>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (46960, 'Bánh Sữa Chua Xoài', '<p style="text-align: justify;">Bánh sữa chua xoài là món ăn ngon tráng miệng rất tuyệt vời. Không cần dùng lò nướng, chỉ với một chiếc máy xay thực phẩm hoạt động tốt, bạn có thể triển ngay món bánh sữa chua xoài thơm ngon khó cưỡng này cho cả gia đình dùng thử rồi nhé. Món bánh mát lạnh, vị chua ngọt, béo ngon chắc chắn khiến bạn hài lòng.</p>', 4, '2022-03-22 00:00:00', 35, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (47264, 'Gà ác tiềm thuốc Bắc', '<p>Gà Ác Tiềm Thuốc Bắc là một<strong><a href="https://www.cooky.vn/bo-suu-tap/mon-ngon-voi-thit-ga-601004" target="_blank"> món ngon từ gà</a></strong> được nhiều người biết đến với công dụng bồi bổ cho sức khỏe. Ngoài ra, gà ác có tính bình, vị ngọt giúp giải toả độc tố trong cơ thể, cho giấc ngủ ngon hơn, món gà ác tiềm thuốc bắc cho bà bầu cực kì tốt. Hãy cùng Cooky khám phá cách nấu gà ác tiềm thuốc bắc cho bà bầu ngon, đơn giản ngay tại nhà nhé!</p>', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (47438, 'Chân gà lắc sa tế tắc cóc non', '<p><strong>Chân gà lắc sa tế tắc cóc non</strong> là món ăn vặt mới lạ hấp dẫn, góp phần làm phong phú list các món ăn chơi ngon miệng với chân gà. Với sự kết hợp sa tế cay nồng cùng vị chua ngọt từ cóc non hay tắc, món ăn này sẽ là một biến tấu mới làm thoả mãn các tín đồ nghiện "chân gà". <strong><a href="https://www.cooky.vn/cong-thuc/chan-ga-lac-sa-te-tac-coc-non-47438/"><span style="color:#B22222;">Cách làm Chân gà lắc sa tế tắc cóc non</span></a></strong> đơn giản, nguyên liệu lại dễ tìm, bạn hãy cùng Cooky vào bếp và làm món này ngay thôi!</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (47657, 'Canh măng thịt xá xíu cực ngon', '<p>Canh măng thịt xá xíu mang hương vị vô cùng đậm đà. Giòn, ngọt của măng non vừa tới, đậm đà của thịt xá xíu sẽ mang lại sức khỏe và hạnh phúc cho bữa cơm gia đình. Nguyên liệu dễ tìm, hơn 10 phút vào bếp là chúng ta đã có món canh măng thịt xá xíu đậm vị để thưởng thức rồi. Nhanh chân vào bếp thôi giờ ăn sắp đến rồi !!</p>', 1, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (47745, 'Gỏi măng cụt miền tây chuẩn vị', '<p>Gỏi măng cụt là đặc sản của vùng miệt vườn Lái Thiêu. Với những quả măng cụt thơm ngọt, mọng nước, nước chấm ngon ngọt cùng thịt gà luộc thì món ăn đặc sản này khiến không ít người phải tìm đến miền Tây để thưởng thức món ăn đặc sản đó. Nhưng hãy vào bếp cùng mình nào vì đã có sẵn công thức ngon đúng, chuẩn vị rồi đây.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (48240, 'Chân Giò Hầm Kiểu Hàn Quốc', '<p style="text-align: justify;">Chân giò hầm kiểu Hàn Quốc là món ăn ngon dễ làm có thịt chân giò chín dai ngon, ngấm đều gia vị thật hấp dẫn. Để làm thịt chân giò heo hầm ngon và đậm đà cần tẩm ướp gia vị và dùng chỉ buộc chặt trước khi cho vào nồi hầm. Bên cạnh đó, cắt lát thịt chân giòn hầm chấm ăn cùng mắm tép đặc trưng hương vị xứ sở kim chi luôn nhé.</p>', 4, '2022-03-22 00:00:00', 65, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (48273, 'Rau muống xào chao', '<p>Rau muống xào chao là một món ngon dân dã với cách chế biến vô cùng đơn giản. Sự kết hợp giữa vị giòn ngọt của rau muống cùng chao trắng béo ngậy sẽ đem lại vị ngon, lạ và đầy thú vị cho bữa cơm gia đình. Cùng học làm món ăn thanh mát, ngon miệng với công thức đơn giản dưới đây nhé!</p>', 4, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (48404, 'Bún Riêu Ả Đào', '<p>Món bún riêu mang phong cách ẩm thực hiện đại, kết hợp giữa riêu cùng với trứng vịt lộn, thịt bò, chả cây như một nồi lẩu riêu cua thu nhỏ. Nước lèo thơm mùi riêu cua đồng cùng mắm tôm thoang thoảng, ăn tô bún khi còn nóng để cảm nhận hết mùi vị thơm ngon mà nó mang lại. Ngày trời mưa mà có tô bún riêu ngon lành như vầy thì tuyệt vời lắm đó nha.</p>', 3, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (49454, 'Gà rang muối sả', '<p style="text-align: justify;">Cùng Cooky vào bếp và thực hiện món gà rang muối sả thơm lừng, gọi là muối nhưng hỗn hợp này được làm từ gạo tẻ, đậu xanh và muối biển, được rang chín rồi xay thành hỗn hợp bột mịn, sau đó xóc đều với gà để tạo nên vị thơm bùi, mằn mặn rất hấp dẫn. Món ăn có lớp vỏ giòn tan, thơm bùi, thịt gà bên trong chín mềm, đậm đà, thích hợp cho bữa ăn hằng ngày và cả các dịp lễ, Tết, tiệc tùng bởi hương vị thơm ngon cùng hình thức bắt mắt. Gà rang muối tưởng chừng cầu kỳ, phức tạp nhưng bạn hoàn toàn có thể vào bếp thực hiện món ăn này thường xuyên để chiêu đãi cả nhà.</p>', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (49496, 'Bánh trung thu rau câu trà sữa trân châu', '<p>Bạn thích trà sữa trân châu, bạn thích không khí đầm ấm của đêm trung thu. Hãy cùng "pha trộn" sở thích của bạn với món bánh trung thu trà sữa trân châu siêu ngon, siêu độc đáo này nha.</p>', 2, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (49618, 'Mực Chiên Khổng Lồ', '<p>Cùng Cooky theo dõi cách làm món "Mực chiên khổng lồ", món ăn từng gây bão mạng xã hội trong giới trẻ. Từng con mực to được tẩm lớp bột chiên giòn mỏng, chiên trong dầu sôi ngập cả con nên có vỏ ngoài giòn, bên trong vẫn giữa được độ nước và ẩm, dai của thịt mực. Đã có công thức rồi, bạn còn chờ gì nữa mà không vào bếp ngay để trổ tài nấu nướng của mình nào!</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (49623, 'Mì Quảng tôm thịt trứng', '<p>*Mì quảng là một trong những món ăn đặc trưng của người Quảng Nam-Đà Nẵng. Lần đầu tiên được ăn mì quảng rất ngon cách đây đã lâu, nên cũng không còn nhớ là ở đâu? Và đôi lần vào vài tiệm mì quảng khác nhau để tìm lại hương vị mình đã từng ăn,nhưng vẫn không đúng khẩu vị... *Tìm hiểu qua nhiều cách nấu, rất đơn giản dễ làm. Biến tấu nguyên liệu,đậm đà hương vị theo khẩu vị nhà mình. Thế là có món mì quảng tại gia cho cả nhà cùng thưởng thức.</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (49729, 'Bánh trung thu Đài Loan khoai môn', '<p>Bánh trung thu Đài Loan khoai môn là món bánh hiện đại tuy hơi cầu kỳ nhưng thành phẩm chắc chắn sẽ khiến bạn bất ngờ vì hương vị thơm ngon mà nó đem lại đó. Lớp vỏ giòn ngàn lớp, bên trong là nhân khoai môn béo bùi hòa cùng quả trứng muối, thưởng thức cùng với chén trà, chắc chắn sẽ cho bạn và gia đình cái tết đoàn viên ấm áp.</p>', 5, '2022-03-22 00:00:00', 130, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (49807, 'Lẩu bò nhúng mẻ', '<p>Lẩu bò nhúng mẻ là một món ăn mới mẻ có hương vị chua thanh ngọt ngào rất bắt vị. Nếu cuối tuần này chưa có món gì cùng gia đình thưởng thức thì làm ngay món này theo công thức của mình nhé!</p>', 3, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (49853, 'Lẩu tôm càng', '<p>Nếu đã có lần du hí đến mảnh đất Biên Hòa thì hẳn là các bạn cũng đã tìm hiểu trước về món Lẩu tôm Năm Ri rồi có phải không? Tiếng lành đồn xa, mình đã được nếm thử nồi lẩu này cả tại Biên Hòa và chi nhánh tại Sài Gòn. Vốn là người mê nấu nướng, hiển nhiên là mình không thể bỏ qua món ngon này cho trang công thức cá nhân rồi!</p>', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (49991, 'Bánh Trung Thu hạt sen trứng muối ngon', '<p style="text-align:justify">Bánh Trung Thu Nhân Hạt Sen Trứng Muối đã trở nên quen thuộc mỗi khi thu mùa trăn tròn về. Tạm gác lại bánh trung thu nhân đậu thường nhật, hãy thử sức mình với nhân hạt sen béo bùi thơm hương thanh nhẹ. Vỏ bánh nướng mềm thơm đẹp mắt, nhân hạt sen mịn màng kết hơp với trứng muối mằn mặn mang lại cảm giác ngọt ngào và lưu giữ hoài trong miệng mỗi khi ăn. Nhâm nhi 1 tách trà, ăn một mẫu bánh và cảm nhận mùa thu Việt Nam thật ý nghĩa nhé!</p>', 6, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (50098, 'Đậu hũ chiên giòn sốt trứng muối chà bông', '<p>Món đậu hũ chiên giòn sốt trứng muối chà bông sẽ giúp bữa ăn được mới mẻ hơn thay vào những món quen thuộc như đậu hũ chiên, đậu hũ sốt cà chua,...Vị bùi bùi của trứng muối hoà quyện với lớp bột mỏng giòn giòn bên ngoài, cộng thêm cái mềm mịn của đậu hũ non bên trong, điểm thêm một chút mùi thơm của hành lá dậy cả một gian bếp. Tất cả được kết hợp với nhau trở thành một món ăn ngon không tưởng, các bạn hãy cùng Cooky thực hiện món ngon này nhé!</p><p> </p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (50103, 'Súp hoành thánh lá', '<p>Hoành thánh bắt nguồn từ người Hoa, du nhập và được biến tấu để phù hợp với khẩu vị người Việt nhưng vẫn giữ được nét đặc trưng. Tuy không có nhân nhưng món súp hoành thánh lá ăn kèm tôm, thịt xương ống, trứng cút, giá, huyết. Nghe cái tên đơn giản, nhưng để biết rõ hơn về công thức nấu món súp hoành thánh này thì hãy cùng Cooky vào bếp thử liền nhé!</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (50110, 'Hạnh nhân hữu cơ', '<p>Bơ hạnh nhân hữu cơ không còn quá xa lạ với những bạn đang theo đuổi phong cách ăn uống lành mạnh, là thực phẩm giúp cho quá trình Eat Clean của bạn dễ dàng hơn rất nhiều. Hầu hết bơ hạnh nhân đều được bán sẵn tại các cửa hàng thực phẩm, siêu thị nhưng có giá thành cao, có chất bảo quản hoặc có lượng đường không phù hợp cho những bạn đang muốn hoàn thiện quá trình ăn uống lành mạnh. Vì vậy, hôm nay Cooky sẽ hướng dân các bạn cách thực hiện ngay tại nhà để có một hủ bơ hạnh nhân hữu cơ vô cùng bổ dưỡng nhé!</p>', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (50128, 'Bánh trung thu thập cẩm chay', '<p>Bánh Trung Thu Thập Cẩm Chay là món bánh ngon, mời cả nhà quây quần bên nhau. Cách làm bánh trung thu thập cẩm đơn giản, đặc biệt là người ăn chay cũng có thể ăn được bánh thập cẩm, vừa ngon miệng lại hấp dẫn. Món bánh trung thu thập cẩm chay với mùi thơm đặc trưng của rượu mai quế lộ, kết hợp cùng vị ngọt chua nhẹ của các loại mứt, làm ngay mời cả nhà nhé.</p>', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (50341, 'Canh sườn rau củ', '<p>Món canh sườn heo hầm cùng những loại rau củ thơm ngon, hấp dẫn lại cung cấp được đầy đủ những dưỡng chất thiết yếu sẽ giúp cho bữa cơm của gia đình bạn ấm áp và hấp dẫn hơn rất nhiều. Với nồi Tchef Casserole của Tupperwware món canh sườn heo hầm rau củ sẽ trở nên đơn giản hơn bao giờ hết, bạn đã sẵn sàng để thử chưa?</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (50342, 'Tôm rim nước dừa', '<p>Tôm rim nước dừa là một món ăn vô cùng đơn giản với những nguyên liệu vô cùng thân quen. Chỉ với 20 phút bạn đã có thể làm ra một món ngon cho bữa cơm gia đình. Vị ngọt của tôm cùng vị nước dừa béo béo, thơm lừng sẽ khiến nồi cơm nhà bạn hết veo cho mà xem. Bạn hãy cùng thử nhé!</p>', 4, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (50343, 'Thịt bò xào đậu Hà Lan', '<p>Thịt bò xào đậu Hà Lan là một món ăn quen thuộc đối với mọi nhà nhưng với sự biến tấu, thêm vào một ít nguyên liệu và gia vị thân quen đã có thể biến món ăn này thành một phiên bản khác, thơm ngon hơn và đầy đủ chất dinh dưỡng hơn. Với sự hỗ trợ của chảo Tchef Fry Pan của Tupperwware sẽ giúp bạn rút ngắn thời gian nấu nướng nhưng vẫn giữ được vẹn nguyên hương vị của món ăn. </p>', 4, '2022-03-22 00:00:00', 15, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (50591, 'Gỏi măng trộn tôm thịt', '<p>Gỏi Măng Trộn Tôm Thịt là một món gỏi ngon, lạ miệng. Người miền Trung rất thích ăn măng và nhà mình cũng vậy. Măng ngâm này ngoài xào, nấu canh thì làm gỏi ăn chống ngán cũng là món ngon khó lòng bỏ qua. Món này mình kết hợp với tôm thịt để dễ ăn nhất. Nếu đang cần một món cho thực đơn khai vị thì lưu lại luôn nha</p>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (50592, 'Mì Tàu Hũ Xào Rau Củ', '<p>Dạo này phong trào Eat clean nở rộ lên như diều gặp gió, mình thì chỉ sử dụng thực đơn này cho những ngày ngán dầu mỡ, và những ngày mình mệt mỏi chán ăn. Được hôm vào siêu thị thì thấy loại mì sợi được làm từ đậu hũ này nên mua và làm luôn món này. Rau củ, đậu hũ, mì đậu hũ, đơn giản là xào với nước tương thôi nhưng lại ngon khỏi bàn luôn.</p>', 1, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (50719, 'Chè trôi nước nhân đậu xanh', '<p>Những ngày lễ tết là dịp để chị em trổ tài khéo tay. Món chè trôi nước hoa lá này vừa dễ lại vừa xinh xắn sẽ giúp các chị em ghi điểm với người thân bạn bè. Đặc biệt, ngày Tết Nguyên Đán đến rồi, hãy thực hiện ngay để dâng lên ông bà tổ tiên sẽ rất ý nghĩa đấy bạn nhé!</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (50720, 'Canh sườn non cải chua Tết', '<p>Món canh cải chua có sườn non được hầm mềm bổ dưỡng, vừa có công dụng bồi bổ cơ thể vừa giúp cho bữa cơm gia đình được lạ vị hơn. Hãy cùng Tupperware trổ tài để có một tô canh sườn non cải chua sắc màu đặt vào mâm cơm gia đình vào dịp Tết thật bắt mắt và hấp dẫn nhé!</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (50721, 'Bánh chưng chấm sốt tiêu đen', '<p>Nếu bạn đã chán ngán món bánh chưng thì hãy thử ngay cách làm sốt tiêu đen ăn cùng bánh chưng chiên giòn rụm. Từng miếng bánh chưng vàng giòn, được chấm cùng với nước sốt sóng sánh, thơm nức vị tiêu “ăn đến đâu thấy thấm đến đó".Bánh chưng chiên chấm sốt tiêu đen là món ăn dễ làm, hãy cùng Tupperware vào bếp thực hiện cách làm món ngon này nhé!</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (50972, 'Bánh cuốn cho chế độ ăn Keto', '<p>Chế độ ăn kiêng keto hiện nay đang được nhiều người ưa chuộng và áp dụng để dáng đẹp eo thon. Với bánh cuốn keto được làm từ rau câu dẻo sẽ giúp chị em giảm cơn thèm bánh cuốn chính tông, mà cũng bổ sung vào thực đơn ăn kiêng thêm phong phú.</p>', 2, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (51296, 'Sườn heo chiên sốt cam chua ngọt', '<p>Sườn heo chiên kết hợp cùng nước sốt từ cam ngọt lành, chua thanh tạo nên hương vị hấp dẫn và thơm ngon. Món ăn dễ dàng chế biên và dễ đưa cơm bạn nhất định nên thử.</p>', 2, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (51313, 'Củ cải ngâm nước mắm', '<p>Củ cải ngâm nước mắm là một món ăn kèm ngày tết, dùng ăn chung với bánh chưng, bánh tét, thịt kho để giải ngán và tăng sự ngon miệng. Cách làm củ cải ngâm nước mắm không hề khó, kể cả những ai không khéo cũng có thể dễ dàng làm theo công thức dưới đây mà Cooky hướng dẫn. Nào, cùng bắt tay vào bếp thực hiện món ăn này để chiêu đãi gia đình tết này nha!</p>', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (51622, 'Chả lụa', '<p>Chả lụa thường xuất hiện trong nhiều món ăn như bánh mì, bánh ướt, xôi mặn, bún bò... Đây là một món ăn được nhiều người yêu thích. Cách làm chả lụa ngon để miếng chả vừa có vị ngọt thơm từ thịt, vị cay từ tiêu, có độ giòn, giai vừa phải là điều mà rất nhiều người muốn biết. Để làm được có miếng chả lụa ngon, ngoài phần nguyên liệu phải tươi ngon, bạn cần biết công thức pha chả gồm những gì và có chút mẹo hay làm bếp. Cùng Cooky vào bếp để có công thức chả lụa tuyệt vời này!</p>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (51639, 'Mì Udon thịt heo hầm & trứng ngâm', '<p>Nếu bạn nào đã thưởng thức qua mì Ramen Nhật Bản sẽ nhận ra tô mì Nhật thường dùng kèm trứng lòng đào ngâm. Ăn vào sẽ cảm nhận vị mặn ngọt thơm của gia vị Nhật ngấm vào trứng từ của Mirin và Sake cùng hành boa rô, gừng, tỏi... Team thích ăn trứng và chuộng mì Nhật đều sẽ thấy ngon lành, hấp dẫn nè</p>', 4, '2022-03-22 00:00:00', 80, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (51792, 'Cháo chay rau củ', '<p>Món đơn giản dễ làm, ngọt thanh không ngấy, thích hợp ăn chay cho mọi lứa tuổi.</p>', 6, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (51797, 'Canh bí đỏ nấm hương', '<p>Món canh khá dễ nấu, dễ ăn, lại còn bổ dưỡng. Cùng tìm hiểu cách làm nhé!</p>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (51817, 'Lẩu Kim Chi Bò Mỹ', '<p><span style="background-color:rgb(249, 249, 249); color:rgb(3, 3, 3); font-family:roboto,arial,sans-serif; font-size:14px">Lẩu kim chi thịt bò ba chỉ là món lẩu nổi tiếng ở Hàn và được nhiều bạn Việt Nam yêu thích. Cách nấu vô cùng đơn giản chỉ với các nguyên liệu từ kim chi, thêm vài loại nấm, một ít thịt bò ba chỉ là đã được một nồi lẩu ngon tuyệt cú mèo rồi. Cùng Cooky vào bếp làm món này đãi cả nhà ngày cuối tuần nhé.</span></p>', 5, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (51998, 'Thịt kho trứng cút', '<p>Thịt kho trứng cút là món ăn tương đối dễ dàm, đơn giản nhưng lại là 1 món ăn ngon, phù hợp với bất kỳ mùa nào trong năm. Đặc biệt vào những ngày Tết mà có 1 nồi thịt kho trứng cút đậm đà, nóng hổi thì rất hao cơm. Hãy cùng Tupperware thực hiện món ngon này nhé!</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52426, 'Mực rim me Đà Nẵng', 'Cách làm món mực rim me được nhiều người ưa chuộng nhất hiện nay của Đặc Sản Làm Quà.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52502, 'Ba chỉ rim chao', '<p>Ba chỉ rim chao với cách làm đơn giản, nguyên liệu lại không cầu kì nhưng lại là món ăn cực kì hao cơm. Beo béo của thịt ba chỉ cộng với vị bùi bùi của chao thêm vài lát ớt nữa tạo nên một món ăn siêu hấp dẫn để đãi gia đình. Cùng bắt tay vào bếp thực hiện món ăn này nhé. </p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52649, 'Canh ghẹ kim chi ', '<p>Canh ghẹ kim chi món ăn có biến tấu độc đáo từ ghẹ và kim chi. Vị ngọt tự nhiên của ghẹ kết hợp vị chua cay thêm chút giòn giòn của kim chi . Bạn hãy tưởng tượng trời se se lạnh được húp chén canh ghẹ kim chi nóng hổi thì còn gì sung sướng bằng. </p>', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52656, 'Cá thu nướng mè cùng rau củ', '<p>Cá thu là một món ăn ngon được nhiều người ưa thích bởi giàu dinh dưỡng và chế biến được nhiều thực đơn như cá thu sốt cà chua, sốt rau răm, cá thu chiên, cá thu kho...Nếu bạn yêu thích cá thu hãy thử ngay món cá thu nướng mè cùng rau củ ngon tuyệt dưới đây. Với chức năng nướng tự động từ lò vi sóng siêu nhiệt Healsio của Sharp, việc nấu nướng của bạn sẽ trở nên đơn giản hơn bao giờ hết. </p>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52657, 'Tôm bọc hạnh nhân chiên giòn', '<p>Tôm Bọc Hạnh Nhân Chiên Giòn món ăn sang chảnh cho ngày cuối tuần. Vị tươi giòn của tôm, được bọc bên ngoài lớp giò sống phủ lên lớp bột chiên giòn thơm ngon, thêm vài lát hạnh nhân bùi béo được chiên vàng giòn rụm. Chắc chắn mọi người sẽ phải trầm trồ ngưỡng mộ bạn vì sự thơm ngon độc đáo của món ăn này. </p>', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52658, 'Súp bánh bạch tuộc Nhật', '<p>Súp bánh bạch tuộc Nhật là món ăn vừa lạ vừa quen. Món canh súp này được biến tấu từ những chiếc bánh bạch tuộc nướng quen thuộc từ đường phố. Những chiếc bánh bạch tuộc dai ngon được thả vào nồi nước nấu từ rau củ sẽ mang lại cho bạn một món súp thanh mát và bổ dưỡng. Cùng vào bếp và thực hành ngay món ăn lạ miệng này nhé!</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52666, 'Cà Phê Dalgona', '<p>Cà phê bọt biển hay còn gọi là cà phê Dalgona là một món cà phê đang rất hot tại Hàn Quốc. Cách làm cực kì đơn giản và những nguyên liệu sẵn có tại nhà. Với hương thơm đầy mê hoặc từ cà phê được đánh bông lên cộng thêm vị sữa tươi béo ngậy, đã tạo nên một ly cà phê độc đáo, thơm ngon và lạ mắt. Hãy cùng Cooky bắt trend năm nay nào. </p>', 1, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52667, 'Sò điệp hấp miến', '<p>Sò điệp hấp miến là món ăn thơm ngon, giàu chất dinh dưỡng khá nổi tiếng trong các nhà hàng hải sản. Sò điệp là loại hải sản thanh mát, lành tính bạn không lo bị dị ứng nhé. Trang trí đẹp và ngon là tiêu chí của món ăn này, nhưng không quá tốn nhiều thời gian và khâu chuẩn bị công phu. Thích hợp cho bạn trổ tài nấu nướng của mình vào những ngày trọng đại của gia đình. </p>', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52668, 'Mực và rau củ trộn sốt cay', '<p>Mực và rau củ trộn sốt cay là món ăn giàu dinh dưỡng từ mực tươi và rau củ mà bạn nên thử. Từng miếng mực tươi giòn kết hợp cùng các rau củ bắt mắt và sốt thấm vị, sẽ làm bạn muốn vào bếp thực hiện món này ngay. Cùng Cooky học cách làm mực và rau củ sốt cay siêu hấp dẫn để đãi gia đình thân yêu của mình nhé!</p>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52670, 'Sò lông chiên giòn', '<p>Sò lông chiên giòn, với màu vàng ươm đẹp mắt, điểm thêm xanh xanh của lá chanh. Lớp vỏ giòn thơm mùi lá chanh lạ miệng, thịt sò ngọt, dai kích thích thêm vị giá. Phần nước chấm thơm, beo béo, cay cay thêm phần thăng hoa cho món ăn. Món ăn thích hợp để chiêu đãi bạn bè dịp họp mặt hay đơn giản là những bữa cơm gia đình. Hãy cùng Cooky vào bếp để thực hiện ngay món ăn này nhé!</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52774, 'Pudding đậu nành trân châu (tàu hũ trân châu)', 'Được làm từ nguyên liệu chính là đậu nành nên món ăn này giàu dưỡng chất cũng là điều dễ hiểu. Bởi đậu nành chứa rất nhiều protein và còn nhiều loại vitamin như A, B12, D… Các khoáng chất như sắt, canxi cùng calo cũng có nhiều trong loại hạt này.Đậu nành còn được biết đến với nhiều công dụng tuyệt vời như ngăn ngừa các bệnh loãng xương, tốt cho đường ruột…Ngoài đậu nành thì mật ong, sữa tươi và kem tươi cũng là nguyên liệu quan trọng để tạo nên giá trị dinh dưỡng tuyệt vời cho món pudding đậu nành trân châu. Bạn hãy bắt tay vào làm món ăn tuyệt hảo này ngay sau đây nhé!', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52849, 'Mỳ Tofu xào', 'Mỳ tofu sẽ được xào với xốt thịt bò xào chua ngọt. Ngoài những nguyên liệu trên có thể biến tấu thêm các nguyên liệu khác tùy thích.Cảm ơn các bạn đã theo dõi', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52888, 'Canh thịt viên nấu bắp cải, nấm', 'Một món ăn với sự lựa chọn tuyệt vời cho những ai có khẩu vị nhạt một chút, hơi thanh đạm. (Version by Vân)', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52929, 'Lê chưng thanh mát', '<p>Lê Chưng Thanh Mát - được dân gian ví như bài thuốc trị ho khan hiệu quả. Với vị nóng, cay của gừng và quế làm sạch họng, kháng viêm, kết hợp với vị thanh mát của lê và vị ngọt dịu của mật ong sẽ giúp dễ dùng cho mọi lứa tuổi, nhất là trẻ em. Sự kết hợp hài hòa, cân đối này giúp tạo thành bài thuốc giúp trị ho, giảm đau họng hiệu quả. </p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52930, 'Trứng gà ngâm nước tương', '<p>Món trứng lòng đào ngâm tương đã trở thành món ăn yêu thích của nhiều gia đình, với hương vị đặc trưng thơm ngon và béo ngậy của món ăn quyện cùng cơm trắng nóng hổi tạo nên hấp dẫn cho bữa cơm. Thêm nữa, phần nước tương dùng để ngâm trứng cũng là nước xốt tuyệt vời để bạn trộn cùng cơm nóng hoặc mì nữa nhé!</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52931, 'Trứng bồng bềnh Hàn Quốc', '<p>Món trứng bồng bềnh Hàn Quốc một lần nữa làm dậy sóng cư dân mạng sau cơn sốt bánh mì bơ tỏi, cà phê Dalgona. Với sự mềm mịn, bồng bềnh của trứng - món ăn quá đỗi quen thuộc ngay lập tức hút hồn các bà nội trợ. Thế nhưng nhìn tưởng khó vậy mà món ăn này lại cực đơn giản, bạn chỉ cần đầu tư khoảng 15 phút là xong thôi. Cùng Cooky vào bếp để thi nhau trổ tài món trứng bồng bềnh Hàn Quốc đang sốt này nhé! </p>', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52932, 'Twosome Icebox', '<p>Món bánh Twosome Icebox vừa dễ làm và đặc biệt là chẳng cần đến lò nướng hay hay công cụ phức tạp. Thời gian chuẩn bị cũng chỉ tầm 5-10 phút là bạn đã có ngay thành phẩm để mời cả nhà thưởng thức rồi .Với thành phần chính là phô mai, kem tươi béo ngậy và vỏ bánh Oreo đặc trưng, chẳng có gì đáng ngạc nhiên khi bánh Twosome Icebox luôn luôn nằm trong danh sách ưu tiên của hội hảo ngọt. Cùng Cooky bỏ túi công thức làm bánh Twosome Icebox để giải nhiệt cuối tuần nè! </p><p> </p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52987, 'Bánh sandwich sữa chua', '<p>Bánh Sandwich sữa chua nhỏ xinh, lớp nhân sữa chua thanh mát làm dịu nhẹ độ béo của phô mai, không quá ngấy ăn vào lại muốn ăn tiếp. Bánh ngon, thực hiện nhanh trong vòng 1 nốt nhạc, không cần phải nấu.  Cooky đã chuẩn bị công thức cùng bạn vào bếp trổ tài. Bạn nhanh tay nhé!</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (52989, 'Ốc giác xào trứng muối', '<p>Ốc giác xào trứng muối, món ăn tuy lạ mà quen. Với cách biến tấu độc đáo, ốc giác giòn giòn hoà quyện với lòng đỏ trứng muối bùi bùi thêm chút tóp mỡ béo ngậy làm tăng thêm hương vị thơm ngon cho món ăn. Dịp họp mặt bạn bè, làm món này lai rai còn gì bằng. </p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53009, 'Mực xào sa tế', '<p>Mực Xào Sa Tế - món ngon cho bữa cơm gia đình hay dịp tụ họp bạn bè. Vị giòn tươi của mực, thanh mát của các loại rau củ kết hợp với vị cay nồng của sa tế, tất cả hoà quyện lại với nhau tạo nên món ăn siêu kích thích vị giác. Hãy cùng Cooky chuẩn bị nguyên liệu và bắt tay vào làm ngay thôi. </p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53011, 'Lẩu lươn chua cay', '<p>Lẩu Lươn Chua Cay là món ăn mang đặc trưng của miền sông nước. Vị nước dùng đậm đà, chua chua cay cay kết hợp với vị ngọt, dai của lươn hòa cùng vị thanh mát của các loại rau ăn kèm. Vừa thơm ngon lại bổ dưỡng, ai ăn một lần cũng nhớ mãi. Hãy chuẩn bị nguyên liệu và thực hiện theo cách bước sau nhé!</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53012, 'Bò cuộn hầm trái cây', '<p>Bò cuộn hầm trái cây là món ăn vừa kết hợp chất dinh dưỡng từ thịt lẫn vitamin từ các loại trái cây.  Với những nguyên liệu dễ kiếm, cách làm đơn giản, chỉ cần bỏ chút thời gian là bạn có thể chế biến được một món ăn khá lạ miệng mà hấp dẫn. Từng miếng thịt bò như những bông hoa nở đều đẹp mắt, thơm mềm, thấm vị, hòa cùng trái cây đủ màu sắc khiến món ăn thêm phần ngon miệng.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53024, 'Bánh Tráng Nướng Bằng Chảo ', '<p>Bánh tráng nướng bằng chảo, với cách làm tại nhà siêu đơn giản và nguyên liệu dễ tìm là bạn có thể tự tay mình làm món ăn vặt thơm ngon này rồi. Bánh tráng nướng giòn rụm, được phủ lên trên các nguyên liệu ngon miệng và đẹp mắt. Cooky sẽ gợi ý hướng dẫn bạn cách làm bánh tráng nướng tại nhà ngon như ngoài tiệm nhé. </p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53052, 'Bánh mì tại nhà thật đơn giản', 'Bánh mì từ lâu đã là lựa chọn nhanh chóng cho bữa sáng của nhiều gia đình. Tuy nhiên bữa sáng với bánh mì đa phần là mua ngoài hàng. Tại sao trong mùa tránh dịch Covid-19 này chúng ta ở nhà có thời gian không thử tự tay làm những chiếc bánh mì thơm ngon cho cả gia đình cùng nhau thưởng thức...', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53069, 'Mâm ăn vặt khổng lồ', '<p>Chỉ cần nhìn mâm ăn vặt “thần thánh” ngập tràn đủ loại topping “ngon mắt ngon miệng” này: chả giò hải sản phô mai, phô mai que, khoai tây chiên, cá viên chiên... đảm bảo hội ghiền ăn uống khó lòng cưỡng lại mà xắn tay vô bếp làm ngay và luôn.  </p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53070, 'Tart trứng phô mai chà bông xúc xích', '<p>Tart trứng phô mai chà bông xúc xích với biến tấu độc đáo từ bánh tart trứng truyền thống, mang lại cảm giác lạ miệng nhưng lại vô cùng hấp dẫn với nhiều hương vị hoà quyện từ vỏ bánh giòn rụm, xúc xích thơm ngon, phô mai beo béo thêm chút mặn mặn của chà bông... Còn chần chừ gì nữa không bắt tay thực hiện ngay món bánh độc đáo này, để cùng gia đình thưởng thức nào. </p>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53071, 'Chả cá bọc trứng muối', '<p>Chả cá bọc trứng muối là một món ăn cùng với cách làm không qua cầu kỳ, nhưng mang lại những viên chả cá thơm ngon cùng với trứng muối béo bùi được chấm cùng tương ớt Chinsu quả thật là một món ăn khó lòng có thể cưỡng lại. Với những bước làm chi tiết của Chinsu dưới đây, chắc chắn bạn có thể tự làm món này chuẩn vị tại nhà mà không cần phải mất công tìm mua ở nơi nào.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53072, 'Lẩu nhúng xiên que', '<p>Cuối tuần ở nhà chưa biết ăn gì, hãy thử ngay món Lẩu nhúng xiên que siêu hấp dẫn dưới đây. Với nước dùng lẩu kim chi quen thuộc, nhưng có thêm sự kết hợp của các loại xiên que hấp dẫn, nhúng cùng thịt bò và râu mực đảm bảo sẽ là sự lựa chọn tuyệt vời cho gia đình bạn cuối tuần.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53074, 'Tôm viên củ sen chiên giòn', '<p>Một chút biến tấu với củ sen thanh ngọt và thịt tôm được bọc bởi một lớp bột giòn xù sẽ đem lại cảm giác thích thú ngay từ cái nhìn đầu tiên. Món tôm viên củ sen chiên giòn đã tạo nên sự kết hợp hài hòa giữa hải sản và củ sen tạo nên một phong cách ẩm thực mới mẻ lạ miệng cho gia đình bạn thưởng thức vào ngày cuối tuần đấy.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53075, 'Chạo tôm cá bọc sả', '<p>Món chạo tôm cá bọc sả chiên có màu sắc hấp dẫn, được làm từ tôm và cá phi lê tươi ngọt hòa cùng mùi thơm lừng của sả. Món chạo tôm cá bọc sả vàng ươm càng đặc biệt hơn khi ăn kèm tương ớt Chinsu cay nồng, bạn nhớ lưu công thức và vào bếp thực hiện món ngon lạ miệng này nhé!</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53076, 'Cơm chiên tôm trứng', '<p>Cơm chiên tôm trứng là món ngon dễ làm, không tốn nhiều thời gian. Cơm chiên tôm trứng thích hợp nhất khi ăn vào buổi sáng. Món ăn sẽ cung cấp đầy đủ chất dinh dưỡng giúp bạn có một ngày mới tràn đầy năng lượng.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53077, 'Mì trộn tóp mỡ', '<p>Mì Trộn Tóp Mỡ, món ăn với cách chế biến đơn giản nhưng hương vị đặc sắc vô cùng. Sợi mì mềm dai,quyện với tóp mỡ vừa bùi vừa béo, thêm nhiều topping ăn kèm như: thịt bò thanh ngọt, tươi ngon của tôm, thanh mát của rau cải, thêm phần trứng ốp-la như tan chảy trong miệng, dùng kèm với tương ớt cay nồng làm tăng thêm hương vị món ăn thì ngon hết sẩy rồi. </p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53078, 'Bánh giò', '<p>Bánh Giò - món bánh dân dã quen thuộc với người Việt. Với lớp vỏ bánh mềm, mịn được là từ bột gạo, hòa quyện với phần nhân được làm từ thịt bằm thanh ngọt và nấm mèo giòn dai sần sực thêm chút bùi bùi của trứng cút. Bánh giò nóng hổi, thơm mùi lá chuối được dùng kèm với tương ớt cay bùng vị thì ngon còn gì bằng.  </p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53079, 'Cơm cuộn trứng', '<p>Cơm Cuộn Trứng, món ăn tuy đơn giản nhưng cực kì hấp dẫn, không tốn quá nhiều thời gian cũng như nguyên liệu không cầu kì. Phần cơm mềm dẻo kết hợp với rau củ thanh mát và thịt gà tươi ngọt. Chỉ mất vài phút là đã có ngay món ăn vừa đầy đủ chất dinh dưỡng lại vừa đẹp mắt rồi. </p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53080, 'Bánh mì chảo', '<p>Bánh mì chảo là món ăn hấp dẫn với đầy đủ hương vị cho bữa sáng đầy năng lượng. Không chỉ thơm ngon nức mũi mà cách trình bày đẹp mắt của bánh mì chảo càng khiến người ta ngay lập tức muốn thưởng thức. Cùng Cooky học cách làm bánh mì chảo cực kỳ đơn giản và nhanh chóng tại nhà, không đòi hỏi bạn có nguyên liệu cần kỳ hay các công đoạn chế biến phức tạp mà vẫn có thành phẩm ngon đẹp như ngoài hàng.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53105, 'Canh cua cho ngày hè nóng', 'Món ngon tự làm (inbox để mình chia sẻ mẹo nấu nhiều gạch nha)', 3, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53109, 'Bánh bèo Nghệ An', 'Bánh bèo Nghệ An là một món ăn đặc sản của Nghệ An. Dưới đây là công thức mình làm món bánh bèo này cho khẩu phần ăn 4 người nhé!', 2, '2022-03-22 00:00:00', 135, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53130, 'Grilled Cheese', 'Vị béo của phô mai và vị thơm bơ tỏi của bơ hành khi nướng sẽ là sự kết hợp tuyệt vời.', 1, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53131, 'Chè khoai lang dẻo', 'Rất dễ dàng thôi nhé mà lại cực nhanh.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53138, 'Trứng Ngâm Nước Tương', '#TRỨNG_NGÂM_NƯỚC_TƯƠNG ❤ ĐẬM ĐÀ YÊU THƯƠNG ?THÀNH PHẨMSau 7-8 tiếng, trứng sẽ ngấm gia vị và bạn có thể thưởng thức được rồi nè. Trữ ở ngăn mát tủ lạnh, bảo quản được 2 tuần. ❤  Trứng thành phẩm có màu nâu cánh gián, vị mặn ngọt của nước ngâm kết hợp cùng vị béo của lòng đỏ,  thêm một chút cay nồng của ớt cho món ăn tròn vị. ?Trứng ngâm nước tương là một lựa chọn tuyệt vời cho những ngày lười nấu nướng.?Đây còn là một món ăn đơn giản thích hợp cho việc chuẩn bị bento đi làm nè. Buổi sáng vội vã chỉ cần một ít cơm, dưa leo, mở hộp trứng ngâm vớt ngay 1 em trứng cắt đôi, thêm thắt một vài chi tiết là bạn đã có ngay 1 hộp bento ngon lành cho bữa trưa lý tưởng.?Chúc bạn thành công.', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53155, 'Mì sợi tươi thơm ngon và an toàn tại nhà', 'Hôm nay muốn chia sẽ với mọi người cách làm sợi mì tươi tại nhà đảm bảo an toàn không phụ gia hay chất bảo quản, lại có hương vị đặc biệt...', 5, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53185, 'Bún riêu cua đồng ngon tuyệt, dễ làm tại nhà', 'Trong những ngày trời se lạnh hay những ngày hè nắng nóng thì bún riêu cua đồng vẫn làm mát lòng hay ấm bụng, món ăn này cũng không quá khó để chế biến, bạn hoàn toàn có thể cho ra lò món bún riêu cua đồng hấp dẫn', 3, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53200, 'Sữa chua', 'Sữa chua ngọt mát, hơi loãng.', 4, '2022-03-22 00:00:00', 490, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53203, 'Spagetty pescatora', 'Một món mỳ hải sản tuyệt vời.', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53213, 'Xôi mỡ hành, gà hấp nước mắm', 'Nếp nấu chín rưới 1 ít mỡ hành, gà ướp nước mắm cùng tiêu tỏi ớt khoảng 15p cho gà thấm vào thịt thì đem đi hấp cho đến khi gà chín.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53227, 'Tôm Cocktail', 'Mùa hè mà làm "ly" tôm cocktail mát lạnh thì tuyệt ghê. Hôm nay mình sẽ giới thiệu cách làm món tôm cocktail cho các bạn nếm thử nhé, hứa là ngon.', 2, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53229, 'Lẩu gà ớt hiểm ', '<p>Lẩu gà ớt hiểm là món lẩu không chỉ thơm ngon mà còn bổ dưỡng, rất thích hợp cho cả gia đình cùng quây quần bên nhau dịp cuối tuần. Món lẩu với ớt hiểm xanh phủ đầy khắp nồi, tô điểm thêm kỷ tử cùng nước dùng với hương vị cay the ngọt thanh rất hấp dẫn. Cùng học cách nấu món lẩu này ngay thôi!</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53231, 'BiBimBap', 'BiBimBap là món cơm trộn Ngon, Dễ làm, Làm nhanh và đầy màu sức của sứ kim chi. Món có đa đạng các loai thực phẩm, từ rau, thịt, v.v. Học cách làm món này cùng mình nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53245, 'Trứng rim mắm hành', 'Trứng Rim Mắm Hành, món ăn khá đơn giả nhưng lại cực kì hao cơm, với cách chế biến nhanh chóng là đã có ngay món trứng rim thơm ngon với lớp vỏ ngoài giòn dai, có màu nâu đẹp thấm đượm vị. Hãy cùng Cooky bắt tay vào làm ngay món trứng rim mắm hành này để đãi gia đình nhưng lại không mất quá nhiều thời gian của bạn nhé. ', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53260, 'Milo Bọt Biển - Milo Dalgona', 'Milo Bọt Biển - Milo Dalgona đang hot rần rật hiện nay, bạn đã kịp cập nhật chưa. Với nguyên liệu dễ tìm có ngay trong bếp nhà bạn cách, thực hiện lại đơn giản và nhanh chóng mang hương vị cực lạ miệng  thu hút. Dành cho những tín đồ không không thích vị đắng của cà phê, hay đã quá quen thuộc với Milo truyền thống thì đây sẽ là sự lựa chọn ngọt ngào và mới lạ.  Hãy cùng Cooky vào bếp thực hiện thức uống hot trend này nhé! <br>', 5, '2022-03-22 00:00:00', 25, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53271, 'Mì Ý cua', 'Mì Ý Cua - sự kết hợp tuyệt vời. Lạ miệng bởi Ricosta là một loại phô mai béo, thơm ngon nhưng không ngậy, kết hợp đặc biệt với vị gạch cua đọng lại hậu vị. Điểm nhấn là sự tươi ngon của thịt cua quyện theo sự mềm dai của từng sợi mì. Mì ý cua với hương vị đậm đà và hấp dẫn sẽ khiến bạn bị mê hoặc bởi sự độc đáo của món ăn này. Hãy cùng Cooky bắt tay vào thực hiện ngay món ăn này nhé!', 1, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53272, 'Bánh bông lan bằng chảo đơn giản', 'Bánh bông lan thường được làm với 3 thành phần nguyên liệu cơ bản nhất bao gồm bột – đường – trứng, sau các bước làm đơn giản bánh sẽ được đem đi nướng để làm chín. Yêu cầu thành phẩm là những chiếc bánh bông lan thơm ngon với hương vị bơ hòa quyện cùng với, bánh đạt được độ mềm xốp, nở đều không bị lõm mặt bánh hay nứt… Nếu như không có sẵn lò nướng bạn có thể tham khảo cách làm bánh bông lan không cần lò nướng mà vẫn thơm ngon dưới đây bằng chảo ^_^', 2, '2022-03-22 00:00:00', 18, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53281, 'Cơm hến', 'Mỗi món ta nếm đôi lúc còn là cảm giác, là ký ức của 1 khoảng không gian nào đó. Vị chát, đắng hay ngọt cũng mang ta theo về lại những ngày ấy. Cơm Hến là món ăn tuổi thơ của nhiều người con lớn lên trên đất Huế với dòng sông Hương và chùa Thiên Mụ. Mình không hẳn lớn lên ở huế nhưng có một khoảng thời thơ ấu sống tại đây và mỗi lần thưởng thức món ăn này gợi cho mình bao thương nhớ.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53283, 'Bánh bột lọc', 'Bánh bột lọc là một loại bánh Việt Nam, có nguồn gốc từ Huế được làm từ bột lọc. Nhân bánh thường làm bằng tôm và thịt heo trộn với gia vị. Thường kèm thêm nước chấm chua ngọt.', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53285, 'Bánh mì sandwich kẹp trứng cho bữa sáng chỉ trong 7 phút', 'Bạn có thể mua sẵn những chiếc bánh sandwich ở lò bánh mì và bảo quản cùng nguyên liệu khác trong ngăn lạnh của tủ lạnh, bất kể sáng nào bạn có thể chế biến mà không lo thiếu nguyên liệu để làm, cùng đi tìm hiểu bữa sáng đơn giản này nhé.', 1, '2022-03-22 00:00:00', 7, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53292, 'Mứt tắc đơn giản', '<p>Trái tắc (quất) chứa nhiều pectin 10% (nhiều hơn ở vỏ tắc), Vitamin C, Fe, Cu. Dịch quả có đường, axit hữu cơ, fortunelin. Mứt tắc khi ăn có vị chua nhẹ của quả, vị ngọt thơm của đường, vị hơi the do tinh dầu có trong vỏ tắc nên khi ăn rất thú vị. Thật chẳng có gì tuyệt vời hơn khi được quây quần bên nhau chuyện trò ríu rít, lại nhâm nhi món mứt tắc do chính bạn làm ra. Vừa ngon, bổ, rẻ lại vừa ý nghĩa. Nghe có vẻ hấp dẫn lắm đúng không nào? Vậy còn chờ gì mà không làm ngay món mứt này để cho bộ sưu tập mứt của bạn thêm phong phú chứ. Chúc các bạn thành công nhé!</p>', 5, '2022-03-22 00:00:00', 40, null, null);
