insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53314, 'Cà Phê Bọt Biển', 'Dalgona Coffe - Cà Phê Bọt Biển món thức uống đình đám được pha chế khá mới lạ, độc đáo với mùi thơm đầy mê hoặc và hương vị cực ngon. Ngoài Nescafe, Cooky sử dụng cà phê G7 đến từ nhà Trung Nguyên nhé các bạn. Thưởng thức một ly Dalgona coffee hấp dẫn  không chỉ giúp bạn giải khát, mà còn khiến cho tâm hồn trở nên thư thái hơn vì thức uống đẹp mắt và hội tụ đủ hương vị độc đáo. Hãy cùng Cooky vào bếp thực hiện nhé.<br><br><br>', 4, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53316, 'Trà bắc thái nguyên đúng chuẩn người sành trà', 'Pha trà rất đơn giản tuy nhiên để pha được một chén trà đầy đủ thần và vị thì không phải chuyện đơn giản, sau đây mình sẽ hướng dẫn cách pha trà của cao thủ trà.', 4, '2022-03-22 00:00:00', 5, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53318, 'Bánh ít trần nhân thịt', 'Mùi thơm của lá chuối dùng để lót bánh khi hấp kết hợp với vị dẻo của bánh, giòn sật của củ sắn và nấm mèo, vị chua của nước mắm ăn cùng, thanh mát của rau mùi tạo nên cảm giác đậm đà', 5, '2022-03-22 00:00:00', 240, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53319, 'Khoai tím chiên giòn', 'Cho bữa ăn sáng đỡ tốn kém, đỡ mất thời gian. Hoặc làm bữa ăn vặt. Dành cho những ai nhớ mùi vị bánh chưng, bánh tét chiên. Vâng đó là vị của nó đó ạ.', 4, '2022-03-22 00:00:00', 20, null, null);
