insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53322, 'Trứng gà ngâm tương', 'Trứng lòng đào vị béo ngậy, kết hợp mè rang làm ngây ngất ???, chay mặn đều dùng được. Nước có thể dùng để chấm rau ăn kèm, thích hợp cho những ai làm biếng ? ', 3, '2022-03-22 00:00:00', 20, null, null);
