insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53324, 'Lẩu vịt nấu chao', 'Thịt Vịt sẽ được nấu với chao và các loại rau củ ăn kèm với bún tươi hoặc bánh mì. Nó sẽ thích hợp với những ngày mưa bạn cần một nồi lẩu ấm cúng cùng hàn thuyên bên vài cốc bia.', 4, '2022-03-22 00:00:00', 45, null, null);
