insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53345, 'Bún thang chuẩn vị Hà Nội', 'Bún thang là một trong những món bún đặc sản của miền Bắc. Tên gọi thang như trong "thang thuốc", bởi món bún này cần nhiều nguyên liệu phối hợp với nhau, mỗi thứ một ít, như bốc thanh thuốc Đông Y vậy. Nước lèo trong, mùi vị đậm đà nhưng vẫn thanh tao.', 8, '2022-03-22 00:00:00', 120, null, null);
