insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53356, 'Bánh bao nướng nhân bò sốt tiêu đen', 'Bánh bao nướng nhân bò sốt tiêu đen là một món ăn du nhập từ Trung Hoa. Đây là một món ăn mới lạ với vỏ bánh ngàn lớp thơm béo mùi bơ quyện với phần nhân thịt bò sốt tiêu đen đậm đà. Chính vì vậy ít ai biết phương pháp để tạo ra một chiếc bánh bao nướng chuẩn vị.', 0, '2022-03-22 00:00:00', 180, null, null);
