insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53359, 'Cánh gà sốt chua ngọt Thái lan', 'Cánh Gà Sốt Chua Ngọt Thái Lan - món ăn ngon dễ làm, cực hấp dẫn cho cả nhà. Cách làm Cánh gà sốt chua ngọt Thái Lan đơn giản với thịt gà dai béo, quyện sốt đậm đà, ngon cơm.<br data-mce-bogus="1">', 3, '2022-03-22 00:00:00', 40, null, null);
