insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53382, 'Đậu hũ chiên sốt trứng muối', 'Đậu hũ chiên sốt trứng muối có mùi vị thơm ngon, lạ miệng và còn rất bổ dưỡng. Từng miếng đậu hũ vàng ươm quyện cùng vị trứng muối béo ngậy và chấm cùng tương ớt Chinsu cay nồng sẽ làm món ăn càng thêm hấp dẫn hơn với tất cả mọi người. Bạn nhớ lưu công thức món đậu hũ chiên sốt trứng muối này lại để thực hiện nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53386, 'Bánh bao chiên', '<p>Được làm từ bột mì và nhân thịt bằm trứng cút, bánh bao chiên chinh phục mọi người bằng hương vị thơm ngon khó cưỡng. Món bánh này hứa hẹn sẽ là bữa xế tuyệt vời, giúp bạn nạp thêm năng lượng để tiếp tục làm việc, học tập, vui chơi.</p>', 4, '2022-03-22 00:00:00', 30, null, null);
