insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53388, 'Cá hồi hấp măng tây cà rốt', 'Món ăn #eatclean không dầu mỡ, đẹp mắt, lại thơm ngon, hòa quyệt vị đậm đà béo ngậy của cá hồi, vị ngọt của cà rốt, vị cay nồng của hạt tiêu và ớt, cũng như vị tươi mát của măng tây, tất cả chỉ trong vòng 15 phút!', 2, '2022-03-22 00:00:00', 15, null, null);
