insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53394, 'Kem Bơ Viên - Avocado Ice Cream', 'Kem Bơ Viên món kem bơ được nhiểu người yêu thích, giờ đây bạn không cần ra hàng vẫn có thể ăn thỏa thích ngay Kem Bơ theo phong cách của bạn. Cách làm nhanh chóng siêu tiện lợi. Vị thơm ngậy, béo của bơ kết hợp sữa làm nên hương vị kem bơ không chỉ khiến trẻ e mê hoặc mà còn khiến cả người lớn thích thú. Món ngon dễ làm đặc biệt không cần máy làm kem bạn vẫn có thể có ngay 1 li kem bơ ngay tại nhà. Hãy bắt đầu màu hè thú vị với món kem bơ ngon bổ dưỡng từ công thức siêu đơn giản của Cooky nhé. <br>', 5, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53396, 'Sữa chuối', 'Sữa chuối là một trong những thức uống nổi tiếng đến từ Hàn Quốc được cả người lớn và trẻ em yêu thích. Trong công thức này, Cooky sẽ gửi đến mọi người công thức làm sữa chuối siêu đơn giản, siêu dễ nhưng đảm bảo ngon và rất chất lượng luôn nha. Cùng xem công thức và làm thôi nào!<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53399, 'Sườn nướng tảng sốt BBQ', '<p>Món sườn tảng sốt BBQ đã rất quen thuộc với tất cả mọi nhà, thế nhưng để học được bí kíp làm ra món sườn nướng thơm mềm mọng nước, tan ngay trong miệng thì không phải ai cũng biết. Chia sẻ với mọi người món ăn bắt mắt lại ngon miệng này nhé!</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53401, 'Khoai tây bọc phô mai chiên', 'Khoai tây bọc phô mai chiên với lớp vỏ ngoài giòn rụm, khi bẻ đôi phần nhân phô mai bên trong kéo sợi vừa dai dai vừa béo ngậy vô cùng hấp dẫn, bạn khó lòng mà cưỡng lại. ', 4, '2022-03-22 00:00:00', 30, null, null);
