insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53414, 'Thịt gà hầm bông Atiso', 'Thịt gà ta hầm có màu vàng, thơm mềm, vị của bông Atiso hòa quyện một cách đầy tinh tế vào trong thịt gà tạo nên một hương vị ngon tới khó cưỡng. Không chỉ là món ăn thơm ngon và bổ dưỡng, Atisô còn có tác dụng trong việc bảo vệ sức khoẻ, giải nhiệt cho cơ thể và đặc biệt là loại cây này còn giúp chúng ta chống, ngăn ngừa một số bệnh như ung thư, lão hóa da và phục hồi chức năng gan.', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53415, 'Bao Tử Cá Sốt Mắm Me', '<p class="MsoNormal" style="text-align: justify; line-height: 150%;" data-mce-style="text-align: justify; line-height: 150%;">Bao Tử Cá Sốt Mắm Me món ăn dùng cơm đậm đà hay nhâm nhi cốc bia đều tuyệt vời. Chắc chắn sẽ mang đến cho bạn và gia đình một bữa ăn thật hấp dẫn, hương vị độc đáo lạ miệng hẳn đây là món " khoái khẩu" yêu thích của nhiều người. Cách chế biến vô cùng đơn giản và nhanh chóng. Theo chân Cooky cùng  thực hiện và thưởng thức món ngon này nhé!<br></p>', 13, '2022-03-22 00:00:00', 30, null, null);
