insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53417, 'Bánh gạo cay từ bánh tráng', '<div>Bánh Gạo Cay Từ Bánh Tráng, nghe có vẻ hơi kỳ lạ nhưng lại cho kết quả vô cùng bất ngờ. Bánh tráng dai dai, dẻo dẻo mùi vị thì không khác biệt lắm so với bánh gạo thật. Thêm nhiều topping nữa, tạo nên món ăn vô cùng độc đáo và mới lạ, nhưng thời gian chế biến cực kì nhanh. Hãy cùng Cooky bắt tay vào thực hiện món ăn này nhé. </div><div> </div>', 2, '2022-03-22 00:00:00', 30, null, null);
