insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53423, 'Kẹo sữa lowcab', 'Cơm dừa: Ngâm sữa, light cream, cốt dừa, gelatin 10 phút. Sau đó, đun vừa sôi với lửa vừa. Tắt bếp đổ tất cả hỗn hợp vào khay để tủ lạnh. Lưu ý, dùng màng bọc khay rồi đổ vào sẽ dễ lấy ra hơn. Khi hỗn hợp đã đông mình sẽ cắt thành những viên kẹo hình vuông rồi lăn những viên kẹo vào cơm dừa. Bạn nào muốn ăn ngọt có thể cho thêm đường vào lúc đun sôi hỗn hợp.', 3, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53436, 'Bánh Mì Mini - Bánh Mì Chuột', 'Bánh Mì Mini đang hot rần rật làm khuấy đảo giới trẻ. Dân tình đang truy lùng khắp nơi, chiếc bánh thu hút bởi size mini bé xíu chỉ bằng đốt ngón tay, hương vị thơm béo vỏ bánh giòn ruột xốp được mọi người yêu thích. Bao nhiêu tinh túy đều nằm trong chiếc bánh nhỏ xinh này. Nhanh tay cùng Cooky thực hiện nhanh và thưởng thức nhé. ', 6, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53438, 'Bạch tuộc sốt bơ phô mai cay', 'Bạch tuộc sốt bơ phô mai cay là món ăn ngon và rất được giới trẻ ưa thích, cũng là một món lai rai lý tưởng cho những ngày mưa lạnh. Xé miếng bánh mì chấm vào sốt bơ cay, nhẹ nhàng cảm nhận hương vị cay the và beo béo ngay đầu lưỡi, thêm những miếng bạch tuộc giòn sật rất vui miệng. <br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53439, 'Bánh cuốn bằng bánh tráng', 'Món bánh cuốn bằng bánh tráng đang gây xôn xao vì sự tiện lợi, nhanh gọn, phù hợp với những gia đình không có nhiều thời gian nấu nướng. Đặc biệt là khi ăn, các bạn sẽ thấy vị tương đối giống bánh cuốn truyền thống làm từ bột gạo thông thường do bánh mềm, dẻo, độ dai vừa phải. Nhanh tay lưu công thức của Cooky, để trổ tài cho gia đình bạn món bánh cuốn siêu nhanh gọn này nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53440, 'Chân gà sốt cay Hàn Quốc', 'Chân gà sốt cay Hàn Quốc là một món ăn bắt mắt với vị cay đặc trưng của ớt Hàn Quốc, phần lớp vỏ ngoài giòn tan, còn bên trong chân gà dai ngon sần sật. Bạn hãy cùng vào bếp với Cooky để chế biến món chân gà sốt cay Hàn Quốc này nhé!<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53441, 'Thịt heo nướng kiểu Thái - Ko Mu Yang', 'Món thịt heo nướng kiểu Thái - Ko Mu Yang là món ăn quen thuộc của xứ sở chùa vàng, từng miếng thịt được nướng xém nhẹ bên ngoài và bên trong thịt mềm thơm đặc sắc vô cùng. Với nước chấm me đậu phộng sẽ giúp món thịt heo nướng Thái Lan lại càng hợp vị hơn, hãy vào bếp cùng Cooky thực hiện ngay nào.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53442, 'Bánh sandwich xúc xích phô mai bằng máy nướng bánh mì mini đa năng', 'Nếu đang trong ngày cuối tuần mà bạn đang không biết làm gì thì thử làm sandwich cuộn xúc xích phô mai ăn thử nhé, vì nếu chưa thử món này sẽ tiếc ngẩn ngơ đấy. Mà đã ăn là lại ghiền cho mà coi.', 1, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53444, 'Bò Xào Khoai Trứng (Corned Beef Hash VietNam)', 'Ông bà mình có câu:" Ăn sáng là ăn cho mình, ăn trưa là ăn cho bạn, ăn tối là ăn cho kẻ thù"Ai muốn giảm cân thì hạn chế ăn tối lại, ăn sáng thoải mái đi anh chị em. ?Món "Bò xào khoai trứng" dựa theo phong cách món Corned Beef Hash ở phương Tây. Nghe tên Tây sang bao nhiêu, qua tên Việt "bựa" bấy nhiêu ??Website bài viết : http://lynfood.com/cach-lam-corned-beef-hash-pha-cach-kieu-viet-nam/', 1, '2022-03-22 00:00:00', 10, null, null);
