insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53446, 'Bánh mì nướng muối ớt', 'Món bánh mì nướng muối ớt đủ vị: cay nồng của sa tế và tương ớt; ngọt thanh của đường; bùi béo của sốt mayonaise; mặn mặn của ruốc thịt. Bữa sáng đầy đủ dinh dưỡng ?Thành công ngay từ lần đầu tiên làm thử', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53449, 'Bún bò', 'Để có một bữa ăn ngon cho mình và gia đình là điều không đơn giản phải không nào. Hôm nay Tupperware sẽ giới thiệu cho bạn cách nấu bún bò thơm ngon và không quá phức tạp đâu nhé.<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 60, null, null);
