insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53452, 'Bún riêu cua đồng', 'Món ngon dân dã. Đầy đủ chất dinh dưỡng, với những nguyên liệu đồng quê làm cho bữa ăn thêm đảm bảo sức khỏe. ', 3, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53456, 'Snack cơm', 'Chỉ với cách làm đơn giản dưới đây là bạn đã có ngay món ăn vặt thơm ngon khiến cả người lớn và trẻ nhỏ đều thích. Cùng bắt tay vào làm nào!', 4, '2022-03-22 00:00:00', 45, null, null);
