insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53463, 'Thịt Chưng Mắm Tép', 'Thịt Chưng Mắm Tép  món ăn ngon chuẩn vị Bắc, đậm đà hương vị quê hương. Rất được lòng thực khách và được các mẹ nội trợ thường xuyên chế biến trong mâm cơm gia đình mình. Vị mằn mặn, béo béo, ngậy ngậy và mùi thơm đặc trưng của Mắm kết hợp với thịt và riềng dễ dàng dùng kèm cùng nhiều món ăn. Chắc chắn là sự lựa chọn không thể thiếu trong mâm cơm gia đình nhà bạn rồi. Hãy cùng Cooky lưu vào sổ tay nội trợ cách làm Mắp tép Chưng Thịt hấp dẫn chiêu đãi gia đình mình nhé.', 11, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53467, 'Thịt ba chỉ chiên giòn', 'Thịt ba chỉ chiên giòn với lớp vỏ giòn tan, bên trong thịt mềm mọng nước được ăn cùng nước chấm chua cay siêu hấp dẫn, hứa hẹn sẽ hạ gục gia đình bạn đấy. Bạn nhớ lưu lại công thức và cùng vào bếp với Cooky ngay nào!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53468, 'Cá kho kiểu Thái', 'Món cá kho kiểu Thái có hương thơm của lá chanh non thoang thoảng và vị mặn mà của nước kho, kết hợp cùng mùi riềng dậy mùi thơm nồng ấm; giúp cho món cá kho kiểu Thái thật ăn ý đậm đầy chất đạm.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53469, 'Ốc hương sốt trứng muối', 'Món ốc hương xào trứng muối là món ăn vặt có hương vị hấp dẫn và màu sắc đẹp mắt, khiến cho các tín đồ thổn thức không yên. Ốc hương được kết hợp cùng trứng muối béo mặn, giúp món ăn càng trở nên hấp dẫn hơn bao giờ hết, hãy cùng Cooky vào bếp chinh phục cách làm món ốc hương xào trứng muối bạn nhé.<br>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53470, 'Xôi hoa đậu', 'Xôi hoa đậu là món ăn cực hot trên mạng xã hội thời gian vừa qua. Món xôi này xuất phát từ Hàn Quốc, chiếc bánh gốc có tên gọi là Bojagi cake. Khi về Việt Nam, nhân vốn là bột gạo được biến tấu thành xôi, còn vỏ từ đậu sên cũng được biến tấu đôi chút, tạo hình thì tùy độ khéo léo và tưởng tượng, sáng tạo của mỗi người. Xôi này khi ăn nóng sẽ rất ngon hoặc bạn có thể dùng để trang trí cho những bữa tiệc trong gia đình thêm đẹp mắt.<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53471, 'Gà giòn cay phô mai', '<p>Nếu bạn là một tín đồ của các món ăn chế biến từ gà, đặc biệt là gà rán thì với món gà giòn cay phô mai Hàn Quốc dưới đây sẽ khiến bạn phải thèm thuồng. Bạn còn chần chừ gì mà không thử ngay công thức làm gà cay phô mai đặc biệt này từ Cooky. Chắc chắn nó sẽ “đánh thức” mọi vị giác của bạn.<br></p>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53473, 'Dưa leo ngâm Nước tương', 'Dưa Leo Ngâm Nước Tương - món ngon dễ làm, giúp bữa cơm gia đình thêm ngon miệng hơn.<br data-mce-bogus="1">', 5, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53477, 'Chân giò hầm thuốc bắc', null, 4, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53484, 'Cua sốt hoàng kim', 'Cua Sốt Hoàng Kim, món ăn sang chảnh thường được thấy trong thực đơn các nhà hàng. Nhưng với công thức của Cooky, bạn có thể tự tay làm món ăn này để đãi gia đình mà không cần ra nhà hàng. Thịt cua dai ngọt, được phủ lên lớp sốt hoàng kim từ trứng muối và kem béo là đã tan trong miệng từ miếng đầu tiên rồi. Giờ thì chuẩn bị nguyên liệu và vào bếp ngay thôi. ', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53485, 'Sữa chua dẻo trái cây', 'Sữa Chua Dẻo Trái Cây - Món ăn giải nhiệt trong mùa hè oi bức này. Sữa chua dẻo mềm, mịn mát lạnh kết hợp với nhiều loại trái cây thanh mát. Màu sắc bắt mắt cộng thêm hương vị đầy hấp dẫn bạn sẽ khó cưỡng lại với món ăn đầy thơm ngon mát lạnh này. Hãy cùng Cooky bắt tay vào làm món sữa chua dẻo trái cây cho ngày hè rực rỡ', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53486, 'Kem chuối', 'Kem Chuối được xem là món ăn mang hương vị tuổi thơ, với nhiều cách chế biến nhưng kem chuối vẫn giữ được hương vị độc đáo riêngmình. Với nguyên liệu chính là chuối chín, dừa và đậu phộng rang và cách chế biến cũng chẳng cầu kì, bạn có thể tự tay làm được nhưng cây kem chuối thơm ngon mát lạnh, giải nhiệt trong mùa hè này. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53487, 'Banh bông lan phô mai tươi ngàn sợi', 'Bánh bông lan phô mai tươi ngàn sợi, nghe có vẻ hơi ngán nhưng không hề nha, với 3 lớp phô mai từ mặn đến ngọt hòa quyện cùng lớp bánh bông lan mềm, mịn tất cả sẽ tan chảy trong miệng ngay miếng đầu tiên. Thơm ngon là vậy, cách làm cũng đơn giản không kém, chỉ cần một chút khéo léo là bạn đã có ngay món bánh phô mai béo ngậy chất ngất này rồi.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53489, 'Nui chiên xốt tương ớt', '<p class="MsoNormal"><span style="font-size: 10.0pt; line-height: 107%; font-family: "Arial",sans-serif; color: black; background: white;" data-mce-style="font-size: 10.0pt; line-height: 107%; font-family: "Arial",sans-serif; color: black; background: white;">Nui chiên xốt tương ớt là món ăn vặt vừa ngon miệng nhưng cực kì dễ làm, những bạnkhông rành về bếp núc cũng có thể làm được món ăn này. Nuichiên giòn rôm rốp được phủ bên ngoài lớp sốt tương ớt thấm đượmvị cay. Càng ăn càng ghiện,liệu bạn có kiềm chế đượcmón ăn vặt thơm ngon này. </span><o:p></o:p></p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53490, 'Cappuchino dừa foam sữa tươi và thạch cà phê', 'Cappuchino Vị Dừa, sự kết hợp khá lạ nhưng cho ra hương vị vô cùng độc đáo, vị cappuchino thơm ngon truyền thống, thêm chút beo béo của nước cốt dừa, một chút dòn dài sần sực của thạch cà phê. Tưởng chừng chỉ khi ra quán cà phê mới được thưởng thức ly cappuchino tuyệt hảo, nhưng giờ đây ngay tại nhà bạn cũng có thể trổ tài barista của mình với những nguyên liệu vô cùng đơn giản. Hãy bỏ túi ngay công thức này nhé! ', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53491, 'Cà phê Tequila', '<p>Cà phê Tequila -  Với hương vị mạnh mẽ, khỏe khoắn có chút hoang dã của rượu Tequila kết hợp với những hạt cà phê tuyệt hảo giòn,tươi mang đầy mùi hương. Sự kết hợp hoàn hảo tròn vị này, giờ đây bạn đã có thể trổ tài pha chế của mình tại nhà dễ dàng chỉ với vài thao tác đơn giản. Hãy cùng theo dõi xem bí quyết này là gì nhé! </p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53493, 'Trà sữa trân châu đường đen', 'Món nay mình rất thích vì dễ làm, nguyên liệu cũng dễ kiếm với rẻ nhưng ngon lắm hihi. trân châu dai dai nấu với đường đen thơm kinh khủng, nước đường đen tan vào sữa tươi làm sữa vừa béo vừa thơm mùi đường đen ngon thực sự. <3', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53494, 'Thạch rau câu lá dứa thanh long ruột đỏ', 'Thạch rau câu dẻo Thanh long ruột đỏ Lá dứa thơm', 6, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53497, 'Hàu né phô mai', 'Món né không phải là món ăn mới của ẩm thực Sài Gòn, tuy nhiên nhờ một chút biến tấu về nguyên liệu nên hàu né phô mai đã nhanh chóng được yêu thích. Sự hòa quyện của thịt hàu cùng trứng, xúc xích và phô mai đã tạo nên sự hấp dẫn khó cưỡng cho món ăn. Cùng Cooky học ngay cách làm món hàu né phô mai thơm nức mũi này nhé!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53498, 'Bánh mì nướng muối ớt', 'Bánh mì nướng muối ớt là món ăn vặt phổ biến từ lâu, nay được Cooky biến tấu lại với topping vô cùng hấp dẫn. Những miếng bánh mì được phết mỡ hành muối ớt rồi mang nướng giòn, ăn kèm với chà bông, xúc xích, trứng cút hẳn sẽ làm bạn “đổ” ngay lập tức đấy.<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53499, 'Bánh tráng nướng bằng chảo', 'Bánh tráng nướng là món ăn vặt rất được ưa chuộng, có lẽ không quá khi nói rằng bánh tráng nướng là chiếc bánh pizza phiên bản Việt. Không cần tới lò than, chỉ với một chiếc chảo chống dính bạn đã có thể làm ngay món ăn vặt siêu hấp dẫn này tại nhà!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53500, 'Mận lắc muối tôm', 'Mận hậu hay mận Hà Nội là cái tên để chỉ thứ quả tròn nhỏ màu đỏ, ăn vào giòn giòn, vị chua và chát. Mận hậu còn là món ăn được rất nhiều bạn trẻ yêu mến bởi cái vị ăn mãi rồi hóa thành nghiện. Hôm nay, Cooky sẽ thử kết hợp hai tuyệt phẩm khi đem quả Mận lắc với muối tôm Tây Ninh như bao món trái cây khác. Các bạn cùng Cooky khám phá công thức món Mận lắc muối tôm này nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53501, 'Siro mận', 'Chỉ với những trái mận chín thơm ngon, cùng với một lượng đường vừa đủ là bạn đã có ngay một hủ Siro Mận, vừa ngon, vừa dễ làm mà lại không quá tốn kém. Siro Mận còn có thể pha thành nhiều loại nước uống khác nhau giải nhiệt cho bạn trong những ngày hè oi bức. Bạn hãy khác phá công thức đó ngay với Cooky ở dưới đây nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53504, 'Xôi trứng ruốc', 'Ngon giòn tan Phù hợp cho việc thích ăn vặt k muốn ra ngoài ?Bao gồm Xôi Trứng Ruốc Mỡ ', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53506, 'Mì Trộn Tokbokki Sốt Cay', 'Mì Trộn Tokbokki Sốt Cay - món ăn đường phố nổi tiếng ở Hàn Quốc. Với món bánh gạo dẻo dai thấm đượm phần sốt cay cay kết hợp thêm với sợi mì mềm dai, phủ lên lớp phô mai béo ngậy tan chảy. Càng ăn càng ghiền, hẳn những tín đò mê món Hàn phải siêu lòng với món ăn này. Với các bước làm đơn giản là bạn đã có ngay món ăn siêu hot này rồi.', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53509, 'Bánh Biscotti', 'Đây là một trong những món ăn dễ làm, tốt cho sức khoẻ vì thành phần có nhiều loại hạt và trái cây khô', 6, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53513, 'Bún ngan phố cổ', 'Bún ngan phố cổ', 5, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53515, 'Tương Hột', 'Mâm chay thì ăn kèm cơm trắng, rau củ luộc chấm tương, xào rau hay chang cháo trắng húp.Mâm mặn thì mang đi chưng cá, kho cá, xào thịt,..', 3, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53516, 'Bánh Cuốn Keto - Bánh Cuốn Rau Câu', 'Bánh Cuốn  Keto 1 phiên bản bánh cuốn không tinh bột dành cho các tín đồ ăn kiêng. Lớp vỏ bánh rau câu dẻo dai, nhân thịt sần sật của thịt và nấm mèo. Bạn tha hồ ăn mà không lo mập. Giờ đây bạn có thể tự làm cho mình và gia đình món ngon, nhưng lại không gây béo. Món bánh cuốn keto này giúp cho các các chị em có thể theo đuổi chế độ ăn kiêng keto mà không cảm thấy ngán. Nhanh tay vào bếp thực hiện với Cooky nào.', 5, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53517, 'Bánh Khoai Lang Phủ Mè', 'Bánh Khoai Lang Phủ Mè đang khiến các tín đồ ăn vặt chao đảo vì độ hot của nó. Chiếc bánh nhỏ xinh khoai lang dẻo mịn, béo ngọt còn mè đen thì thơm lừng. Cắn đến đâu nao nức đến đó. Nguyên liệu rất đơn giản, thực hiện nhanh chóng nhưng thành phẩm rất ngon và đẹp mắt. Món ăn vặt ai cũng có thể làm được. Hãy cùng Cooky nhanh tay thực hiện cho gia đình mình cùng thưởng thức nhé.  ', 5, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53526, 'Panna Cotta chanh dây', 'Đây là 1 món ăn vừa làm tráng miệng vừa bổ dưỡng nữa ạ.', 6, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53529, 'Sữa Gạo', 'Sữa Gạo thức uống đặc trưng xứ Hàn nay đã có trên bếp của Cooky. Cáchh nấu sữa Gạo đơn giản, hương vị lại thơm ngon, độc đáo và có lợi cho sức khỏe. Bởi sữa Gạo không chỉ có tác dụng giải nhiệt, ngủ ngon mà còn cung cấp các loại vitamin và khoáng chất giúp trẻ hóa làn da mà còn bồi bổ cơ thể. Vị sữa béo ngọt thơm lừng mùi Gạo rang, thức uống ngon dinh dưỡng nhưng thực hiện lại rất nhanh chóng. Cùng vào bếp cùng Cooky thực hiện nhé.', 5, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53530, 'Chân Giò Hầm Coca', 'Chân Giò Hầm Coca món ăn ngon dùng cơm, món ngon đãi tiệc hay nhăm nhi món nhậu đều rất hợp vị. Thịt chân giò mềm, lớp da dai béo ngậy nước sốt sóng sánh vị ngon cuốn hút. Món ăn ngon dùng với cơm nóng rất bắt cơm thích hợp vào những ngày thu se lạnh dù là trẻ con hay người già đều có thể thưởng thức. Đích thị là món ngon gắn kết bữa cơm sum vầy gia đình bạn. Bạn nhanh tay cùng vào bếp với Cooky thực hiện nhé.', 5, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53531, 'Bánh mỳ đặc ruột ', 'Những chiếc bánh bé bé xinh xinh nhưng “có võ” vì nặng tay lắm phần ruột bánh mềm ẩm & thơm, cứu đói những khi phải overtime hay ngại ra ngoài tìm đồ ăn. ', 4, '2022-03-22 00:00:00', 40, null, null);
