insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53541, 'Sữa chua úp ngược ngon, mịn bằng nồi cơm điện', 'Sữa chua úp ngược ngon, mịn bằng nồi cơm điện, rất tốt cho sức khỏe, lợi cho đường ruột.<br data-mce-bogus="1">', 5, '2022-03-22 00:00:00', 55, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53553, 'Sữa bắp', 'Hạt ngô ngọt, thơm ngon, bổ dưỡng, thích hợp cho những người táo bón, huyết áp cao và xơ vữa động mạch. Ngoài đường, đạm, chất xơ, chất béo và các khoáng chất như Canxi, Phốt pho, Kali, Natri, Magie, Sắt, Kẽm…, mà còn các vitamin như A, B1, B2, E…Ngoài ra còn có công dụng giảm cân, giảm táo bón, ngăn ngừa viêm ruột và ung thư, đồng thời ức chế tác dụng phụ của thuốc điều trị ung thư. Làm chậm lão hóa da, chống lão hóa mắt, tăng cường thị lực và trí nhớ. Hạ huyết áp, làm giảm mỡ máu và xơ vữa động mạch. Râu ngô chứa chất béo, tinh dầu, saponin, vitamin C, K, cryptoxanthin, acid malic, stigmasterol, sitosterol… nên giúp giảm mệt mỏi và chống oxy hóa. Theo y học cổ truyền, râu ngô có vị ngọt, tính bình, giúp an thần. Đặc biệt, râu ngô tốt cho thận và bàng quang, giúp lợi tiểu trong điều trị tiểu gắt, tiểu buốt, tiểu vàng, viêm đường tiết niệu, sỏi niệu, sỏi thận và bệnh lậu. Bên cạnh đó, râu ngô còn được dùng để điều trị huyết áp cao, phù thủng, viêm gan, vàng da, viêm túi mật và sỏi túi mật.', 4, '2022-03-22 00:00:00', 60, null, null);
