insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53578, 'Kim chi dưa chuột', 'Nguyên liệu : 1 củ cà rốt 6 quả dưa chuột 1 nửa củ hành tây100g hành lá150g nước mắm 200g muối 100g dầu mè 100g mè  100g bột ớt Hàn Quốc Cách làm : Làm sạch cà rốt dưa chuột , hành tây và hành lá . Cắt đôi dọc dưa chuột rồi cắt chéo thành hình chiếc lá (độ dày 0,5cm) ướp với 200g muối khoảng 5p . Hành tây cắt mỏng , hành lá thái dài 3cm , cà rốt bào sợi . Sau khi ướp dưa chuột vắt nước lấy phần dưa chuột trộn đều với tất cả nguyên liệu còn lại . Và để khoảng 5" là có thể thưởng thức rồi ^^ . Phần chưa ăn đến hãy đóng hộp để trong tủ lạnh ( dùng trong 1 tuần ) ❤️ Chúc mọi người thành công và ngon miệng ! •.~', 4, '2022-03-22 00:00:00', 40, null, null);
