insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53606, 'Chao tàu hủ', 'Món chao được làm từ tàu hủ nhanh, đơn giản, an toàn cho sức khỏe. Mân chay hay mâm mặn đều có món ngon để đánh sạch nồi cơm.Rau luộc chấm chao, bát cơm nóng ăn với viên chao mặn mặn cay cayViti nấu chao, thịt heo giả cầy,..', 3, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53610, 'Panna Cotta Xoài Chanh Dây', 'Panna Cotta Xoài Chanh Dây- một món bánh ngọt xuất xứ từ Ý, nay được biến tấu thêm xoài và chanh dây để tăng thêm hương vị và màu sắc cho món bánh. Món bánh có 5 lớp, mỗi lớp có mùi vị khác nhau khiến cho cảm giác ăn sẽ không bị ngán.Vị chua nhẹ của chanh dây và ngọt thanh của xoài hòa quyện với vị ngọt mềm mại vốn có của Panna cotta . Hãy cũng Cooky vào bếp để thực hiện món bánh đầy ngọt ngào này nhé. ', 4, '2022-03-22 00:00:00', 120, null, null);
