insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53631, 'Tôm Nướng Phô Mai', 'Tôm là thực phẩm có chứa hàm lượng canxi, dinh dưỡng cao, vì thế đây là thực phẩm được nhiều người dùng để chế biến thành nhiều món ăn khác nhau. Đặc biệt là tôm nướng phô mai được rất nhiều người yêu thích bởi hương vị đậm đà, “lôi cuốn” của món ăn. Cùng Cooky thực hiện món ăn này nhé!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 60, null, null);
