insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53634, 'Cút Chiên Bơ', 'Cút Chiên Bơ- món ăn chơi không thể bỏ qua cho những tính đồ ăn vặt, cút được tẩm ướp vừa vị, rồi đem chiên qua lớp bơ thơm phức càng làm kích thích vị giác, nhìn là khó có thể cưỡng lại được. Chỉ với vài bước thực hiện đơn giản là bạn đã có ngay món ăn để lai rai với bạn bè vào dịp cuối tuần rồi. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53642, 'Chè xoài bột báng', 'Những ngày thời tiết nóng bức, một món tráng miệng ngon lành, dễ làm là những tiêu chí được ưu tiên hàng đầu. Và món chè xoài bột báng mát rượi, ngon lành chính là lựa chọn tuyệt vời cho bạn. Vị xoài ngọt, thơm quyện trong vị béo ngậy của nước cốt dừa mát lạnh, dai dai của bột báng khiến món chè xoài bột báng trở nên thật hấp dẫn.<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53643, 'Bánh heo quay', 'Bánh Heo Quay - là bánh ngọt truyền thống nổi tiếng khắp xứ sở Trà Vinh. Bánh có hình dạng giống miếng thịt heo quay, được làm từ các nguyên liệu tự nhiên như khoai môn, bột gạo, nước cốt dừa rất ngon và hấp dẫn cho gia đình thưởng thức. Cùng theo chân Cooky học cách làm món bánh có hình dạng độc đáo này ngay thôi!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53648, 'Chè hạt sen đậu ván', 'Chè hạt sen đậu ván là món ăn tráng miệng thanh nhiệt rất thích hợp trong thời điểm nắng nóng. Vừa ngon vừa mát vừa bổ.', 4, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53651, 'Sinh tố Bơ', 'Công dụng của quả bơ: Tốt cho hệ tim mạch, giảm nồng độ cholesterol trong máu. Đẹp da. Giảm căng thẳng. Tốt cho hoạt động của não bộ. Tốt cho tóc và móng. Thúc đẩy phục hồi và tái tạo cơ bắp. Tăng cường chức năng quan sát của mắt. Kháng viêm. Chống lão hóa, chống oxy hóa.', 1, '2022-03-22 00:00:00', 10, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53652, 'Tiết Canh Củ Dền', 'Nếu bạn là fan của món tiết canh nhưng lại sợ không an toàn, vệ sinh.  Giờ đây món Tiết Canh Từ Củ Dền vừa an toàn lại đảm bảo vệ sinh thì ngại gì mà bạn còn không thử ngay.  Giòn sần sật từ mề và cuống họng kết hợp với vị thanh mát của củ dền thêm một chút đậu phộng rang và rau húng, đơn giản lại vô cùng dễ làm là bạn đã có ngay bát tiết canh củ dền ngon mát giống như thật rồi.', 3, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53657, 'Bánh mì caramel', '<div><span style="color: #222222;" times="" new="" roman="" georgia="" serif="" font-size:="" 17px="" background-color:="" rgba="" 255="" 0="" 95="" data-mce-style="color: #222222;">Món bánh mì caramel gần đây đang làm mưa làm gió trên mạng bởi độ ngọt ngào, giòn tan khó ai cưỡng lại được. Và cùng với cách làm </span><span style="color: #222222;" times="" new="" roman="" georgia="" serif="" font-size:="" 17px="" background-color:="" rgba="" 255="" 0="" 95="" data-mce-style="color: #222222;">không hề khó chút nào cả, các bạn hãy nhanh lưu công thức để cùng Cooky làm thử thôi nào!</span></div>', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53658, 'Mứt mận Hà Nội', 'Món mứt mận Hà Nội với hương vị thơm ngon, màu sắc hấp dẫn được rất nhiều bạn ưa chuộng mỗi khi mùa mận về. Hãy cùng Cooky với một chút khéo léo để vào bếp làm món mứt mận Hà Nội bắt mắt, dẻo ngon lại vô cùng đơn giản sau đây nhé. <br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53659, 'Ngan cháy tỏi', 'Món ngan cháy tỏi là món ăn rất được yêu thích ở Hà Nội, điều đặc biệt của món ăn này chính là vị đậm đà của gia vị tẩm ướp vào thịt ngan, độ dai nhưng không bị khô chút nào. Được kết hợp cùng tỏi phi xém vàng, giòn thơm giúp món ăn càng ngon hơn và kích thích vị giác rất nhiều, cộng thêm nước mắm chua cay ăn kèm bún và rau sống cũng khá lạ miệng.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53660, 'Bánh mì nướng Hạ Long', 'Bánh mì nướng Hạ Long với vị béo thơm nhẹ nhàng nhưng lại vô cùng cuốn hút, khiến bao người đều muốn được thưởng thức. Bánh mì được nướng vừa đủ, giòn cạnh ngoài còn bên trong lại tan chảy, vàng ươm, nhìn đã thấy hấp dẫn mà ăn thì còn gây nghiện hơn nhiều, vậy hãy cùng Cooky vào bếp làm món bánh mì nướng Hạ Long này nhé!<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53668, 'Canh mướp hương nấu tôm khô', 'Nồi canh mướp không dầu mỡ, ngọt mát , đơn giản dễ làm.', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53669, 'Bánh canh chả cá Nha Trang', 'Ai đã từng đến Nha Trang có lẻ sẽ không thể quên mùi vị của món bánh canh chả cá nơi đây. Nước dùng thanh thanh, mùi thơm từ chả cá biển kèm theo một chút hành ngò đơn giản nhưng lại khó quên.', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53672, 'Kim bắp Hàn Quốc', 'Đảm bảo ngon', 2, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53687, 'Mứt atiso đỏ', 'Hoa atiso đỏ hay còn gọi là hoa bụp giấm có thể chế biến rất nhiều món khác nhau. Mứt atiso vừa chua chua - ngọt ngọt và có độ giòn, rất thích hợp cho gia đình cùng nhâm nhi.', 12, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53688, 'Mì Quảng tôm thịt', 'Ai đã từng đến xứ Quảng có lẻ sẽ không bao giờ quên được hương vị đậm đà từ món mì quảng. mì quảng có nhiều loại, có nhiều cách nấu nhưng để nấu được món mì quảng đúng chuẩn tại Tp HCM, không khó. Nhưng quang trọng hơn hết, chúng ta phải tìm được nguyên liệu đặc biệt để làm nên hương vị chính của món ăn đó là củ nén. Để làm được món ăn này, mình phải di chyển hơn 20km từ Bình Chánh đến Chợ Bà Hoa để mua ít củ nén, bánh đa và ít sợi mì quảng. Cùng thực hiện nhé', 10, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53689, 'Cá hú kho trái bần', 'Trái bần có lẻ không quá xa lạ với người dân Nam Bộ. Bần vừa có giá trị y học, vừa là nguyên liệu để tạo nên vị độc đáo cho một số món ăn như thịt kho, cá kho, lẩu, canh chua... bần rất dễ tìm thấy tại các tỉnh miền tây sông nước. Cứ đi dọc theo bờ sông là bạn sẽ thấy ngay. Bần với nhiều cô chú 6x, 7x các anh chị 8x có lẻ đôi khi là cả một thời tuổi thơ. Cứ mỗi độ trưa hè, hay chiều ta là vắt vẻo trên mấy nhánh bần... đúng là thật khó quên cái vị chua chua... chát chát vốn có.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53690, 'Chè xoài hoa đậu biếc', 'Cứ Tần độ tháng 3- tháng 4 là Bình Chánh lại bước vào mùa xoài 3 mùa mưa. giá khá rẻ chỉ tầm khoảng 10-13k/ký. Kèm theo giàn hoa đậu biếc trước nhà đang trổ hoa... thế là có ngay món chè xoài hoa đậu biếc. Đơn giản nhưng rất ngon. Mọi người có thể thay xoài bằng Mít, Dâu cũng sẽ rất ngon đấy', 10, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53691, 'Bánh khoai mì sầu riêng nướng', 'Món ăn dân dã nhưng cũng không kém phần tinh tế. Kèm theo hương vị sầu riêng thơm ngát, mọi người sẽ ngất ngây với món bánh này. làm món này mọi người nhớ rèn luyện thể dục thể thao cho kỹ... mài khoai mà nổi gân lên chuột chứ chả đùa', 5, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53694, 'Cháo lươn', 'Món ngon bổ dưỡng, thích hợp cho mọi lứa tuổi.', 2, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53695, 'Lòng Heo Khìa Xá Xị', 'Lòng heo khìa xá xị, nghe tuy lạ nhưng lại rất ngon, thử một lần là ghiền ngay. Lòng heo giòn sần sựt, được khìa với nước xá xị tạo ra mùi thơm nức mũi. Với cách chế biến vô cùng đơn giản, nhưng sẽ không làm bạn phải thất vọng vì độ ngon của món ăn này. Hãy cùng Cooky vào bếp thực hiện món ăn này mang thêm hương vị vào bữa cơm gia đình nào. ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53696, 'Hoành Thánh Chiên Sốt Bò Cay', 'Hoành Thánh Chiên Giòn Sốt Bò Cay, món ăn chơi tuy lạ miệng nhưng hương vị độc đáo của nó sẽ khiến bạn phải mê mẩn hết miếng này đến miếng khác. Lá hoành thánh được chiên giòn rôm rốp, hòa quyện với sốt bò cay thơm lừng càng ăn càng ghiền. Hãy bỏ túi ngay công thức món ăn thơm ngon này nhé. ', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53699, 'Ốc nấu chuối đậu', 'Ốc nấu chuối đậu - món ăn ngon cho bữa cơm gia đình.<br data-mce-bogus="1">', 5, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53701, 'Xôi mặn', null, 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53702, 'Mỳ quảng cá lóc miền Trung', 'Rời quê hương lâu lâu lại thèm món mỳ quảng cá lócHương vị đậm đà ngon ngọt dân dã mà người miền trung khó quên ', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53703, 'Mì trộn bò viên', 'Là dân "Đạo mì" Ric tin chắc các bạn đã bỏ hẵn trong túi bí kíp 1000 món ăn với mì gói. đơn giản, nhanh chống nhưng cũng không kém chất lượng ở tiệm, vào bếp một buổi sáng bận rộn với Mì Trộn Bò Viên', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53709, 'Trứng Chiên Nhúng Sốt Hoàng Kim', 'Trứng chiên, món ăn vặt chắc ai cũng biết, nhưng nhúng với sốt hoàng kim thì bạn đã thử chưa? Sốt hoàng kim có vị béo ngậy từ trứng muối và phô mai được chế biến thành sốt có mùi thơm nồng, hấp dẫn, trứng cút chiên được nhúng vào sốt thấm đượm vị, ăn 1 lần là nghiện ngay. Hãy cùng Cooky bắt tay vào làm ngay món ăn vặt này, sẽ không khiến bạn phải thất vọng đâu.', 3, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53714, 'Thạch mochi hoa anh đào muối', 'Mình có ít hoa anh đào muối nên phải triển luôn món thạch Mochi giọt nước ao ước được làm bấy lâu. ', 5, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53719, 'Xôi Mặn Kiểu Quảng Đông', 'Xôi mặn, món ăn hầu như rất quen thuộc với mọi người. Với cách chế biến hoàn toàn khác xôi mặn truyền thống, sẽ cho các bạn một mùi vị khác, khá đặc biệt, mềm dẽo. Mọi người thử làm món này nha.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53720, 'Gà kho sả nước dừa', 'Món gà ngon không thể thiếu cho bữa cơm Việt. Chút cay cay của ớt tươi, đặc trưng của sả, hành, chút bột nghệ, và vị béo của nước cốt dừa. Tất cả hoà quyện tinh tế vào món ăn này. ', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53721, 'Xíu mại', 'Món này có thể dùng làm điểm tâm sáng khi kết hợp cùng bánh mì. Hoặc dùng với cơm nóng nhé các bạn!', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53722, 'Bánh flan', 'Thơm hột gà, ngọt ngào vị sữa, đậm đà lớp caremen, mịn màng khó quên', 7, '2022-03-22 00:00:00', 55, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53723, 'Chè đậu xanh bột báng phổ tai', 'Đậu xanh nấu cùng phổ tai( tảo bẹ của nhật ) là một món ăn vừa giàu dinh dưỡng, vừa giúp giải nhiệt cơ thể vào mùa hè.', 6, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53729, 'Hến xào sả ớt đơn giản', 'Hến xào sả ớt đơn giản - Món dùng ăn cơm hoặc ăn chơi đều ngon', 0, '2022-03-22 00:00:00', 5, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53730, 'Chả trứng nướng', 'Với món chả trứng hấp quen thuộc mọi người hay dùng chung với cơm sườn, thì nay mời mọi người cùng trải nghiệm với món chả trứng nướng cách thủy. Chả dẻo, thơm lừng luôn.', 5, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53737, 'Bánh đậu gà mix khoai lang tím nhân phomai cho bé', 'Không gì bằng khi tự làm bánh cho con trong giai đoạn bé bắt đầu tập ăn dặm, vừa đảm bảo an toàn vừa giúp bé cảm thấy thú vị khi ăn. Bánh mềm và ngọt thơm mùi khoai lang rất dễ ăn. Mình người lớn ăn còn ghiền đó ạ! Các mẹ hãy thử nấu cho bé thay đổi món ăn hàng ngày bé sẽ rất thích nè. Chúc các mẹ thành công!! ♥️♥️', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53738, 'Nấm biển ngâm sốt ớt', 'Nấm biển 220k/kg, hộp làm sẵn 170k/hộp', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53744, 'Thịt nướng với nồi chiên không dầu', 'Hầu hết các bạn đều gặp vấn đề thịt bị cháy khi nướng. Đặc biệt khi ướp với mật ong. hãy xem cách của Ric có giúp các bạn không gặp phải tình trạng này nữa không nha.', 3, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53756, 'Cà bát muối xóc tỏi', 'Cà bát muối xóc tỏi là món ăn dân dã được người miền Trung rất yêu thích. Nếu nhớ hương vị quê nhà thân thuộc thì bạn hãy thử làm món ăn này nhé!', 12, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53757, 'Mì trứng sốt tương đen đơn giản', 'Chỉ với lọn mì trứng nhưng bạn có thể chế biến nhiều món ăn khác nhau. Một trong số đó là mì trứng sốt tương đen.', 2, '2022-03-22 00:00:00', 35, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53759, 'Bánh Flan Rau Câu', 'Bánh flan râu câu không làm từ trứng mà được làm từ râu câu. Bánh có vị mát, mềm mịn lại còn ngọt nữa', 2, '2022-03-22 00:00:00', 65, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53761, 'Crepe xoài ngàn lớp', 'Crepe xoài vàng tươi với nhiều lớp bánh xen kẽ là kem tươi béo ngậy, thiết nghĩ đây là món ngon đặc biệt cho trời hè khiến bất cứ ai khi ăn cũng ngây ngất thoả mãn vị giác...', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53765, 'Kem trái cây', 'Chưa bao giờ cách làm kem lại vô cùng đơn giản mà còn là giải pháp tối ưu để thanh lý mớ trái cây tồn kho trong tủ lạnh. Mời cả nhà cùng vào bếp thực hiện món kem giải nhiệt cho mùa hè này nhé', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53766, 'Ổi Luộc Và Muối Ớt Xào', 'Ôỉ luộc- đặc sản vùng Tây Bắc, ổi khi luộc lên thì có màu vàng óng tỏa hương thơm lừng, vừa mềm lại rất ngọt nhưng không hề bị chát, chấm thêm chút muối ớt xào cay thì thơm ngon còn gì bằng. Hãy cùng Cooky thử ngay món ăn chơi thơm ngon này bạn nhé. ', 3, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53771, 'Trà sữa Ô long Tứ Qúy', 'Hướng dẫn làm trà sữa tại nhà cùng Ô long Tứ Qúy, đơn giản tiện lợi nhưng ngon không kém hàng quán, giải pháp cho ngày hè nắng nóng', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53773, 'Gỏi Sứa Xoài Xanh', 'Gỏi sứa xoài xanh - món ăn thanh mát, giải nhiệt cho mùa hè nóng bức. Sứa giàu chất dinh dưỡng, dễ ăn dễ chế biến, ngon giòn sần sựt, kết hợp với vị xoài chua nhẹ đước rưới thêm nước sốt chua ngọt, tất cả hòa quyện vào nhau tạo nên món ăn thơm ngon lạ miệng chinh phục cả những người khó tính. ', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53776, 'Bánh xèo kim chi', 'Món ăn dễ làm,nguyên liệu dễ kiếm ?????', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53780, 'Cơm chiên', 'Món ăn ai cũng có thể làm được. Đêm đói nên mình lấy tạm đồ sót trong tủ lạnh làm ăn chống đói thôi:))  ăn thêm ít salad cùng cốc coca là ngon tuyệt nha ', 1, '2022-03-22 00:00:00', 10, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53796, 'Nha đam hạt chia', 'Nha đam hạt chiaNước nha đam và hạt chia là sự kết hợp tuyệt vời của hai loại thực phẩm tốt cho sức khỏe. Không những vậy, đây còn là thứ nước uống vô cùng thơm ngon .', 2, '2022-03-22 00:00:00', 35, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53797, 'Thịt kho đông pha', 'Thịt kho đông pha, món ăn nổi tiếng của người hoa. Món thịt mềm đậm đà hương vị vì được đun trên lửa nhỏ gần 3h đồng hồ.', 4, '2022-03-22 00:00:00', 210, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53798, 'Tàu Hủ Hoa Đậu Biếc', 'Tàu hủ- món ăn đơn giản nhưng giàu dinh dưỡng từ đậu nành, nay với một chút biến tấu là đã có ngay món tàu hủ hoa đậu biếc vừa mát lạnh, thơm ngon vừa giải nhiệt lại thơm mùi hoa nhài nữa. Chỉ với vài bước thực hiện đơn giản thôi là đã có món ngon đãi gia đình rồi. Cùng Cooky theo dõi bí quyết này nhé. ', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53799, 'Pudding Sữa Tươi Trân Châu', 'Pudding sữa tươi trân châu, món ăn vặt rất được ưa chuộng. Pudding sữa béo ngậy, núng nín, căn tròn, mềm mịn kết hợp với trân châu mềm, dai thấm đều đường nâu tạo ra mùi thơm rất đặc trưng. Bạn sẽ cảm nhận được pudding sữa tươi trân châu mát lạnh tan trong miệng ngay miếng đầu tiên. Vừa đơn giản, mà lại ngon mát thì ngại gì không thử. ', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53800, 'Kem que Oreo', 'Những que kem được làm từ bánh Oreo nổi tiếng và được nhiều bạn yêu thích, với vị ngọt mát rượi sẽ khó cưỡng lại trong những ngày hè oi nóng này. Bạn có thể tự tay làm món kem Oreo mà không cần những vật dụng hay nguyên liệu cầu kỳ, cùng Cooky trổ tài khéo tay để khiến cả nhà ngạc nhiên.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53801, 'Tôm rang muối Hảo Hảo', 'Món tôm rang muối Hảo Hảo là món ăn hấp dẫn, lạ miệng và có thể cùng Cooky vào bếp trổ tài ngay tại nhà. Mà không cần phải ra ngoài hàng quán vẫn có được món ăn thơm ngon, đảm bảo vệ sinh.  <br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53802, 'Gỏi gà măng cụt', '<div> Với cách làm gỏi gà măng cụt độc đáo và hấp dẫn này, sẽ mang đến cho bạn một sự kết hợp lạ miệng nhưng vẫn thơm ngon. Gắp một miếng gỏi gà măng cụt, sẽ cảm nhận được vị thịt gà ngọt bùi cùng với vị đặc trưng của măng cụt, thêm sốt chua ngọt cay cay, chút béo bùi của đậu phộng rang làm nên món gỏi hương vị khó cưỡng.</div><div><br></div>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53803, 'Thịt nướng kim tiền', '<span style="color: #4d5156; font-family: arial, sans-serif; font-size: 14px;" data-mce-style="color: #4d5156; font-family: arial, sans-serif; font-size: 14px;">Món thịt nướng kim tiền không còn quá xa lạ đối với người dân Nam Bộ, với lớp thịt bò được bọc bên ngoài mềm và thịt heo bên trong ngọt thịt không bị khô. Hãy cùng Cooky thực hiện món ăn hấp dẫn khó từ chối để chiêu đãi bạn bè vào những ngày rãnh rỗi nhé!</span><br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53804, 'Nhộng tằm chiên sốt cay', '<div>Món nhộng tằm chiên sốt cay béo ngậy kết hợp với bánh đa nướng càng làm món ăn trở nên đặc biệt hơn. Đây chắc chắn sẽ là món ăn ngon, dân dã để đổi bữa cho gia đình.</div><div><br></div>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53805, 'Thịt viên trứng cút sốt cà', 'Cách làm món thịt viên trứng cút sốt cà với vị ngọt mềm, béo ngậy hòa cùng nước sốt cà chua đậm đà sẽ làm bữa cơm nhà bạn ngon miệng hơn. Với món ăn hấp dẫn này sẽ tiết kiệm được rất nhiều thời gian và một hương vị mới lạ từ Xốt Gia Vị Hoàn Chỉnh Barona - Sườn Xào Chua Ngọt.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53806, 'Canh bí đao nấu mọc', 'Canh bí đao nấu mọc với vị ngọt mát của bí đao, được nấu cùng với mọc viên tuy trông đơn giản nhưng rất bổ dưỡng giúp cho bữa cơm càng thêm trọn vị. Cùng vào bếp với Barona để nấu bữa cơm gia đình cùng canh bí đao nấu mọc này nhé!<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53807, 'Bánh Flan Rau Câu Mè Đen', 'Bánh flan rau câu mè đen, món bánh lạ miệng nhưng đầy dinh dưỡng từ mè đen, với cách làm đơn giản là bạn đã có ngay món bánh thơm ngon để chiêu đãi gia đình. Với vị bùi bùi của mè đen được xay nhuyễn, kết hợp với sữa tươi và trứng gà, tạo nên hương vị vô cùng độc đáo. Hãy cùng Cooky thử ngay món bánh flan rau câu này nhé.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53808, 'Sủi cảo chay', 'Bí quyết nấu nước lèo chay đậm đà, đặc biệt không sử dụng mì chính cũng như câc chất tạo mùi. mời cả nhà cùng vào bếp ạ.', 5, '2022-03-22 00:00:00', 150, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53814, 'Pad Thái - Mì xào kiểu Thái', 'Món ăn đường phố Thái Lan nổi tiếng khắp thế giới. Pad Thái đúng của Thái sẽ ko ăn kèm các loại thịt mà chỉ dùng chủ yếu tôm khô, tôm tươi, trứng, đậu hủ và cải muối. mời mọi người cùng vào bếp và chế biến ngay tại nhà', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53821, 'Lẩu cua đồng', 'Món ăn dân dã với công thức tròn vị đậm đà, ngọt thanh của cua đồng ', 5, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53822, 'Bánh Bèo Từ Bánh Phồng Tôm', 'Bánh bèo - món ăn trứ danh xứ Huế, nhưng bạn lại ngán ngẩm khâu pha bột cầu kì, giờ đây với một chút biến tấu là bạn đã có ngay dĩa bánh bèo thơm ngon. Bánh bèo từ bánh phồng tôm đáp ứng được tiêu chí nhanh- gọn của bạn nhưng hương vị không thua gì bánh bèo làm bằng bột. Hãy cùng Cooky bỏ túi ngay công thức này nhé.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53823, 'Càng Ghẹ Rang Muối Ớt', 'Càng Ghẹ Rang Muối luôn là món khoái khẩu của tín đồ mê hải sản, được áo bên ngoài lớp muối mằn mặn, cay cay có màu đỏ bắt mắt , còn phần thịt ghẹ bên trong thấm đượm vị kích thích vị giác cuốn hút người ăn. Cuối tuần, làm món này lai rai với bạn bè thì còn gì bằng. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53825, 'Trà sen vàng', 'Trà sen vàng từ lâu đã là thức uống hấp dẫn dành cho giới trẻ.  Sự kết hợp độc đáo giữa trà ô long thanh mát, hạt sen thơm bùi. Thêm vào chút sữa sẽ để vị thêm ngọt ngào. Cùng Cooky thực hiện ngay món uống ngon tuyệt này thôi!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53826, 'Chè hạt đác', 'Món chè hạt đác chinh phục bất cứ ai khi thưởng thức nhờ cảm giác thích thú khi nhai những hạt đác giòn dai rất đặc trưng, có vị ngọt mát vừa phải, dễ chịu. Kết hợp hạt đác cùng các nguyên liệu trái cây như dâu tây, mít càng làm cho món chè thêm lôi cuốn khó cưỡng. Theo dõi công thức dưới đây của Cooky để biết cách làm món chè ngon mát này nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53827, 'Lẩu khô không nước', 'Thông thường nói đến lẩu là bạn nghĩ ngay đến nồi nước dùng và các loại nguyên liệu. Tuy nhiên với món lẩu khô này bạn sẽ được thưởng thức với một phong cách hoàn toàn khác. Bằng cách sử dụng nồi Tchef, với khả năng tỏa nhiệt nhanh giúp hầm mềm các nguyên liệu dễ dàng, bạn sẽ được tự tay đảo các nguyên liệu đã được tẩm ướp sẵn và thưởng thức chúng. Cùng Tupperware thực hiện món Lẩu khô độc đáo này nhé!<br>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53828, 'Bánh mì khoai lang tím', 'Vào những dịp cuối tuần sum họp của gia đình, hoặc những ngày lễ như Tết Trung thu, bạn có muốn làm một món bánh thơm ngon để thưởng thức cùng với những người thân thương trong gia đình. Mọi người cùng nhau nhâm nhi miếng bánh, uống một tách trà nóng, cùng nhau trò chuyện, tăng không gian gần gũi và ấm cúng. Cooky xin giới thiệu cho bạn món Bánh mì khoai lang tím vừa dễ làm lại thơm mềm, vừa đẹp mắt sẽ làm hấp dẫn cả nhà đấy. Tham khảo cách làm bánh mì khoai lang tím dưới đây bạn nhé!<br>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53829, 'Sốt chấm bánh tráng', 'Món sốt chấm bánh tráng là một trong những thú vui để thưởng thức bánh tráng, với những thành phần quen thuộc những lại mang đến cho hội ăn vặt đầy thích thú. Món sốt này với điểm nhấn chính là sự kết hợp giữa sốt bơ và sốt tắc, mang đến mùi thơm đặc trưng cùng độ béo béo mới lạ. Hãy cùng Cooky điểm xem cách làm món ăn gây nghiện giới trẻ này nhé!', 4, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53830, 'Cá cơm chiên giòn sốt me', '<font color="#4d5156" face="arial, sans-serif"><span style="font-size: 14px;" data-mce-style="font-size: 14px;">Cách làm cá cơm chiên giòn sốt me hấp dẫn với lớp bột giòn rụm, phần thịt cá béo thơm được phủ đều sốt nước mắm me chua cay, món ăn này rất thích hợp để dùng cùng với cơm trắng nóng hổi vào những ngày mưa này.</span></font><br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53831, 'Tương ớt tại nhà', 'Tương ớt, loại nước chấm phổ biến dùng kèm với nhiều món ăn sẽ làm tăng thêm hương vị của  món ăn. Từ cay nhẹ đến cay nồng tùy theo khẩu vị người dùng. Cooky sẽ hướng dẫn bạn cách làm tương ớt tại nhà với vị cay nhẹ của ớt kết hợp với nhiều mùi thơm của các loại gia vị, đặc biệt là đảm bảo an toàn, sạch sẽ cho cả gia đình bạn. Còn chần chừ gì nữa hãy bắt tay vào làm ngay thôi.', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53840, 'Mì Ý Chay', '<p>Mì ý chay là một món chay dễ nấu, dễ ăn, tốt cho sức khỏe. Mì ý chay với nước sốt rau củ quả và nấm tại nên hương vị mới lạ, thơm ngon cho những ngày ăn chay</p>', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53845, 'Bánh sữa chua phô mai', 'Món bánh với công thức đơn giản, dễ thực hiện nhưng hương vị thơm ngon khó cưỡng cho trẻ nhỏ.', 4, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53847, 'Hủ tiếu chay', ' ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53848, 'Bò kho', 'Bò Kho - món ăn ngon giúp đổi vị cho bữa ăn gia đình thêm hấp dẫn. Cách nấu Bò kho đơn giản theo định lượng nguyên liệu và gia vị nêm nếm chuẩn của nhà mình nhé!<br data-mce-bogus="1">', 5, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53849, 'Cá xào cần nước', 'Cá Xào Cần Nước - món ăn ngon dễ làm, đổi vị cho bữa cơm gia đình thêm hấp dẫn.<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 10, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53850, 'Nộm hoa chuối non', 'Nhiều người khi sử dụng búp chuối thường có thói quen bỏ phần hoa chuối non. Thực ra đây là nguyên liệu làm một món ăn dân dã rất ngon. Món nộm hoa chuối vừa hơi chát, mặn mặn, cay cay - chắc chắn sẽ rất đưa cơm.', 4, '2022-03-22 00:00:00', 35, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53852, 'Chè khúc bạch', 'Chè khúc bạch là món ăn giải nhiệt phổ biến vào mùa hè của đa số người Việt Nam. Chè được bán phổ biến tại các quán chè, quán sinh tố. Chè khúc bạch đơn giản giống như rau cau, được biến tấu nhiều mùi vị, như là vị matcha, vị dâu, vị cacao. Với nhiều thành phần đi kèm như là trái cây, trái vải, hạt é, hạt hạnh nhân khiến cho chè khúc bạch trở nên hấp dẫn hơn, và nó là món chè mà tất cả mọi người nên nấu cho gia đình của mình ăn trong những ngày hè nóng nực này <3', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53884, 'Gà xé phay', 'Quá tuyệt vời với món gà này.', 4, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53888, 'Miến Chua Cay Trùng Khánh', '<p>Ẩm thực Trùng Khánh luôn mang nét đặc trưng với những hương vị cay nồng, đậm đà, cảm giác không ngừng xuýt xoa mỗi khi thưởng thức. Trong đó, Miến chua cay Trùng Khánh là một món ăn đường phố phổ biến khắp Trung Quốc, bên cạnh đó còn là một món đang rất "hot" của giới trẻ hiện nay. Cooky sẽ chia sẻ cho bạn công thức nấu miến chua chua, vị cay nồng này nhé!</p>', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53889, 'Sườn sốt ớt chuông chua cay', 'Món sườn sốt với ớt chuông chua chua cay cay nhưng không ngọt nhé=)))', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53891, 'Bánh Hẹ Người Hoa', 'Bánh hẹ người Hoa là món ăn đặc trưng có mặt từ rất lâu đời ở Sài Gòn. Những chiếc bánh dân dã, mộc mạc nhưng đậm đà hương vị được mọi tầng lớp người dân ưa chuộng. Thường thì món ăn này chỉ xuất hiện ở những khu người Hoa sinh sống nên muốn tìm một nơi bán món này ngon không phải là dễ. Tuy nhiên, với những gợi ý dưới đây thì bạn sẽ tìm được một hàng bánh hẹ thơm ngon và chất lượng để lót dạ vào những buổi xế chiều. Dù bạn không thích ăn hẹ, thế nhưng hãy cứ thử ăn ít nhất 1 lần, biết đâu lại mê luôn món banh này đó.', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53892, 'Bánh Sầu Riêng Chiên Giòn', 'Bánh sầu riêng chiên giòn vô cùng thơm ngon mà lại đơn giản, dễ làm. Đối với những người nghiện sầu riêng thì đây là món bánh mà bạn không thể bỏ qua. Vị ngọt và bùi của sầu riêng, xen lẫn chút béo ngậy của sữa, cùng lớp vỏ bánh giòn rụm sẽ khiến bạn ăn mãi chẳng ngừng đấy. ﻿Bạn hãy bỏ túi ngay công thức làm món bánh này nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53893, 'Phở Chay', 'Phở được biết đến là một món ăn truyền thống của Việt Nam, cũng có thể xem là một trong những món ăn tiêu biểu cho nền ẩm thực nước ta. Thành phần chính của phở là bánh phở, nước dùng và thịt bò hay thịt gà cắt mỏng. Tuy nhiên, nếu bạn là người ăn chay thì bạn đừng lo vì Cooky sẽ chia sẻ cho bạn cách nấu món Phở chay vô cùng tham đạm, dễ làm ngay tại nhà. Bạn hãy tham khảo công thức ngay dưới đây nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53894, 'Bún Xào Lòng Nghệ', 'Bún xào lòng nghệ là một ngón ngon nổi tiếng của xứ Huế. Bún được nấu với lòng heo, nghệ tươi, hẹ, ớt và gia vị, đã đem đến một món ăn vô cùng đậm đà xen kẽ vị cay nồng. Có lẽ bởi sự kết hợp giữa những nguyên liệu và cách chế biến rất lạ nên món ăn cũng mang đến một hương vị rất riêng. Các bạn hãy cùng Cooky bỏ túi công thức nấu món này nhé!', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53895, 'Mắm đu đủ', 'Mắm đu đủ là món ăn dân dã nhưng lại rất hấp dẫn với nhiều người, nó thường xuất hiện trong bữa cơm của người dân miền Tây. Với các nguyên liệu đơn giản quen thuộc, chỉ cần áp dụng đúng các bước thực hiện là bạn sẽ có ngay dưa mắm đu đủ chua chua ngọt ngọt đậm đà, giòn ngon khó cưỡng. Thêm món dưa mắm đu đủ này vào thực đơn để sẵn sàng trổ tài nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53896, 'Chanh tươi tuyết', 'Chanh tươi, đường ,sữa: Bỏ tất cả vào xay nhuyễn? tầm 5p.', 1, '2022-03-22 00:00:00', 5, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53898, 'Xôi đậu biếc nước cốt dừa và thịt chiên', 'Xôi hoa đậu biếc nước cốt dừa và thịt chiên là món ăn chơi đơn giản mà lại ngon miệng và đẹp mắt. Khi ăn cắn một miếng xôi béo ngọt kèm với món thịt chiên đậm đà sẽ khiến khẩu vị của bạn thay đổi thích thú. Cùng theo dõi công thức làm xôi hoa đậu biếc ngon và hấp dẫn dưới đây của Cooky nhé!<br>', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53899, 'Bánh xèo', 'Bánh xèo là món ăn được nhiều người yêu thích bởi vị béo bùi của bột gạo kết hợp vị ngọt từ tôm thịt giá và vị mặn ngọt chua chua của nước chấm khiến bạn ăn hoài không chán. Hãy tham khảo cách làm bánh xèo ngon giòn rụm dưới đây để thực hiện cho các thành viên gia đình thưởng thức bạn nhé!<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53902, 'Gỏi gà măng cụt - Đặc sản Bình Dương', 'Măng cụt là loại trái cây ngon, giàu dinh dưỡng. ngoài cách ăn trực tiếp bạn cũng có thể chế biến măng cụt thành các món ngon bổ dưỡng. Nhân tiện mua được ít măng cụt già, mời mọi người cùng vào bếp với món gỏi gà măng cụt. nếu mọi người thích món ăn, hãy đăng ký kênh youtube để ủng hộ Ric nha.', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53904, 'Tu hài đỏ nướng mỡ hành', 'Tu hài đỏ nướng mỡ hành dai giòn ngọtTu hài luộc qua bóc bỏ bụng đen rửa sạch. Ướp với chút hạt nêm mì chính. Xếp vào vỏ vỏ lò nướng 200 độ trong vòng 10 phút. Hành thái dọc phi thơm. Hành lá cho vào mỡ phi hành đảo cho teo lại. Lấy tu hài rắc hành lá hành phi lên trên. Chấm tương ớt và thưởng thức', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53905, 'Bánh tráng nướng bằng chảo', 'Bánh tráng nướng là món ăn không thể thiếu khi bước chân lên Đà lạt. Bánh tráng nướng được nướng trên bếp than, với đầy đủ mùi vị từ phô mai, pate, trứng, xúc xích, bánh tráng nướng là món ăn vặt thật sự không thể thiếu đối với mọi người.', 1, '2022-03-22 00:00:00', 5, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53909, 'Canh đậu hũ non Hàn Quốc | SUNDUBU JJIGAE |순두부찌개', 'Canh đậu hũ non một món ăn truyền thống của người Hàn Quốc. Đặc biệt sau khi xem hết series phim Itaewon Class... Ric đã tự thử thách mình bằng món canh siêu hót của nhà hành Danbam, Itaewon. mời cả nhà vào bếp.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53910, 'Cơm chiên trứng vịt muối', 'Bữa ăn sáng đầy đủ dinh dưỡng mà lại tiết kiệm. Vị béo của lòng đỏ trứng hòa tan cùng cơm giống như vị của xôi vò vậy á. Mọi người nên thử 1 lần cho biết. Riêng mình thì ghiền món này luôn ?', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53912, 'Mít xào chay', 'Mít Xào Chay - món ngon dễ làm, đổi vị cho bữa cơm gia đình trong những ngày ăn chay.<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 10, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53915, 'Ngô Chiên Bột', '<p>Ngô chiên bột là món ăn vặt quen thuộc ở miền Bắc nè, cách làm cực kì đơn giản mà lại ngon, nhâm nhi trong lúc xem phim hoặc lai rai cùng bạn bè cũng thú vị lắm. Cùng bắt tay vào bếp làm thôi nè.</p>', 1, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53919, 'Bún Bò Chay', 'Bún bò chay là món ăn được nấu từ nguyên liệu chủ yếu là rau củ và nấm thích hợp cho những người ăn chay hoặc cho gia đình thưởng thức vào dịp ngày Rằm. Món ăn thanh đạm, dễ nấu nhưng lại có hương vị thơm ngon, đậm đà chẳng khác gì bún bò vị mặn thông thường. Hãy cùng Cooky bắt tay vào nấu ngay món bún bò chay để đãi gia đình nhé. ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53920, 'Bánh Bắp Chiên', 'Bánh bắp chiên- món ăn món ăn vặt khá quen thuộc với mọi người, bánh có màu vàng ươm đẹp mắt, mùi thơm hấp dẫn, vị mặn ngọt hòa quyện vừa phải , khiến thực khách ăn một lần là mê ngay.  Được là từ cách nguyên liệu đơn giản, chế biến nhanh chóng là bạn đã có ngay món ngon chiêu đãi gia đình.', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53928, 'Cánh Gà Sốt Teriyaki', 'Món gà sốt teriyaki kiểu Nhật đơn giản, đậm đà hương vị, thơm ngon khó lẫn. Món ăn khá dễ thực hiện tại nhà cho các bữa tụ họp gia đình và bạn bè. ', 6, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53929, 'Chả Nấm Chiên', 'Chả Nấm Chiên- món ngon không khó thực hiện, chỉ với vài bước chế biến đơn giản là bạn đã có ngay dĩa chả nấm chiên thơm ngon đầy dinh dưỡng từ nấm và rau củ cho ngày dài hoạt động. Thanh mát từ các loại nấm và rau củ, kết hợp cùng thịt heo mềm, béo được trộn bột chiên giòn  làm cho món ăn thơm ngon khó cưỡng.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53932, 'Trà vải ngâm', '<div style="text-align: justify;" data-mce-style="text-align: justify;">Trà Vải Ngâm - thức uống thơm ngon, giúp giải nhiệt mùa hè cực hấp dẫn. Cách làm Trà vải ngâm đơn giản với nguyên liệu dễ tìm mua tại siêu thị, bạn có thể điều chỉnh lượng đường, dùng nóng hay lạnh tùy theo sở thích. Bảo quản trà vải thiều trong ngăn mát tủ lạnh dùng cả ngày rất tiện lợi nhé. (Tham khảo: Mrs. Amy - atasteofjoyandlove.com)</div>', 2, '2022-03-22 00:00:00', 15, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53933, 'Nước chanh yakult mát lạnh ngày hè', '<div style="text-align: justify;" data-mce-style="text-align: justify;">Nước Chanh Yakult có vị chua chua, ngọt ngọt, thơm hương tinh dầu chanh rất đặc trưng. Cách làm Nước chanh yakult đơn giản, không chỉ giúp giải nhiệt mùa hè mà còn rất tốt cho sức khỏe. Vị chua của chanh kích thích vị giác giúp bữa cơm gia đình thêm ngon miệng. Ngoài ra, yakult còn rất tốt cho tiêu hóa nữa nhé. (Tham khảo: angsarap.net)</div>', 1, '2022-03-22 00:00:00', 5, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53935, 'Cocktail Cherry thơm ngon tuyệt đỉnh', '<div style="text-align: justify;" data-mce-style="text-align: justify;">Thấy cherry tươi chín mọng giảm giá quá chừng nên mua về 500gr để tủ lạnh dùng dần. Sẵn tiện hôm nay trời nóng mà dạo web thấy có cách làm cocktail cherry rất hay ho nên quết định làm theo, sẵn chia sẻ cho mọi người ai có nhu cầu. (Tham khảo: thefreshcooky.com)</div>', 2, '2022-03-22 00:00:00', 10, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53937, 'Cherry Nướng Olive', '<div style="text-align: justify;" data-mce-style="text-align: justify;">Bỏ túi thêm một cách chế biến cherry để giải quyết lượng trái cây còn nhiều trong tủ lạnh. Cherry Nướng Olive cùng với mùi tây (parley), nêm ít muối và tiêu, tuy đơn giản nhưng ăn khá vừa miệng. Dùng trộn salad hay ăn kèm với bánh đa nướng đều tuyệt vời. (Tham khảo: withfoodandlove.com)</div>', 4, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53940, 'Chả Lụi Nướng LaGi', 'Chả lụi- món ăn đặc sản xuất xứ ở LaGi- Bình Thuận. Chả lụi được nướng giòn rụm, thơm phức với phần nhân tôm, thịt tươi ngon, chấm với nước sốt đặc biệt beo béo của đậu phộng ăn kèm với rau sống thanh mát. Chả lụi lấy lòng thực khách ngay từ miếng đầu tiên với hương vị thơm ngon, lạ miệng. Với nguyên liệu không mấy cầu kì, cách chế biến cũng đơn giản. Hãy cùng Cooky vào bếp thực hiện ngay món ăn này chiêu đãi gia đình dịp cuối tuần nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53942, 'Bạch Tuộc Nhúng Lá Me', 'Bạch tuộc nhúng lá me, món ăn thanh nhẹ cho ngày cuối tuần. Vị chua, thanh từ lá me non,kết hợp với vị thơm phức của sả được phi thơm. Bạch tuộc tươi được nhúng vài lần qua nước canh lá me sôi rồi vớt ra, vẫn còn giữ được độ giòn tươi mà không hề bị dai, chấm với một ít muối ớt xanh làm đánh thức khức giác và vị giác. ngon khó mà cưỡng lại được. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53946, 'Vịt cháy tỏi', 'Vịt hoặc bạn có thể thay thế bằng ngan. cháy tỏi rất hấp dẫn, hương vị khó quên.', 3, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53949, 'Gà Kho Sả Ớt', 'Gà kho sả ớt là món ăn vô cùng phổ biến trong những bữa cơm của người Việt Nam. Cooky sẽ chia sẻ cho bạn cách nấu thịt gà kho sả mềm thơm, vị cay cay, rất dễ thực hiện. Đây cũng là món lý tưởng để đổi khẩu vị cho bữa cơm gia đình của bạn đấy!', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53950, 'Canh gà lá giang', 'Canh gà lá giang là món canh dễ làm, nhưng hương vị lại vô cùng hấp dẫn. Vị chua dịu của lá giang kết hợp với vị béo mềm của thịt gà sẽ mang đến một bữa ăn đầy mùi vị. Bạn hãy làm ngay món canh gà lá giang vị chua chua, cay cay đậm chất Nam Bộ cho những bữa cơm của gia đình nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53951, 'Gà Hấp Mắm Nhĩ', 'Gà hấp mắm nhĩ- món ăn vừa dễ làm lại thơm ngon khó cưỡng, nước mắm nhĩ thơm phức được ướp với thịt gà ta, cộng thêm một ít  tỏi và tiêu xanh làm tăng thêm hương vị của món ăn, càng ăn càng ghiền, vị ngọt, dai trong từng thớ thịt gà ngấm đều vị ăn hoài không ngán. Chỉ cần bỏ chút thời gian là ta đã có ngay món ăn hấp dẫn này rồi. Cùng vào bếp cùng Cooky ngay thôi. ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53954, 'Rau câu flan cheese', 'Món rau câu flan cheese với hình thức bắt mắt xen kẽ từng lớp màu nâu và màu trắng. Lớp rau câu giòn thơm, thoang thoảng hương cà phê, mát lạnh hòa quyện giữa vị mềm mịn, béo ngậy của flan cheese sẽ rất phù hợp để chiêu đãi cả nhà trong ngày hè nóng bức này.<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53955, 'Nước mát long nhãn', 'Nước mát long nhãn là loại thức uống giúp cho cơ thể thanh nhiệt, thanh lọc hiệu quả và nâng cao sức khỏe. Hãy tự bảo vệ sức khỏe mình bằng cách tự nấu nước mát long nhãn ngay tại nhà với tất cả những nguyên liệu tự nhiên, sạch và an toàn nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53956, 'Kim chi Việt Nam', 'Món kim chi Việt Nam khá là đặc sắc được làm từ nhiều loại nguyên liệu như cà rốt, củ sen, củ kiệu, cải bẹ dưa, cải thảo,... Đặc biệt món ăn tuy được biến tấu phong phú hơn nhưng vẫn giữ được hương vị giòn ngon và hợp khẩu vị của người Việt. ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53957, 'Mì trộn bò cay', 'Món mì trộn bò cay độc lạ với hương thơm của sốt bò kết hợp cùng sợi mì dai ngon óng ánh, chút vị cay cay ớt và chút vị beo béo trứng lòng đào sẽ khiến bạn ngất ngây khó quên. Nhanh tay lưu công thức này để cuối tuần thực hiện mời người thân bạn dùng nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53958, 'Dumpling áp chảo', 'Món Dumpling áp chảo cũng là một món ăn mặn ngon miệng mà bạn có thể dùng làm món khai vị, món ăn phụ hoặc đồ ăn vặt trong bất cứ dịp nào. Đặc biệt với công thức này bạn sẽ cảm nhận được hương vị bánh khá lạ, món ăn sẽ không bị khô và vẫn giữ vị mềm ẩm, giòn giòn rất ngon. Nhanh tay lưu lại ngay cách làm Dumpling áp chảo này nhé.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53959, 'Bánh tráng trộn', 'Món bánh tráng trộn mềm và dai, có vị khô mặn mà, vị chua ngọt của xoài và đặc biệt hơn khi được trộn cùng Sa tế tôm X.O Barona. Cùng Barona trổ tài trộn bánh tráng siêu ngon mà nhanh gọn để cùng bạn bè nhâm nhi nào.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53960, 'Phở chiên phồng', 'Món phở chiên phồng giòn rụm ăn kèm với thịt bò xào và các loại rau, thêm nước chấm chua cay mặn ngọt giúp hương vị bữa cơm gia đình càng thêm đậm đà và ngon lạ vô cùng. Để rõ hơn cách làm hấp dẫn món phở chiên phồng này Tupperware mời bạn cùng tham khảo công thức bên dưới đây nhé!<br><br><br>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53961, 'Bánh Mì Táo', 'Bánh mì táo với lớp vỏ bên ngoài mềm, thơm mùi bơ, bọc lấy phần nhân táo hòa quyện cùng sốt caramel ngọt thanh, điểm thêm một chút mùi hương của bột quế. Với công thức của Tupperware món bánh mì táo này sẽ khiến cả nhà bạn mê mẩn mãi nhé!', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53964, 'Miến trộn Hàn Quốc | JAPCHAE | 잡채', '<div style="text-align: justify;" data-mce-style="text-align: justify;">Ric cũng như rất nhiều bạn khác rất mê ẩm thực Hàn Quốc. Nhân dịp cuối tuần, nên Ric cũng vào bếp thử ngay món Miến Trộn Hàn Quốc. với công thức được nghiên cứu kỹ từ bạn bè đang sống ở Hàn để hương vị sao cho chuẩn nhất. Mời các bạn vào bếp cùng mình thực hiện món này nhé. nếu yêu thích thì nhớ đăng ký kênh cho Ric nha. cảm ơn cả nhà nhiều</div>', 4, '2022-03-22 00:00:00', 40, null, null);
