insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53988, 'Dồi Tiết Lợn Luộc', '<p>Dồi tiết lợn luộc - món ăn khoái khẩu của nhiều người, vị giòn sần sật từ cuống họng, bùi béo của mỡ lá và mềm, ngọt của huyết. Món ăn nhiều nguyên liệu là thế nhưng cách chế biến lại vô cùng đơn giản, chỉ với vài bước thực hiện là bạn đã có ngay món ăn nhâm nhi cuối tuần rồi.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53992, 'Bánh Bông Lan Lá Dứa Phú Sĩ', '<p>Bánh bông lan lá dứa Phú Sĩ là loại bánh ngọt được hấp thay vì nướng như những món bánh bông lan khác. Đây là loại bánh bông lan mềm mềm, bông xốp với phần kem lá dứa, dẻo dẻo, thơm và béo ở trên và trên cùng được rắc một lớp dừa bào nhuyễn. Bạn hãy tham khảo ngay công thức làm món bánh ngọt vừa ngon, vừa dễ làm trong những ngày cuối tuần này nhé!</p>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53993, 'Phở Cuốn', 'Phở cuốn là món ăn được rất nhiều người, nhiều gia đình yêu mến, bên cạnh đó, phở cuốn còn là món ăn thuộc hàng đặc sản của Việt Nam. Tuy vậy, phở cuốn lại là một món ăn đơn giản, dễ làm ngay tại. Một món cuốn thanh mát, đầy ụ đủ thứ rau xanh tươi ngon chính là món ăn lý tưởng cho bạn trong những ngày hè oi bức. Bạn hãy tham khảo ngay cách làm món ngon này ngay dưới đây nhé!', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53994, 'Xôi Mít Lá Nếp', 'Xôi mít lá nếp là món ăn được đông đảo bạn trẻ yêu thích bởi vị giòn thơm của mít, mùi thơm dịu nhẹ của lá nếp, vị dẻo bùi của xôi lẫn chút béo ngọt của nước cốt dừa. Công thức Cooky chia sẻ dưới đây lại rất đơn giản và dễ làm. Bạn hãy làm ngay món ngon này cho gia đình thân yêu vào những dịp cuối tuần nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53995, 'Nước uống dâu tằm', 'Nước Uống Dâu Tằm thơm ngon, cực tốt cho sức khỏe, thích hợp giải nhiệt những ngày nắng nóng.<br data-mce-bogus="1">', 8, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53997, 'Sandwich trái cây kiểu nhật', 'Sandwich Trái Cây Kiểu Nhật - món ngon dễ làm, đổi vị cho bữa sáng gia đình thêm hấp dẫn.<br data-mce-bogus="1">', 6, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53998, 'Cơm chiên cá giòn', 'Cơm Chiên Cá Giòn - món ngon dễ làm, giàu dinh dưỡng cho bữa ăn gia đình bạn.<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (53999, 'Bò nhúng giấm', 'Bò nhúng giấm là món ăn ngon có vị chua thanh vừa kích thích vị giác vừa bổ dưỡng, phù hợp cho những dịp họp mặt gia đình hay những buổi tiệc đơn giản. Với công thức món bò nhúng giấm nhanh gọn của Cooky này sẽ giúp bạn có thể chiêu đãi cả nhà mà không mất nhiều thời gian.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54000, 'Chân giò ngâm mắm', 'Chân Giò Ngâm Mắm - món ăn ngon cực hấp dẫn cho bữa cơm gia đình.<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 40, null, null);
