insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54008, 'Cơm cuộn', 'Cơm Cuộn - món ngon dễ làm, giúp đổi vị cho bữa ăn gia đình thêm hấp dẫn. Cách làm cơm cuộn đơn giản, hạt cơm kết dính cùng với trứng chiên, thanh cua, dưa leo...', 2, '2022-03-22 00:00:00', 20, null, null);
