insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54011, 'Lavender Coffee', '<p>Trong thời gian gần đây, Cold Brew Coffee là một xu hướng thức uống mới mẻ, dễ thực hiện, được mọi người ưa chuộng. Với vị nhẹ, thanh, Cold Brew Coffee mang đến một hương vị mới, sảng khoái hơn, và dịu dàng hơn đối với những tín đồ mê cà phê nhưng không uống được cà phê phin hay cà phê pha máy thông thường. Hôm nay mình sẽ giới thiệu đến mọi người một công thức kết hợp giữa vị Lavender và Cold Brew Cà Phê dành cho mùa hè này!</p>', 1, '2022-03-22 00:00:00', 5, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54015, 'Nước detox hỗ trợ giảm cân mùa hè ', 'Nước Detox - thức uống thơm ngon, mát lạnh, giúp giải nhiệt mùa hè, thanh lọc cơ thể và giảm cân cực hiệu quả.<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 10, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54016, 'Chè Táo Xọn', 'Chè táo xọn hay còn được gọi là chè hoa cau, là món chè có nguồn gốc xuất sứ tại Huế. Chè hoa cau có vị bùi bùi của đậu xanh hòa quyện với nước cốt dừa béo ngậy làm tạo nên một món chè vô cùng thơm ngọt và bỗ dưỡng. Một ly chè có hương vị thanh tao, ngọt mát trong những ngày nắng nóng thì còn gì bằng, bạn hãy tham khảo ngay công thức làm món chè ngay dưới đây nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54017, 'Bánh Bò Dừa Nướng Bằng Chảo', 'Bánh bò dừa là món ăn vặt khá được rất nhiều người yêu thích, có lẽ bởi vị ngọt dịu của vỏ bánh, bên trong là nhân dừa ăn vào bùi bùi không ngán. Bánh bò có nhiều cách nấu như nướng hay hấp. Hôm nay, Cooky sẽ chia sẻ cho bạn món Bánh bò dừa nướng bằng chảo, vô cùng dễ lại tại nhà. Bạn hãy tham khảo công thức ngay dưới đây nhé!', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54019, 'Pate Chay', 'Pate là món thơm ngon, bổ dưỡng được rất nhiều người yêu thích. Pate được làm từ gan heo, bò, gà, vịt,... Tuy nhiên, nếu bạn là người ăn chay thì bạn hãy tham khảo ngay công thức làm món Pate chay từ đậu đỏ, thơm ngon, bổ dưỡng ngay dưới đây nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54020, 'Bún Cá Sứa', 'Bún cá sứa là một món ăn đặc sản của những vùng biển Việt Nam, đặc biệt là Nha Trang. Bạn không nên bỏ qua món ăn này nếu có dịp được đi đến Nha Trang. Nhưng nếu bạn không có dịp thì hãy tham khảo ngay công thức dưới đây để làm món bún sứa ngay tại nhà nhé!', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54021, 'Cari', 'Món ăn thơm, béo, đậm đà. Khoai, đậu, carrot bùi ngọt dẻo. Dùng kèm với bánh mỳ, hoặc bún đều ngon', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54023, 'Bánh Cuốn Ngọt Campuchia', 'Bánh cuốn ngọt Campuchia, món ăn đặc sản của người Campuchia với vỏ bánh mỏng dẻo, ngọt dịu không ngấy, kết hợp với phần nhân làm từ dừa nạo và đậu xanh bùi béo, chấm thêm một ít muối mè làm tăng thêm hương vị món bánh. Cooky sẽ hướng dẫn bạn làm món bánh đặc sản này chỉ bằng một cái chảo chống dính, nhưng vẫn giữ được hương vị và độ ngon của món bánh. Hãy theo dõi bí quyết này là gì nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54025, 'Bao tử heo khìa nước dừa', 'Bao tử giòn giòn sật sật, nhậu hết sảy con bà bảy.Có thể ăn cơm hoặc ăn bánh mì.', 2, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54028, 'Snack hành', 'Snack hành béo, ngậy, giòn... siêu ngonnn,  làm siêu dễ, nguyên liệu siêu siêu dễ kiếm ??', 6, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54032, 'Kim chi Hàn Quốc', 'Món kim chi Hàn Quốc có màu sắc đỏ hấp dẫn có vị chua cay, giòn tan, đậm đà sẽ khiến bữa ăn trở nên ngon miệng hơn. Với công thức này cùng gói Xốt muối kim chi Hàn Quốc Barona sẽ cho bạn cách muối kim chi đơn giản và nhanh gọn nhất.<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54033, 'Cà phê sầu riêng', 'Cà phê sầu riêng là sự kết hợp rất lạ lẫm giữa hương vị đắng truyền thống của hạt cà phê và vị béo bùi ngọt ngào của sầu riêng. Từng giọt cà phê từ phin nhỏ xuống thấm vào sầu riêng cùng với sữa bên dưới. Tất cả hương vị hoà trộn vào nhau tạo nên món cà phê với hương vị béo ngậy ngọt thơm tuyệt hảo.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54034, 'Canh Bún Bạch Tuộc', 'Canh Bún, món ăn quen thuộc, dễ tìm khắp mọi nơi ở Sài Gòn, nhưng với một chút biến tấu thì canh bún bạch tuộc hương vị lạ nhưng không kém phần hấp dẫn, tạo nên sứt hút thực khách ngay lần đầu trải nghiệm. Điểm nhấn của món ăn là phần bạch tuộc được nấu với nước canh bún tạo màu cam bắt mắt. Cooky sẽ hướng dẫn bạn cách nấu món canh bún bạch tuộc thơm ngon để thay đổi khẩu vị gia đình dịp cuối tuần này nhé.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54037, 'Lẩu chao vịt', 'Lẩu chao vịt là món ăn đậm nét miền Tây, mùi thơm của sả, béo bùi của khoai môn, vị lạ của chao làm cho món càng thêm khó tả. ', 4, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54039, 'Cơm chiên hải sản sốt teriyaki', 'Món đổi gió cho cơm chiên sốt teriyaky', 2, '2022-03-22 00:00:00', 35, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54044, 'Chè đậu đen', 'Đậu đen tốt cho sức khỏe ngủ sâu giấc khi bạn ăn mỗi ngày, còn giúp tóc mọc đen trở lại theo thời gian ăn lâu. Hy vọng #bếpnhàdulịchởmỹ chia sẻ cách làm nhanh gọn lẹ anh chị em tiết kiệm thời gian mà món ăn lại ngon lại bổ dưỡng ?', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54048, 'Bánh sữa chua phô mai', 'Vị chua chua ngọt dịu, cắn 1 miếng nhân sữa tan trong miệng... béo béo, ngậy ngậy ??', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54049, 'Cá file sốt teriyaki', 'Món ngon cho a/c em văn phòng nhé', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54050, 'Cơm Chiên', 'Món cơm chiên tôm trứng là 1 món ăn dễ làm, nhanh gọn và cứu cánh cho những ngày chẳng biết ăn gì. Chỉ mất 5-10 phút thôi đã có ngay món cơm chiên ấm bụng rồi nè.', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54053, 'Bánh đùm miền Tây', '<p>Đây không phải bánh đùm Nghệ An, giống bánh đúc, bột pha đặc hơn và gói trong lá chuối để hấp. Bánh đùm Miền Tây làm từ bột gạo có pha chút nếp cho dẻo tráng mỏng, gói với nhân hoặc là nấm mèo, thịt bằm hoặc là ruốc khô, củ sắn. Ở đây mình tận dụng rau củ trong tủ lạnh nhà mình để thay đổi phần nhân cho bánh Đùm. Vì vậy, các bạn có thể biến tấu sao cho phù hợp để tạo ra những món ăn ngon cho gia đình mình nhé ❤️</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54055, 'Bánh đúc gân', 'Bánh đúc là món ăn khá quen thuộc đối với mỗi gia đình Việt Nam. Những chiếc bánh đúc ngon, đúng vị sẽ mang đến cho người thưởng thức vị thanh mát, giòn. Nhưng không chỉ còn là những chiếc bánh đúc đơn thuần nữa mà bạn có thể dùng lá dứa để để tạo thành những đĩa bánh đúc lá dứa ngọt, màu sắc đẹp và hấp dẫn hơn. Cùng Cooky học cách làm bánh đúc gân lá dứa thơm ngon và đẹp mắt dưới đây nhé!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54056, 'Tôm ngâm tương', 'Tôm ngâm tương là món ăn truyền thống được yêu thích tại Hàn Quốc. Cách làm món ăn này khá đơn giản, với việc tập trung giữ cho thịt tôm tươi nguyên vẹn, chinh phục mọi tín đồ sành ăn ngay từ lần thử đầu tiên. Cùng Cooky học cách làm món tôm ngâm tương chuẩn vị Hàn nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54057, 'Xôi sầu riêng', 'Xôi sầu riêng là món ăn chơi với vị ngon khó chối từ. Đặc trưng của món xôi sầu riêng chính là mùi thơm, béo và ngọt của sầu riêng quyện trong gạo nếp dẻo thơm, khiến bạn ăn một lần là nhớ mãi không quên. Cách nấu xôi sầu riêng rất đơn giản, nếu bạn là tín đồ của sầu riêng thì đừng nên bỏ qua công thức làm món xôi sầu riêng thơm ngon này nha!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54058, 'Bánh phô mai Tom và Jerry', 'Tom and Jerry cheescake - chiếc bánh nguỵ trang như một miếng phô mai béo ngậy, gợi nhớ đến series phim hoạt hình bất hủ của tuổi thơ "Tom and Jerry" gây sốt cộng đồng Hàn Quốc nay đã du nhập đến Việt Nam. Hầu hết những ai biết đến bộ phim hoạt hình Tom và Jerry hẳn rất ấn tượng với món bánh phô mai yêu thích của anh chàng Jerry bé nhỏ. Cùng vào bếp với Cooky và thực hiện những chiếc bánh phô mai đáng yêu này nhé!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54061, 'Bánh canh cua hải sản', 'Khi bạn đã bí lối hay ngán ngẫm về các món hải sản đơn thuần như hấp, luộc, rang, v.v... Thì có thể chuyển sang món bánh canh bột lộc xem s nhé... Thanh nhẹ, chua chua cay cay mặn mặn hợp nhất cho những ngày mưa ntn', 4, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54062, 'Tôm Sốt Chua Ngọt', 'Tôm sốt chua ngọt là món ăn ngon, vị đậm đà, dễ làm. Đây là món ăn phổ biến trong những bữa cơm của gia đình Việt Nam. Vỏ tôm giòn rụm, kết hợp với sốt chua chua, ngọt ngọt sẽ khiến cho bạn vét sạch cả nồi cơm. Bạn hãy bổ sung món tôm sốt chua ngọt vào thực đơn hằng ngày cho gia đình của bạn nhé!', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54063, 'Tôm Rang Trứng Muối', 'Tôm rang trứng muối, món ăn ngon làm mới khẩu vị của gia đình. Tôm tươi được chiên giòn nhưng vẫn giữ được độ ngọt, bao phủ bên ngoài là lớp trứng muối bùi, béo thấm đượm vị. Cuối tuần, cùng Cooky vào bếp làm ngay món ăn hấp dẫn này ngồi nhâm nhi với gia đình hoặc bạn bè thì còn gì tuyệt vời bằng.', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54064, 'Tôm sú hấp cá thác lác', 'Món tôm sú hấp cá thác lác với thịt cá dai ngọt bọc quanh tôm chắc thịt tươi ngon, góp phần tăng hương vị đậm đà cho bữa ăn thêm hấp dẫn. Để biết thêm chi tiết cách làm món tôm sú hấp cá thác lác cùng Cooky xem qua công thức ở đây nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54069, 'Cánh gà chiên nước mắm', 'Thơm, giòn, mặn mặn, đậm đà.', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54072, 'Pudding Castella', 'Pudding Castella là sự kết hợp giữa lớp pudding béo ngậy, cùng với lớp bánh castella mềm xốp ở trên cùng. Chính nhờ sự kết hợp này đã giúp tạo nên một món tráng miệng vô cùng mới lạ và độc đạo. Bạn hãy thử vào bếp và làm ngay món tráng miệng thơm ngon này nhé!', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54073, 'Xôi Bắp Nhão', 'Xôi bắp nhão hay còn được gọi là bắp hầm, là món ăn có xuất xứ từ các tỉnh miền Nam. Xôi bắp nhão còn là món ăn ngon, dễ làm, phù hợp cho những bữa sáng đầy dinh dưỡng. Bạn hãy tham khảo ngay công thức dưới đây nhé!', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54074, 'Cà phê thạch', 'Bên cạnh cách pha cà phê truyền thống thì Cà phê thạch sẽ mang lại cho bạn một món thức uống vừa quen thuộc, vừa lạ miệng. Thạch dẻo dẻo, thơm phức mùi cà phê kết hợp cùng với sữa tươi béo béo, thơm thơm sẽ là món thức uống lý tưởng cho bạn trong mùa hè này. Bạn hãy thử làm ngay để cảm nhận hương vị đặc biệt của cà phê thạch nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54079, 'Tàu hũ ky cuộn tôm thịt chiên giòn', '<p>Món tàu hũ ky cuộn tôm thịt chiên giòn được bọc lớp mỏng tàu hũ ky vàng giòn, bên trong là nhân tôm thịt béo bùi. Hãy cùng Cooky bắt tay thực chiện món tàu hũ ky cuộn tôm thịt chiên giòn thơm ngon, bổ dưỡng và đẹp mắt này. </p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54080, 'Mì Xào Xúc Xích Sốt Chua Ngọt', 'Món mì xào xúc xích sốt chua ngọt giúp cho bữa ăn lạ miệng hơn, có lẽ bạn đã quá quen thuộc với các món mì xào xúc xích, mì xào trứng,… hoặc trong các bữa ăn khác. Món nui được xào với những cây xúc xích cocktail thơm ngon bổ dưỡng quyện vào sốt cà chua làm cho món ăn trở nên bắt mắt hơn, và với cách làm vừa nhanh vừa gọn bạn có thể thực hiện vào bữa sáng. Vậy hôm nay hãy cùng Legourtmet vào bếp thay đổi một chút với nui xào xúc xích lạ miệng nhé.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54082, 'Trứng Cá Kho Tiêu', 'Trứng cá kho tiêu, món ăn hấp dẫn lại giàu chất dinh dưỡng được nhiều gia đình yêu thích. Trứng cá giòn, dai sần sựt được sơ chế kỹ càng nên khi kho không hề bị tanh. Món ăn có màu vàng ươm đẹp mắt, hương vị mặn béo đậm đà. Hãy cùng Cooky thử ngay món ăn này đảm bảo bạn sẽ hài lòng. ', 4, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54090, 'Thịt chiên mỏng giòn', 'Món thịt chiên mỏng giòn vừa lạ, vừa ngon mà cách chế biến cũng không quá cầu kỳ. Với lớp vỏ giòn tan hoà quyện cùng nước chấm chua cay thơm mùi đặc trưng của thính rang siêu hấp dẫn, hứa hẹn sẽ hạ gục tất cả thành viên trong gia đình bạn.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54091, 'Bánh bao trái quýt', 'Món bánh bao trái quýt với sự khéo léo và sáng tạo, cùng theo dõi công thức của Cooky để làm ra tác phẩm bánh bao đáng yêu mang hình dáng như trái quýt, sẽ khiến cả gia đình, trẻ nhỏ hay bạn bè bạn đều thích thú.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54092, 'Bạch tuộc trộn cóc non', '<p>Bạch tuộc trộn cóc non là món ăn vặt cực hấp dẫn, những miếng bạch tuộc dai giòn, trộn cùng cóc non, xoài xanh chua ngọt cùng nước mắm đường cay cay sệt sệt, chỉ nghe thôi là đã cảm thấy kích thích vị giác lắm rồi. Cùng vào bếp với Cooky và thực hiện món này ngay nào!</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54094, 'Bún Bung', 'Bún Bung hay còn gọi là bún dọc mùng là món ăn dân dã của người Hà Nội, món ăn mộc mạc, hương vị thanh ngọt không quá nhiều mỡ dễ ngấy của nước dùng. Dọc mùng được sơ chế kỹ giòn dai sần sựt, ăn không bị ngứa, bát bún có màu xanh lá nhẹ rất là đẹp mắt. Đặc biệc, viên mọc được làm từ nguyên liệu chính là giò sống và nấm hương nên khi nấu tạo một mùi thơm khó cưỡng, vị ngon, ngọt cảm nhận được ngay từ miếng cắn đầu tiên. Để nấu được món ngon này không khó, chị em hãy theo dõi công thức của Cooky nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54099, 'Đậu hũ khìa nước dừa', 'Món ăn có vị béo thơm, vị đậm đà đưa cơm', 4, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54101, 'Gỏi Bò Rau Càng Cua', 'Gỏi bò rau càng cua là món gỏi ngon, thanh mát và dinh dưỡng cho bữa ăn gia đình. Bí quyết làm gỏi bò rau càng cua ngon nằm ở cách tẩm ướp giúp cho thịt bò mềm ngọt, không bị dai và rau càng cua giữ nguyên được độ giòn tự nhiên. Cùng vào bếp với Cooky và chế biến món gỏi rau càng cua trộn thịt bò hấp dẫn này nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54102, 'Cơm Cà Ri Xúc Xích Nhật Bản', 'Cà ri là một món ăn quen thuộc, thơm ngon với nhiều người. Hãy cùng vào bếp thực hiện món cà ri xúc xích Nhật Bản mới lạ, độc đáo lại cực kì dễ làm cho bữa ăn gia đình thêm hấp dẫn!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54104, 'Bánh Khoai Lang Tím Bọc Phô Mai Chiên', 'Bánh khoai lang tím bọc phô mai chiên là món ăn vặt vô cùng thơm ngon, hấp dẫn. Lớp vỏ bánh bên ngoài giòn giòn, mềm mềm, phần nhân bên trong thì béo ngậy sẽ khiến bạn ăn mãi thành nghiện đấy. Bạn hãy bỏ túi ngay công thức làm món này nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54105, 'Bánh trứng muối ngàn lớp tan chảy', 'Món bánh trứng muối ngàn lớp tan chảy với vỏ ngoài vàng ruộm, thơm nức bao bọc lớp trứng muối tan chảy, không chỉ hấp dẫn nhiều bạn vào dịp trung thu mà còn vào những ngày trời se lạnh, được thưởng thức món bánh cùng ly trà ấm áp. Cùng Cooky thực hiện món bánh trứng muối ngàn lớp tan chảy đong đầu yêu thương chuẩn bị cho mùa trung thu này nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54119, 'Cà Tím Sốt Thịt Bằm', '<p>Cà tím sốt thịt bằm, món ngon dân dã của người Việt Nam. Món ăn đậm đà, bắt vị với hương vị thơm ngon từ thịt heo thấm đượm phần nước sốt, áo đều bên ngoài miếng cà tím săn chắc, không hề bị nhũng nát. Nếu bạn chưa biết thực đơn hôm nay của gia đình là gì? thì món cà tím sốt thịt bằm là lựa chọn khá hợp lý cho bữa ăn gia đình hôm nay. Vừa đơn giản lại nhanh chóng, bạn hãy cùng vào bếp ngay với Cooky nhé.</p>', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54130, 'Chè Trôi Nước Nhân Đậu Đỏ | Món Ngon Tết Đon Ngọ 5. 5', 'Nhắc đến chè thì Má Ríc nấu là số 1... số 1 theo kiểu duy nhất 1 món chè. Cứ hễ dịp gì đó hay rằm mà nghe Má nấu chè là biết ngay chè trôi nước. ở nhà cứ hay ghẹo má và gọi là món chè cũ... cũ theo cách của má, cũ theo năm tháng nhưng đâu đó vẫn có nhiều thứ mà chỉ có má mới có. Hầu hết chè trôi nước, được làm bằng nhân đậu xanh, cũng lại có riêng má nấu bằng nhân đậu đỏ. Mọi người tham khảo công thức đặc biệt từ Má của Ric nha.', 5, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54132, 'Teba Shioyaki | Cánh Gà Nướng Muối Nhật Bản', 'Với gia vị đơn giản để tạo nên món ăn cùng với hương vị của vị rượu sake tạo nên sự độc đáo. Mời cả nhà cùng vào bếp với món cánh gà nướng muối kiểu Nhật nha.', 3, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54133, 'Bánh Bò Dừa Nướng Bằng Chảo', 'Bánh bò dừa nướng bằng chảo, vừa nhanh gọn vừa tiện lợi, chỉ cần một cái chảo chống dính tốt là đã làm được ngay món bánh bò dừa nướng thơm lừng. Lớp vỏ bánh bên ngoài giòn giòn, bên trong thì mềm xốp thêm một chút béo béo từ nhân dừa sợi khiến bạn ăn một lần là nhớ mãi. ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54134, 'Bánh đúc mặn miền Tây', 'Bánh đúc mặn miền Tây là món ngon dân dã ai cũng thích vì bánh rất dẻo mềm, béo thơm, có vị đặc trưng của nước cốt dừa, nhân thì giòn ngọt của tôm thịt & củ sắn... ăn cùng đồ chua và nước mắm rất hài hòa và ngon miệng. Hôm nay Cooky sẽ hướng dẫn các bạn cách làm món bánh đúc mặn miền Tây hấp dẫn này nhé!', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54135, 'Egg Foo Young', 'Egg Foo Young là món ăn truyền thống của Trung Quốc. Ngoài trứng là nguyên liệu chính, món ăn này còn có các thành phần khác như thịt heo xay, tôm, các loại rau củ cắt sợi. Món trứng này thường được ăn với với cùng 1 loại nước sốt đặc biệt và có vị rất gần gũi với khẩu vị của người Việt Nam. Cùng Cooky biến tấu cách làm món Egg Foo Young ngon miệng và bắt mắt dưới đây nhé!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54136, 'Salad Hạt Chia', 'Hạt chia đã quá quen thuộc với mọi nhà về những lợi ích mà nó mang lại, nay được áp dụng vào món salad đơn giản. Với vị chua nhẹ của chanh, kết hợp cùng vị ngọt của xoài và độ béo của sốt mayonnaise, Salad hạt chia hứa hẹn sẽ là món vô cùng bổ dưỡng cho cả nhà.', 1, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54138, 'Gỏi lươn hoa chuối', 'Món gỏi lươn hoa chuối tuy nghe khá lạ nhưng lại là món ăn hấp dẫn đối với nhiều người, với các hương vị đặc trưng cùng màu sắc bắt mắt, đây hẳn là món gỏi sẽ khiến cả nhà bạn trầm trồ. Cùng Cooky vào bếp khám phá bí quyết làm món gỏi này mời cả nhà dùng nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54139, 'Sấu Ngâm', 'Nước sấu là một trong những đặc sản nổi tiếng ở Hà Nội. Đây là món thức uống rất được nhiều yêu thích bởi vị chua chua, ngọt ngọt của nó. Bên cạnh đó, cách làm sấu ngâm lại không quá phức tạp, chỉ với những nguyên liệu dễ tìm và cách nấu lại vô cùng đơn giản là bạn đã có ngay một lọ sấu ngâm để dùng dần rồi đấy. Bạn hãy tham khảo ngay cách ngâm sấu ngay dưới đây nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54140, 'Bánh Trôi Tàu', '<p>Bánh trôi tàu hay còn lại là sủn dìn có nguồn gốc xuất xứ từ Trung Hoa. Đây là món bánh vô cùng thơm ngon, vỏ bánh mịn, dẻo dẻo, bên trong nhân là vừng đen ăn vào ngọt bùi, xen lẫn một chút vị hơi chua của dừa xào, một chút cay nhẹ của nước gừng. Bạn hãy tham khảo ngay công thức bên dưới nhé! </p>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54141, 'Bánh Tart Trứng', 'Bánh tart trứng giòn tan, thơm ngon, béo ngậy là món khoái khẩu của không ít người. Bánh tart trứng có lớp vỏ bánh giòn tan kết hợp cùng với nhân trứng sữa mềm mịn, thơm nức mũi sẽ khiến cho bạn càng ăn càng mê. Ngoài ra, món bánh tart trứng (Custas trứng) ngàn lớp này còn là một món ăn đặc trưng vào buổi sáng của người Hồng Kông, họ thường ăn kèm với một ly cà phê sữa. Bạn hãy tham khảo ngay công thức dưới đây để làm món bánh này cho những người thân yêu nhé!', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54144, 'Gỏi Mít Non', 'Gỏi mít non, món ăn đặc sản của miền Trung, mít non được luộc chín nhưng không quá mềm, vẫn giữ được độ dai, ngọt của mít kết hợp với tai heo và tôm rang đậm đà rưới lên nước sốt trộn gỏi chua ngọt hòa quyện khiến bạn phải thích mê. Khi ăn gỏi mít non thì không thể thiếu bánh tráng mè nướng giòn rụm, bẻ miếng bánh tráng, xúc một ít gỏi mít rồi đưa lên thưởng thức mới cảm nhận được trọn cái vị ngon của món ăn. Hãy cùng Cooky bắt tay vào thực hiện ngay món ăn này đảm bảo sẽ không khiến bạn thất vọng. ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54146, 'Bánh rau câu sinh nhật từ quả bơ', 'Một món bánh sinh nhật dễ làm, phù hợp với tất cả mọi người.', 5, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54155, 'Cơm Thố Bò Nhật Bản | Steak Rice Bowl | Sutekidon', 'Hôm nay Ric được 1 anh từng sống tại Nhật chỉ cho một món khá ngon... lần đầu làm bò beefsteak và and beefsteak kiểu này luôn. Mọi người tham khảo nếu hay là lưu lại ngay để có dịp trải nghiệm nha.', 1, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54160, 'Chè Bà Ba (chè thưng)', 'Chè bà ba là món có thể ăn nóng cũng ngon mà ăn lạnh cũng ngon. Chán cơm có thể thay thế món chè này, vì trong chè đã có phần tinh bột của các loại khoai.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54165, 'Thịt Kho Trứng', '<p>Dù nắng nóng nhưng kho thịt với trứng kiểu này ăn vẫn "vào cơm". Thịt lợn đậm đà, mềm thơm béo ngậy, trứng lên màu đẹp mắt khiến bất cứ ai nhìn thấy cũng muốn ăn ngay. Thịt kho trứng là món dễ ăn và quen thuộc với nhiều gia đình. Tuy nhiên, món ăn này vẫn là thách thức với các bà nội trợ: kho thịt nạc thì bị khô, kho thịt mỡ thì dễ ngấy. Dù vậy, thịt kho trứng vẫn thường phải sử dụng loại thịt ba chỉ, chân giò hoặc nạc mông có phần mỡ mới tạo được hương vị đúng điệu. Vậy làm thế nào để nấu món này từ thịt mỡ mà khi ăn, không có cảm giác bị béo và ngấy? Hãy thử ngay cách chế biến dưới đây nhé:</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54170, 'Bao Tử Cá Kho Tiêu', 'Bao tử cá kho tiêu là một món kho có hương vị đậm đà thấm trong từng miếng bao tử cá. Nếu yêu thích những món ăn hấp dẫn từ bao tử cá thì đừng bỏ lỡ cách làm món bao tử cá basa kho tiêu hấp dẫn dưới đây nhé. Chắc chắn đây sẽ là một món ăn lôi kéo nồi cơm của bạn vơi đi nhanh chóng!', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54173, 'Lẩu gà Tứ Xuyên', 'Lẩu gà Tứ Xuyên được xem như một món ăn truyền thống nổi tiếng trong ẩm thực Trung Hoa. Còn gì tuyệt vời hơn vào ngày cuối tuần cả gia đình cùng quây quần bên nồi lẩu gà chua cay thơm lừng đặc trưng mùi vị Tứ Xuyên. Không tốn quá nhiều thời gian sơ chế nhưng vẫn đảm bảo một bữa ăn hấp dẫn và đầy đủ dinh dưỡng cho mọi thành viên trong nhà. Cùng Cooky vào bếp và thực hiện món lẩu thơm ngon này cho cả gia đình thưởng thức nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54174, 'Salad Xúc Xích Nấm Rau Củ', 'Salad là món ăn quen thuộc cho những bạn theo chế độ ăn uống lành mạnh hay giảm cân. Các loại rau củ khi kết hợp cùng với nhau sẽ cho ra món ăn ngon miệng đặc biệt khi trộn cùng xúc xích nấm rau củ sẽ không tạo cảm giác nhàm chán. Đây cũng là món ăn đổi vị cho mâm cơm gia đình những ngày chán thịt thà và dầu mỡ đấy nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54175, 'Ba Món Chiên Tổng Hợp', 'Trong bữa ăn, các món chiên luôn khiến các thành viên trong gia đình, nhất là các bé thêm phấn khởi. Các món chiên giòn, đậm đà hương vị sẽ giúp cả nhà thêm ngon miệng và thú vị hơn. Gà thì ngọt thịt, bên ngoài được tẩm bột thì giòn rôm rốp, còn tôm thì tươi, được nhúng qua lớp bột chiên vừa chín vàng vẫn giữ được độ giòn của tôm, khoai tây bùi bùi được chiên giòn hấp dẫn. Các món chiên được chấm với sốt mayonnaise giúp cho món ăn càng hấp dẫn, tăng thêm hương vị của món ăn. <br><br>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54186, 'Lẩu Mắm Miền Tây', 'Lẩu mắm - món ăn đặc sản của người miền Tây, du khách không thể bỏ lỡ khi đến với vùng đất này. Mắm cá linh, cá sặc được nấu lấy nước cốt rồi đem đi nấu lẩu, mùi thơm lan tỏa khắp nơi vô cùng kích thích vị giác, hương vị đậm đà của nước lẩu là sự kết hợp của nhiều loại hải sản tươi ngon, được ăn kèm với nhiều loại rau thanh mát đặc trưng của nơi đây. Với các bước chế biến đơn giản là bạn đã có ngay nồi lẩu mắm thơm ngon, đậm đà rồi. ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54189, 'Vịt om sấu Hà Nội', 'Vịt om sấu là món ăn đặc trưng của người miền Bắc và được nhiều gia đình ưa thích trong mọi thời tiết. Thịt vịt thơm mềm, nước dùng có vị cay nồng của ớt, sả, và vị chua thanh thanh đặc trưng của sấu, ăn với bún chắc chắn sẽ khiến bạn nhớ mãi không quên. Cùng Cooky học cách nấu món Vịt om sấu thom ngon cho cả nhà thưởng thức nhé!', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54190, 'Bò bít tết', 'Thịt bò chín tái ăn kèm măng tây giòn ngọt.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54195, 'Ba Chỉ Tôm Thẻ Cơm Dừa Rim Mặn', 'Ba chỉ tôm thẻ cơm dừa rim mặn là món ăn tuy bình dân nhưng lại được khá nhiều gia đình Việt Nam yêu thích. Sự kết hợp hài hòa giữa vị mặn ngọt của thịt ba chỉ, tôm và cơm dừa, cùng với màu sắc cực kỳ hấp dẫn sẽ khiến cho nồi cơm của bạn hết sạch ngay trong tích tắc. Bạn hãy tham khảo cách làm dưới đây và bổ sung món này vào thực đơn cho gia đình bạn nhé!', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54196, 'Cách làm Mì Udon - Hoành Thánh', 'LƯU Ý: Khi luộc Mì tránh để quá lâu mì bị bủn ăn không ngon. Nguyên liệu bánh cân đúng chuẩn. Trong trường hợp bánh quá khô nghĩa là bột bạn mua bị cũ nên thêm nước. Nhân hoành thánh bạn thay đổi tùy thích không bắt buộc phải theo đúng cách mình chia sẻ. CHÚC BẠN THÀNH CÔNG VÀ ĂN NGON!', 4, '2022-03-22 00:00:00', 55, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54199, 'BÁNH BAO XÁ XÍU | CHAR SIU BAO | Cách làm bánh bao lên men truyền thống', 'Hầu hết các loại bánh bao hiện tại đều sử dụng men nở và phương pháp lên men theo cách của người Malaysia. vì vậy phương pháp lên men bánh bao từ phương phâp truyền thống cũng bị lãng quen và dường như chỉ còn tồn tại tại các nhà hàng lớn. mời cả nhà cùng tham khảo cách Ric làm bánh bao xá xíu bằng cách lên men từ bột khai nhé', 5, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54200, 'Bánh Mì Gà Xé Trứng Non', 'Nếu quá nhàm chán với bánh mì bình thường thì hãy cùng vào bếp với Cooky và làm ngay 1 ổ bánh mì gà xé trứng non đặc biệt theo công thức dưới đây nha! Bánh mì giòn, trứng non còn lòng đào nên rất là béo, thịt gà xé mềm ngọt, quyện cùng nước sốt đậm đà thì còn gì bằng.<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54201, 'Avocado & Tuna Pasta | Cách Làm Pasta bơ sáp & cá ngừ', 'Wafu pasta - một loại Pasta khá phổ biến tại Nhật Bản. Chế biến cũng khá đơn giản và rất thích hợp cho các buổi sáng nhẹ nhàng cuối tuần. Mời mọi người vao bếp nha', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54206, 'Miến thịt bò viên thơm ngon hết sảy', 'Một món ăn sáng vô cùng hấp dẫn ?', 3, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54217, 'Bánh Khoai Tây Nhân Phô Mai', 'Bánh khoai tây nhân phô mai với phần vỏ bánh giòn giòn kết hợp cùng với nhân phô mai dai dai chắc chắn trẻ con sẽ thích mê. Chỉ với những nguyên liệu dễ mua, cách làm đơn giản là bạn đã ngay món bánh thơm ngon, mềm mịn này rối đấy. Bạn hãy tham khảo công thức bên dưới đây nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54218, 'Bún Tôm Hải Phòng', 'Món bún tôm Hải Phòng hay còn được gọi là canh bún tôm là đặc sản được nhiều người biết đến với nguyên liệu chính là tôm và bún. Bún tôm có vị ngọt đậm đà từ nước dùng, vị thanh mát của tôm kết hợp với cà chua, nấm hương thơm nồng quyến rũ. Bạn hãy tham khảo ngay công thức nấu món canh bún tôm bên dưới đây nhé!', 3, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54219, 'Soda Galaxy', 'Hãy phá tan cái nóng của hè bằng một ly soda chanh mát lạnh. Tuy nhiên, Cooky sẽ chia sẻ cho bạn một cách biến tấu hoàn toàn mới. Công thức này sẽ "hô biến" ly soda chanh thông thường của bạn thành cả dải ngân hà đủ màu sắc chỉ bằng những nguyên liệu đơn giản. Bạn hãy tham khảo ngay công thức ngay bên dưới đây nhé!', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54220, 'Rau Củ Viên Sốt Thịt Bằm', 'Chỉ với những nguyên liệu đơn giản, dễ tìm là bạn đã có ngay món rau củ viên sốt thịt bằm thơm ngon, bổ dưỡng. Vị ngọt tự nhiên từ rau củ quyện với nước sốt thịt heo sẽ tạo nên món ăn không chỉ giàu dinh dưỡng mà lại rất trôi cơm. Bạn hãy vào bếp và làm ngay món này nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54223, 'Nước Ép Cần Tây - Dứa - Táo', 'Cần tây ngâm rửa thật sạch dể ráo, dứa gọt vỏ tỉa mắt cắt nhỏ, táo ngâm rửa cắt dọc bỏ hạt.', 1, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54226, 'Gà Nướng Xá Xíu', 'Gà nướng xá xíu - món ăn khai vị cho bữa tiệc ngon miệng. Lớp da gà beo béo có màu đỏ nâu nổi bật đặc trưng của xá xíu phủ ngoài từng thớ thịt gà thấm đượm gia vị nhưng vẫn giữ được vị ngọt, mềm vốn có của thịt. Mùi thơm phức của gà nướng lan tỏa khắp nơi làm kích thích vị giác khiến bạn nhìn thôi cũng thèm, chỉ cần chấm một chút muối tiêu thôi là ăn sẽ nhớ mãi. <br>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54229, 'Canh chua sườn', 'Những món canh có vị chua chua, ngọt ngọt thường được nhiều người ưa thích vì dễ ăn mà còn tạo được cảm giác ngon miệng. Cùng biến tấu món canh chua thông thường với công thức canh chua sườn hấp dẫn dưới đây nhé! Cũng là món canh chua nhưng thêm chút biến tấu cho hương vị lạ và hấp dẫn hơn. Vị chua cay từ cà chua và gói Gia vị nêm sẵn Lẩu Thái đậm đà, quyện cùng những miếng sườn chín mềm và rau muống, rau bắp cải giòn giòn. Một lựa chọn tuyệt vời cho những ai thích ăn canh chua cùng cơm nóng!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54230, 'Lẩu Nấm Chay', '<p>Lẩu nấm chay rất đặc trưng với văn hóa ẩm thực Á Đông và được biết như một món ăn dinh dưỡng cho cả người cao tuổi và trẻ nhỏ. Nếu bạn đã ngán ngẩm với hải sản hay thịt, chả thì lẩu chay chính là sự gợi ý tuyệt vời dành cho bạn. Cùng theo dõi cách làm món lẩu nấm chay thơm ngon và hấp dẫn dưới đây nhé!</p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54233, 'Gà Nướng Mắc Khén', 'Ai đã từng đến Tây Bắc, có lẻ ngoài cảnh tượng thiên nhiên hùng vĩ cùng con người thân thiện thì cũng không quên được mùi vị mắc khén, một gia vị hết sức đặc trưng của vùng núi Tây Bắc. Trong nền văn hóa ẩm thực Bắc, Trung, Nam dường như đâu đó tinh hoa ẩm thực của vùng núi tây bắc vẫn chưa vượt qua biên giới của vùng đại ngàn và cũng còn xa lạ với nhiều người dân Việt. May mắn thay, Ric được dịp trải nghiệm ở Tây Bắc, được gặp và có duyên với vài người bạn nên được chia sẻ công thức của loại gia vị đặc trưng này. Mời mọi người cùng vào bếp nhé.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54235, 'Bánh Chuối Nướng', 'Món bánh chuối nướng có màu vàng đều, thơm phức và đặc biệt các bước thực hiện nhanh gọn bằng nồi Tupperware. Khi thưởng thức bạn sẽ cảm nhân được vị ngọt dịu nhẹ, không quá ngấy, cốt bánh mềm ẩm. Với món bánh này bạn có thể thưởng thức với một tách trà hoặc bất cứ thức uống nào mình yêu thích để tăng thêm hương vị.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54237, 'Phở Chiên Hà Nội', 'Món phở chiên Hà Nội với miếng phở giòn rụm quyện với vị thanh mát của rau củ, vị ngọt của thịt bò sẽ giúp bạn thay đổi khẩu vị cho bữa ăn của cả gia đình. Cùng Cooky tham khảo công thức làm phở chiên Hà Nội mới lạ và hấp dẫn ngay sau đây nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54240, 'Cháo Thịt Bằm Và Trứng Bắc Thảo Bằng Nồi Cơm Điện', 'Đôi khi đổi vị với món cháo rất đơn giản nhưng cũng đủ đầy dinh dưỡng...Mình rất thích cái vị ngọt ngọt của gạo, thịt, cà rốt cộng với vị béo ngậy của trứng bắc thảo. Cảm giác nóng nóng, cay cay rất đã.', 2, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54254, 'Kim Chi Cuộn Ba Chỉ Om', 'Kim chi cuộn ba chỉ om là một món ăn khá phổ biến ở "xứ sở kim chi" và là một món ăn khá "hao cơm". Vị thịt đậm đà, mềm béo, quyện với chút chua chua, cay cay của kim chi, ăn cùng với cơm nóng thì không còn gì phải bàn. Bạn hãy tham khảo ngay công thức bên dưới để đổi gió một chút cho những bữa cơm hằng ngày thêm thú vị nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54260, 'Gà Quay Me', 'Gà quay me, món ăn bình dân với hương vị thơm ngon, lạ miệng. Nếu bạn là tín đồ của các món gà thì món ăn này bạn không thể bỏ qua được. Lớp da gà óng vàng được phủ lên phần nước sốt me chua chua ngọt ngọt, làm tăng thêm sự hấp dẫn, quyến rủ và bắt mắt của món ăn. Các món ăn kèm cùng vô cùng phong phú với xôi chiên phồng ngoài giòn tan còn bên trong thì mềm dẻo, thêm một ít rau xanh và kim chi thanh mát. Còn chần chờ gì nữa, đây là món ăn rát dễ gây nghện vì độ ngon của nó buộc bạn phải nếm thử ngay thôi. ', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54262, 'Sườn Non Kho Trứng Cút', 'Sườn non kho trứng cút,  món ăn thơm ngon đầy hấp dẫn. Vị đậm đà và ngọt tan từ thịt, thơm bùi, thấm vị của trứng cút, được kho kỹ với nước dừa tươi làm cho món ăn hòa quyện thêm phần quyến rủ hơn, dùng kèm với cơm nóng vào ngày mưa nữa thì thật tuyệt vời. Với mâm cơm cuối tuần mẹ nấu thì sườn non kho trứng cút sẽ là sự lựa chọn hoàn hảo cho bữa ăn gia đình. ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54266, 'Sườn Non Kho Thơm Nấm', 'Món sườn non kho thơm nấm là món ăn ngon vị, đậm đà và cũng khá mới lạ với nhiều bữa cơm gia đình. Từng miếng sườn non mềm thịt được kết hợp cùng vị thơm chua thanh, vị ngọt mát của nấm rơm khiến cho cả nhà bạn quên đi cảm giác nhàm chán với các món thịt sườn kho. Bạn nhớ lưu cách làm món sườn non kho thơm nấm của Cooky để đổi vị cho bữa cơm mình nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54267, 'Sườn Non Chiên Sả', 'Món sườn non chiên sả có vị cay nóng của sả ớt quyện trong những miếng sườn non thơm ngọt, là món ăn hấp dẫn rất phù hợp với bữa cơm nóng hổi. Cùng lưu lại công thức món sườn non chiên sả hấp dẫn của Cooky này nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54271, 'Cơm Trộn Mayonnaise', 'Món cơm trộn mayonnaise được kết hợp nhiều nguyên liệu bổ dưỡng như thịt bò, cá ngừ, rong biển, trứng gà, rau củ và phủ 1 lớp sốt mayonnaise Aji-mayo, tạo nên một chút chua thanh và béo thơm cho món ăn đặc biệt hơn. Cùng Ajinomoto thực hiện món cơm trộn đầy đủ chất dinh dưỡng này cho bữa ăn nhé!<br><br><br><br><br><br>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54273, 'Sấu Ngâm Mắm Tỏi Ớt', 'Sấu rửa sạch, nạo vỏ ngâm vào nước muối cho đỡ bị thâm. Vớt sấu đã nạo vỏ ra rổ, phơi cho khô hoàn toàn. Cho nước mắm nước lọc và đường vào nồi khuấy đều cho tan, đun vừa sôi thì tắt bếp để nguội. Tỏi bóc vỏ, ớt rửa sạch<br>Chuẩn bị sẵn hũ thuỷ tinh (hoặc lọ nhựa) sạch khô, xếp sấu và tỏi ớt vào cho phần sấu và tỏi ớt đan xen nhau <br>Đổ nước mắm đã đun vào. Đậy kín đợi khoảng 1 ngày là có thể dùng được', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54276, 'Bún Cá Rô - Ẩm Thực Bắc Bộ', 'Nếu miền Nam có bún cá lốc, miền Trung có bún cá nha trang thì miền Bắc có món bún cá rô. Với vị nước lèo thanh thanh, với cá rô phi hoặc cá rô đồng chiên giòn kết hợp với mùi thơm của rau thì là, tạo nên một nét đặt trưng không thể nào nhầm lẫn vào đâu được.', 5, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54279, 'Thịt Kho Tàu ', 'Món thịt kho tàu ăn mùa đông thì hợp hết sảy luôn . Nhưng không có nghĩ là mùa hè nóng nực này k hợp nha. Xem mk hướng dẫn làm nha', 0, '2022-03-22 00:00:00', 180, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54284, 'Sủi Cảo Chiên Giòn', 'Nhắc đến ẩm thực Trung Hoa mà không kể tên sủi cảo thì quả là một sự thiếu sót vô cùng to lớn. Sủi cảo có thể làm thành sủi cảo nước, sủi cảo hấp,... Nhưng hôm nay, Cooky sẽ mách cho bạn món sủi cảo chiên giòn tan ngon miệng, cách làm hết sức đơn giản, nguyên liệu dễ tìm. Bạn hãy tham khảo công thức ngay bên dưới đây nhé.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54285, 'Tớp Mỡ Cháy Tỏi Ớt', 'Tóp mỡ cháy tỏi ớt là món ăn vặt được rất nhiều bạn trẻ yêu thích. Tóp mỡ giòn tan, béo ngậy, phần cháy tỏi ớt rất đậm đà sẽ khiến bạn càng ăn càng ghiền. Bên cạnh đó, đây là món ăn siêu dễ làm mà lại ngon cực kỳ. Bạn còn chần chờ gì mà không xắn tay vào bếp làm ngay một hủ tóp mỡ cháy tỏi ớt', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54288, 'Bún Ốc Hà Nội', 'Bún ốc Hà Nội là một trong những món đặc sản dân dã của người Hà Thành, là sự kết hợp tinh tế giữa các nguyên liệu với nhau mà chắc chắn bạn sẽ không bao giờ quên nếu một lần được thưởng thức. Nước dùng trong, đậm đà, cay cay, bên cạnh đó còn có vị chua nhẹ của giấm bỗng, những con ốc to béo khi nhai vào có cảm giác giòn giòn, sần sật, đậu hũ và huyết thơm bùi. Bạn hãy cùng với Cooky khám phá cách làm món bún ốc chuẩn vị Hà Nội bên dưới đây nhé!<br><br>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54290, 'Cá Thác Lác Nấu Ngót', 'Cá thác lác nấu ngót ngon tuyệt vời mà bạn không thể bỏ lỡ, bởi đây là món canh hội tụ vị chua dịu của cà chua, vị ngọt thanh từ cá thác lác, hương thơm ngào ngạt từ rau cần. Một món ăn vừa giàu dinh dưỡng lại mát lành sẽ giúp cho bữa cơm của gia đình thêm ngon miệng. Bạn hãy tham khảo ngay công thức bên dưới để làm món canh này cho bữa cơm gia đình ấm cúng nhé!', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54293, 'Kimbap Hoa', 'Kimbap hay gimbap là món ăn rất nổi tiếng ở Hàn Quốc và được rất nhiều người yêu thích. Cooky sẽ biến tấu cách làm kimbap thông thường để tạo thành món mới có tên là Kimbap hoa. Cách làm món kimbap hoa rất đơn giản, với những nguyên liệu quen thuộc, đây chắc chắn là món ăn ngon cho gia đình mà bạn không thể bỏ qua.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54294, 'Thịt Heo Nướng Mỡ Hẹ', 'Món thịt heo nướng mỡ hẹ với những lát thịt vừa giòn giòn, lớp mỡ hẹ béo béo hòa hợp ăn ý cùng bánh hỏi và nước mắm đậm đà đã làm cho món ăn thêm phần đặc sắc hơn. Hãy vào bếp để khám phá cách làm món thịt heo nướng mỡ hẹ cùng Ajinomoto nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54298, 'Cơm Thịt Cuộn Hấp', '<p>Đổi vị với món cơm thịt cuộn hấp cho bữa ăn thêm ngon miệng. Chỉ với một chút khéo léo, thêm các bước tẩm ướp đơn giản là đã có ngay món ngon chiêu đãi gia đình. Từng miếng thịt mềm, ngọt mà lại thấm đẫm gia vị, dùng kèm với cơm nóng sẽ là món ăn lý tưởng cho bữa tối se lạnh. </p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54299, 'Bún Bề Bề', 'Món bún bề bề với đặc trưng bởi phần thịt bề bề tươi ngon, ngọt lịm hòa quyện cùng nước dùng đậm đà, thơm phức, mang tới cho người dùng món ăn được khá nhiều người ưa chuộng. Bạn nhớ lưu lại công thức của Cooky cách nấu món bún bề bề này để mời cả nhà mình thưởng thức nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54304, 'Lẩu Nấm Thập Cẩm Chay', 'Lẩu Nấm Thập Cẩm Chay là một trong những món ăn ngon , chế biến lại vô cùng đơn giản. Lẩu nấm chay có hương vị thanh đạm, thích hợp khi ăn cùng gia đình vào những ngày rằm. Nước dùng ngọt thanh từ các loại nấm và rau củ, khi ăn nhúng các nguyên liệu vào là bạn có thể thưởng thức món lẩu nấm chay ngon đúng điệu, ăn kèm với nước mắm chay thêm vài lát ớt nữa là tròn vị, kích thích mọi giác quan. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54306, 'Bún Sườn Sụn Cải Chua', 'Bún sườn sụn cải chua - món ăn hấp dẫn bởi mùi thơm từ sườn non và cải chua. Nước dùng thanh ngọt và chua nhẹ giúp người thưởng thức dùng không bao giờ bị ngán. Sườn được hầm mềm, ngon từ thớ thịt còn phần sụn thì giòn sần sật, kết hợp với phần cải chua được thấm đều gia vị. Tất cả hòa quyện đầy đủ mùi vị mặn ngọt, chua cay  càng kích thích vị giác của người dùng.', 4, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54310, 'Bún Nước Kèn An Giang', 'Bún nước kèn An Giang là món ăn dân dã, đặc biệt là nước lèo món bún này khá độc đáo, được nấu từ nước cốt dừa có vị là lạ gần giống như cà ri. Mùi vị đặc trưng của cà ri, béo ngậy của nước cốt dừa, ngọt dai đậm đà của thịt cá lóc đồng, bùi bùi của đậu phộng, giòn thơm của các loại rau, dưa chấm với một chút muối ớt chanh cay cay, chua chua… khiến người ăn cứ vương vấn, ăn hết tô này lại muốn thêm tô khác nữa.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54313, 'Bánh Trung Thu Trứng Muối Tan Chảy', 'Bánh trung thu trứng muối tan chảy là một loại bánh thơm ngon có nguồn gốc từ Hồng Kông. Vỏ bánh mềm, nhân trứng muối beo béo  tan chảy trong miệng sẽ khiến cả nhà bạn thích mê. Với sự trợ giúp đắc lực từ nồi Tchef, bạn có thể nướng bánh trực tiếp mà không cần lò nướng. Cùng theo dõi công thức bên dưới để biết cách làm món bánh hấp dẫn này nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54314, 'Bánh Flan Bí Đỏ - Khoai Lang', 'Món ăn dặm cho Cherry', 1, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54315, 'Dưa Leo Xào Trứng', 'Có những ngày mưa rả rích mà chúng ta ngại đi chợ, bỗng chợt nhận ra trong tủ lạnh chỉ còn mỗi dưa leo và trứng, những thứ chúng ta hay ăn hàng ngày,  cái bụng đói lại sôi ùng ục....Do vậy, hôm nay mình xin hướng dẫn cho các bạn món ăn rất ngon, nhanh chóng và thanh mát - dưa leo xào trứng. Hy vọng cả nhà đều thích.', 2, '2022-03-22 00:00:00', 10, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54321, 'Bánh Bò Thốt Nốt', 'Bánh bò một trong những loại bánh truyền thống của Việt Nam khá giản nhưng cũng không kém phần hấp dẫn và gắng bó với nhiều thế hệ. Nhắc đến bánh bò là nhớ đến mấy buổi đi học vội, ba đèo trên chiếc xe máy đến trường rồi mua vội cái bánh bò nhét vào bánh tiêu rồi ăn vội vàng trước khi vào lớp. Mời mọi người cùng tham khảo món bánh của Ric, với cách làm có pha chút hiện đại, không dùng cơm rượu nhưng tinh thần món ăn vẫn không thay đổi.', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54323, 'Gân Bò Cháy Tỏi', '<p>Cuối tuần lai rai với món gân bò cháy tỏi, đậm đà đầy hấp dẫn ăn là ghiền ngay. Gân bò được hầm mềm nhưng vẫn giữ được độ giòn sần sật, áo bên ngoài lớp tỏi phi thơm phức bùi bùi thấm đều gia vị.Món ăn có đầy đủ vị mặn ngọt và hương thơm nồng nàn của tỏi phi, tất cả hòa quyện tạo nên súc hút đặc biệt cho món ăn này.</p>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54324, 'Mắm Kho Miền Tây', 'Mắm kho miền Tây là món ăn dân dã, không quá cầu kỳ mà mang lại cho bữa cơm gia đình bạn mới mẻ và trở nên ngon miệng hơn. Cùng vào bếp với Cooky để làm món ăn hấp dẫn này cho cả nhà cùng thưởng thức nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54325, 'Chè Cốm Dẹp', 'Chè cốm dẹp với  màu xanh bắt mắt cùng hương vị thơm ngon đặc trưng của Hà Nội. Món chè cốm dẹp vừa bổ dưỡng, vừa thanh mát tạo nên hương vị đặc biệt, bạn nhớ nhanh tay lưu lại công thức món chè cốm dẹp này của Cooky nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54326, 'Tàu Hũ Dẻo Nước Cốt Dừa', 'Tàu hũ dẻo nước cốt dừa thích mê cái sự vừa thơm, vừa béo, vừa ngọt mát dễ ăn. Những miếng tàu hũ dẻo mềm, mát lạnh quyện cùng nước gừng đường ngọt thanh và đặc biệt khó thể bỏ qua phần nước cốt dừa, thêm chút đậu phộng béo thơm sẽ làm bạn khó thể cưỡng lại. Cùng Cooky vào bếp thực hiện món tráng miệng giải nhiệt hấp dẫn mời bạn bè và gia đình cùng thưởng thức nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54330, 'Phở Xào Bò', 'Phở xào bò, món ăn cho bữa sáng thêm ngon miệng. Bánh phở mềm dai, hòa quyện với thịt bò được tẩm ướp vừa miệng và rau củ giòn tươi, chắc chắn sẽ chinh phục được các thực khách khó tính.', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54337, 'Đậu Hủ Bao Bố', '<p>Đậu hủ bao bố chay là món ăn thanh mát và nhẹ bụng thích hợp cho vào thực đơn chay của gia đình. Phần nhân bên trong mềm, ngọt từ nấm và củ, còn lớp vỏ bên ngoài thì vàng ươm, thấm đượm nước xốt. Món ăn được trình bày đẹp mắt, hương vị thanh đạm lại còn đảm bảo đủ chất dinh dưỡng cho cả gia đình. </p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54352, 'Bánh Trung Thu Thập Cẩm Gà Quay', 'Làm bánh trung thu tương đối dễ, nhưng để được một chiếc bánh ngon, không hề đơn giản. Ngoài việc chọn lựa các nguyên liệu thật kỹ và tỉ mỉ còn có cả tâm huyết của người làm bánh. Hy vọng với công thức này, mọi người sẽ có thêm một công thức ngon trong gian bếp nhà mình cũng như có dịp chiêu đãi bạn bè trong dịp trung thu tới. Lưu ý: để bánh ngon, hãy nhớ tuân thủ đúng định lượng của từng loại nguyên liệu nhé. nên hãy chuẩn bị ngay 1 cái cân thật chuẩn xác nha', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54354, 'Chả Giò Thịt Bắp', 'Chả giò thịt bắp là lạ có thể đổi vị nếu bạn ngán món chả giò truyền thống. Nhìn thế mà cách làm lại vô cùng đơn giản nhé. Thử làm chả giò bắp tại nhà nào', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54357, 'Cá Om Dưa', 'Món Cá Om Dưa rất hợp với thời tiết se lạnh. Đúng chuẩn hương vị miền Bắc. Thơm ngon đến miếng cuối cùng (Đây là mình nấu theo cách nhúng rau sống nên mình cho nhiều nước chút xíu - mình thấy ăn như vậy rất ngon )', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54358, 'Gà Nướng Chao', 'Gà Nướng Chao- món ăn khai vị cho bữa tiệc gia đình, Món ăn đặc biệt hấp dẫn bởi màu sắc đẹp mắt của chao đỏ, và có hương vị đặc biệt từ các loại chao và chút mùi thơm từ rượu vang. Món ăn này ghi điểm bởi độ mềm, thơm đậm đà của thịt gà thấm đượm gia vị. ', 2, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54364, 'Bã Bắp (Ngô) Chiên Giòn', 'Nếu bạn chưa biết làm thế nào với xác bắp khi làm sữa bắp xong thì hãy thử theo cách này để tận dụng hết các phần của bắp nhé!Bữa sáng ngon lành với ly sữa bắp và xác bắp chiên giòn. Bao no, bao rẻ ?', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54365, 'Canh Củ Quả', 'Lạ mắt với canh củ quả hay còn gọi canh ngũ sắc. Món ngon bổ dưỡng dành cho mọi lứa tuổi.', 2, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54368, 'Hạt Sen Xóc Muối (Rang Muối)', 'Hạt sen già ngoài chế biến các món khác như: nấu chè, làm sữa, nấu cháo, hầm canh... Còn có thể xóc muối làm đồ ăn sáng hay đồ ăn vặt cũng rất ngon miệng. ( không mặn nha mọi người)', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54371, 'Cháo Sò Huyết', 'Sò huyết là một loại thực phẩm giàu chất sắt và canxi, rất tốt cho việc bồi bổ sức khỏe. Cùng Cooky vào bếp và thực hiện món cháo sò huyết thơm ngon và hấp dẫn cho cả gia đình nhé!', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54386, 'Ốc Hương Rang Muối Ớt', 'Với những ngày cuối tuần ta có thể chiến cùng đồng bọn bằng món náy nhé! Cay cay mặn mặn quất liền ngụm bia lạnh thì còn gì bằng nè... ', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54388, 'Gỏi Khoai Môn Chiên Giòn Hải Sản', 'Giữa rất nhiều các món gỏi ngon thì gỏi khoai môn chiên giòn hải sản dễ dàng “lấy lòng” người ăn nhờ khoai môn giòn rụm hấp dẫn hòa quyện cùng vị ngọt thanh của tôm mực và các loại rau củ. Nếu chưa biết chế biến món gỏi khoai môn thế nào thì đừng bỏ qua công thức dưới đây của Cooky nhé!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54389, 'Gà Áp Chảo', 'Đây là món ăn mình làm để cho chế độ giảm cân. Mình dùng phần ức gà. Các bạn có thể dùng phần khác cũng được nhé', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54400, 'Bún Cá Chấm', 'Món bún cá chấm Hà Nội với vị nước lèo thanh thanh, phần thịt cá được chiên giòn kết hợp với mùi thơm của rau thì là, tạo nên hương vị đặc trưng khó có thể nhầm lẫn được. Cùng vào bếp với Cooky để thực hiện món bún cá chấm hấp dẫn, nhiều người mê này nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54401, 'Súp Bắp Gà', 'Súp bắp gà là món ăn bổ dưỡng, thích hợp cho những ngày mưa gió', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54410, 'Thạch Rau Câu Phô Mai', 'Thạch rau câu phô mai với phần nhân beo béo được bao bọc phần rau câu lá dứa bắt mắt, giúp món tráng miệng này trở nên thơm ngon và hấp dẫn hơn nhiều lần. Cùng lưu lại bí quyết cách làm món tráng miệng thạch rau câu phô mai đơn giản của Cooky này nhé!<br>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54425, 'Ốc Nấu Chuối Đậu', 'Ốc nấu chuối đậu, món ăn mang hương vị dân dã, cái dai dai sần sậ̣t của thịt ốc kết hợp với sự bùi dẻo của chuối, vị ngọt mềm của thịt ba chỉ và đậu phụ khiến bạn không thể chối từ khi nghĩ đến món ốc nấu chuối đậu thơm ngon hấp dẫn. Hương vị thơm ngon, lạ miệng, món ăn này dùng nóng với bún tươi hoặc cơm thì ngon không kể xiết. ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54427, 'Canh Cua Đồng Nấu Mướp - Mồng Tơi - Nấm Rơm', 'Trưa hè mà làm một nồi canh cua ăn với cà pháo có vẻ hao cơm lắm nè. Ba mình nói đây là món đặc trưng của miền Bắc ngày xưa, giờ thì miền nào cũng có.  Người miền Bắc thường hay nấu với rau đay. Nhưng mình lại không thích rau đay cho lắm nên nấu theo kiểu của mình...', 4, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54436, 'Nhân Bánh Trung Thu', 'Sau bài chia sẻ về cách làm bánh trung thu thập cẩm gà quay tại nhà, hôm nay Ric update thêm mọi người cách nấu nước đường cũng như cách làm mỡ đường tại nhà. Mọi người tham khảo cách của mình bên dưới nhé', 2, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54437, 'Đậu Đũa Cuộn Thịt Chiên', 'Đậu đũa cuộn thịt chiên- món ăn thanh đạm cả nhà đều thích. Phần nhân vừa ngọt của thịt, vừa giòn giòn của tôm, được bọc bên ngoài đậu đũa cuộn tổ chim xanh mướt đẹp mắt. Món ăn được các bé rất ưa thích, vừa ngon vừa lại cung cấp đủ chất dinh dưỡng nên các mẹ khỏi phải lo thực đơn hôm nay của bé là gì nữa nhé. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54444, 'Thịt Heo Xào Kiểu Thái', 'Thịt heo xào kiểu Thái là món ăn đơn giản từ trong nguyên liệu, nhanh gọn trong bước làm, lại mang hương vị vô cùng đặc trưng của ẩm thực Thái Lan và đặc biệt hơn khi dùng chung cùng cơm nóng. Với cách làm món thịt heo xào kiểu Thái này của Cooky sẽ làm bạn khó có thể kiềm lại sức hấp dẫn nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54445, 'Cá Cơm Chiên Bột', 'Cá cơm chiên bột, món ăn giúp thay đổi khẩu vị cho bữa cơm gia đình thêm phần hấp dẫn hơn. Cá cơm mềm, bùi bùi được bọc bên ngoài lớp bột giòn giòn kết hợp cùng rau sống và nước chấm chua ngọt sẽ là món ăn ngon cho cả gia đình thưởng thức. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54464, 'Thịt Kho Trứng', 'Thịt kho trứng là món ăn ngon, rất dễ đưa cơm, từng miếng thịt ba rọi mềm thơm cùng trứng kho có màu đẹp mắt sẽ giúp cho bữa cơm gia đình bạn trọn vẹn hơn. Cùng với công thức nhanh gọn của Ajinomoto này bạn sẽ không quá vất vả để thực hiện món thịt kho trứng nữa.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54467, 'Sủi Cảo Khô', 'Sủi cảo là món ăn ngon khá phổ biến ở nước ta, có nguồn gốc từ Trung Quốc. Sủi cảo có rất nhiều cách chế biến như hấp, chiên,...  Nhưng hôm nay, Cooky sẽ hướng dẫn cho bạn một cách chế biến món sủi cảo rất mới lạ, độc đáo, đó chính là sủi cảo khô. Những viên sủi cảo vòng óng, tròn ụ với phần vỏ dai dai, mềm mượt, phần nhân bên trong có thịt tôm vừa giòn vừa tươi được bao bọc bởi phần thịt được nêm nếm vừa vị, chan lên sủi cảo là nước sốt đậm đà. Bạn hãy tham khảo ngay cách làm bên dưới đây nhé!', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54469, 'Đậu Phụ Sốt Nấm Kim Châm', ' ', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54477, 'Măng Ớt', 'Măng ớt là món ăn kèm vừa bắt mắt vừa ngon miệng, sẽ khiến cả nhà bạn khó cưỡng lại với hương vị chua cay khi thưởng thức cùng cơm nóng hay dùng cùng các món mặn. Hãy cùng Cooky lưu lại cách làm măng ớt nhanh gọn, ăn giòn ngon và không bị đóng váng nhé!  ', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54485, 'Gỏi Măng Tươi Lưỡi Heo', 'Gỏi măng tươi lưỡi heo, món ăn khai vị còn lạ miệng với nhiều người nhưng chỉ cần bạn nếm thử một lần là nghiện ngay. Vị giòn, ngọt có chút nhẫn nhẫn của măng tươi, còn lưỡi heo thì dai dai, sần sật thêm một chút rau mùi nữa, tất cả thấm đượm nước sốt chua ngọt, cay cay, làm cho món ăn hài hòa về mùi vị lẫn màu sắc càng làm kích thích các giác quan của thực khách hơn.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54495, 'Hoành Thánh Nhân Chả Cá Chiên', 'Nhân thịt ăn chán rồi, sao không thử nhân chả cá mới lạ này nhỉ?', 3, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54497, 'Chè Dừa Non Hoa Đậu Biếc', 'Chè dừa non hoa đậu biếc là món chè ngon và hấp dẫn giới trẻ thời gian gần đây. S<span style="color: rgb(51, 51, 51); font-family: arial; font-size: 14px; white-space: pre-line;">ợi dừa non có màu xanh từ hoa đậu biếc ăn cùng hạt lựu, sương sáo và nước cốt dừa ngon tuyệt. </span>Clip dưới đây sẽ giúp bạn làm món tráng miệng đơn giản, mát lạnh này ngay tại nhà.<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54498, 'Cua Sốt Trứng Muối', 'Cua sốt trứng muối là món ăn được ưa chuộng của hầu hết mọi người. Cua biển ngọt thịt kết hợp cùng sốt trứng muối béo thơm hấp dẫn chắc chắn là món ăn ngon tuyệt chiêu đãi gia đình bạn ngày cuối tuần. Theo dõi công thức dưới đây để biết cách thực hiện món ăn này nhé!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54500, 'Cánh Gà Chiên Nước Mắm', 'Cánh gà chiên nước mắm là một trong những món ưa thích của người Việt Nam và nhiều du khách nước ngoài tới thăm Việt Nam. Cánh gà giòn giòn cùng sốt nước mắm mặn mặn ngọt ngọt có thể khiến người ta khó mà cưỡng lại việc dùng tay bốc ngay một miếng để thưởng thức. Cách làm đơn giản, cực bắt cơm khi ăn cùng. Tại sao bạn không thử làm cho mình một đĩa cánh gà chiên nước mắm ngay nhỉ.<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54501, 'Chả Cá Thu Kho Tiêu', '<span style="font-size: 13px;" data-mce-style="font-size: 13px;">Sau một ngày làm việc vất vả, một bữa ăn ấm áp bên người thân sẽ làm bạn bớt căng thẳng mệt mỏi hơn rất nhiều đấy. Tuy nhiên, không ai muốn vừa đi làm về lại phải chế biến những món ăn phức tạp cả. Vậy nên món Chả cá thu kho tiêu dưới đây sẽ là sự lựa chọn hoàn hảo cho bữa ăn gia đình bạn. Món này cực dễ làm mà lại ngon cơm nữa, cùng vào bếp thực hành ngay bạn nhé!</span><br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54502, 'Canh Bầu Nấu Tôm', 'Canh bầu nấu tôm là món canh ngon và quen thuộc của mọi gia đình. Vị thanh mát của bầu, kết hợp với vị ngọt của tôm sẽ là món ăn giải nhiệt cho những ngày nóng bức. Hãy cùng tìm hiểu cách làm món canh bầu nấu tôm nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54510, 'Xúc Xích Chiên Bơ Khoai Tây Nghiền', 'Nếu bạn là fan của xúc xích và khoai tây thì không thể bỏ qua món Xúc xích chiên bơ khoai tây nghiền độc đáo dưới đây. Xúc xích Deli Sumo được chế biến từ thịt và kết hợp với phô mai béo ngậy kết hợp cùng khoai tây nghiền và rau củ ngọt mát chắc chắn sẽ tạo nên bữa sáng tuyệt vời cho gia đình bạn.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54511, 'Ba Rọi Xông Khói Cuộn Ức Gà Sốt Kem', 'Ba rọi xông khói cuộn ức gà là một món ăn độc đáo mang lại hương vị mới mẻ và cực kỳ quyến rũ cho bữa ăn gia đình. Vị ngọt thơm tự nhiên của thịt gà được giữ nguyên vẹn vì được bao bọc bởi lớp thịt xông khói cuộn óng ả. Thêm sốt kem quyện vào toả hương thơm phưng phức, khiến bất kỳ ai cũng khó lòng cưỡng lại. Theo dõi công thức bên dưới để biết cách làm ngay nhé!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54512, 'Rau Củ Xào Chua Ngọt', '<span style="font-size: 13px;" data-mce-style="font-size: 13px;">Chỉ với các nguyên liệu đơn giản, bạn đã có thể chế biến cho gia đình mình món rau củ xào chua ngọt rất ngon và giàu dinh dưỡng đổi vị cho bữa ăn trong những ngày bận rộn. Món rau củ xào chua ngọt rất ngon với độ giòn của rau và chua chua, ngọt ngọt của nước sốt hòa quyện với nhau rất thú vị và lạ miệng.</span><br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54513, 'Tôm Thịt Ram Mặn', '<span style="font-size: 13px;" data-mce-style="font-size: 13px;">Tôm thịt ram mặn món ăn được nhiều người yêu thích, có cách làm đơn giản, nhanh gọn, hương vị đậm đà, hấp dẫn. Vị béo ngậy của thịt ba chỉ cùng với vị ngọt thanh tự nhiên của tôm hòa lẫn vào nhau tạo nên món tôm thịt ram mặn vô cùng hấp dẫn và đưa cơm.</span>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54514, 'Canh Chua Cá Lóc', '<span style="font-size: 13px;" data-mce-style="font-size: 13px;">Canh chua cá lóc là một món ăn khá dân dã, quen thuộc trong bữa ăn gia đình người Việt Nam. Tuy nhiên, để có thể nấu món canh này thơm ngon đúng vị, không mùi tanh nồng khó chịu của cá, cũng cần có bí quyết. Cùng theo dõi cách nấu món canh chua cá lóc thơm ngon chuẩn vị dưới đây để thực hiện cho gia đình mình bạn nhé!</span><br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54518, 'Chè Xoài Singapore', 'Nếu bạn là một "fan cứng" của chè thì bạn không nên bỏ qua món Chè xoài Singapore. Đây là món chè có vị ngòn ngọt, beo béo của sữa tươi và nước cốt dừa, vị chua và hương thơm đặc trưng của xoài sẽ khiến cho bạn càng ăn càng mê. Bên cạnh đó, cách làm món chè xoài này thì rất đơn giản, nguyên liệu lại dễ mua. Bạn hãy tham khảo ngay công thức bên dưới và làm ngay món chè xoài Singapore nhé!', 5, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54519, 'Chè Thưng', 'Chè thưng còn có tên gọi khác là Chè bà ba. Đây là món chè thập cẩm đặc trưng nhất và rất được yêu thích của vùng Nam Bộ. Chè thưng có vị béo ngậy của nước cốt dừa, vị bùi của khoai và đậu hòa quyện với nhau tạo nên mùi thơm đặc trưng vô cùng độc đáo. Bạn hãy tham khảo công thức bên dưới để nấu món chè chiêu đãi người thân và bạn bè nhé.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54520, 'Vải Sấy Khô Bằng Nồi Chiên Không Dầu', 'Vải sấy khô bằng NCKD vừa tiện lợi, lại không mất nhiều thời gian của bạn, thì ngại gì mà không thử ngay. Với cách làm vải thiều sấy khô này chúng ta sẽ có quả vải khô hương vị đặc trưng, thơm vị ngọt đậm, ăn dẻo và mềm, rất dễ ăn, giữ nhiều vitamin, khoáng chất, dưỡng chất vốn có của trái vải. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54523, 'Chè Đỗ Đen Trân Châu', 'Công thức từ chị Trang Nguyễn - Cộng đồng Cooky<br>Đỗ đen rất tốt và có nhiều cách chế biến khác nhau, cách đơn giản nhất để làm món ăn từ đỗ đen này phải kể đến món chè đỗ đen trân châu. Những ngày nắng nóng mà có chén chè đỗ đen thêm xíu đá lạnh lạnh thì còn gì bằng', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54524, 'Flan Gato', 'Flan Gato là sự kết hợp giữa lớp flan béo ngậy cùng với lớp bánh socola mềm mịn, đã giúp tạo nên một món bánh tráng miệng vô cùng mới lạ, độc đáo. Bên cạnh đó, cách làm Flan Gato khá đơn giản, nguyên liệu dễ tìm và không tốn nhiều thời gian. Bạn hãy tham khảo ngay công thức bên dưới để làm một chiếc bánh vô cùng thơm ngon với phần caramel đắng nhẹ, lớp flan béo ngậy, cốt bánh mềm mịn để chiêu đãi bạn bè và người thân nhé.<br>', 6, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54527, 'Lagu Bò', 'Lagu Bò không còn xa lạ với người miền Tây nhất là vào những dịp đám tiệc thì món Lagu Bò đều có mặt bởi hương vị thơm ngon đậm đà khiến ai cũng thích<br>Nguồn: Trang Nguyễn - Cộng đồng Cooky', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54532, 'Bánh Tráng Trộn', 'Bánh tráng trộn là món được nhiều người biết đến, thậm chí là ghiền lắm luôn bởi vị chua chua, ngọt ngọt, dai dai, cay cay, mặn mặn của các loại nguyên liệu kết hợp hài hòa với nhau.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54535, 'Táo Đỏ Phổ Tai', 'Sự kết hợp giữa phổ tai giòn sần sật, cộng thêm hương vị thơm thơm bùi bùi của táo đỏ, kỷ tử tạo nên một thức uống rất ngon, thanh lọc giải nhiệt cơ thể và giúp ngủ ngon hơn. Đây là món thích hợp cho những bạn hay bị nóng trong người!', 6, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54539, 'Sandwich Hàn Quốc', 'Sandwich Hàn Quốc là món ăn vô cùng bắt mắt ở Hàn Quốc, với nhiều màu sắc từ các loại rau củ, chỉ cần nhìn lướt qua một cái là mê ngay, nhờ vẻ ngoài hấp dẫn của món ăn này. Với vị thơm ngon của trứng, beo béo của bơ và phô mai, vị mằn mặt của thịt nguội, thêm một ít tươi mát của rau củ, chắc hẳn sẽ là món ăn nhanh ngon miệng của nhiều người.', 3, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54540, 'Bánh Đậu Xanh', 'Bánh đậu xanh thường được dùng khi uống trà, khi đó sẽ tạo cảm giác thư thái. Khi ta tự làm có thể sắp xếp theo kiểu mình thích để bắt mắt hơn và ăn sẽ có cảm giác ngon hơn. Cách làm cực dễ chỉ với 2 bước đơn giản.', 3, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54541, 'Phở Trứng Gà Non', 'Có những ngày ngán cơm thì chuyển qua ăn phở mọi người nhỉ?<br>Nguồn: Trang Nguyễn - Cộng đồng Cooky', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54542, 'Miến Xào Tôm Bề Bề', 'Sài gòn, mấy bữa nay cứ tới chiều là mưa dữ dội ngập cả đường phố nên chiều nay em làm biếng đi chợ . Nhà có món gì nấu món đó luôn. Tôm với bề bề hay còn gọi là tôm tích là 2 loại hải sản giàu chất dinh dưỡng mà lúc nào cũng có trong tủ lạnh nhà mình<br>Nguồn: Trang Nguyễn - Cộng đồng Cooky', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54544, 'Bánh Trung Thu Đài Loan', 'Vậy là một mùa trung thu nữa lại đang đến thật gần. Ngoài những chiếc bánh trung thu truyền thống thơm lừng, để góp phần tạo nên sự phong phú cho ngày tết trung thu, Ric chia sẻ thêm mọi người món bánh trung thu đài loan, nhân sầu riêng trứng muối tan chảy với lớp mochi dai dai dẽo dẽo hấp dẫn kết hợp với lớp vỏ mềm mịn nhiều lớp sẽ tạo nên một sự trải nghiệm vô cùng thú vị. Từ công thức này mọi người có thể sáng tạo tạo ra các nhân khác siêu hấp dẫn như: khoai môn trứng muối tan chảy, đậu đỏ trứng muối tan chảy…', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54545, 'Trứng Cút Lộn Sốt Chua Ngọt', 'Trứng cút lộn sốt chua ngọt, cay cay, nóng hổi sẽ là món ăn chơi tuyệt ngon cho những ngày thu mát mẻ.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54548, 'Súp Bắp Gà', 'Súp bắp gà là món ăn bổ dưỡng, thích hợp cho những ngày mưa gió', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54564, 'Canh Nấm Mọc', 'Món canh nấm mọc có cách làm khá đơn giản lại ngon miệng. Món này thường ăn kèm cơm, bún, hoặc phở đều ngon. Ăn no mà không ngán khi điểm chút rau gia vị.', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54567, 'Trà Đào', 'Trà đào là thức uống giải nhiệt được nhiều người yêu thích bởi vị chua chua ngọt ngọt kèm thêm miếng đào ngâm tươi ngon khiến bạn uống hoài không chán.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54568, 'Đậu Hủ Sốt Cay Kiểu Thái', 'Đậu hủ sốt cay kiểu Thái, thơm ngon thấm đượm vị. Từng miếng đậu mềm mượt, chút beo béo của thịt băm, cay cay của sa tế, mùi thơm đặc trưng của lá chanh, Với cách làm đơn giản đã có ngay món đậu hủ sốt cay kiểu Thái ngon tuyệt, thơm ngon khó cưỡng. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54569, 'Đậu Hủ Sốt Tỏi', 'Đậu hủ sôt tỏi, món ngon thanh đạm, thơm ngon. Đậu hủ vừa bùi vừa có vị beo béo đặc trưng, vị tỏi phi thơm phức hòa quyện với nước sốt đậm đà áo đều xung quanh từng miếng đậu. Càng làm cho bữa cơm chiều của gia đình thêm phần thú vị hơn. <br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54571, 'Bánh Canh Chả Cá Nha Trang', 'Nguồn: Chị Thanh Thoa - Cộng đồng Cooky<br>Nhân ngày nghỉ mình trổ tài làm bánh canh chả cá đặc sản Nha Trang. Nhìn thế thôi chứ món này dễ làm lắm nè, không tốn nhiều thời gian và ai cũng có thể nấu được nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54572, 'Bánh Trung Thu Hoa Sen', 'Bánh trung thu hoa sen cùng với phần sen hồng đẹp mắt, phần nhụy vàng ẩn nấp gợi lên một mùa trung thu ấm áp tình đoàn viên. Với ý tưởng làm bánh trung thu hoa sen ngàn lớp thơm ngát của Cooky bạn có thể mang biếu tặng tới người thân, đồng nghiệp hay bạn bè mà vẫn đầy ý nghĩa, cách thực hiện cũng không cầu kỳ. Hãy lưu lại công thức món bánh trung thu này để vào bếp trổ tài nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54575, 'Bánh Mè Vừng Giòn', 'Bánh mè vừng giòn là một trong các loại bánh dân dã của Việt Nam, đặc biệt nó còn gắn liền với tuổi thơ của nhiều người. Công thức, cách làm và nguyên liệu khá đơn giản. Chúng ta cùng bắt tay vào làm luôn nhé!', 3, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54580, 'Cháo Hàu', 'Cuối tuần ngán cơm thì cả nhà mình ăn cháo. Thời tiết này mà ăn tô cháo Hàu thì ngon tuyệt. Hàu tươi, chất lượng, thịt ngọt, béo núc ních luôn, bát cháo thì nóng vừa thổi vừa ăn. Nguồn: Chị Thanh Thoa - Cộng đồng Cooky', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54584, 'Bún Bò Nam Bộ', 'Bún bò Nam Bộ là một trong những món bún rất dễ làm nhưng khá ngon miệng với người Việt. Nguyên liệu của món bún này đơn giản như bún, bò và các loại rau thơm như xà lách, rau mùi, húng chó và một chút hành phi, lạc rang. Điểm hấp dẫn của món ăn chính là nước sốt chua ngọt rưới vào trộn cùng bún. Nước sốt có vị chua cay mặn ngọt vừa đủ để kết hợp với thịt bò đã được nêm nếm đậm đà. Chỉ gần 30 phút là gia đình bạn  đã có một bữa ăn nhang gọn, dễ ăn và cực kỳ hấp dẫn.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54588, 'Salad Trái Cây Sốt Chanh Dây Mật Ong', 'Salad trái cây không chỉ là món ăn giảm cân mà còn giúp đẹp da, nhẹ người nữa. Quna trọng nhất là cách làm cực kì dễ mà ai cũng có thể làm được<br>Nguồn: Trang Nguyễn - Cộng đồng Cooky', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54590, 'Kem Chuối Mix Trái Cây', 'Kem chuối là món ăn ngon, mát giúp giải tỏa cái nóng bức của ngày hè. Với vị chuối chín thơm lừng, ngọt mát hòa cùng với trái cây, đậu phộng và vị béo của sữa vô cùng hấp dẫn.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54593, 'Bánh Mì Truyền Thống Việt Nam', 'Bánh mì là một trong những món ăn hấp dẫn truyền thống của Việt Nam. Với cách làm đơn giản này bạn đã làm được những chiếc bánh mì thơm ngon cho cả gia đình.', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54595, 'Ruốc Chân Nấm Hương', 'Nếu đã từng thưởng thức ruốc chân nấm hương một lần chắc hẳn bạn sẽ nhớ mãi cái hương vị đặc biệt ấy. Một chút dai dai, bùi bùi cùng hương thơm đặc trưng của nấm sẽ khiến bạn khó mà cưỡng lại được.', 3, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54598, 'Cá Cơm Kho Tiêu Đường Thốt Nốt', 'Cá mơn kho tiêu đường thốt nốt, món ăn dân dã nhưng vô cùng thơm ngon. Cá cơm tươi được kho lửa liu riu thấm đượm gia vị hòa quyệt với vị chút cay cay của tiêu và ớt,món ăn có màu vàng ươm đẹp mắt của đường thốt nốt càng làm thêm phần bắt mắt hơn. Hãy thêm món ăn đầy quyến rủ vào thực đơn cuối tuần cho bữa cơm gia đình thêm ấm cúng hơn. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54599, 'Mì Bay', 'Mì bay là món ăn khá độc đáo với vẻ bề ngoài. Ăn ngon, lạ miệng bởi có những món gia vị ăn kèm. Các bạn có thể xào mì với xúc xích, trứng, dăm bông, thịt bò... Cách làm khá đơn giản.', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54602, 'Xúc Xích Bạch Tuộc Lắc Phô Mai', 'Xúc xích bạch tuộc lắc phô mai là món ăn siêu dễ thương dàng tặng cho các bé nhà mình. Chỉ cần một chút khéo léo, là bạn đã " hồ biến" thành những chú bạch tuộc đáng yêu. Xúc xích chiên vàng thơm, áo đều lớp phô mai beo béo có màu đẹp mắt. Vừa ngon vừa bắt mắt thì ngại gì mà không thử ngay để dành tặng cho các thành viên nhỏ trong gia đình.', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54603, 'Bò Nướng Nấm Giấy Bạc', 'Bò nướng nấm giấy bạc, món ăn mang màu sắc sặc sỡ, đẹp mắt. Thịt bò thơm lừng, mềm ngọt, kết hợp với nấm và rau củ tươi làm tăng thêm độ ngọt thơm cho món ăn. Không cần đợi tới cuối tuần nữa, bạn có thể chế biến ngay món ăn hấp dẫn này chỉ với 30 phút, đảm bảo bạn sẽ thích mê món ăn này cho mà xem.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54604, 'Dẻ Sườn Bò Đút Lò', 'Dẻ Sườn Bò Đút Lò là món ăn mang đặc trưng của ẩm thực Tây Âu, có màu vàng đẹp quyến rũ, hương vị thơm lừng ngon khó cưỡng. Dẻ sườn mềm ngọt thấm đượm gia vị, phủ lên lớp phô mai bé ngậy. Món ăn sẽ mang lại cho bạn một trải nghiệm thật tuyệt với ngay từ đầu thưởng thức.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54606, 'Bacon Cuộn Trứng', 'Bacon cuộn trứng là món ăn mới lạ nhưng hương vị vô cùng thơm ngon và rất bổ dưỡng. Chỉ với những nguyên liệu đơn giản, cách làm không quá phức tạp là bạn sẽ có ngay một món ăn cho bữa sáng thêm phần thú vị, giúp tạo nên một ngày mới tràn đầy năng lượng. Bạn hãy cùng LeGourmet tham khảo công thức bên dưới để nấu ngay món Bacon cuộn trứng này nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54607, 'Xôi Đậu Phộng', 'Xôi đậu phộng hay còn gọi là xôi lạc là món ăn vô cùng quen thuộc với người Việt. Xôi đậu phộng có vị béo thơm tự nhiên của gạo nếp, vị bùi bùi của đậu phộng sẽ khiến bạn càng ăn càng ghiền. Một món xôi dễ nấu, dễ ăn, chắc bụng mà hương vị cũng rất tuyệt, vậy bạn còn chần chờ gì mà không lưu lại ngay công thức nấu món xôi này cho những bữa sáng thơm ngon và đầy dinh dưỡng. ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54608, 'Đùi Gà Nướng Khoai Lang', 'Đùi gà nướng khoai lang là món ăn thơm ngon, hấp dẫn, bổ dưỡng rất thích hợp để chiêu đãi bạn bè, người thân. Thay vì cách làm thông thường bạn sẽ mất hơn vài giờ đồng hồ để hoàn thành thì Nồi chiên không dầu Corosi sẽ giúp bạn giải quyết vấn đề đó, mặt khác còn giúp cho bạn giảm thiểu tối đa lượng mỡ thừa khi chế biến thức ăn. Bạn hãy tham khảo ngay công thức bên dưới để làm ngay món ngon này nhé.', 3, '2022-03-22 00:00:00', 35, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54609, 'Xôi Xoài', 'Đa số các món xôi đều ăn với chả, thịt, trứng... Bạn đã bao giờ thử món xôi kết hợp với trái cây như xoài chưa? Chúng ta cùng vào bếp để làm món này nhé!', 3, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54611, 'Xúc Xích Hầm Rau Củ', 'Xúc xích hầm rau củ là món ăn hấp dẫn ít dầu mỡ kết hợp với các loại rau củ dinh dưỡng, mang lại nhiều lợi ích sức khỏe cho các thành viên gia đình bạn. Cùng tham khảo công thức món xúc xích hầm rau củ của Le Gourmet để vào bếp thực hiện và thưởng thức nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54614, 'Heo Quay Bằng Nồi Chiên Không Dầu', 'Muốn làm heo quay nhưng ngại không thành công thì mình xin chỉ cho các bạn cách làm bằng nồi chiên không dầu, đơn giản nhưng nhất định sẽ thành công<br>Nguồn: Mai Hoang', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54615, 'Mứt Vỏ Chanh Leo', 'Mứt vỏ chanh leo là một trong những món mứt được mọi người yêu thích bởi vị ngọt thanh, hương thơm tự nhiên và uống kèm với nước chanh leo khi đi nóng về thì còn gì bằng.', 3, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54616, 'Cá Viên Chiên Chay', 'Món chay tính ra cũng phong phú đa dạng không thua kém món mặn à nha<br>Nguồn: Chị Mai Hoang - Group Cộng đồng Cooky', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54619, 'Bánh Tằm Khoai Mì', '<span style="color: #1d2129; font-family: Helvetica, Arial, sans-serif; font-size: 14px;" data-mce-style="color: #1d2129; font-family: Helvetica, Arial, sans-serif; font-size: 14px;">Món bánh của Tuổi Thơ...ăn một lần sẽ nhớ mãi</span><br style="color: #1d2129; font-family: Helvetica, Arial, sans-serif; font-size: 14px;" data-mce-style="color: #1d2129; font-family: Helvetica, Arial, sans-serif; font-size: 14px;"><span style="color: #1d2129; font-family: Helvetica, Arial, sans-serif; font-size: 14px;" data-mce-style="color: #1d2129; font-family: Helvetica, Arial, sans-serif; font-size: 14px;">Màu xanh lá dứa</span><br style="color: #1d2129; font-family: Helvetica, Arial, sans-serif; font-size: 14px;" data-mce-style="color: #1d2129; font-family: Helvetica, Arial, sans-serif; font-size: 14px;"><span style="color: #1d2129; font-family: Helvetica, Arial, sans-serif; font-size: 14px;" data-mce-style="color: #1d2129; font-family: Helvetica, Arial, sans-serif; font-size: 14px;">Màu hồng thanh long ruột đỏ</span><br>Nguồn: Chị Mai Hoang - Cộng đồng Cooky', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54621, 'Tôm Nướng Phô Mai Bơ Tỏi', 'Tôm nướng là món ăn đã rất quen thuộc, thế nhưng tôm nướng bơ tỏi thì sao? Chút tỏi nướng sẽ làm tôm thơm lừng trong khi bơ và phô mai sẽ giúp món ăn trở nên béo hơn, vỏ tôm cũng có độ bóng mượt hơn. Tôm là loại thực phẩm chứa hàm lượng canxi và dinh dưỡng cao, chính vì thế mà được nhiều người lựa chọn để chế biến trong bữa ăn.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54626, 'Cá Bạc Má Kho Cà Chay', 'Dễ làm, góp phần cho món chay phong phú', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54627, 'Bánh Tráng Xoài Dẻo', 'Bánh tráng xoài dẻo - đặc sản nức tiếng ở Nha Trang. Là loại bánh tráng mềm dẻo, khi ăn có vị chua, ngọt thơm mùi đặc trưng của xoài, kết hợp thêm một ít mè trắng bùi bùi và gừng cay cay thì khi ăn có cảm giác vô cùng thích thú. Chỉ với một chút khéo léo là bạn đã làm được ngay món bánh tráng xoài dẻo thơm ngon nức tiếng này rồi.', 3, '2022-03-22 00:00:00', 240, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54628, 'Đậu Hũ Trứng Hấp Tôm', 'Cùng vào bếp và học cách làm món đậu hũ trứng hấp tôm đơn giản. Với sự trợ giúp đắc lực từ xửng hấp Steam it, đậu hũ khi hấp xong giữ được độ mềm mịn béo ngon, kèm với nước sốt đậm đà ngon không thể tả, đây nhất định là món ngon không thể bỏ qua đâu các bạn nha.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54629, 'Mực Nhồi Thịt Hấp', 'Mực nhồi thịt hấp chứa hàm lượng dinh dưỡng cao và có hương vị thơm ngon nên thường được nấu trong các buổi tụ họp cuối tuần với gia đình hay bạn bè. Với sự trợ giúp đắc lực từ xửng hấp Steam It, không tốn quá nhiều thời gian là bạn đã có thể làm một món ăn lạ miệng, thay đổi khẩu vị cho gia đình, cùng vào bếp và thực hành ngay thôi!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54632, 'Sushi Trứng Cuộn Cơm', 'Sushi trứng cuộn cơm là món ăn làm khá dễ dàng, nhanh chóng và ăn ngon. Nguyên liệu và cách làm khá đơn giản.', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54635, 'Thịt Kho Tiêu Cay', 'Món ăn này ăn cùng cơm nóng thì không còn gì bằng. hihi', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54637, 'Bánh Da Heo', 'Bánh da heo vừa mềm mềm, dai dai lại có mùi vị thơm ngon của lá dứa kết hợp nước cốt dừa thật đã miệng. Ngoài ra chúng ta sẽ rất thích thú khi người thân cầm chiếc bánh trên tay tưởng miếng thịt heo thật và còn cảm thấy rất... ngấy nữa cơ đấy!', 3, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54640, 'Bánh Flan', 'Bé Mình vừa hết bệnh lại thèm món này. Nên Mẹ mần xong là Bé chén sạch 2 cái liền một lúc. Mình làm bằng trứng Gà Tre nên bánh thơm ngon lắm luôn. Nguồn: Mai Hoang - Group Cộng đồng Cooky', 2, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54641, 'Mỳ Ý Sốt Bò Bằm', 'Mỳ Ý sốt bò bằm là món ăn dễ làm, ngon lại thay đổi khẩu vị của gia đình các ngày trong tuần. Món này sẽ được các thành viên trong nhà yêu thích.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54643, 'Mắm Kho', 'Ai đã từng về miền Tây nên thử qua 1 lần ăn món mắm kho, món đặc sản của người miền Tây. Ai là con dân miền Tây đi xa chắc sẽ không bao giờ quên nó. Vì đặc trưng là đồ đồng nên sạch, ngon, chỗ khác lại không có rau đồng đúng chuẩn miền Tây .Mình là người miền Trung ban đầu ăn ko quen nhưng lấy chồng miền Tây bị ăn riết thành ghiền ? nay nấu chuẩn như con dân miền Tây luôn rầu ?', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54644, 'Bánh Quy Earl Grey', 'Công thức bánh quy này không có trứng, phù hợp cho những ai bị dị ứng trứng. Bánh xốp mềm, thơm đậm vị trà Earl Grey', 10, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54649, 'Bánh Chocolate Từ Oreo Không Lò Nướng', 'Bánh chocolate từ Oreo không cần lò nướng cực dễ dàng và nhanh chóng. Chúng ta cùng vào bếp để trổ tài chiêu đãi cả nhà nhé!', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54652, 'Trứng Vịt Muối Quế Hồi', 'Trứng muối là món ăn được nhiều người biết đến và yêu thích khi kết hợp với nhân bánh trung thu, hay ăn kèm với cháo trắng hoặc cơm. Chúng ta cùng bắt tay vào làm ngay những quả trứng muối thơm ngon nhé!', 5, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54654, 'Cà Tím Kiểu Hàn - Gajinamul', 'Món cà tím này dai, sần sật, ăn cùng cơm rất ngon, dành cho những ai thích ăn chay và chuộng những món chay.', 3, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54656, 'BiBimBap', 'Món cơm trộn siêu ngon, cực kì dễ làm. Chỉ cần chút rau củ và một vài nguyên liệu thân thuộc đã có ngay món ăn biến tấu cho gia đình bạn rồi đó!', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54657, 'Mắm Chay', '<p>Đã bao giờ bạn nghe về món mắm chay thơm thơm, béo béo mới lạ chưa? Hãy cùng vào bếp ngay hôm nay và chế biến món chay ngon cho gia đình mình những bữa chay thêm phong phú nhé!</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54659, 'Bánh Mì Bơ Tỏi', 'Bánh mì bơ tỏi có xuất xứ từ nước Pháp và dùng để ăn vặt, ăn xế, ăn sáng,... lúc nào cũng hoàn hảo. Món bánh này vừa thơm ngon lại vô cùng đơn giản', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54660, 'Bánh Tráng Nướng Đà Lạt', 'Từ lâu bánh tráng nướng đã trở thành một món ẩm thực phổ biến tại nhiều nơi trên khu vực khắp cả nước Việt Nam. Do đó, dù đã từng ăn bánh tráng nhiều lần nhưng khi đến với thành phố hoa hay còn gọi xứ sở sương mù thì món bánh tráng nướng Đà Lạt lại mang nhiều nét riêng với một hương vị hấp dẫn, thu hút khác không giống bất cứ nơi nào. Để có được món bánh tráng nướng ngon, mang hương vị giống của Đà Lạt bạn cần chọn những nguyên liệu chất lượng và thực hiện thật khéo léo. Bạn nên chọn các loại bánh tráng dày vừa phải để dễ dàng khi làm. Vì trứng cút khi nướng sẽ không bị ngán như trứng gà, nên bạn hãy sử dụng trứng cút để món ăn thêm phần hấp dẫn hơn nhé.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54677, 'Xôi Vò', 'Xôi vò là món ăn dễ nấu, dễ ăn và đặc biệt dễ kết hợp với các món mặn khác. Cách nấu xô vò cũng vô cùng đơn giản, các bạn tham khảo cách làm của mình nha!!<br>Nguồn: Mai Hoàng - Cộng đồng Cooky', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54678, 'Gà Bó Xôi', 'Gà bó xôi là món ăn 2 trong 1 cực kì tiện lợi. Gà được không trong lớp xôi chiên giòn ăn mốt lần là nghiện. Quan trọng là món này cũng không khó làm như bạn đã nghĩ đâu nhé!', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54679, 'Gà Lăn Hồ Đào Mỹ Chiên Giòn Với Salad Nho Khô Dưa Hấu', 'Gà lăn hồ đào mỹ chiên giòn với salad nho khô dưa hấu là món salad vừa thơm ngon lại rất bổ dưỡng. Một món ăn chứa đầy đủ chất dinh dưỡng từ các loại rau xanh, dưa hấu, nho khô, thịt gà ăn kèm cùng với sốt giấm đặc biệt sẽ khiến cho bạn càng ăn càng mê. Bạn hãy lưu lại ngay công thức bên dưới để làm món salad này nhé.<br>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54680, 'Hàu Né Phô Mai', 'Nếu muốn nấu một bữa nhanh gọn lẹ mà  lại cực ngon thì mọi người không thể  bỏ qua món này. Chấm kèm bánh mì nữa là ngon hết xảy con bà bảy.', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54681, 'Bánh Mây Bồng Bềnh', 'Lại thêm một món bánh nhập hội Bồng Bềnh cùng với Dalgona và Trứng Bồng Bềnh, đó là Bánh Mây - Cloud Bread. Chiếc bánh này đang cực hot trên mạng xã hội Tiktok vì vẻ ngoài phúng phính, mềm xốp và bắt mắt mà cách làm siêu đơn giản. Hãy cùng xem hướng dẫn chi tiết nha.', 1, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54683, 'Bánh Trung Thu Khoai Lang Tím', 'Sắp đến ngày Trung thu, để làm được những chiếc bánh đẹp mắt, đơn giản và nhanh chóng thì  chúng ta không nên bỏ qua kiểu làm bánh Trung thu từ khoai lang tím này.', 3, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54686, 'Lẩu Bò Viên Khổng Lồ', 'Lẩu bò viên sa tế khổng lồ là món ăn ngon cho những ngày cuối tuần ấm cúng, với mùi thơm đặc trưng của nước lẩu từ xương bò kết hợp cùng Bò viên Xì Tin thơm ngon, sẽ mang đến cho cả gia đình bạn bữa ăn đầy bổ dưỡng. Hãy cùng lưu lại công thức của Sài Gòn Food để thực hiện món lẩu bò viên sa tế khổng lồ mời cả nhà bạn cùng thưởng thức nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54687, 'Chả Cá Chiên Lá Lốt', 'Biến tấu chả cá với một loại lá quen thuộc', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54688, 'Xôi Xéo', 'Xôi xéo là món ăn quen thuộc của người Việt, đặc biệt rất được người Hà Nội yêu thích. Sự hòa trộn tinh tế mà quyến rũ giữa sắc vàng tươi của nếp, vàng nhạt của đậu xanh chín mềm cùng hành phi vàng sậm, giòn tan tạo nên một món xôi vô cùng hấp dẫn và thơm ngon.', 4, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54690, 'Ba Chỉ Chiên Giòn Sốt Cam', 'Ba chỉ chiên giòn sốt cam là món ăn vừa nghe xa lạ nhưng lại khá quen thuộc với nhiều bữa cơm gia đình, với công thức hấp dẫn của Bord Bia sẽ khiến cả nhà bạn tấm tắc khen. Từng miếng thịt với lớp ngoài giòn tan nhưng bên trong vẫn mọng nước mềm ngọt, đặc trưng hơn phần sốt cam bắt mắt và hương vị chua ngọt khó cưỡng lại được. Để thưởng thức rõ hơn món ba chỉ chiên giòn sốt cam hãy cùng vào bếp với Bord Bia để thực hiện nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54693, 'Bê Xào Lá Lốt', 'Món này ăn tốn cơm không chịu được luôn', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54694, 'Gà Mỹ Hầm Nhân Sâm Wisconsin Mỹ', 'Gà Mỹ hầm nhân sâm Wisconsin Mỹ - món ăn ngon, đậm đà chứa đầy đủ các chất dinh dưỡng, Với cách chế biến đơn giản, chỉ cần bạn bỏ ra một chút thời gian là đã có ngay món ngon đầy dưỡng chất cho những người thân yêu mình thưởng thức. Thịt gà được hầm mềm đến từng thớ thịt, hòa quyện với nhân sâm Wisconsin hảo hạng và nước dùng đậm đà, thì chắc chắn rằng bạn thử một lần là sẽ nhớ mãi.', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54695, 'Bánh Mì Thịt Bò Bít Tết Vùng Philly', 'Bánh mì thịt bò bít- tết vùng Philly- món ăn lạ miệng nhưng để lại nhiều ấn tượng cho thực khách. Bánh mì giòn rụm bên ngoài, kẹp bên trong thịt ba chỉ bò Mỹ tươi ngon, kết hợp với rau củ xào thanh ngọt được phủ lên lớp phô mai Mỹ béo ngậy, tạo nên cảm giác thơm ngon, vừa miệng ngay miếng cắn đầu tiên. <br><br><br>', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54696, 'Nui Xào Bò Bằm Sốt Cà Chua', 'Món nui xào không yêu cầu nguyên liệu cầu kỳ, cũng như tốn nhiều thời gian chế biến. Cách làm đơn giản mà ngon miệng.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54697, 'Nem Heo Nướng', 'Nem nướng là một trong những món ăn được nhiều người yêu thích bởi hương thơm, vị ngọt đậm tự nhiên. Tuy nhiên, ăn nem nướng tại các hàng rong vỉa hè, nhiều người lo lắng về vấn đề an toàn vệ sinh thực phẩm. Sau đây, Cooky giới thiệu với bạn cách làm nem  heo nướng tại nhà, vừa ngon vừa an toàn vệ sinh cho cả gia đình thưởng thức.<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54698, 'Lẩu Bò Măng Chua', 'Lẩu bò măng chua, món ăn thích hợp cho những ngày se lạnh. Măng chua có vị chua nhẹ, giòn thơm được nấu chung với nước dùng ngọt nước, nhúng thêm miếng thịt bò tươi mềm chấm một chút nước sốt chấm lẩu cay cay, tất cả hòa quyện thành nồi lẩu thơm ngon, nóng hổi sẽ làm hài lòng những thực khách khó tính. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54704, 'Súp Sườn Non Mỹ Đậu Mỹ Và Rau Củ', 'Món súp sườn non Mỹ đậu Mỹ và rau củ với vị ngọt tự nhiên và đầy dinh dưỡng, thơm phức nóng hổi. Cùng với hương vị thanh ngọt của các loại đậu Mỹ và sườn non Mỹ hứa hẹn sẽ mang cho cả gia đình bạn thưởng thức món súp ngon miệng và bổ dưỡng nhất. Hãy lưu lại công thức của USDA để vào bếp thực hiện món súp hấp dẫn này nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54705, 'Pancake Tạo Hình', 'Bánh pancake với những màu sắc rực rỡ và bắt mắt, đồng thời vẫn mang lại hương vị thơm ngon đặc trưng giúp cho bé nhà bạn cuốn hút hơn. Để có những chiếc bánh đầy mới lạ này hãy cùng tham khảo công thức của Vedan, bạn sẽ bất ngờ về sự sáng tạo của mình đấy nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54707, 'Thăn Bò Sốt Tiêu Đỏ', '<span style="font-size: 13px;" data-mce-style="font-size: 13px;">Với sản phẩm thăn bò chuẩn Âu từ Gkitchen, chỉ với thời gian ngắn chế biến bạn sẽ có ngay món thăn bò xốt tiêu đỏ hấp dẫn. Đặc biệt, cách làm xốt tiêu đỏ chuẩn vị sẽ giúp cho ăn món trở nên hoàn hảo hơn. Sự kết hợp của thịt bò mềm ngọt cùng với rau củ thanh mát và nước xốt đậm đà của món ăn sẽ “đốn tim” người thưởng thức ngay từ lần đầu tiên. </span><br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54708, 'Salad Bò Trái Lê', '<span style="font-size: 13px;" data-mce-style="font-size: 13px;">Salad thịt bò trái lê là món rau trộn gồm thịt bò với lê, xà lách và một số nguyên liệu dễ tìm. Sự kết hợp đối lập mà nhịp nhàng giữa những miếng thịt thơm mềm, mặn mà với các loại trái cây thanh ngọt. Vị béo và chua nhẹ từ dầu ô liu và nước cốt chanh làm nước sốt càng tô điểm cho món ăn thêm phần đặc sắc.</span><br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54710, 'Chè Khoai Mì', 'Chè khoai mì luôn là món ăn khoái khẩu của nhiều chị em phụ nữ bởi sự dẻo dai của khoai mì, vị thơm và béo của nước cốt dừa và vani. Với công thức đơn giản của Cooky dưới đây, bạn có thể nấu món chè này tại nhà mà không tốn nhiều thời gian, thử ngay bạn nhe!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54711, 'Flan Mè Đen', '<span style="font-size: 13px;">Mè đen là loại thực phẩm giàu dinh dưỡng và rất tốt cho sức khỏe. Bên cạnh những món ăn quen thuộc được làm từ loại nguyên liệu này như chè hay sinh tố mè đen, các tín đồ ẩm thực còn khám phá thêm được một món mới có tên gọi là bánh flan mè đen. Cùng Cooky vào bếp và thực hiện món bánh flan mè đen thơm ngon và độc đáo với công thức đơn giản dưới đây nhé!</span><br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54712, 'Bánh Mì Hấp Nhân Thịt Băm', 'Bánh mì hấp nhân thịt băm là món ăn dễ làm nhưng rất ngon miệng, thích hợp cho bữa sáng và những ngày ngán cơm. Cùng Cooky học ngay cách làm món bánh mì hấp ngon tuyệt dưới đây nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54713, 'Bánh Canh Cá Nục Chiên', 'Bánh canh cá nục chiên - món tưởng lạ mà lại ngon không tưởng. Món bánh canh được nêm nếm đậm đà, cá chiên với lớp vỏ giòn, ko tanh mà béo bùi rất ngon, nước chấm hòa hợp tuyệt vời với món. Cùng theo dõi cách làm dưới đây để thực hiện ngay cho gia đình bạn nhé!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54715, 'Tép Đồng Rang', 'Nếu đã quá quen với những mâm cơm đầy ắp thịt cá béo ngậy thì bạn hãy làm món tép đồng rang thơm ngon, đậm đà mà lại rất bổ dưỡng. Một món ăn giản dị, nguyên liệu đơn giản, dễ làm mà cực kỳ hấp dẫn, đưa cơm, bên cạnh đó còn được rất nhiều gia đình Việt yêu thích. Bạn hãy tham khảo công thức bên dưới để làm món ngon này nhé!', 3, '2022-03-22 00:00:00', 15, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54716, 'Nộm Tép Đồng Rau Muống', 'Nộm tép đồng rau muống là một trong các món ăn dân dã của người Việt từ xưa đến nay, được rất nhiều người yêu thích. Món ăn có hương thơm đặc trưng của tép rang giòn rụm, vị chua dịu và cay nhẹ của nước trộn nộm cùng với rau muống dai dai, giòn giòn sẽ khiến cho ai ăn một lần sẽ nhớ mãi. Bạn hãy lưu lại công thức bên dưới để làm món nộm tép đồng rau muống này nhé.', 3, '2022-03-22 00:00:00', 25, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54718, 'Trứng Muối Siêu Tốc', 'Thông thường trứng muối phải 1 tháng để ngấm vị, tròn đều. Song bên cạnh đó có công thức siêu tốc chỉ sau 24 giờ. Hương vị và màu sắc cũng không kém phần thơm ngon và bắt mắt như trứng muối thường.Sắp tới ngày Trung thu, nếu các bạn thích làm bánh có nhân trứng muối nhưng lại sợ lâu thì có thể hoàn toàn làm theo cách này.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54730, 'Lồng Đèn Tôm Đỏ', 'Lấy ý tưởng từ chiếc đèn lồng đỏ mình làm ra món ăn được làm từ tôm để chào đón mùa Trung thu về với mọi nhà.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54734, 'Khoai Tây Chiên Bơ', 'Khoai tây chiên bơ là món ăn vặt được yêu thích. Hương vị thơm ngon, đã miệng và cách làm đơn giản, nhanh chóng.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54736, 'Gà Rán Hàn Quốc', 'Xin chào mọi người.Lại là Ric đây, tiếp tục trong series các món ngon Hàn Quốc mà Ric chia sẻ, nay giới thiệu thêm mọi người món gà rán Hàn Quốc nhé. Nhắc tới gà rán thì chắc không còn quá xa lạ, nhưng điều gì đã tạo nên sưc hấp dẫn và lôi cuốn trong món gà rán Hàn Quốc… haha bật mí trong chi tiết cách của Ric làm bên dưới.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54738, 'Bánh Donut', 'Bánh donut là món tráng miệng được rất nhiều yêu thích. Những chiếc bánh đầy màu sắc hình tròn hấp dẫn người thưởng thức ngay từ cái nhìn đầu tiên. Bánh donut có hai cách chế biến là donut chiên và donut nướng. Cách làm bột bánh donut thông thường mất khá nhiều thời gian. Tuy nhiên, nếu bạn là người bận rộn không có thời gian để chế biến hay bạn không có lò nướng thì gói Bột chiên bánh Pancake sẽ thay bạn làm điều đó, chỉ cần vài thao tác đơn giản là bạn đã có ngay một mẻ bánh donut chiên thơm ngon. Bạn hãy tham khảo cách làm bên dưới đây cùng Vedan nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54739, 'Nạc Vai Nướng Thái', 'Món Nạc vai nướng Thái thơm ngon, hấp dẫn khiến bạn cảm thấy bị lôi cuốn bởi mùi vị cay nồng, miếng thịt mềm mọng, càng ăn càng mê. Hãy đổi vị cho gia đình vào những ngày mưa mát lành với món ăn mới lạ, độc đáo này. Bạn hãy cùng với Maggi xắn tay áo vào bếp làm ngay món Nạc vai nướng Thái nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54740, 'Thịt Ba Chỉ Nướng Tây Bắc', 'Thịt ba chỉ nướng Tây Bắc có vị ngọt và béo của thịt, quyện với hương thơm của các loại gia vị tạo nên một món ăn vô cùng đặc biệt, hấp dẫn đến khó cưỡng. Bạn không cần nhất thiết phải lên tận Tây Bắc mới có thể cảm nhận được hương vị của thịt ba chỉ nướng hạt dổi, hạt mắc khén mà với cách làm bên dưới đây thì bạn cũng có thể làm tại nhà để chiêu đãi cả gia đình rồi đấy!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54741, 'Lẩu Riêu Cua Miền Bắc', 'Lẩu riêu cua là món ăn dân dã, thơm ngon được nhiều người yêu thích trong các dịp trời se lạnh, với các nguyên liệu thơm ngon, hấp dẫn và bổ dưỡng cho sức khỏe. Để nấu được nồi lẩu riêu cua ngon đúng vị sẽ mất rất nhiều thời gian, nhưng giờ đây đã có gói Nước dùng hoàn chỉnh Barona Lẩu riêu cua sẽ giúp bạn nấu cả nồi lẩu chỉ trong tích tắc. Bạn hãy tham khảo ngay công thức bên dưới để xem "bí quyết" nấu món lẩu thơm ngon này nhé!<br>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54742, 'Cơm Cháy Rau Luộc Kho Quẹt', 'Cơm cháy rau củ kho quẹt, món ăn miền quê dân dã. Món ăn là sự kết hợp của cơm cháy vàng, giòn giòn chấm với niêu kho quẹt vàng nâu sóng sánh, thêm vị beo béo của tóp mỡ, cay cay của tiêu và ớt, vị ngọt đậm đà của tôm khô, không thể thiếu vị mặn mòi của nước mắm, thêm dĩa rau xanh mướt thanh mát ăn kèm. Hương vị đậm đà là thế làm cho những người thưởng thức không thể nào quên được. <br><br>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54743, 'Da Heo Chiên Tỏi Ớt', 'Da heo chiên tỏi ớt, món ăn vặt được nhiều người yêu thích. Da heo chiên vẫn giữ được độ giòn rụm, cảm giác nhai như tan vào trong miệng. Trong đó, còn hòa quyện chút vị mằn mặn vừa phải, chút cay nồng từ ớt, cháy giòn thơm của tỏi. Với hương vị nồng nàn, hấp dẫn, bạn hãy từ từ cản nhận độ giòn, xốp của từng miếng da heo đậm đà và thỏa mãn vị giác của mình.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54746, 'Cá Tầm Nướng Rau Củ', 'Cá tầm nướng rau củ thơm ngon, luôn là món ăn yêu thích của nhiều người, với vị cay mặn nồng của sốt ớt bên ngoài, vị béo thơm, dai ngọt của thịt cá tầm bên trong cùng chút thoang thoảng thơm phức vô cùng. Đây sẽ là món ăn thích hợp đổi vị cho bữa cơm gia đình mà vẫn giàu dinh dưỡng, bạn nhớ lưu lại công thức món cá tầm nướng rau củ của Maggi để thực hiện nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54749, 'Sắn Hấp Cốt Dừa', 'Vị sắn ngọt, đậm đà, thơm bùi vị cốt dừa chắc hẳn sẽ là một món ăn chơi hấp dẫn không kém phần tinh tế trong tiết trời chuyển thu.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54750, 'Mì Xoài Trộn', 'Mì xoài trộn với hương vị mới lạ, đang làm mưa làm gió với bao tín đồ ẩm thực hiện nay, cùng các nguyên liệu hấp dẫn sẽ khiến cho bạn thêm trải nghiệm mới về cách thưởng thức món mì trộn. Nếu có dịp rảnh rỗi hãy dành chút thời gian để vào bếp cùng Cooky trổ tài món mì xoài trộn mời cả nhà cùng thưởng thức nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54753, 'Lẩu Cá Linh Bông Điên Điển', 'Lẩu cá linh bông điên điển với hương vị đúng điệu của miền Tây sông nước sẽ là một trong những món ngon cuối tuần cho gia đình bạn. Chỉ mất vài phút chuẩn bị và chế biến theo công thức của Cooky, bạn sẽ có một nồi lẩu thơm nức mũi, hương vị ngọt ngào, đậm đà để mời cả nhà cùng thưởng thức nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54754, 'Chân Gà Sốt Thái', '<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54761, 'Cheesecake Bơ', 'Nếu thích vị ngậy béo và quá chán với cheesecake chanh leo vốn đã quá phổ biến thì cheesecake sẽ là một làn gió mới.', 8, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54768, 'Brownie Dairy-Free', 'Công thức thay bơ lạt bằng dầu oliu, healthy hơn và phù hợp cho ai không dung nạp được những sản phẩm từ sữa động vật', 8, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54771, 'Khoai Lang Nướng Bằng Lò', 'Khoai lang nướng vừa ngon vừa lành là món khoái khẩu của nhiều người. Chiều chiều đói lòng có củ khoai lang thơm ngọt ấm bụng thì còn gì bằng.', 3, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54774, 'Keto', 'Keto là một món ăn được kết hợp giữa xúc xích, trứng và một số rau củ thơm ngon và bổ dưỡng. Món có vị đậm đà từ xúc xích vườn bia Le Gourmet được kết hợp với vị béo béo trứng cùng hỗn hợp rau củ thanh mát. Bạn hãy tham khảo ngay công thức bên dưới để làm ngay món này giúp cho những bữa ăn của gia đình thêm phần thú vị, đổi mới nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54777, 'Da Má Bò Ngâm Sung Sả Tắc', 'Món ngâm', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54779, 'Thịt Ba Chỉ Kho Trứng Cút', 'Thịt kho là món ăn quen thuộc trong mỗi bữa ăn gia đình và cách làm món này cũng vô cùng đơn giản, dễ thục hiện', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54780, 'Thịt Hấp Trứng Muối', 'Thịt hấp trứng muối - món ăn dinh dưỡng cho bữa cơm gia đình thêm ngon miệng hơn trong những ngày mưa mát mẻ. Sự kết hợp giữa trứng muối bùi bùi, mặn mặn và vị béo ngậy của thịt được hấp chín, mùi thơm lan tỏa càng làm cho món ăn càng hấp dẫn.', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54781, 'Nem Bì Thính', 'Nem bì thính là một trong những loại nem dân dã được ưa chuộng của người Việt. Cách làm khá đơn giản và nhanh chóng.', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54787, 'Xúc Xích Hình Bạch Tuộc', 'Xúc xích là một trong những món khoái khẩu của trẻ em. Để có hình thù ngộ nghĩnh, tạo cảm giác ăn ngon hơn cho trẻ thì chúng ta chỉ cần biến tấu một chút sẽ làm nên một món ăn dễ thương, hấp dẫn.', 3, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54797, 'Sườn Chay Chiên Sả Ớt', 'Sườn chay chiên sả ớt, món ăn thanh đạm nhưng vẫn mang hương vị đậm đà và hấp dẫn, miếng sườn chay chiên giòn xốp trong miệng vô cùng mới lạ, phủ lên lớp sả ớt thơm nồng, làm cho món ăn càng thêm hoàn hảo. Chỉ mất vài phút vào bếp là bạn đã có ngay món ăn ngon miệng nay rồi. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54798, 'Cơm Hấp Hải Sản Nồi Cơm Điện', 'Cơm hấp hải sản nồi cơm điện, món ăn vừa ngon, vừa tiện lợi giành cho những ngày không có thời gian nấu nướng thì đây là giải pháp nhanh chóng giành cho bạn. Hạt cơm tơi, xốp thấm điều gia vị, kết hợp với các loại hải sản tươi ngon và rau củ giúp tăng thêm hương vị đậm đà của món ăn, chỉ mất 45 phút là đã có ngay món ăn dinh dưỡng dành cho những ngày bận rộn.', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54799, 'Bông Điên Điển Muối Chua', 'Bông điên điển muối chua, mang đậm hương vị miền Tây. Dưa chua bông điên điển giòn ngon, vị chua chua, món ăn không những ngon mà còn hấp dẫn bởi màu vàng của bông điên điển, màu trắng của giá, xanh của hẹ, thêm chút đỏ đỏ của ớt. Món ăn có thể ăn kèm với nhiều món thịt luộc, thịt kho, cá kho... thì rất bắt cơm vào mùa mưa. ', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54803, 'Gà Xào Tokbokki', 'So với gà chiên đảo tokbokki vẫn thường thấy, công thức này khiến gà mềm, ngọt hơn, có một chút nước sốt có thể ăn kèm cơm khá ngon', 1, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54807, 'Bún Bò Xào Nam Bộ', 'Món bún bò xào Nam Bộ tuy là Nam bộ nhưng lại bắt nguồn ở miền Bắc, có lẽ vì thế mà món này hợp khẩu vị với mọi người <3 mình tin đây là món mà già trẻ lớn bé ai cũng sẽ rất thích. Vừa đậm đà lại nhiều rau củ, làm lại vừa nhanh vừa dễ, món cho ngày cuối tuần lười biếng là đâyy', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54812, 'Bánh Khoai Mì Chiên', 'Trời mưa lành lạnh ăn 1 chút bánh nóng hổi thì sẽ như thế nào nhỉ ?????', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54813, 'Mắm Kho Miền Tây', 'Đơn giản mà tuyệt vời', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54814, 'Ngao Xào Sa Tế', 'Ngao xào sa tế là một trong những món nhậu được ưa chuộng nhất bởi hương vị thơm thơm, cay cay ăn mãi không ngán.', 3, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54819, 'Thạch Đậu Nành Lá Dứa', 'Xin chào các bạn, hôm nay mình tiếp tục vào bếp với 1 món thạch nghe tên là đã thấy thanh mát, hấp dẫn rồi đó chính là thạch đậu nành lá dứa.', 3, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54821, 'Thạch Bi Hoa Quả', 'Xin chào các bạn, làm thạch hoa quả rất dễ, chỉ với những nguyên liệu đơn giản bạn có thể tự làm nhiều loại thạch khác nhau như thạch hoa quả nhiều tầng, thạch hoa quả thập cẩm nhiều màu sắc…và bây giờ vào bếp cùng mình nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54823, 'Khô Gà Lá Chanh', 'Món khô và ăn vặt nức tiếng được lòng rất nhiều người có vị cay cay, đậm đậm thơm mùi lá chanh, sả ăn một lần sẽ nhớ mãi. Cách làm khô gà xé cay đơn giản này vừa ngon sạch lại làm quà tặng cũng được nhé!', 3, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54829, 'Snack Chuối Chiên Mè', '<span style="font-size: 13px;" data-mce-style="font-size: 13px;">Chuối xanh là loại quả không mấy phổ biến bởi nó vừa cứng vừa chát. Tuy nhiên, vẫn có một cách sẽ khiến cho quả chuối xanh vô vị này trở nên hấp dẫn hơn rất nhiều. Cùng bắt tay vào làm món Snack chuối chiên mè sau đây bạn nhé, bạn sẽ thấy món này cực dễ làm mà độ ngon thì đúng chuẩn.</span><br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54830, 'Gà Om Ớt Xanh Cay', 'Gà om ớt xanh là một trong món không thể bỏ qua đối với những bạn thích cay, đặc biệt là vị cay nồng của ớt xanh. Còn gì bằng những miếng thịt gà chắc nịch mọng nước hòa quyện cùng vị cay nồng của ớt xanh, ăn chung với một chén cơm trắng, vậy là đủ làm ấm lòng bạn trong những chiều mưa lành lạnh. Còn ngại ngần gì mà không cùng Cooky thực hiện món ăn tuyệt vời này cho bữa cơm gia đình ấm áp của bạn nào.', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54832, 'Chè Xoài', '<p>Hôm nay thì Bảo Linh khá nóng lòng muốn gửi tới mọi ng 1 món chè rất là ngon. Đó chính là món chè xoài. Với cách làm đơn giản thì các bạn có thể tự tay làm tại nhà mà vẫn ngon như ngoài quán.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54836, 'Cá Hồi Sốt Mật Ong', 'Cũng giống như những món cá sốt khác, Cá Hồi Sốt Mật Ong là một trong những món ăn cuối tuần thơm ngon và cách chế biến vô cùng đơn giản. Lúc này, các đầu bếp sẽ sử dụng mật ong giống như một loại gia vị tẩm ướp, vị ngọt thanh của mật ong sẽ ngấm vào từng thớ thịt tạo ra hương vị hấp dẫn cho món ăn. ', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54837, 'Trà Kem Cheese', 'Đơn giản, dễ làm, không còn phải tốn tiền mua ở các hãng trà sữa nữa :D', 1, '2022-03-22 00:00:00', 10, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54839, 'Thịt Kho Tàu', 'Thịt kho tàu là món ăn rất được ưa thích trong những bữa cơm gia đình người Việt. Để nấu một nồi thịt thơm ngon, chuẩn vị thì khâu nêm nếm gia vị cực kỳ quan trọng, đòi hỏi người thực hiện phải khéo tay trong bước nêm nếm gia vị. Tuy nhiên, từ nay bạn sẽ không còn lo lắng vì đã có Gia vị hoàn chỉnh Barona - Thịt kho tàu giúp món kho ngon chuẩn vị mà không cần nêm nếm thêm bất kỳ gia vị nào khác. Cùng Barona triển ngay công thức nấu thịt kho tàu chỉ 3 bước đơn giản nào!<br>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54840, 'Thịt Viên Thập Cẩm Chiên', 'Thịt viên chiên thập cẩm là món ăn thơm ngon, đơn giản và dễ làm. Món có vị béo, đậm đà từ thịt, kết hợp cùng ngọt tự nhiên của rau củ sẽ khiến cho bạn càng ăn càng mê. Đây chắc chắn sẽ là món ăn rất "được lòng" các bạn nhỏ trong gia đình. Bạn hãy tham khảo ngay công thức bên dưới và ngay món này cho các bé nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54841, 'Cá Kho Nước Mía', 'Cá kho nước mía là món ăn dân dã mang đậm hương vị Nam Bộ. Thịt cá tươi ngon được ướp gia vị đậm đà, kho cùng nước mía ngọt thanh tạo nên nước sốt vàng nâu óng ánh, đậm đà, ăn kèm với cơm nóng và rau xanh thì vô cùng thơm ngon. Bạn hãy tham khảo ngay công thức bên dưới và làm ngay món cá kho nước mía này nhé!<br>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54842, 'Chè Viên Khoai Lang', 'Những viên khoai lang vừa mềm vừa dẻo vừa ngon ngọt dễ ăn sẽ là món ăn vặt hấp dẫn trong thời tiết se lạnh.', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54843, 'Cá Pháo Ngâm', 'Cà pháo ngâm là món ăn kèm quen thuộc trong nhiều bữa cơm gia đình dân dã, giòn tan hấp dẫn nhiều người. Với cách làm món cà pháp ngâm trắng giòn của Cooky sẽ mang đến hương vị đặc trưng cho bữa ăn được đổi vị.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54844, 'Rau Câu Sữa Milo', 'Xin chào cả nhà, với chủ đề bột Milo, Bảo Linh sẽ cùng các bn làm 1 món tráng miệng đảm bảo ai cũng thích, đặc biệt là các bạn nhỏ đó là rau câu sữa milo. Và bây giờ, vào bếp cùng Bếp quà vặt nhé.', 3, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54848, 'Tai Heo Ngũ Vị', 'Tai heo ngũ vị là món ăn khá nhiều người ưa thích bởi hương vị thơm ngon của ngũ vị kết hợp với độ dai, giòn của tai heo.', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54851, 'Chanh Muối', 'Chanh muối là thức uống vừa có lợi cho sức khỏe vừa thơm ngon, cùng sắc vàng bắt mắt khiến bạn sẽ khó có thể chối từ. Bạn có thể thưởng thức món nước chanh muối này vào những ngày thời tiết oi bức, giúp thanh mát giải nhiệt và còn bổ sung thêm năng lượng. Với cách làm của Cooky dưới đây bạn sẽ có được chanh muối để dành uống khi thích đấy nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54852, 'Trứng Cuộn Xúc Xích', 'Trứng cuộn xúc xích chỉ với vài bước thực hiện đơn giản là bạn đã có bữa ăn sáng đẹp mắt và đầy đủ chất dinh dưỡng, giúp cho một ngày làm việc hiệu quả. Cùng nhanh tay lưu lại công thức món trứng cuộn xúc xích của Le Gourmet để chuẩn bị cho mình hay gia đình giúp bữa ăn thêm phong phú nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54854, 'Bánh Oreo Rau Câu', '<p>Xin chào các bạn, với những chiếc bánh Oreo, thì mọi người sẽ làm được những món ăn vặt nào nhỉ? Không để các bạn nghĩ lâu, hôm nay mình xin gợi ý tới các bạn 1 món thạch rất lạ miệng, đó chính là thạch rau câu Oreo. Và bây giờ cùng vào bếp với Linh nhé.</p>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54855, 'Thạch Trái Dừa', '<p>Xin chào các bạn, hôm qua thì mình mới mua thử 1 quả dừa rau câu, sau khi ăn và cảm nhận thì mình đã rút ra 1 công thức làm món thạch này khá đơn giản mà vẫn ngon như ngoài tiệm. Chúng ta cùng vào bếp và thử làm món này xem sao nhé.</p>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54856, 'Mochi Giọt Nước Trà Xanh', '<p>Xin chào các bạn, bánh mochi từ lâu đã nổi danh là loại bánh được làm từ bột gạo nếp rất thơm ngon và hấp dẫn của Nhật Bản. Hôm nay mình sẽ giới thiệu các bạn 1 loại bánh Mochi nổi tiếng không kém đó là mochi giọt nước trà xanh.</p>', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54858, 'Rau Câu Flan Trái Dừa', 'Rau Câu Flan Trái Dừa, nghe cái tên thôi đã khiên nhiều chị em " mê mẫn", đẹp mắt, thơm ngon lại tốt cho sức khỏe thì rất đáng để thử nhé. Trái dừa mềm cơm bọc ngoài lớp flan trứng béo ngậy kết hợp với rau câu lá dứa giòn dai, thơm mùi đặc trưng. Hãy cùng làm món rau câu flan trái dừa đầy hấp dẫn cho bữa tráng miệng cuối tuần này. ', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54859, 'Bánh Quế Hoa', '<p>Xin chào các bạn, hôm nay mình xin giới thiệu tới các bạn 1 món ăn thường hay xuất hiện trong các bộ phim cổ trang Trung Quốc, đó là bánh quế hoa hay còn gọi là quế hoa cao. Nguyên bản của bánh được làm từ bột gạo, bột nếp, đường và mật quế hoa. Về sau món bánh này được cải biên theo nhiều công thức khác nhau để phù hợp với khẩu vị của nhiều người.</p>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54861, 'Bánh Mì Chấm Kem Trứng - Cacao', 'Bánh mì chấm kem trứng - cacao là món ăn sáng quen thuộc chứa đựng hương vị tuổi thơ của nhiều người Hà Nội, với món kem trứng siêu béo ngậy cùng mùi thơm đặc trưng sẽ làm những bạn chưa thưởng thức món ăn này khó mà cưỡng lại được. Hãy tham khảo công thức món bánh mì chấm kem trứng - cacao của Cooky cho bạn bè hay cả nhà mình nhé!<br>', 4, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54862, 'Thạch Cà Phê Nhân Phô Mai', '<p>Hi, xin chào các bạn, chào mừng các bạn quay trở lại căn bếp thân quen của mình. Tiếp tục với chủ đề thạch rau câu thì Linh xin giới thiệu tới mọi người món thạch cà phê phô mai cực kỳ đơn giản, dễ làm nhé.</p>', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54863, 'Chè Thạch Đậu Biếc', '<p>Những năm gần đây, màu thực phẩm từ thiên nhiên được người tiêu dùng để ý nhiều hơn. Đặc biệt là hoa đậu biếc có màu xanh dương rất đẹp được sử dụng trong nhiều món ăn. Bếp quà vặt xin chia sẻ tới các bạn công thức chè hoa đậu biếc.</p>', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54866, 'Cá Bống Kho Tộ', 'Để có được nồi cá kho màu cánh gián đậm mà không cần nước hàn (nước màu vì ăn nhiều nước màu không tốt). Và chỉ kho 1 lần cá đã cứng không cần phải kho đi kho lại. Cách làm chi tiết mọi ngừoi xem clip nhé.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54868, 'Bim Bim Khoai Lang', 'Bim bim khoai lang là món ăn vặt được yêu thích bởi sự giòn tan của khoai lang mà vẫn giữ được hương vị ban đầu.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54870, 'Pad Thái', 'Món Pad Thái với hương vị đậm đà, rất đặc biệt mang phong cách ẩm thực của xứ sở Thái Lan. Phần sốt chua ngọt thấm đều vào từng sợi phở, thêm vị tươi ngon của tôm và chút béo bùi của đậu hũ, khiến cho món Pad này càng khó cưỡng hơn khi thưởng thức. Hãy cùng lưu lại công thức cách làm món Pad Thái này của Cooky để thực hiện nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54872, 'Bún Vịt Xáo Măng', 'Bún vịt xáo măng vừa lạ miệng vừa quen thuộc, thích hợp thưởng thức trong những ngày se lạnh. Với phần thịt vịt chín mềm, đậm vị, nước dùng thanh ngọt, thơm thơm, hơi béo. Mùi măng thơm nức mũi cùng mùi gừng nóng ấm. Tất cả hòa quyện thành món ăn hoàn hảo. ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54873, 'Canh Chua Bông So Đũa', 'Nhắc đến bông so đũa hay còn gọi là sua đủa, chắc không còn xa lạ gì với các bạn ở khu vực miền Nam nhưng chắc hẵn cũng khá xa lạ với nhiều bạn ở khu vực miền Bắc. Tuy nhiên nếu ai có một lần được thưởng thức các món ăn được chế biến bởi bông so đủa chắc chắn sẽ bị cuốn hút. Mấy nay Sài Gòn mưa nhiều, cũng dịp này bông trổ nhiều hơn nên lại có cơ hội được ăn món canh chua bông so đủa quen thuộc của má rồi ăn kèm với tép ram mặn trong mấy chiều mưa Sài Gòn... là cực kỳ hao cơm luôn. Ric chia sẻ mọi người món canh chua của má Ric bên dưới nha:', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54874, 'Cơm Chiên Trứng', '<p>Món cơm chiên vừa tơi vừa dẻo, vừa thơm phức mùi tỏi phi, vừa beo béo vị trứng gà ngon quên lối thoát :D</p>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54876, 'Cream brulee bí đỏ', 'Halloween là một lễ hội đặc trưng từ phương tây đã du nhập vào Việt Nam từ vài năm trở lại đây. Một trong những nguyên liệu đặc trưng cho mùa lễ hội này là bí đỏ. Cream brulee là một món gần như tương tự với bánh flan, là một món tráng miệng ưa thích của đa số mọi người. Vậy tại sao chúng ta không thử kết hợp giữa vị béo của cream brulee và vị ngọt của bí đỏ để tạo ra một món tráng miệng tuyệt vời dành cho gia đình mình vào dịp Halloween nhỉ. Còn chờ gì nữa, hãy cùng xắn tay áo lên và vào bếp cùng Cooky nào. ', 8, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54877, 'Pâté chaud', 'Pâté Chaud (hay Pateso), một cái tên nghe rất "Pháp" nhưng có nhiều câu chuyện lại chứng minh chiếc bánh này lại bắt nguồn từ Việt Nam. Lớp vỏ bánh ngàn lớp giòn rụm, cùng nhân pate thịt thơm lừng nóng hổi, vậy cũng đủ cho bữa sáng bận rộn của chùng ta. Cách làm tuy hơi cầu kỳ nhưng bạn sẽ không hối hận khi bạn cắn từng miếng bánh thành phẩm của mình. Muốn ăn hãy lăn vào bếp cùng Cooky nào bạn ơi.   ', 4, '2022-03-22 00:00:00', 240, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54878, 'Lạc Rang Húng Lìu', 'Để có món lạc rang húng lìu thơm ngon không chỉ phụ thuộc vào cách lựa chọn nguyên liệu ra sao mà còn quyết định bởi cách rang lạc của bạn.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54879, 'Bún Cá Nha Trang', '<p>Công thức nấu nhanh nhưng cũng đủ vị, phù hợp 2 người ăn nha.</p>', 2, '2022-03-22 00:00:00', 15, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54886, 'Gân Bò Xào Ngũ Sắc', 'Nếu bạn thích gân bò thì món Gân bò xào ngũ sắc dưới đây là món ăn lý tưởng cho bữa tối cuối tuần. Gân bò nâu trong, nhai giòn giòn rất thú vị, lại thêm rau củ nhiều màu sắc và dinh dưỡng, chắc chắn sẽ khiến bữa ăn gia đình bạn thêm phong phú và lôi cuốn.<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54889, 'Mì Lạnh Hàn Quốc', 'Mì lạnh Hàn Quốc vừa mát vừa bổ, với hương vị độc đáo và thơm ngon sẽ khiến cho bạn khó quên khi ăn. Món mì trông đơn giản nhưng lại rất bổ dưỡng nhờ vào phần súp lạnh thơm ngon, cùng các nguyên liệu sẽ đem lại cho cả gia đình bạn một trải nghiệm thú vị. Để không mất thời gian tìm kiếm bạn nhớ lưu lại công thức món mì lạnh Hàn Quốc của Cooky dưới đây nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54892, 'Bánh Tẻ', 'Bánh tẻ còn được gọi là bánh lá hay bánh răng bừa, món bánh truyền thống của vùng Bắc Bộ. Bánh được là bằng bột gạo tẻ, có độ mềm, xốp bên trong là phần nhân thịt và các loại nấm được nêm nếm vừa ăn, góp ngoài bằng lá dong nên có mùi thơm rất đặc trưng. Món ăn vừa ngon vừa thanh vừa bùi, cho vị đậm chất truyền thống của người Việt. ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54893, 'Mắm Tép Đu Đủ', '<span style="font-size: 10pt;" data-mce-style="font-size: 10pt;">Mắm tép đu đủ, món ăn dân dã "hao cơm". Với đu đủ giòn sần sựt hòa quyện cùng mắm tép chua chua mặn mặn, vị cay cay đầu lưỡi của tỏi và ớt, nghe thôi là đã thấy thèm. Mắm tép đu đu không chỉ ngon khi ăn kèm với cơm nóng, bạn có thể dùng để ăn kèm với rau sống, bún, thịt luộc.... <span style="font-family: Roboto, sans-serif;">Ngay từ cái nhìn đầu, hũ mắm tép chua đỏ au mượt mà đã kích thích vị giác của thực khách và đến khi thưởng thức thì tất cả đã thỏa mãn giác quan của người dùng. </span></span>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54894, 'Cá Nục Kho Coca', 'Thịt ba chỉ heo kho coca, cánh gà kho coca... thì đều là những món quá quen thuộc trong bữa cơm của mỗi gia đình rồi đúng không nào. Thế nhưng đã có chị em nào đã từng thử kho cá với coca không nhỉ? Nghe lạ lẫm đúng không nào, ấy vậy mà sự kết hợp giữa cá với coca lại tạo nên một món ăn vô cùng độc đáo, mới lạ và nhất là hương vị vô cùng hấp dẫn. Cùng vào bếp làm thử món cá nục kho coca ngon tuyệt dưới đây với công thức của Cooky nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54896, 'Sườn Kho Thơm', '<span style="font-size: 13px;" data-mce-style="font-size: 13px;">Sườn kho thơm là món ăn được nhiều gia đình yêu thích. Món sườn kho thơm có vị ngọt của thịt cùng vị chua chua của khóm và các gia vị hòa quyện vào nhau, tạo nên một món ăn có hương vị ngon khó cưỡng làm hao cơm nhanh chóng. Cùng Cooky học cách làm món sườn kho thơm hấp dẫn này cho gia đình nha!</span><br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54904, 'Bánh Bò Hấp Thốt Nốt', '<p>Bánh bò hấp thốt nốt thơm ngào ngạt mùi cốt đừa với thốt nốt, lại mềm xốp, ai ăn cũng sẽ thích mê. Các bạn xem clip để biết cách làm chi tiết nhé</p>', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54907, 'Trứng Cá Sốt Cà Chua', 'Trứng cá sốt cà chua là món ăn đơn giản, dễ làm tuy nhiên còn khá mới lạ với nhiều gia đình. Để thay đổi khẩu vị cho gia đình trở nên hấp dẫn hơn thì một đĩa trứng cá sốt như này sẽ là lựa chọn tuyệt vời.', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54908, 'Bánh Cam Lúc Lắc', 'Bánh cam với lớp vỏ ngoài giòn tan, bên trong lại deo dẻo và béo ngậy nhân đậu xanh. ', 3, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54913, 'Bún Hến', 'Chỉ mất khoảng 30 phút chúng ta đã có một nồi nước dùng thơm ngon và tròn vị với vị chua thanh nhẹ của khế, cà chua và đặc biệt là mẻ hoà quyện cùng vị ngọt và mùi thơm đặc trưng của hến.', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54915, 'Trứng Chiên Rim Nước Mắm', 'Trứng chiên rim nước mắm là món ăn đơn giản, dễ làm, hương vị món ăn vô cùng đậm đà và rất đưa cơm. Trứng chiên mềm mịn, beo béo kết hợp với nước sốt nước mắm có vị mặn ngọt vừa ăn giúp tạo nên một món ăn mới lạ, độc đáo. Bạn hãy tham khảo ngay công thức dưới đây để làm món này cho cả gia đình cùng thưởng thức nhé!', 4, '2022-03-22 00:00:00', 15, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54916, 'Trứng Chiên Rim Sa Tế', 'Trứng chiên rim sa tế là món ăn đơn giản, dễ làm mà lại cực kỳ hấp dẫn. Món ăn có vị cay nồng, đậm đà của nước sốt sa tế, mùi thơm của bơ hòa quyện với trứng chiên mềm mềm, beo béo sẽ khiến cho bạn vét sạch cả nồi cơm trong tích tắc. Bạn hãy tham khảo ngay công thức bên dưới để làm món ngon này nhé.', 4, '2022-03-22 00:00:00', 15, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54918, 'Galaxy Mojito', 'Chỉ cần một chút thay đổi là có ngay một ly mojito thật đẹp và lạ mắt', 2, '2022-03-22 00:00:00', 10, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54919, 'Bánh Xèo', 'Không biết trong group mình có bà mẹ nào như bà má nhà Ric không? Mỗi lần mà lỡ buông miệng hôm nay thèm cái này… là ngày mai má làm cho ăn ngập mặt. Nhớ có lần lỡ nói nhỏ nhỏ thèm bánh xèo quá má ơi, thế là bà đổ cho mấy ký bột. Vừa ăn vừa đổ, ăn xong vẫn còn đổ, rửa bát xong má vẫn còn đổ bánh xèo. Mà nhắc tới bánh xèo chắc cũng không còn xa lạ gì nữa. Hầu như miền nào cũng có, rồi mỗi miền mỗi cách làm khác nhau, có người cho nước cốt dừa, có người cho bia, có người dùng bột nghệ, có người dùng bột cà ri. Rồi nước chấm bánh xèo mỗi miền cũng khác nhau. Nhưng cái điểm chung duy nhất là bánh xèo dù ở miền nào cũng ngon, cũng đậm đà và khó cưỡng. Nên cho dù miền nào cũng có nhưng lỡ bước chân qua miền khác thì bánh xèo cũng không nên bỏ qua nha. Nhà Ríc thì có thói quen không mua bột bánh xèo pha sẵn mà pha bột bằng bột gạo. Ngày xưa mỗi lần muỗn ăn bánh xèo má làm cực lắm. Phải bồng bột đâu đó qua đêm, rồi mang qua nhà hàng xóm xay ra. Còn nhớ rất rỏ cái cói bằng đá huyền thoại xay bột ngon dã man. Xay xong là lên chuột lên cơ luôn chứ chẳng đùa. Thôi không buôn dưa lê, dưa cải nữa, Ric chia sẻ mọi người cái bánh xèo nhà mình bên dưới nha', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54922, 'Phở Tôm Nấm', 'Lâu ngày đổi gió với món phở tôm nấm lạ miệng nha các bạn ơi!', 3, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54928, 'Chả Cua Chiên Áp Chảo', 'Chả cua chiên áp chảo, món ăn dinh dưỡng, ngon miệng cho cả gia đinh. Chả cua thơm, ngọt từ thịt cua và các loại ra củ, được chấm kèm với sốt chấm thơm ngon, béo ngậy ăn thích vô cùng. Vừa đơn giản, vừa giàu dinh dưỡng thì bạn còn chần chừ gì mà không thử ngay để chiêu đãi cho cả gia đình mình nào. ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54931, 'Tôm Nướng Muối Ớt', 'Tôm nướng muối ớt ngon chuẩn vị được rất nhiều người yêu thích. Một chút cay nồng của ớt, một chút dai, ngon ngọt của thịt tôm cùng với vị mặn đậm đà của gia vị, sự hòa quyện hoàn hảo này chắc chắn sẽ mang lại cho bạn những trải nghiệm thú vị, khó quên. Dưới đây, Tupperware sẽ chia sẻ cho bạn cách làm nên hương vị hoàn hảo nhất cho món ăn này.<br>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54932, 'Lẩu Bầu Cua', 'Lẩu bầu cua là món lẩu phổ biến, được nhiều người yêu thích. Món lẩu bầu cua là sự kết hợp tuyệt vời giữa vị ngọt của cua và vị thanh mát của bầu đã tạo nên một hương vị vô cùng thơm ngon, hấp dẫn. Bên cạnh đó, đây còn là món ăn có tính giải nhiệt tốt và bổ dưỡng. Bạn hãy tham khảo ngay cách làm bên dưới đây để nấu ngay món lẩu này cho cả gia đình cùng thưởng thức nhé!<br>', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54934, 'Cocoa Milkshake', 'Cocoa Milkshake là thức uống khá phổ biến ở ẩm thực đường phố Ấn Độ. Cocoa milkshake có vị đậm đà của bột cacao, vị beo béo của sữa tươi và một chút đắng nhẹ từ socola đen, đây chắc chắn là một món thức uống lý tưởng cho bạn trong những ngày hè oi bức. Bạn hãy tham khảo công thức bên dưới để làm món thức uống này nhé!', 4, '2022-03-22 00:00:00', 15, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54935, 'Bánh Quẩy Đường', 'Bánh quẩy đường (bánh quẩy thừng) là món ăn vặt tuổi thơ được rất nhiều người yêu thích. Chiếc bánh quẩy vàng thơm, giòn tan, được bọc bên ngoài bởi một lớp đường ngọt ngào sẽ khiến cho bạn "say đắm" ngay từ lần đầu khi được thưởng thức. Bên cạnh đó, cách làm bánh quẩy rất đơn giản, dễ làm, nguyên liệu gần gủi, dễ tìm. Bạn hãy tham khảo công thức bên dưới, vào bếp làm ngay món bánh ngọt này nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54936, 'Trứng Lòng Đào Chiên Sốt Me', 'Trứng lòng đào chiên sốt me, món ăn đơn giản nhưng hấp dẫn vô cùng. Lớp vỏ trứng được chiên giòn vàng óng ả, lòng đỏ bên trong thì béo ngậy, sóng sánh áo lên trên phần sốt me chua chua ngọt ngọt bắt cơm vô cùng khiến bạn chỉ muốn ăn mãi, phải ăn liền khi còn nóng để thưởng thức trọn vẹn vị ngon nhé.<br><br><br>', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54938, 'Bánh Tôm Hồ Tây', 'Bánh tôm Hồ Tây là một trong những món ăn nổi tiếng và được nhiều người yêu thích. Nhưng nói về cách làm thì không hề khó như nhiều người nghĩ.', 5, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54947, 'Đậu Hũ Non Sốt Nấm', 'Đậu hũ non sốt nấm với vị béo hoà quyện cùng vị ngọt nấm hương và vị giòn nấm mèo sẽ khiến bữa ăn gia đình bạn rất hao cơm. Cùng Cooky xem cách thực hiện món đậu hũ non sốt nấm cho những ngày thèm bữa cơm thanh đạm cùng gia đình mình nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54948, 'Pudding Milo', 'Pudding là món tráng miệng được rất nhiều người yêu thích. Hôm nay, Cooky sẽ bật mí cho bạn một công thức pudding mới đó là pudding milo thơm béo, mềm mịn với cách làm siêu đơn giản, chỉ với 2 nguyên liệu là bạn đã ngay những "bé" pudding xinh xắn. Pudding mịn màng, núng nính ăn vào mát lịm, vị ngọt vừa phải sẽ khiến cho bạn càng ăn càng mê. Bạn hãy vào bếp với Cooky và học ngay cách làm món pudding milo này nhé!<br>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54949, 'Bánh Đậu Xanh', 'Bánh đậu xanh là món bánh ngọt được nhiều người yêu thích. Bánh đậu xanh thơm ngon, có vị béo ngậy, mềm mịn. Bánh đậu xanh mềm ngon, hấp dẫn ăn kèm với ly trà nóng thì vô cung tuyệt vời. Bạn hãy cùng với Cooky xắn tay áo vào bếp và làm ngay những mẻ bánh đậu xanh siêu thơm ngon cho cả gia đình cùng thưởng thức nhé.', 4, '2022-03-22 00:00:00', 135, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54950, 'Canh Khổ Qua Rừng Nhồi Cá Thác Lác', 'Canh khổ qua rừng nhồi cá thác lác, món ăn dân dã của người Việt. Cá thác lác được quết dai, béo ngon cùng với những miếng khổ qua nhân nhẫn vị đắng nhưng lại thanh mát, quả thật là một cực phẩm. Canh không những ngon, mà khổ qua còn mang lại nhiều công dụng tuyệt vời cho sức khỏe của gia đình bạn.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54953, 'Bún Cua Thối', 'Bún cua thối là đặc sản ở phố núi Pleiku, Gia Lai và được mệnh danh là một trong những món lạ của Việt Nam.<b> </b>Nước dùng của món bún cua thối được chế biến bằng cách lọc cua xong thì ủ một ngày đêm, vì thế có mùi rất nặng và đặc trưng. Hãy xcùng Cooky học hỏi cách làm món bún cua thối này nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54955, 'Xôi Dừa Hạt Sen', 'Xôi dừa hạt sen là món ăn khoái khẩu của nhiều người, đặc biệt là bữa sáng. Với những chỉ dẫn cụ thể này chắc chắn bạn sẽ trổ tài nấu cho cả nhà mình một bữa sáng tuyệt vời.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54959, 'Mac & Cheese', 'Mac & Cheese là viết tắt của từ Macaroni and Cheese, là một trong những món ăn phổ biến tại Anh và Mỹ. Macaroni là loại nui tròn, ngắn, khi kết hợp cùng vị béo ngậy, thơm lừng quyến rũ của các loại phô mai sẽ trở thành một món ngon khó cưỡng với bất kỳ thực khách khó tính nào. Với cách làm đơn giản, nhanh gọn, nó có thể trở thành bữa ăn sáng, trưa hoặc tối cho mọi thành viên trong gia đình, đặc biệt là trẻ nhỏ. Hãy cùng Cooky thực hiện một món ăn sáng kiểu Mỹ cho gia đình nào.', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54960, 'Bánh Lá Mơ', 'Bánh lá mơ, lá mít là một trong những thức quà tuổi thơ rất đỗi thân thương. Bánh lá mơ có màu xanh đen quyến rũ, dai dai chấm cùng nước cốt dừa béo béo, đậu phộng giòn giòn, bùi bùi, làm bạn không ngừng miệng được. Bạn có thể dễ dàng vào bếp cùng Cooky để làm món bánh dân dã này. Còn chờ gì mà không đi hái lá mơ, lá mít để thực hiện món ăn tuổi thơ này nào.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54975, 'Trà Quế Hồng Táo', '<p>Trà quế hồng táo thường được uống vào mùa lạnh rất tốt cho cơ thể. Trà được uống sau bữa cơm để tiêu hóa được tốt hơn. Hồng táo có tác dụng an thần, điều trị bệnh mất ngủ. Quế giảm lượng cholesterol và rất tốt cho người bệnh tiểu đường. Hai loại này kết hợp làm ra thức uống bổ, khỏe cho ngày đông.</p>', 5, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54981, 'Bún Mắm Nêm Đà Nẵng', 'Không biết đã biết bao nhiêu vát ba lô đi Đà Nẵng, nhưng lúc nào Đà Nẵng cũng có sức cuốn hút ghê gớm lắm và hình như năm nào cũng phải đi Đà Nẵng một lần. Rồi cứ một lần Bún Mắm Nêm Đà Nẵng', 2, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54982, 'Bắp Cải Cuộn Thịt Bằm', '<p data-field="sapo">Bắp cải cuộn thịt hấp là món ăn ngon, vị ngọt thanh. Sự kết hợp hài hòa của bắp cải và thịt sẽ giúp bạn có 1 món ăn ngon cho bữa cơm gia đình</p>', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54983, 'Ruốc Tôm Khô', 'Ruốc tôm khô là món ăn rất đưa cơm mà không tốn quá nhiều thời gian hay công đoạn thực hiện, với vị mặn mặn ngọt ngọt của ruốc sẽ khiến cả nhà bạn khó mà cưỡng lại được. Món ruốc tôm khô rất tiện lợi vì có thể bảo quản trong hũ để dùng dần, cùng học hỏi vài bí quyết để làm ruốc tôm khô hấp dẫn này nhé!', 4, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54985, 'Lẩu Hoa Mực', 'Lẩu hoa mực là món lẩu chua cay quen thuộc mà lạ miệng và cực kỳ đơn giản từ gói gia vị nêm sẵn Aji-Quick® Lẩu Thái. Nhũng bông hoa mực giòn giòn với nhân cá thác lác dai mềm nóng hỏi ăn kèm với bún, nước mắm và các loại rau thì quả là một sự kết hợp tuyệt vời. Bạn hãy tham khảo ngay công thức bên dưới để nấu món lẩu này cho cả gia đình cùng thưởng thức nhé!<br>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54986, 'Thịt Nguội Serrano Cuộn Nước Sốt Mè Rang', 'Thịt nguội serrano cuộn nước sốt mè rang là món ăn có sự kết hợp hoàn hảo giữa ẩm thực Châu Âu và Việt Nam. Món có vị đậm đà từ những cuộn dăm bông serrano của Tây Ban Nha, vị béo của phô mai của Đức, mùi thơm nức mùi bơ của Pháp, vị ngọt tự nhiên của các loại rau củ, được chấm kèm với nước sốt mè rang béo ngậy, thơm lừng, sẽ khiến cho bạn vét sạch nồi cơm trong tích tắc. Bạn hãy tham khảo ngay công thức bên dưới để làm ngay món này cho gia đình cùng nhau thưởng thức nhé.', 3, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54987, 'Chè Sắn', 'Chè sắn là một món ăn vặt rất hợp với thời tiết lạnh mà công thức cực kỳ đơn giản.', 5, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54991, 'Bánh Khúc Cây', 'Giáng sinh bắt nguồn từ các nước phương Tây, và hiện nay cũng trở thành một trong những dịp lễ đặc biệt không thể thiếu ở Việt Nam. Và khi nhắc tới Noel, người ta không thể không nhắc tới Yule Log Cake, hay còn gọi là bánh khúc cây. Loại bánh này được lấy cảm hứng từ tục lệ dâng rượu của người phương Tây vào đêm giáng sinh. Còn chờ gì mà không cùng Cooky thử làm một "khúc cây" thật ngọt ngào cho gia đình và người yêu bạn vào đêm Noel sắp tới nào.', 6, '2022-03-22 00:00:00', 180, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54993, 'Gà Kho Nấm Hương', 'Gà kho nấm hương là món mặn, ăn cơm đậm đà, rất dễ ăn với mùi thơm đặc trưng của nấm hương tỏa ra từ khi nấu vô cùng hấp dẫn. ', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54995, 'Gà Hấp Lá Chanh', ' Gà hấp lá chanh nghe có vẻ hơi xa lạ một chút. Tuy nhiên, với những người sành ăn, gà hấp mới chính là món ăn nên thưởng thức hơn. Gà hấp lá chanh không chỉ bảo được độ chín và mềm của thịt, mà còn vừa đảm bảo gà không bị mất đi quá nhiều chất dinh dưỡng vào nước luộc, giúp giữ được hương vị thơm phức lá chanh mà chúng ta sử dụng để hấp. <br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (54996, 'Trứng Hấp Chả Lụa Lạp Xưởng', 'Trứng hấp chả lụa lạp xưởng là món ăn khá mới mẻ nhưng vô cùng dinh dưỡng rất thích hợp cho người lớn tuổi và trẻ nhỏ trong gia đình. Không quá cầu kỳ nhưng kiểu trứng hấp này sẽ đem lại cảm giác hấp dẫn khi thưởng thức với mặt trứng vàng ươm, điểm thêm màu đỏ của lạp xưởng, màu xanh củ hành lá và màu trắng củ chả lụa càng làm món ăn thêm nổi bật. ', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55002, 'Gà Hấp Mắm Nhĩ', 'Gà hấp hay gà luộc là món ăn quá quen thuộc rồi nay nhà mình biến tấu thêm chút với mắm nhĩ để cho gà thêm đậm đà. Món này làm cực kì dễ luôn nha.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55004, 'Ốc Hương Sốt Hoàng Kim', 'Ốc hương sốt hoàng kim (trứng muối) là món ăn đơn giản dễ làm nhưng không kém phần ngon miệng và dinh dưỡng. Chỉ với 60 phút là đã có ngay một món ăn cực kì ngon miệng rồi<br data-mce-bogus="1">', 1, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55006, 'Thịt Kho Nước Tương Kiểu Nhật', 'Món thịt kho quen thuộc sẽ trở nên ngon lành và hấp dẫn hơn với công thức món Thịt kho nước tương kiểu Nhật dưới đây. Với thịt heo được chọn là thịt ba chỉ vừa có mỡ vừa có thịt hài hòa nên khi kho tạo độ ngon mà không ngấy cùng nước tương Kikkoman ướp đậm đà, chắc hẳn sẽ khiến bửa ăn gia đình bạn thêm phần hấp dẫn.<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55007, 'Sụn Gà Sốt Trứng Muối', 'Sụn gà sốt trứng muối là món ăn vặt yêu thích của giới trẻ. Bên cạnh những món sụn phổ biến như sụn gà rang muối, sụn gà chiên nước mắm, sụn gà cháy tỏi,…thì sụn gà trứng muối ra đời và gây sốt, đặc biệt là đối với những bạn trẻ là fan của trứng muối. Những miếng sụn gà được chiên vàng giòn, phủ quanh mình lớp trứng muối siêu ngon. Cùng Cooky học ngay cách làm món sụn gà sốt trứng muối theo công thức bên dưới nha!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55008, 'Bánh Lá Dứa', 'Bánh lá dứa là món bánh có mùi vị thơm ngon và màu sắc bắt mắt có nguồn gốc từ đất nước Thái Lan. Bánh có mùi thơm và màu xanh  đặc trưng của lá dứa, ăn cùng dừa bào sợi thoang thoảng vị ngọt cực hấp dẫn. Cùng Cooky học ngay cách làm món bánh lá dứa dưới đây nhé!', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55009, 'Đùi Ếch Chiên Bơ Cay', 'Đùi ếch chiên bơ cay là món ngon hấp dẫn để chiêu đãi gia đình nhân dịp cuối tuần. Ếch chiên giòn rụm quyện với vị bơ béo ngậy, thơm lừng tỏa ra hết sức quyến rũ. Đây chắc hẳn là món ăn khoái khẩu của rất nhiều người, đặc biệt là các thành viên nhí trong gia đình bạn. Hãy cùng khám phá bí kíp làm món Đùi ếch chiên bơ cay ngon tuyệt dưới đây nhé!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55011, 'Nghêu Hấp Sả', '<span style="color: #444444; font-family: ''Times New Roman''; font-size: 16px; text-align: justify;" data-mce-style="color: #444444; font-family: ''Times New Roman''; font-size: 16px; text-align: justify;">Nghêu hấp sả là món ăn giá rẻ nhưng lại có thành phần dinh dưỡng cao. Món ăn này thanh mát, thơm và ngọt rất dễ ăn nên được rất nhiều gia đình yêu thích. Và cách làm cũng thật sự rất đơn giản</span><br data-mce-bogus="1">', 1, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55013, 'Bún Thịt Nướng', 'Bún thịt nướng là món ăn không quá cầu kỳ bước thực hiện, nhưng để có được công thức ướp thịt vừa hấp dẫn vừa ngon miệng thì không dễ dàng. Ngoài ra, phần nước chấm cũng khá quan trọng đôi với món bún thịt nướng. Với công thức đầy đủ của Cooky đây sẽ bật mí cho bạn cách làm bún thịt nướng đậm vị, mời cả nhà đổi vị bữa ăn thường ngày.  ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55016, 'Cóc Ngâm Chua Ngọt', 'Cóc ngâm chua ngọt là sự hòa quyện giữa vị chua thanh của cóc, chút cay cay của ớt, vị ngọt của đường. Chắc chắn hương vị tuyệt vời như thế khiến từ người già cho tới trẻ nhỏ đều mê mệt. Món ăn này khi thưởng thức sẽ có sự giòn giòn của cóc hòa quyện với vị chua cay mặn ngọt nhẹ nhàng cực kỳ hòa quyện, chấm thêm muối tôm nữa thì còn gì bằng!', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55018, 'Bánh Bông Lan Rau Câu', 'Khám phá cách thức giúp bạn làm được chiếc bánh bông lan rau câu đẹp mắt, hấp dẫn trong thời gian ngắn nhất.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55021, 'Chè Con Ong', 'Hẳn các bạn đều biết món chè con ong quen thuộc trong những ngày lễ tết hay mồng một đầu tháng. Cách làm món chè này khá dễ dàng, chúng ta cùng vào bếp trổ tài món này nhé!', 5, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55023, 'Ốc Bươu Xào Sả Ớt', 'Thời tiết se se lạnh của mùa đông mà có thêm 1 dĩa ốc bươu xào xúc bánh tráng thì còn gì bằng nữa đúng không cả nhà. Ốc bươu xào xả ớt cay cay, thơm thơm mà cách làm còn vô cùng đơn giản nữa.', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55024, 'Bánh Bông Lan Cuộn Chà Bông Gà Cay Sốt Dầu Trứng', '<p>Bánh bông lan cuộn chà bông gà cay sốt dầu trứng là một món dễ làm, dễ ăn, rất cuốn hút trẻ nhỏ. Sự mềm mịn của bánh bông lan hòa cùng vị mặn mặn ngọt ngọt của sốt dầu trứng, và sự dai giòn, cay cay từ chà bông gà và vị chua chua béo ngậy từ phô mai, đủ để thỏa mãn cái miệng và cái bụng của bạn vào một chiều chủ nhật đầy nắng. Còn chờ gì mà không vào bếp cùng Cooky và làm một ổ bánh bông lan cuộn chà bông gà cay đầy mê hoặc này nào.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55027, 'Bánh Đa Cua', 'Bánh đa cua đậm đà hương vị thơm ngon, không quá cầu kỳ với gói Nước dùng hoàn chỉnh Barona Lẩu Cua Đồng. Cùng với các nguyên liệu hấp dẫn ăn kèm chả cá dai giòn, chả lá lốt béo ngậy, rau ăn kèm tươi mát,... Bạn nhớ lưu lại công thức nấu bánh đa cua của Barona để chiêu đãi mời cả nhà cùng thưởng thức nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55028, 'Gà Nướng Muối Ớt', 'Món gà nướng muối ớt <strong></strong>thơm lừng với lớp da bắt mắt, thịt mềm thấm vị mặn của muối và vị cay nồng của ớt chinh phục cả những người ăn khó tính. Cùng Barona vào bếp thực hiện gà nướng muối ớt ngon mê ly và không tốn quá nhiều công đoạn để mời cả gia đình bạn bữa ăn thật bắt mắt nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55029, 'Bánh Tráng Mắm Ruốc Tóp Mỡ Phô Mai Nướng', 'Bánh tráng mắm ruốc tóp mỡ phô mai nướng hấp dẫn biết bao bạn trẻ, với cách hướng dẫn chi tiết của Cooky dưới đây bạn sẽ không cần tốn thời gian di chuyển hay xếp hàng đợi dài ở hàng quán. Món bánh tráng nướng đặc trưng sốt mắm ruốc thơm ngon cùng các topping bắt mắt như tóp mỡ, ruốc tôm khô, nem chua,... khiến bạn chỉ muốn ăn mãi với sự kết hợp tuy "lạ mà ngon" ở ngay đây nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55033, 'Blueberry Ginger Toddy', 'Nguyên liệu tươi chính là ngôi sao của món Blueberry Ginger Toddy này. Đây là một loại cocktail rất đơn giản dễ làm cho mùa lễ hội hoàn hảo, hâm nóng lại bầu không khí  trong những ngày se se lạnh. Việt quất, chanh tươi và một chút gừng mang đến cho món Blueberry Ginger Toddy này một hương vị độc đáo khó cưỡng!', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55034, 'Trà Sữa Tinh Than Tre', 'Các tín đồ trà sữa đang “truy lùng” một món đồ uống mới toanh, đó là trà sữa tinh than tre. Với hương vị thơm ngon, màu sắc cuốn hút, món trà sữa này đã nhanh chóng thu hút sự quan tâm của nhiều bạn trẻ. Thức uống có màu đen huyền bí, đặc trưng của nguyên liệu pha chế là bột tinh than tre. Sự kết hợp độc đáo này đã giúp trà sữa than tre dù chỉ mới ra mắt trong thời gian ngắn nhưng đã được nhiều người yêu thích. Cùng thưởng thức món trà sữa mới lạ độc đáo này với công thức pha chế hết sức đơn giản từ Cooky nhé !', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55035, 'Ốc Xào Cay Miền Trung', 'Ở miền Trung có một món ốc ngon mà hầu như ai cũng thích và cũng mê đó chính là món Ốc xào cay. Ốc khi ăn có vừa béo mềm vừa có mùi thơm rất nồng của sả, lá chanh hòa cùng vị cay xé lưỡi của ớt. Trời càng lạnh thì ăn ốc xào cay càng ngon và càng ấm bụng. Cùng Cooky học cách làm món ốc xào cay miền Trung hấp dẫn để chiêu đã gia đình nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55036, 'Gà Chiên Nước Mắm', '<span style="font-size: 13px;" data-mce-style="font-size: 13px;">Gà chiên nước mắm là món ăn quen thuộc trong mâm cơm gia đình. Bất kỳ ai dù bận mấy cũng có thể làm món gà ""bất hủ"" này trong tích tắc mà vẫn ngon đỉnh cao. Miếng gà chiên vàng ươm óng ả, phủ lớp xốt thơm mặn đậm đà, ăn kèm cơm nóng và dưa leo nữa là khỏi chê luôn. Cùng xắn tay áo vào bếp làm liền cho cả nhà thôi mọi người ơi!</span><br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55037, 'Bánh Ngô Sữa Chiên', 'Từng miếng bánh mềm ngọt, thơm mùi ngô và sữa khiến ai nếm thử cũng sẽ ghiền.', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55038, 'Chân Gà Tàu Xì', 'Chân gà tàu xì dùng ăn vặt, ăn bánh mì, chan cơm, ăn bún đều được.', 6, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55039, 'Rau Củ Ngâm Chua Ngọt', 'Rau củ ngâm chua ngọt với cách làm của Cooky giòn ngon, chóng ngán và sẽ là món ăn kích thích vị giác trong những bữa ăn gia đình vào ngày Tết. Hãy cùng Cooky thực hiện món rau củ ngâm chua ngọt để Tết trong vị hơn nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55040, 'Bột Chiên Hàu Sữa', 'Bột chiên là một trong những món ăn đường phố nổi tiếng tại Sài Gòn. Món ăn này sẽ càng tuyệt vời hơn nếu bạn kết hợp sự giòn giòn của bột chiên cùng những con hàu sữa múp míp, béo ngậy. Nếu bạn là fan hâm mộ của bột chiên, chắc chắn bạn sẽ không thể bỏ qua phiên bản nâng cấp "Bột chiên hàu sữa" này. Còn chờ gì mà không vào bếp cùng Cooky để làm cho mình một dĩa bột chiên hàu sữa thật to nào.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55041, 'Mứt Dừa Viên Ba Màu', 'Mứt dừa là một trong những món ăn truyền thống quen thuộc trong ngày Tết cổ truyền tại Việt Nam. Mứt dừa viên là một phiên bản được biến tấu từ mứt dừa sợi, tuy vậy nó vẫn giữ được hương vị đặc trưng của mứt dừa. Cùng với nhiều màu sắc rực rỡ, mứt dừa viên ba màu sẽ tạo sự thích thú cho những vị khách đến chúc Tết. Năm mới đến rồi, còn chờ gì mà không cùng Cooky thực hiện món mứt dừa viên ba màu để đón Tết nào.', 6, '2022-03-22 00:00:00', 270, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55042, 'Sushi Bento', 'Sushi Bento vừa dễ thương vừa đẹp mắt, với những nguyên liệu đầy dinh dưỡng từ Nhật Bản hứa hẹn sẽ mang đến cho bạn bữa ăn ngon miệng. Hãy cùng JFoodo trổ tài thực hiện món Sushi Bento để mời cả gia đình bạn cùng thưởng thức nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55043, 'Bánh Chuối Chiên', 'Bánh chuối phồng to, giòn tan lại không ngấy dầu, ai ăn cũng thích mê!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55053, 'Siro Atiso Đỏ', 'Cây Atiso đỏ hay còn gọi cây hoa lac thần hay bụp giấm là một loại cây khá quen thuộc với người Nam Trung Bộ và cưe tầm độ tháng 10 tháng 11 là cây lại đơm hoa kết trái. Cùng xem cách Ric làm Siro từ loài cây này bên dưới nha.', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55056, 'Trứng Cút Nướng Rong Biển', 'Với cách làm cực kỳ đơn giản và không mất nhiều thời gian, bạn có thể hoàn thành món trứng cút nướng nóng hổi, thơm phức. Trứng cút ngon béo cùng rong biển mằn mặn đặc trưng ăn một lần là nhớ mãi. Bạn ăn kèm với tương ớt và tương cà để thêm phần đậm đà.', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55057, 'Muối Dưa Chuột', 'Muối dưa chuột (muối dưa leo) là món ngâm đặc trưng của nền ẩm thực Việt Nam. Muối dưa chuột có vị chua chua, ăn vào giòn giòn là món ăn chống ngán hiệu quả trong những dịp Tết đầy các món thịt mỡ, nhiều chất đạm. Bạn hãy tham khảo ngay công thức dưới đây, đảm bảo sẽ có ngay lọ dưa muối ngon tuyệt!', 4, '2022-03-22 00:00:00', 105, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55058, 'Trứng Hấp Kiểu Nhật Chawanmushi', 'Trứng hấp kiểu Nhật Chawanmushi là món ăn phổ biến và được rất nhiều người yêu thích ở các gia đình tại xứ sở hoa anh đào. Sự hòa quyện giữa trứng gà cùng với nước dùng Dashi ngọt thanh đặc trưng của Nhật tạo nên một hương vị tuyệt vời. Đây là món trứng hấp không chỉ dễ làm mà còn rất bổ dưỡng và ngon miệng. Nếu bạn là người yêu thích ẩm thực Nhật Bản thì chắc chắn không nên bỏ qua công thức để thực hiện món này.<br><br>', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55060, 'Dưa Hành Tím', 'Dưa hành tím chua chua, giòn giòn là món ăn đặc trưng và phổ biến trong dịp Tết.  Dưa hành có màu tím trông vừa đẹp mắt, khi ăn lại có vị chua chua, ngọt ngọt, chút cay của ớt và độ giòn của hành. Hành tím chua ngọt có thể ăn kèm với nhiều món, làm tăng thêm hương vị của ngày Tết. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55061, 'Lẩu Cua Đồng', 'Lẩu cua đồng món ăn mang hương vị đồng quê, kết hợp từ nước hầm xương ngọt thanh, gói lẩu riêu cua Barona tiện dụng và hơi chua nhẹ từ cà chua sẽ làm cho món ăn này trở nên vô cùng ngon miệng. Với những nguyên liệu dễ tìm mua, chúng ta có thể tự tay nấu một nồi lẩu cua đồng cực ngon ngay tại nhà một cách nhanh chóng rồi.', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55063, 'Snack Hành Tây', 'Snack hành tây, món ăn vặt được nhiều người ưa chuộng vì vị thơm ngon của nó. Vị thơm, ngọt của hành tây được áo bên ngoài lớp bột được chiên vàng giòn óng ánh, chấm với sốt béo ngậy. Cắn một miếng nhưng tan ngay trong miệng. Cuối tuần, các mẹ hãy làm ngay món này cho cả gia đình nhăm nhi thư giãn nhé. <br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55067, 'Bánh Flan Táo Washington', 'Bánh Flan táo Washington với phần bánh mềm mịn từ hỗn hợp trứng sữa kết hợp thêm vị chua ngọt của mứt táo. Ngoài hương vị thơm ngon, bánh Flan táo càng trở nên bắt mắt hơn nhờ lớp caramel óng ánh. Để biết rõ thêm cách làm chi tiết của món bánh Flan táo Washington, mời bạn cùng U.S.D.A vào bếp trổ tài nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55074, 'Tôm Chiên Xù', 'Tôm chiên xù là món ăn ngon, dễ làm. Chúng ta cùng bắt tay trổ tài nhé!', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55095, 'Đậu Phộng Da Cá', 'Đậu phộng là món ăn quen thuộc với tất cả mọi người. Trong các món ăn vặt được làm từ đậu phộng, chắc hẳn nhiều người đã từng thưởng thức món đậu phộng da cá. Những đậu phộng được áo qua lớp bột mì khi chiên vàng giòn, ăn vào giòn rụm, vị béo béo ngon ngất ngây. Bạn hãy lưu lại công thức bên dưới để làm món đậu phộng da cá này nhé.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55096, 'Rươi Kho', 'Rươi kho là món ăn dân dã, thơm ngon, hấp dẫn không phải ai cũng có cơ hội thưởng thức. Nồi rươi kho thơm nức mũi, thơm mùi của vỏ quýt, của lá gừng, thịt rươi ngọt và cực mềm. Gấp một miếng nóng hổi ăn với cơm trắng thì còn gì bằng. Cách làm món rươi kho còn cần những bí quyết riêng để tạo nên hương vị đặc trưng hấp dẫn, bạn còn chần chờ gì mà không tham khảo ngay cách làm bên dưới nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55097, 'Giò Thủ Chay', 'Giò thủ chay là món ăn ngon, mà những người kiêng ăn thịt có thể ăn được. Bên cạnh đó, cách làm giò thủ chay đơn giản từ những nguyên liệu như nấm, bột rau câu, bạn sẽ dễ dàng làm được món giò thủ này. Giò thủ chay ăn vào dai dai, giòn sần sật, đậm đà hương vị. Bạn hãy tham khảo ngay cách làm món ăn chay này dưới đây nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55098, 'Thịt Nướng Nhật Bản Kushiyaki', 'Thịt nướng Nhật Bản Kushiyaki hay còn được biết đến với tên gọi khác là xiên nướng Nhật Bản, là món ăn đặc sắc không thể thiếu trong văn hóa ẩm thực xứ sở hoa Anh Đào. Xiên thịt nướng cùng với rau củ có vị ngọt thơm tự nhiên, đậm đà hương vị thịt, mang lại cảm giác đặc biệt cho người thưởng thức. Bạn hãy tham khảo ngay công thức bên dưới để làm ngay những xiên nướng chuẩn vị Nhật Bản chiêu đãi cả gia đình vào những ngày cuối tuần hay các dịp đặc biệt nhé.<br>', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55099, 'Lẩu Tôm Càng', 'Những món ăn chế biến từ tôm càng xanh có sức hấp dẫn đặc biệt, nhất là món lẩu. Thay vì phải ra ngoài hàng để thưởng thức, giờ đây bạn hoàn toàn có thể chế biến Lẩu Tôm Càng cực thơm ngon ngay tại nhà. Công thức đơn giản, nguyên liệu dễ tìm đến từ thương hiệu Hải sản Hoàng Kim. Chỉ cần làm theo các bước dưới đây, bạn sẽ chinh phục vị giác của mọi người ngay trong lần thử đầu tiên nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55100, 'Bánh Bò Cốt Dừa Lá Dứa', 'Bánh bò là một trong những món bánh truyền thống của Việt Nam. Có rất nhiều biến thể từ bánh bò truyền thống, và bánh bò cốt dừa lá dứa là một trong số đó. Màu xanh bắt mắt từ lá dứa và mùi thơm beo béo từ nước cốt dừa, khó có ai có thể cầm lòng được. Hãy cùng Cooky vào bếp để thực hiện món bánh này nào.', 6, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55101, 'Gỏi Vỏ Dưa Hấu Tôm Thịt', 'Dưa hấu là một trong những loại trái cây đặc trưng cho dịp Tết Nguyên Đán ở các nước Đông Nam Á. Dưa hấu không chỉ có tác dụng giải khát, mà vỏ dưa hấu giòn mát còn có thể được dùng làm gỏi. Gỏi vỏ dưa tôm thịt tươi mát, chua ngọt sẽ là một món khai vị tuyệt vời cho những ngày đầu năm mới. Hãy cùng Cooky trổ tài làm gỏi cho dịp tết sắp tới nào.', 6, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55102, 'Trứng hấp đậu hủ non thịt bằm', 'Trứng hấp đậu hủ non thịt bằm là một món ăn tuy đơn giản dễ làm nhưng lại rất ngon. Đậu hủ non có nhiều tác dụng như ngăn ngừa rụng tóc, giảm nguy cơ tiểu đường, ung thư. Đậu hủ hấp trứng mềm mịn hòa quyện cùng vị mặn mặn ngọt ngọt của nước tương, sẽ làm đắm say mọi đối tượng từ người lớn tới trẻ em. Hãy cùng Cooky vào bếp thực hiện món này thôi nào.', 6, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55103, 'Macaron Phô Mai', '<p>Macaron là một loại bánh ngọt nổi tiếng tại Pháp và nhiều nước trên thế giới. Tuy nó rất nổi tiếng ở Pháp nhưng hiện nay người ta vẫn chưa xác định được nguồn gốc của nó là Ý hay Pháp. Macaron thường được ăn kèm với ganache (hỗn hợp làm từ chocolate và cream) hoặc kem bơ. Nhưng hôm nay Cooky sẽ hướng dẫn bạn Macaron cùng nhân cream cheese chua chua ngọt ngọt không ngán. Còn chờ gì mà không cùng Cooky chinh phục thử thách này nào.</p>', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55106, 'Bánh Sắn Nướng', 'Bánh sắn nướng vỏ se se, mềm dẻo bên trong, lại có vị ngọt bùi của sắn kết hợp với dừa tươi, vừng thơm phức có sức lan tỏa, lôi cuốn mạnh mẽ chúng ta khi trời trở lạnh.', 4, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55120, 'Trứng cút sốt cà chua', 'Trứng cút sốt cà chua là một món ăn phổ biến trong bữa cơm của nhiều gia đình người Việt Nam. Vị chua chua ngọt ngọt từ sốt cà chua kết hợp cùng vị bùi bùi béo béo của trứng cút chiên, không chỉ cuốn hút trẻ em mà còn cả người lớn. Còn chờ gì mà không cùng Cooky vào bếp thực hiện món ăn này cho gia đình thân yêu của mình nào.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55124, 'Bắp Mỹ Hấp Sữa Tươi', 'Bắp là cách luộc bắp thông thường nhưng thay vì nước lọc thì thay bằng sữa tươi và thêm lá dứa, Hà đã làm nên “Cực phẩm” tại nhà cho Ba Mẹ và thằng em ... cả nhà đã có buổi tối ăn bắp vui vẻ ? ', 5, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55126, 'Sữa Gạo Lứt Mè Đen', 'Sữa gạo lứt mè đen là một thức uống vô cùng tốt cho sức khỏe nè. Hôm nay, Hà xin chia sẻ kinh nghiệm của mình. Cả nhà cùng tham khảo và làm hen ', 3, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55128, 'Chả Rươi', ' Rươi rất giàu dinh dưõng, chế biến được nhiều món ăn ngon. Chả rươi là một trong những món ăn ngon, đặc trưng của vùng Bắc Bộ. Từ màu vàng óng của chả rươi đến hương thơm không thể cưỡng lại được, vị ngon bùi của rươi hòa quyện với vị ngọt của thịt heo và trứng gà cộng với hương thơm của vỏ quýt, thì là và hành lá, khiến bất cứ ai đã một lần được thưởng thức đều không thể nào quên được.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55129, 'Giò Bì Cuộn', 'Giò bì cuộn, cùng với một chút biến tấu những nguyên liệu từ món giò chả truyền thống,được kết hợp thêm với đậu Hà Lan, cà rốt và nấm, tạo nên món giò bì có màu sắc bắt mắt và một hương vị khác biệt. Đây sẽ là một món ăn khó có thể cưỡng lại, đặc biệt vào dịp Tết thì một dĩa giò bì cuộn càng góp phần cho mâm cơm gia đình bạn thêm tròn vị và ấm cúng.<br>', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55130, 'Đậu Hũ Trứng Sốt Heo Cay', 'Đậu hũ trứng sốt heo cay là món ăn không quá cầu kỳ với những nguyên liệu dễ tìm, bạn có thể vào bếp thực hiện món ăn này để đổi vị cho bữa cơm gia đình. Món ăn với màu sắc bắt mắt với vị cay nồng của sốt heo cay hòa quyện cùng vị béo đậu hũ trứng, sẽ khiến món ăn trở nên đặc biệt hơn và giúp cho thực đơn gia đình bạn phong phú hơn.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55131, 'Gỏi Ổi Tai Heo', 'Việt Nam là một trong những nước có món gỏi rất phong phú, các nguyên liệu được chế biến rất tươi ngon. Hôm nay, Cooky sẽ chia sẻ một món gỏi đặc sản nổi tiếng ở tỉnh Hà Nam - Gỏi ổi tai heo. Gỏi có vị giòn của ổi cùng với tai heo giòn dai, giòn sần sật hòa quyện cùng với nước mắm chưa ngọt mang lại cảm giác lạ miệng, tươi mới. Bạn hãy tham khảo ngay công thức bên dưới để làm món gỏi ổi tai heo cho cả gia đình cùng thưởng thức nhé!<br>', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55132, 'Đậu Hủ Non Sốt Tương', '<p>Đậu hủ non sốt tương là một món ăn đơn giản, thanh đạm, thích hợp cho mọi đối tượng kể cả người ăn chay. Đậu hủ non có nhiều tác dụng như giảm nguy cơ ung thư, tiểu đường, ngăn ngừa lão hóa,... lại mềm mịn dễ ăn, thích hợp cho nhiều đối tượng từ trẻ em tới phụ nữ. Hãy cùng Cooky vào bếp để thực hiện món đậu hủ non sốt tương này nào. </p>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55133, 'Kẹo Chuối Đen', 'Kẹo chuối đen là một trong những món kẹo được dùng để tiếp khách trong dịp Tết Nguyên Đán. Độ dẻo dẻo, dai dai, kết hợp cùng vị chua ngọt từ chuối, cộng thêm vị giòn giòn, bùi bùi, béo béo từ đậu phộng, mè rang, dùa khô đủ để tạo thành một món kẹo hoàn hảo để tiếp khách đầu năm mới. Còn chờ gì mà không cùng Cooky làm một mẻ để đón Tết cùng gia đình nào.', 10, '2022-03-22 00:00:00', 360, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55135, 'Kim Chi Dưa Leo', 'Kim chi một món ăn nổi tiếng quen thuộc của đất nước Hàn Quốc, có rất nhiều loại kim chi ngon khác nhau, và hôm nay Cooky sẽ gửi đến bạn một công thức kim chi rất dễ làm với gói xốt muối kim chi chuẩn vị Hàn Quốc đó là món Kim Chi Dưa Leo. Những miếng dưa leo được chế biến giữ được độ dai giòn sần sật đem muối để tạo nên hương vị chua cay đặt trưng chuẩn Hàn Quốc, hứa hẹn sẽ là một món kim chi không thể bỏ qua đặt biệt là trong các dịp lễ tết, đây sẽ là một món ăn kèm và giải ngán hiệu quả. Hãy cùng bỏ túi công thức Kim Chi Dưa Leo siêu hấp dẫn này nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55136, 'Muối Kim Chi Củ Cải', ' Kim chi củ cải là món ăn truyền thống của người Hàn Quốc. Không chỉ hấp dẫn ở hương vị cay nồng, chua thanh, giòn tan kích thích vị giác mà kim chi còn là món ăn mang đến nhiều giá trị sức khỏe cho người sử dụng. Bên cạnh đó, kim chi củ cải còn là món phụ dùng kèm với một số món chính như: thịt nướng, cơm, canh, mì… giúp hoàn chỉnh hương vị và khiến món ăn đặc sắc hơn rất nhiều.<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55137, 'Chả Lụa Bì Ớt Xiêm Xanh', 'Chả lụa bì ớt xiêm xanh được chế biến từ da heo giòn dai cùng với vị ngọt của giò sống và vị nồng cay của ớt xiêm xanh, thêm chút cay nhẹ của hạt tiêu giúp món chả lụa ớt xiêm xanh trở nên hấp dẫn hơn bất cứ món chả nào khác. Chả lụa ăn kèm với dưa chua, củ kiệu, dưa leo đều ngon hết sẩy. Miếng chả giòn ngọt ăn hoài không ngán, đặc biệt lai rai với bia thì Tết còn gì bằng. ', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55138, 'Xôi Đậu Xanh', 'Xôi đậu xanh đã trở thành món ăn quen thuộc của gia đình Việt Nam. Hạt nếp nở đều mềm dẻo, kết hợp với đậu xanh lòng vàng bùi bùi thêm chút vị béo của nước cốt dừa. Một đĩa xôi thơm ngon, dẻo bùi sẽ chiều lòng được tất cả các thành viên trong gia đình. ', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55139, 'Xôi Tam Sắc', 'Xôi tam sắc là món ăn đẹp mắt, hấp dẫn nhiều người khi thưởng thức. Chỉ với cách nấu xôi bình thường, biến tấu thêm chúng với nhiều màu sắc từ rau củ tự nhiên là bạn có ngay món xôi mới lạ rồi. Hạt nếp nở đều, dẻo mềm với nhiều hương vị, màu sắc khác nhau, hòa quyện với đậu xanh nghiền bùi bùi, béo ngậy, mê ngay từ miếng đầu tiên. <br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55140, 'Cơm Nguội Bọc Trứng Cút Chiên Xù', 'Cơm nguội bọc trứng cút chiên xù là món ăn biến tấu từ những nguyên liệu thường có sẵn trong căn bếp. Để không lãng phí phần cơm nguội thì trong công thức dưới đây, Cooky sẽ hướng dẫn bạn cùng với 1 chút khéo léo là bạn đã có thể mời cả nhà thưởng thức món cơm nguội bọc trứng cút chiên xù thơm lừng và giòn tan này rồi!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55144, 'Bánh Đùm Miền Tây', ' Bánh đùm với vỏ bánh mềm, dẻo, trong, làm tôn lên phần nhân có màu sắc đẹp mắt bên trong,  khi ăn sẽ cảm nhận được vị ngọt từ thịt và tôm, giòn sần sật của rau củ kết hợp với nhau ăn lạ miệng. Bạn có thể dùng kèm rau sống hoặc đồ chua khi ăn bánh đùm, sau đó chan thêm nước mắm chua chua cay cay khiến bạn sẽ mê mẫn ngay lần ăn đầu tiên đó.<div><br></div>', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55146, 'Cà Na Đập Muối Ớt', 'Cà na một loại quả dân dã quen thuộc với khá nhiều người nhưng không phải ai cũng biết cách tận dụng cà na để chế biến thành những món ăn ngon. Hôm nay Cooky sẽ chia sẽ đến các bạn một món ăn vặt không thể bỏ lỡ <strong>cà na đập muối ớt. </strong>Với vị cay cay mặn mặn từ muối ớt được kết hợp với một chút chua chua chát chát của cà na sẽ là một món mà bạn không thể dừng ăn. Nào hãy cùng bắt tay làm ngay món <strong>cà na đập muối ớt</strong> thôi!', 4, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55147, 'Chả Lụa Rim Mắm', 'Chả lụa rim mắm mặn mà từ vẻ ngoài lẫn hương vị, gắp miếng chả lụa thơm ngon thưởng thức với cơm nóng kèm dưa leo khiến món ăn càng trở nên dậy vị hơn. Cùng với vài thao tác nhanh gọn bạn sẽ không quá vất vả suy nghĩ món hay tốn nhiều thời gian chuẩn bị bữa cơm cho cả gia đình mình. Hãy cùng Cooky vào bếp thực hiện món chả lụa rim mắm hao cơm này nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55149, 'Lẩu Thái Cua Đồng', '<strong>Lẩu Thái Cua Đồng - </strong>món ăn vừa lạ vừa quen cho những dịp tụ họp bạn bè, gia đình.  Lẩu Thái với hương vị chua cay đặc trưng cùng độ đậm đà khó cưỡng khi dùng <strong>nước mắm Maggi, </strong>nay kết hợp với sự thơm ngon thân thuộc từ cua đồng hứa hẹn sẽ cho các bạn một món lẩu thơm ngon lạ miệng. Còn chờ gì nữa hãy lưu ngay công thức món <strong>Lẩu Thái Cua Đồng </strong>cho những dịp tụ họp gia đình, bạn bè sắp tới thôi nào!', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55150, 'Bánh Xu Xê', 'Bánh xu xê hay còn được biết đến là bánh phu thê, là món bánh truyền thống không thể thiếu trong những dịp cưới hỏi ở nước ta. Bánh xu xê có vị ngọt dịu, thơm mùi lá dứa. Bánh ăn dai dai ngoài vỏ, được trộn cùng với dừa sợi giòn giòn, nhân đậu xanh bên trong bùi béo,  hương vị lá dứa rất đặc trưng. Cooky sẽ chia sẻ công thức làm món bánh truyền thống này bên dưới, bạn hãy lưu lại nhé!', 5, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55151, 'Gỏi Khô Cá Lóc', 'Gỏi khô cá lóc là món ăn miệt vườn rất được ưa chuộng của nhiều người dân ở Nam Bộ. Gỏi cá lóc có vị thơm bùi của cá lóc khô, vị chua chua, giòn tan của xoài xanh cùng mùi hương dễ chịu của rau răm sẽ đem tới cho bạn một món ăn không chỉ ngon, bổ dưỡng mà còn khiến bạn không thể nào quên được hương vị dân dã này. ', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55153, 'Sườn Xào Chua Ngọt', '<p>Nếu muốn ăn thịt kho nhưng lại quá ngán với món kho, món luộc hay rang thì bạn hãy đổi khẩu vị với sườn xào chua ngọt. Chỉ cần ít thời gian, bạn sẽ có ngay một món ăn vừa hấp dẫn, vừa thơm ngon lại rất tốn cơm nữa đấy.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55161, 'Canh Bắp Cải Cuộn Thịt', 'Canh bắp cải cuộn thịt là một món ăn nhẹ nhàng, hay xuất hiện trong những bữa cơm gia đình. Màu sắc bắt mắt, dễ thu hút trẻ em, nước dùng thanh ngọt nấu từ hành và cà rốt, đó là những lý do bạn không thể bỏ qua cho món canh này. Nếu bạn đã ngán những món canh nhiều dầu mỡ, sao bạn không thử canh bắp cải cuộn thịt. Còn chờ gì mà không cùng Cooky vào bếp nào.', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55162, 'Mứt Bí Đao', 'Mứt là một trong những món đặc trưng cho ngày Tết cổ truyền của người Việt Nam. Ở Việt Nam, người ta có thể làm mứt gần như từ mọi loại trái như: dừa, chôm chôm, chùm ruột,... Nếu bạn muốn có một loại mứt ngọt ngào, nhưng lại thanh mát, bạn sẽ không thể bỏ qua mứt bí đao. Tuy cách làm hơi dài nhưng bạn sẽ có một món mứt khá mới lạ để thưởng thức trong những ngày tết sắp tới. Cùng Cooky mau mau vào bếp nào.', 6, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55163, 'Heo Quay Kho Cải Chua', 'Heo quay kho cải chua là một trong những món ăn phổ biến trong những bữa cơm gia đình của người Việt. Heo quay béo giòn, kết hợp cùng dưa cải chua, là đã có một món ăn đầy đủ hương vị mặn, ngọt, chua, cay, béo, siêu bắt cơm. Còn chờ gì mà không cùng Cooky lăn vào bếp và chuẩn bị món heo quay kho cải chua cho bữa cơm cuối tuần nào.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55169, 'Rau Củ Xào Hạnh Nhân', '<p>Món ăn nhẹ đầy đủ chất dinh dưỡng từ các loại rau củ, bắt mắt và hấp dẫn</p>', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55170, 'Canh Măng Lưỡi Lợn', 'Canh Măng Lưỡi Lợn là món ăn bổ dưỡng cho sức khoẻ, với cách chế biến tuy hơi mất thời gian nhưng thành phẩm đem lại thì lại hấp dẫn mọi giác quan.', 3, '2022-03-22 00:00:00', 720, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55171, 'Trà Chanh Hồng Kông', 'Trà chanh Hông Kông, món nước đang được nhiều giới trẻ " săn đón". Vị chua dịu, thơm đặc trưng của chanh vàng, kết hợp sự chát nhẹ của trà đen và vị ngọt thanh từ mật ong và đường phèn, giòn sần sựt vui miệng của trân châu trắng. Tất cả tạo nên một món nước độc đáo, giải khát cho những ngày nóng oi.', 3, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55175, 'Bánh Bạch Tuộc Takoyaki', 'Bánh bạch tuộc Takoyaki là món ăn vặt truyền thống của Nhật Bản. Đây là món bánh được làm từ bột mì có nhân bên trong là bạch tuộc, gừng hồng, hành lá được nướng lên có màu vàng hấp dẫn ăn kèm với sốt Takoyaki mặn ngọt, sốt Mayonnaise béo ngậy, phủ lên trên là lớp cá bào và rong biển khô. Bạn còn chần chờ gì mà không lưu lại ngay công thức dưới để làm món Takoyaki cho gia đình cùng thưởng thức.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55176, 'Bò Xốt Vang', 'Bò xốt vang là món ăn phổ biến và được nhiều người yêu thích. Thịt bò hầm chín, mềm ngọt tự nhiên, ăn vào bạn sẽ cảm nhận được vị ngọt từng thớ, phần gân thì vừa dai vừa giòn rụm, nước sốt đậm đà, hơi sánh nâu đỏ và sền sệt, thoang thoảng hương vang vừa thơm mùi đặc trưng của thịt bò. Tuy vậy, cách làm bò xốt vang thông thường sẽ rất phức tạp và cầu kỳ nhưng gói xốt Gia vị hoàn chỉnh Barona - Bò kho sẽ giúp bạn đơn giản hóa vấn đề đó, bạn hãy tham khảo ngay công thức bên dưới đây nhé.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55177, 'Phở Gà', 'Phở món ăn truyền thống chứa đựng nhiều tinh túy của ẩm thực Việt Nam. Khởi động buổi sáng bằng một tô phở gà nghi ngút khói, với mùi thơm thanh dịu quen thuộc đặc trưng của nước dùng phở, cùng độ đậm đà ngon ngọt từ xương gà, những miếng thịt gà dai ngọt trong từng thớ thịt chấm với muối tiêu chanh thì còn còn gì bằng nữa. Hôm nay Cooky sẽ gửi đến bạn cách nấu phở gà chuẩn vị truyền thống. Hãy cùng thực hiện món Phở Gà thơm ngon cho bữa điểm tâm sáng gửi đến gia đình, người thân thêm phần ấm cúng thôi nào.', 2, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55179, 'Bánh Chuối Chiên Không Dầu Mỡ', 'Bánh chuối thường tốn rất nhiều dầu mỡ, nhưng hôm nay mình làm bánh chuối không cần dầu mỡ.', 3, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55180, 'Chả Tôm Chiên Xù', 'Chả tôm là một món ăn ngon, dễ ăn, dễ chế biến, nhiều dinh dưỡng và không ngán cho gia đình, đặc  biệt là gia đình có trẻ nhỏ. Hôm nay Cooky sẽ chia sẻ đến bạn một công thức chế biến chả tôm, món Chả Tôm Chiên Xù. Với lớp vỏ ngoài giòn rụm cùng nhân chả tôm dai ngọt tự nhiên từ thịt tôm rất vừa vừa ăn, hứa hẹn sẽ cho bữa cơm gia đình thêm phần ngon miệng, đặc biệt bé sẽ thích mê món này cho xem. Hãy cùng vào bếp trổ tài nội trợ cho bữa cơm gia đình thêm đầm ấm nhé.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55181, 'Chả Cá Thái Lan', 'Chả cá món ăn rất quen thuộc với mọi người và chả cá cũng có rất nhiều cách chế biến thơm ngon khác nhau. Hôm nay Cooky sẽ gửi đến bạn một cách chế biến chả cá mới đến từ Thái. Món Chả Cá Thái Lan kết hợp giữa cảm giác dai ngọt trong chả cá cùng mùi thơm của lá chanh thái, màu sắc bắt mắt từ cà ri, chấm cùng sốt chấm chua cay kiểu thái. Hứa hẹn sẽ là một món ngon lạ miệng trong bữa cơm gia đình và cũng là món lai rai tuyệt vời cùng bạn bè. Nào hãy thực hiện ngay món Chả Cá Thái Lan mới lạ mà dễ làm này thôi nào.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55182, 'Đậu Phộng Rang Sả Ớt', 'Đậu phộng rang một món nhắm lai rai ưa thích của nhiều người. Và hôm nay Cooky sẽ hướng dẫn bạn làm mới món đậu phộng rang thông thường. đậu phộng rang sả ớt kiểu thái, Vời từng hạt đậu phộng rang vàng giòn bùi bùi cùng mùi thơm của riềng, sả, lá chanh thái, áo một lớp đường thốt nốt và muối ngọt ngọt mặn mặn thêm một chút vị cay của ớt khô dùng làm đồ nhắm hay ăn vặt thì phải nói là tuyệt vời. Nào cùng vào bếp cùng Cooky để có món nhắm tuyệt vời chỉ muốn nhai hoài không muốn ngừng này thôi!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55187, 'Chè Đậu Xanh Đánh Nước Cốt Dừa', 'Chè đậu xanh đánh nước cốt dừa thơm ngon, bùi ngọt, giúp bạn giải nhiệt trong những ngày trời oi bức. Vị đậu xanh bùi bùi được đánh nhuyễn dễ ăn, hòa quyện với hương vị thơm ngon của nước cốt dừa béo ngậy, ăn kèm với hạt đậu phộng rang và dừa nạo sợi giòn giòn, chắc chắn sẽ khiến bạn càng ăn càng nghiện. Bạn hãy lưu lại ngay công thức bên dưới đây nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55189, 'Gân Bò Ngâm Xoài Sả Tắc', 'Gân bò ngâm xoài sả tắc là món ngon được chế biến từ gân bò, trộn cùng với xoài, tắc chua thanh. Với vị dai giòn sần sật của gân bò, hoa quyện cùng với vị chua thanh của xoài chắc chắn sẽ đem cho bạn một trải nghiệm thú vị, ngon đến lạ lùng. Vào dịp Tết đầy những món ăn dầu mỡ thì gân bò ngâm xoài sả tắc sẽ là món ăn giúp chống ngán cho bạn đấy, bạn hãy lưu lại công thức bên dưới đây nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55190, 'Lẩu Cà Chua Bò Mỹ', 'Cách làm Lẩu cà chua thơm ngon, hấp dẫn, đậm đà hương vị. Nước lẩu có vị ngọt tự nhiên của nước xương bò hầm, vị chua dịu của cà chua cùng với hương thơm của các loại thảo mộc, rất thích hợp cho cả gia đình cùng nhau thưởng thức vào những ngày trời se lạnh. Bạn hãy tham khảo ngay cách làm bên dưới, vào bếp và trổ tài ngay nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55196, 'Bún Cá', 'Cuối tuần ngán cơm, mình quyết định chế biến món bún cá Hà Nội đãi cả nhà. Bún cá nhìn vậy nhưng cách nấu vô cùng đơn giản thôi.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55197, 'Lòng Xào Nghệ', '<p><span style="font-size:13px">Lòng heo xào nghệ là món xào quen thuộc trong bữa ăn của người miền Trung với hương vị độc đáo và còn là vị thuốc để chữa đau bao tử, tăng sức đề kháng cho cơ thể. Từ này, bạn có thể bổ sung vào thực đơn bữa ăn của gia đình với món lòng xào nghệ đưa cơm với cách chế biến cực kì đơn giản như công thức bên dưới nhé. Hãy cùng Cooky vào bếp để thực hiện ngay món ăn này ngay thôi!</span></p>', 2, '2022-03-22 00:00:00', 85, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55198, 'Khô Gà Lá Chanh', 'Với những tín đồ yêu thích ăn vặt, chắc hẳn khô gà lá chanh là món ăn quá đỗi quen thuộc. Miếng thịt khô gà thấm gia vị có chút mặn quyện cùng vị cay nồng của ớt và hương thơm của lá chanh kích thích vị giác khiến nhiều người mê mẩn. Tết này hãy cùng Cooky vào bếp và thực hiện món Khô gà lá chanh hấp dẫn này để nhâm nhi cùng gia đình và bạn bè trong dịp năm mới nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55199, 'Cơm Chiên Trứng Cuộn Lốc Xoáy', '<span style="font-size: 13px;" data-mce-style="font-size: 13px;">Cơm chiên trứng lốc xoáy là món ăn có nguồn gốc từ Hàn Quốc và đang được nhiều bạn trẻ Việt Nam thích thú học cách làm. Công thức và các chế biến vô cùng nhanh chóng, đơn giản, tuy nhiên, đòi hỏi một tí sự khéo tay của người thực hiện. Món ngon này vừa bắt mắt lại vô cùng tiết kiệm cho cả gia đình, cùng vào bếp và thực hiện cùng Cooky bạn nhé!</span><br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55200, 'Sườn Chua Ngọt', 'Món sườn chua ngọt ăn kèm cơm nóng đúng là đỉnh của đỉnh luôn đó. Cách làm thì cũng cực kì đơn giản nha.', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55202, 'Cá Trê Chiên Giòn Mắm Xoài', 'Đối với những tín đồ món Thái chắc hẳn ai cũng biết đến món gỏi nổi tiếng "gỏi xoài cá trê" của Thái Lan nay đã có mặt tại Cooky với cách chế biến món này chuẩn như nhà hàng, Cá Trê Chiên Giòn Mắm Xoài, Bánh cá trê được chiên vàng ươm giòn rụm tạo hình bắt mắt ăn kèm với mắm xoài chua chua, cay cay cùng một chút thơm bùi của đậu phộng và các loại rau mùi. Sẽ làm bạn muốn ăn thử ngay khi nhìn thấy, hãy cùng thực hiện món ăn không thể bỏ qua đặc biệt đối với những tín đồ mê món Thái này nhé!', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55203, 'Cơm Chiên Mắm Ruốc', 'Cơm chiên là món ăn quen thuộc và ưa thích của rất nhiều người. Hôm nay Cooky sẽ gửi đến bạn một công thức cơm chiên mới đến từ Thái Lan, món Cơm Chiên Mắm Ruốc, với mùi thơm và màu sắc đặc trưng của mắm ruốc áo đều quanh từng hạt cơm, nhưng không hề lo bị mặn khi ăn kém với các nguyên liệu khác, xoài xanh, hành tím, trứng sợi, dưa leo tôm khô, lạp xưởng cùng một chút cay cay của ớt, một chút chua chua khi vắt thêm chanh tất cả các nguyên liệu sẽ hòa quyện trong miệng bạn hương vị tạo nên món Cơm Chiên Mắm Ruốc nổi tiếng của đất nước Thái Lan. ', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55204, 'Gà Xào Nghệ Cà Ri', 'Thịt lòng gà xào thấm vị vừa phải, thơm mùi nghệ cà ri, rất hao cơm hoặc chấm kèm bánh mì cũng rất ngon.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55209, 'Trà Dâu Ngâm', 'Dâu tây là một trong những loại trái cây được ưa thích của giới trẻ. Nếu bạn có quá nhiều dâu mà không biết làm gì, để lâu lại sợ hư, vậy sao bạn không cùng Cooky làm trà dâu ngâm. Nhanh chóng, tiện lợi, dễ bảo quản, tính giải khát cực cao, chừng đó lý do là đủ để bạn bắt tay vào thực hiện một ly trà dâu ngon tuyệt vời cho bản thân mình rồi.', 1, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55210, 'Trà Ô Long Dâu', 'Trà oolong có chứa nhiều chất khoáng, vitamin, chất chống lão hóa, còn dâu tây là nguồn vitamin C tuyệt vời. Nếu kết hợp trà oolong và dâu ta có được một ly trà oolong dâu giàu dinh dưỡng, lại thanh mát, có tác dụng giải nhiệt cho những ngày hè nực nội. Còn chờ gì mà không cùng Cooky vào bếp và pha cho mình một ly trà oolong dâu chua ngọt mát lạnh tuyệt vời nào. ', 1, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55211, 'Lòng Heo Xào Cải Chua', 'Dưa cải chua là một trong những món muối chua đặc trưng của con người Việt Nam. Và từ dưa cải chua, người ta có thể chế biến ra được rất nhiều món ăn hấp dẫn bắt cơm, lòng heo xào cải chua là một trong số đó. Miếng lòng bùi bùi, sần sật, ăn cùng miếng cải chua nhè nhẹ, được chấm ngập trong chén nước mắm ớt, vậy là đủ để đánh gục bất cứ một người nào có tâm hồn ẩm thực phong phú. Còn chờ gì mà không vào bếp cùng Cooky để thực hiện món ăn dân dã này nào.', 4, '2022-03-22 00:00:00', 75, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55212, 'Ngô Chiên Bơ', 'Món ngô chiên có mùi thơm hấp dẫn, màu vàng đẹp mắt sẽ là món ăn vặt được yêu thích.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55214, 'Chả Bì', 'Chả bì là món ăn chơi hấp dẫn có nguồn gốc từ thủ đô Hà Nội. Cuối năm đến kèm theo những bữa tiệc thì món chả bì này chắc hẳn sẽ là một gợi ý góp phần phong phú bàn tiệc. Ngoài ra còn rất hợp cho cánh mày râu nhâm nhi làm mồi cùng với bia nữa. Nếu bạn đang băn khoăn không biết nên lựa chọn món ăn mới lạ nào để chiêu đãi gia đình, thì đừng bỏ lỡ cách làm chả bì siêu đơn giản cho thực đơn ngày Tết mà Cooky gợi ý ngay sau đây nhé!  <br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55215, 'Mứt Củ Sen', 'Những món ăn làm từ củ sen luôn mang đến hương vị độc đáo hấp dẫn. Trong đó, Mứt Củ Sen là một món ăn rất được ưa chuộng trong dịp Tết. Hãy theo chân Cooky vào bếp và thực hiện món mứt củ sen tươi cho ngày Tết thêm vui vẻ, đầm ấm nhé!<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55216, 'Gỏi Đu Đủ Tôm Tai Heo', 'Tết là lúc đoàn tụ và được ăn những bữa cơm ngon từ gia đình, và trong số vô vàn các món ngon ngày Tết thì gỏi là món ăn không thể không nhắc đến. Trong số các món gỏi ngon giải ngán ngày Tết thì Gỏi đu đủ tôm tai heo là món ăn vừa đơn giản lại vừa hấp dẫn cho cả nhà. Miếng tai heo bóp gỏi với đu đủ xanh giòn giòn, ăn kèm nước mắm tỏi ớt ngon đúng điệu, mới nghe đã muốn…chảy nước miếng! Vậy thì nhanh tay vào bếp chế biến ngay món Gỏi đu đủ tôm tai heo hấp dẩn này ngay thôi!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55217, 'Chè Bơ Viên Nước Cốt Dừa', 'Viên bơ mềm dẻo quyện với nước cốt dừa béo ngậy tạo nên một trải nghiệm mới lạ với món bơ quen thuộc. Nếu như bạn muốn một món chè độc đáo mà không mất quá nhiều công sức thực hiện thì món chè bơ viên nước cốt dừa này là một gợi ý hợp lý. Cùng Cooky thực hiện nha!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55218, 'Chân Gà Lắc Thập Cẩm Trứng Non', 'Chân gà lắc thập cẩm trứng non - món ăn vặt mới độc đáo và mới lạ dành cho các tín đồ ẩm thực. Ngoài chân gà giòn dai sần sật và trứng non béo ngậy, món ăn còn có thêm các topping hấp dẫn khác như tai heo, mề gà, sụn gà. Tất cả cùng hòa quyện với nhau trong hương vị sốt sa tế cay nồng cực kích thích, đảm bảo sẽ khiến bạn và gia đình ăn hoài không thôi!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55219, 'Khô Bò Miếng', 'Khô bò được coi là món ăn vặt được ưa thích của nhiều người. Chỉ cần xé miếng bò khô dai dai, thêm chút nước cốt chanh là bạn đã có thể thưởng thức đầy đủ hương vị vừa cay vừa ngọt của món ăn vặt này.  Bò khô không chỉ hấp dẫn, lạ miệng mà nó còn mang lại nhiều giá trị dinh dưỡng. ', 4, '2022-03-22 00:00:00', 180, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55222, 'Trà Lipton Cam Thảo', 'Trà Lipton cam thảo là một trong những biến tấu đặc biệt từ trà Lipton truyền thống, được kết hợp giữa các nguyên liệu tự nhiên có hương thơm thanh mát và nhẹ nhàng. Với cách làm đơn giản sẽ mang đến cho bạn thức uống tốt cho sức khỏe, thích hợp thưởng thức vào những ngày oi bức hay muốn thanh lọc cơ thể. Để hiểu rõ hơn về hương vị của món trà lipton cam thảo này, hãy cùng Cooky tham khảo thêm phần công thức dưới đây nhé!', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55225, 'Gỏi Ốc Đu Đủ', 'Gỏi ốc đu đủ là sự kết hợp giữa ốc tỏi, sứa tươi, đu đủ và các nguyên liệu khác hòa hợp vào nhau tạo nên một món gỏi trên cả tuyệt vời. Món ăn này vừa thơm ngon lạ miệng vừa đảm bảo dinh dưỡng, sẽ khiến cả gia đình bạn thích thú khi thưởng thức cùng bánh phồng. Hãy cùng Cooky học cách làm gỏi ốc đu đủ ngay nhé, đảm bảo gia đình bạn sẽ thèm ăn nữa đấy.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55233, 'Cá Hồi Sốt Bơ Tỏi', 'Cá hồi không chỉ bổ sung axit béo thiết yếu mang lại sức khỏe cho tim, da và tóc còn tốt cho não. Bởi thế cá hồi là lựa chọn hàng đầu bồi dưỡng sức khỏe. Vị cá hồi béo kết hợp với nước sốt bơ tỏi thơm lừng.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55234, 'Bánh Tét/ Bánh Chưng Chiên', 'Vào ngày Tết, ăn nhiều bánh chưng bánh tét quá ngán, hay nhà còn nhiều 2 loại bánh này mà không biết làm gì thì mọi người triển ngay món Bánh chưng/ Bánh tét chiên nha. Miếng bánh chưng/ bánh tét giòn rụm ăn kèm củ kiệu hay dưa muối thì ngon phải biết.', 4, '2022-03-22 00:00:00', 10, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55236, 'Mứt Chùm Ruột Ngào Ớt Cay', 'Chùm Ruột loại quả thân thuộc với tuổi thơ rất nhiều người, cũng là một loại quả để ăn sống hoặc làm nhiều món ăn khác nhau. Hôm nay nhân dịp sắp tết Cooky sẽ chia sẻ với bạn cách làm món mứt chùm ruột ngào ớt một món ăn ưa thích của nhiều người trong dịp tết Vị chua thanh từ trái chùm ruột được ngào dẻo ngọt cùng một chút cay cay Màu đỏ mọng tượng trưng cho sự may mắn rất bắt mắt và phù hợp trong dịp năm mới. Hãy cùng ôn lại kỉ niệm tết xưa với món Mứt Chùm Ruột Ngào Ớt Cay này từ Cooky nhé.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55237, 'Cá Kèo Chiên Giòn Chấm Mắm Me', 'Cá kèo một thực phẩm quen thuộc miền sông nước, gợi về cho ta những món ăn đơn giản mộc mạc đậm chất quê. Và Cooky xin gửi đến bạn một món ăn cũng đơn giản mộc mạc từ cá kèo, món Cá Kèo Chiên Giòn chấm mắm me. Những con cá kèo được chiên vàng giòn phần thịt dai ngọt chấm cùng với công thức mắm me của Cooky chua chua cay, ăn kèm vài cọng rau răm nữa phải nói là số dách. Hãy cùng làm món Cá Kèo Chiên Giòn chấm cùng công thức mắm me đến từ Cooky để cùng bạn bè chiến hữu lai rai thôi nào.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55238, 'Bánh Khoai Lang', 'Bánh khoai lang là món ăn vặt dễ làm và dễ ăn trong những ngày đông se lạnh.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55239, 'Nước Sâm Hoa Đậu Biếc', 'Nước sâm hoa đậu biếc là một phiên bản cải tiến của nước sâm truyền thống của người Sài Gòn, được kết hợp giữa mùi thơm dịu, ngọt thanh của nước sâm và màu xanh tím đẹp mắt từ hoa đậu biếc. Còn gì tuyệt vời bằng việc sau khi về nhà sau một ngày nóng nực được thưởng thức những ngụm nước sâm hoa đậu biếc thanh mát. Vậy hãy cùng Cooky nấu một nồi nước sâm thật to nào.', 8, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55243, 'Mứt Me', 'Mứt me là món ăn khó có thể thiếu được trong những ngày Tết, vị chua thanh của món mứt này sẽ giúp vị giác mỗi người được kích thích hơn. Với cách hướng dẫn chi tiết của Cooky dưới dây, sẽ cho bạn thành phẩm mứt me đảm bảo vệ sinh mà lại còn chất lượng, hấp dẫn cả nhà nữa đấy nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55247, 'Tai Heo Cuộn Mộc Nhĩ', 'Tai Heo Cuộn Mộc Nhĩ là một món ăn lạ miệng có thể thay thế món giò thủ trong dịp tết mà không mất quá nhiều công chế biến. Với tai heo dai giòn sần sật cuộn xen kẽ với lớp mộc nhĩ (nấm mèo) rất bắt mắt khi bày ra đĩa đãi khách. Món này rất thích hợp để lai rai cùng bạn bè, người thân trong dịp đầu năm cũng như một món ăn lạ miệng không ngán trong bữa cơm gia đình. Hãy lưu ngay công thức món Tai Heo Cuộn Mộc nhĩ từ Cooky để trổ tài nội trợ với các vị khách quý trong ngày đầu năm cũng như cho bữa cơm gia đình thêm đầm ấm nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55264, 'Bánh Chưng Chiên Nước', 'Bánh chưng món bánh truyền thống không thể thiếu mỗi dịp tết đến. Nhưng không nhiều người dùng hết bánh chưng trong dịp tết. Biết được điều đó nên hôm nay Cooky sẽ gửi đến bạn một cách tận dụng bánh chưng sau dịp tết vô cùng đơn giản mà  lại bắt mắt dễ ăn không hề sử dụng dầu ăn nên không bị ngán. Với miếng bánh chưng vàng giòn chấm cùng tương ớt hay nước tương ăn kèm dưa chua, củ kiệu phải gọi là tuyệt vời. Hãy chuẩn bị một chiếc chảo chống dính và làm theo hướng dẫn của Cooky là bạn đã có thể hoàn thành món này rồi.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55269, 'Chả Lá Lốt', 'Chả lá lốt là món ăn ngon, truyền thống cuả người Việt với cách làm đơn giản. Có nhiều cách gói chả lá lốt, nhưng hôm nay mình sẽ gói cách đơn giản nhất.', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55274, 'Ba Chỉ Nướng Xốt Tương Cay', 'Cùng Knorr vào bếp học cách làm ba chỉ nướng xốt tương cay nồng, đậm vị Hàn Quốc sẽ đánh gục bạn ngay từ lần đầu thưởng thức. Với hương vị thơm nồng đầy lôi cuốn và nước xốt sánh quyện, đậm đà thấm đều trong từng thớ thịt, món ăn mang đến cho bạn một trải nghiệm ẩm thực đầy hấp dẫn từ xứ sở kim chi.<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55275, 'Tôm Sống Mắm Nhĩ Thái', 'Tôm sống mắm nhĩ Thái là một món mới rất đáng để các tín đồ món Thái thử qua. Mắm nhĩ được giã nhuyễn cùng tỏi, ớt, chanh, đường...thật vừa ăn. Tôm phải thật tươi sau đó thả vào tô mắm cho tự săn lại, vẫn có độ ngọt tự nhiên & vị giòn của tôm tươi sống, vị cay của nước mắm tạo nên một món nhậu cực kì bắt bia.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55276, 'Đậu Phộng Ngâm Nước Tương', 'Chỉ với vài nguyên liệu chính đơn giản như đậu phộng, nước tương, bạn sẽ chế biến được một món ăn rất đưa cơm lại cực kỳ dễ thực hiện. Món đậu phộng ngâm tưởng này có thể làm sẵn và bảo quản trong tủ lạnh để dùng dần. Cùng Cooky thực hiện nhé!', 5, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55277, 'Kẹo Nougat', 'Kẹo Nougat hay còn gọi là kẹo hạnh phúc là một loại kẹo có lịch sử lâu đời từ vùng Provencal của thị xã Montélimar thuộc về phía Đông Nam của nước Pháp. Có người nói ban đầu người ta cho hạt óc chó vào kẹo, nhưng từ thế kỷ 17, nhà khoa học Olivier đã phát triển và trồng được rất nhiều cây hạnh nhân ở gần thị xã Montélimar, từ đó người ta dùng hạnh nhân thay cho hạt óc chó. Ngày nay, ngoài các loại hạt, người ta còn thêm các loại trái cây khô để cho kẹo Nougat có màu sắc bắt mắt hơn. Nếu bạn muốn "hạnh phúc", còn chờ gì mà không vào bếp cùng Cooky nào.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55279, 'Miến Xào Chay', 'Miến xào chay với sợi miến dai dai cùng nước sốt ướp đậm đà, được xào cùng rau củ ngon ngọt. Đây sẽ là một món ăn ngon phù hợp dùng vào những bữa cần sự thanh đạm, không quá phức tạp để chế biến. Hãy cùng Cooky lưu lại cách hướng dẫn làm món miến xào chay để mời cả gia đình bạn thưởng thức nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55280, 'Hạt Điều Ngào Đường', 'Hạt điều là một trong những loại hạt có rất nhiều khoáng chất, vitamin và chất chống oxy hóa được trồng rất nhiều ở Việt Nam. Nếu bạn muốn có một món ăn vặt thú vị để nhâm nhi vào ngày Tết, sao không thử hạt điều ngào đường. Không khó như làm mứt, nhưng vị ngon cuốn hút không kém gì mứt. Hãy cùng Cooky vào bếp làm hạt điều mặn mặn ngọt ngọt để nhâm nhi cùng trà vào dịp tết nào.', 6, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55290, 'Lạp Xưởng Tôm Trứng Muối', 'Lạp xưởng một món ăn quen thuộc tiện lợi đặc biệt trong dịp tết đến. Biết được điều đó hôm nay Cooky sẽ mách bạn một công thức lạp xưởng mới cho dịp tết sắp tới lạ miệng mà vẫn giữ được hương vị thơm ngon đặc trưng của lạp xưởng. Món Lạp Xưởng Tôm Trứng Muối, kết hợp giữa hương vị đặc trưng cùng với vị mặn mặn bùi bùi của trứng muối cùng vị ngọt của thịt tôm hứa hẹn sẽ là món lạp xưởng hấp dẫn trong dịp tết sắp tới. Nào cùng Cooky thực hiện ngay món lạp xưởng hấp dẫn, dễ làm mà đảm bảo an toàn cho sức khỏe trong dịp tết sắp tới thôi!', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55294, 'Trứng Cút Kho Mặn', 'Trứng cút kho mặn là một món ăn bình dân, dễ làm và rất hao cơm ngày lạnh. Cả nhà bạn sẽ thích khi thay đổi khẩu vị bằng món này.', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55297, 'Cá Hồi Sốt Cam Kèm Măng Tây', 'Cá hồi là một trong những nguyên liệu quý giá cho căn bếp với giá trị dinh dưỡng cao, thơm ngon và cách chế biến phong phú. Một trong những món ăn quen thuộc mà đặc sắc với nguyên liệu này là cá hồi sốt cam. Cá hồi béo ngậy ăn kèm với sốt cam chua chua, măng tây giòn giòn tạo sự cân bằng và hài hoà trong hương vị. Công thức Cá hồi sốt cam tại gia đơn giản nhưng không kém phần sang trọng.<br data-mce-bogus="1">', 1, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55300, 'Cá Bơn Sốt Xì Dầu', 'Thịt cá bơn săn, có vị ngọt nhẹ nên rất hợp sốt xì dầu. Món ngon mà cách chế biến và nguyên liệu đều đơn giản.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55301, 'Gỏi Thịt Bò Cà Pháo', 'Cà pháo không chỉ để chấm mắm tôm hoặc ăn kèm cùng các loại thịt mà còn có thể biến tấu thành nhiều món ăn hấp dẫn. Cùng Cooky kết hợp cà pháo với thịt bò, chuối chát, khế chua cùng một số loại rau thơm thành món gỏi khai vị chua chua giòn giòn lạ miệng. Món ăn tuy đơn giản nhưng chắc chắn sẽ phù hợp với khẩu vị của các thành viên trong gia đình.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55302, 'Kim Chi Củ Kiệu', 'Nếu đã quá quen với kiệu ngâm chua ngọt trong những ngày Tết thì bạn hãy thử ngay món kim chi củ kiệu kết hợp với ớt bột Hàn Quốc. Độ giòn của kiệu vẫn được giữ nguyên kết hợp với vị cay từ nguyên liệu bột ớt làm tăng sự mới lạ cho món ăn. Kim chi củ kiệu dùng kèm với các món thịt và rau sống là một sự hòa quyện hoàn hảo mà bạn không nên bỏ qua. Cùng Cooky thực hiện món ăn này nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55304, 'Măng Nhồi Thịt Xốt Cà Chua', 'Măng thường được dùng để xào hoặc nấu canh nhưng với công thức này Cooky sẽ hướng dẫn bạn biến tấu thành món măng nhồi thịt xốt cà chua. Phần măng giòn quyện với phần thịt xay mềm ẩm cùng nước xốt cà chua sánh phủ quanh ống măng càng làm tăng thêm hương vị đậm đà của món ăn. ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55305, 'Trứng Cút Xốt Phô Mai Trứng Muối', 'Trứng cút không chỉ dùng để kho thịt mà còn được chế biến thành nhiều món ăn vặt hấp dẫn. Trong công thức lần này, Cooky mang đến cho bạn một sự kết hợp hấp dẫn giữa trứng cút chiên và xốt phô mai chà bông trứng muối ngon tuyệt. Bắt tay thực hiện ngay nha!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55306, 'Đậu Hũ Viên Rau Củ Chiên', 'Đậu hũ là món ăn được nhiều người yêu thích. Nhưng nếu bạn đã quá quen với các món chiên, nhồi thịt, xốt cà vậy tại sao không cùng Cooky biến tấu đậu hũ thành món ăn vặt hấp dẫn khi kết hợp cùng ngò và cà rốt. Viên đậu hũ rau củ chiên có thể dùng với cơm như một món chay vừa có thể nhâm nhi ăn vặt. Bắt tay thực hiện nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55308, 'Thịt Đông Trứng Phô Mai', 'Thịt đông là món ăn vô cùng quen thuộc trong ngày Tết truyền thống của mọi gia đình Việt. Hãy thử biến tấu món ăn này bằng công thức sáng tạo và mới lạ hơn với tên gọi Thịt đông trứng phô mai dưới đây. Với hình dáng xinh xắn từ quả trứng cùng nhân thịt và rau củ đa dạng nhiều màu sắc bên trong, lại có thêm viên phô mai Con Bò Cười thơm béo hòa quyện rất hợp nhau nữa, chắc chắn sẽ tạo nên luồng gió mới trong thực đơn mâm cơm Tết nhà bạn đó!', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55310, 'Xúc Xích Nhà Làm', 'Xúc xích là một trong những món ăn ngon được nhiều người biết đến trên thế giới. Từ người lớn đến trẻ nhỏ không ai có thể kiềm chế trước sự quyến rũ của xúc xích. Tại sao bạn không cùng Cooky làm một mẻ xúc xích nhà làm cho gia đình thân yêu của mình nào. Chắc chắn mọi người sẽ trầm trồ không ngớt về món xúc xích nhà làm của bạn đấy.', 6, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55315, 'Thịt Kho Trứng Phô Mai', 'Món thịt kho trứng từ lâu đã là một món ăn quen thuộc trong những bữa cơm của người Việt Nam. Trong những ngày Tết, nó lại càng trở nên đặc biệt vì dễ nấu, lại đậm đà hương vị truyền thống. Khi kết hợp cùng phô mai Con Bò Cười, món ăn này sẽ đem lại hương vị tuy quen mà lạ đấy. Cùng tìm hiểu cách làm món thịt kho trứng với phô mai thơm ngon theo công thức bên dưới nhé!<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55316, 'Ruốc Nấm Hương Chay', 'Ruốc nấm hương là món ăn thuần chay, được chế biến từ chân nấm hương, với cách làm vô cùng đơn giản và phù hợp với mọi gia đình. Ruốc nấm hương thanh đạm lại rất tốt cho sức khỏe, đặc biệt là những người ăn kiêng hay mắc các bệnh tim mạch, tiểu đường. Học ngay cách làm ruốc nấm hương cùng Cooky nhé! <br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55317, 'Món Cuốn Thủy Nguyên', 'Món cuốn Thủy Nguyên với màu sắc rực rỡ, vị ngậy, ngọt tự nhiên của thịt, vị bùi của trứng, vị thanh mát từ các loại rau xanh, chấm kèm với nước mắm chua chua, ngọt ngọt sẽ khiến cho ai đã thưởng thức một lần sẽ nhớ mãi. Bạn hãy cùng Cooky tham khảo ngay công thức bên dưới, vào bếp trổ tài ngay nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55318, 'Chả Giò Rau Củ Phô Mai', '<div>Chả giò là món ăn quen thuộc và nổi tiếng ở Việt Nam và được rất nhiều người yêu thích. Con bò cười sẽ hướng dẫn cho bạn một biến tấu mới với chả giò đó là chả giò rau củ phô mai. Chả giò có lớp vỏ bên ngoài được chiên vàng, giòn rụm. Phần nhân bên trong là sự kết hợp hoàn hảo giữa vị phô mai beo béo cùng với vị ngọt tự nhiên của rau củ. Bạn hãy tham khảo ngay công thức bên dưới nhé!<br></div>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55319, 'Mứt Cà Rốt Phô Mai Vị Cam Và Vị Tắc Xí Muội', 'Mứt cà rốt phô mai vị cam và vị tắc xí muội là món mứt biến tấu mới mẻ, rất thích hợp dùng đãi khách trong dịp Tết cổ truyền. Mứt mang hương vị đặc trưng của cà rốt, thơm thoang thoảng mùi cam, mùi xí muội, khi ăn vào có vị ngọt thanh bên ngoài, bên trong có vị béo bùi của phô mai hòa quyện với nhau tạo nên một hương vị rất đặc biệt. Bạn hãy tham khảo ngay công bên dưới để làm ngay món mứt cà rốt này nhé!', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55320, 'Bánh Mì Sữa Chua Phô Mai', 'Bánh mì sữa chua phô mai thơm ngon, lạ miệng là món ăn vặt hấp dẫn cả người lớn cũng như trẻ nhỏ. Bánh có vị béo của phô mai, vị chua chua và ngọt dịu của sữa chua và trái cây, tất cả được hòa quyện với nhau tạo nên hương vị vô cùng hấp dẫn. Bạn hãy tham khảo ngay công thức bên dưới, vào bếp cùng Con bò cười trổ tài ngay nhé!', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55321, 'Bánh Nếp Chay', 'Món bánh nếp chay thanh đạm có vị ngọt thanh chắc chắn sẽ khiến những ai nhìn thấy đều phát thèm!', 4, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55323, 'Peach Tea Smoothie', 'Trà đào kem tuyết là thức uống còn khá mới mẻ với người thưởng thức. Sự kết hợp hoàn hảo giữa vị trà đào thơm thơm, đào miếng thanh ngọt, sữa chua và kem vanilla mát lạnh, chắn chắn trở thành thức uống đẹp da mới, đốn gục biết bao con tim fan trà đào đấy nhé! ', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55324, 'Trà Dâu Gừng Nhiệt Đới', 'Trà dâu là loại trà khá ngon, được nhiều bạn trẻ yêu thích, nhưng biến tấu thêm với 1 chút bia gừng cay nhẹ sẽ làm cho món nước thêm phần lạ miệng và hấp dẫn hơn.  Không chỉ có tác dụng giải nhiệt nó còn giúp làm đẹp da, giải độc cũng như cung cấp nhiều lợi ích cho cơ thể. Chỉ với những bước đơn giản, bạn đã có ngay cho mình ly trà dâu gừng nhiệt đới ngon lành mát lạnh rồi đấy.', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55325, 'Trà Sữa Bá Tước', '<span style="font-family: NotoSerif-Regular, sans-serif; font-size: 14px;" data-mce-style="font-family: NotoSerif-Regular, sans-serif; font-size: 14px;">Trà bá tước hay còn được gọi với cái tên trà Earl Grey có vị</span><span style="font-family: NotoSerif-Regular, sans-serif; font-size: 14px;" data-mce-style="font-family: NotoSerif-Regular, sans-serif; font-size: 14px;"> dịu thanh mát cùng vị chát nhẹ và mùi hương sang trọng,trầm lắng thoát ra từ tinh chất Bergamot từ dòng trà bá tước, kết hợp với kem vanilla thơm béo được xay mịn tạo thành một món nước giải khác hấp dẫn, thơm ngon mà còn mang lại nhiều lợi ích cho sức khỏe nữa. </span>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55326, 'Trà Olong Ủ Lạnh', '<span style="color: rgba(0, 0, 0, 0.8); font-family: " helvetica="" neue="" arial="" wenquanyi="" zen="" hei="" hiragino="" sans="" gb="" pro="" lihei="" heiti="" tc="" microsoft="" jhenghei="" ui="" sans-serif="" font-size:="" 14px="" white-space:="" pre-wrap="" data-mce-style="color: rgba(0, 0, 0, 0.8); font-family: ">Trà Olong ủ lạnh được tạo nên từ những búp trà thượng hạng,</span><span style="color: rgba(0, 0, 0, 0.8); font-family: " helvetica="" neue="" arial="" wenquanyi="" zen="" hei="" hiragino="" sans="" gb="" pro="" lihei="" heiti="" tc="" microsoft="" jhenghei="" ui="" sans-serif="" font-size:="" 14px="" white-space:="" pre-wrap="" data-mce-style="color: rgba(0, 0, 0, 0.8); font-family: "> giữ trọn vị trà tươi mới, hoà quyện cùng nước soda hoặc nước tinh khiết ở nhiệt độ thấp trong khoảng thời gian hoàn hảo giúp chiết xuất trọn vẹn cả hương lẫn vị của lá trà. </span><span style="color: rgba(0, 0, 0, 0.8); font-family: " helvetica="" neue="" arial="" wenquanyi="" zen="" hei="" hiragino="" sans="" gb="" pro="" lihei="" heiti="" tc="" microsoft="" jhenghei="" ui="" sans-serif="" font-size:="" 14px="" white-space:="" pre-wrap="" data-mce-style="color: rgba(0, 0, 0, 0.8); font-family: ">Trà ủ lạnh không có vị đắng chát mạnh như cách pha nóng truyền thống, trở nên thông dụng và chinh phục được vị giác của nhiều người hơn. </span>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55327, 'Trà Hoa Hồng Thạch Dâu', 'Trà hoa hồng thạch dâu, thức uống khá mới mẻ với nhiều người, chỉ cần thử là bạn có thể nghiện ngay. Trà hoa hồng có rất nhiều công dụng từ làm đẹp đến tốt cho sức khỏe. Hương trà thơm dễ chịu, kết hợp với thạch dâu giòn dai vui miệng. Sự kết hợp hoàn hảo đó tạo nên một món nước vô cùng hấp dẫn và tươi mát.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55328, 'Chè Trôi Nước Tam Sắc', 'Chè trôi nước tam sắc, món chè khá quen thuộc với người Việt Nam, kết hợp với màu sắc tự nhiên từ rau củ làm cho món ăn thêm sinh động và đẹp mắt hơn, cộng với hương vị đậm đà của món chè này sẽ mang đến một làn gió mới cho cả gia đình bạn. ', 4, '2022-03-22 00:00:00', 180, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55330, 'Bò Né Me Rau Càng Cua', 'Bò Né Me Rau Càng Cua hay bò nhúng me một món ăn không phải ai cũng biết đến từ miền tây, với từng thớ bò đỏ mềm cùng nước sốt me chua được làm dịu bớt bằng vị ngọt thanh dịu của đường thốt nốt kết hợp với từng cọng rau càng cua giòn, thanh mát miền sông nước . Sẽ làm bạn muốn ăn hoài không ngưng ngay khi thử đũa đầu tiên. Hãy cùng Cooky đến ngay với công thức bò né me rau càng cua hấp dẫn miền sông nước này nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55331, 'Nem Chua', 'Nem chua món ăn truyền thống quen thuộc ưa thích của nhiều người đặc biệt là dịp tết. Với độ giòn thơm, chua chua, cay cay đặc biệt ăn kèm với tương ớt hoặc muối tiêu chanh sẽ trở thành món nhắm tuyệt vời hơn bao giờ hết. Và hôm nay Cooky sẽ hướng dẫn bạn cách làm nem chua đơn giản đảm bảo được hương vị, độ giòn dai và màu sắc bắt mắt chỉ trong vòng 3 ngày là có thể ăn được. Nào hãy bắt tay làm món Nem Chua để lai rai dịp tết thôi!', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55332, 'Đậu Hũ Ki Khìa Nước Dừa', 'Đậu hũ là một trong những nguyên liệu giàu chất dinh dưỡng và phổ biến nhất trong các món chay ở Đông Nam Á. Đậu hũ ki khìa nước cốt dừa sử dụng đậu hũ và đậu hũ ky, kết hợp cùng nhân nấm thơm bùi, sẽ là một món chay không thể thiếu. Cách làm dễ dàng, nhưng hương vị đậm đà, thơm ngon sẽ không khiến bạn thất vọng. Hãy cùng Cooky thực hiện món chay cho dịp Rằm sắp tới nào.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55333, 'Bánh Đồng Tiền Ngũ Sắc', 'Bánh đồng tiền ngũ sắc là một loại bánh đơn giản, dễ làm, không quá ngọt, lại giàu dinh dưỡng. Bạn có thể biến tấu tùy theo sở thích của bạn. Đây sẽ là một món bánh không thể thiếu trong thực đơn ăn Tết sắp tới. Bạn có thể cho bất kỳ loại hạt khô, trái cây khô, yến mạch,... mà bạn thích lên bánh để tạo ra một phong cách riêng của mình. Còn chờ gì mà không làm nóng lò và cùng Cooky làm bánh ngũ sắc đón tết nào.', 6, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55335, 'Cá Diêu Hồng Hóa Rồng', 'Cá Diêu Hồng loài cá rất quen thuộc của chúng ta vẫn hay sử dụng làm các món ăn hằng ngày, nhưng chỉ với vài thao tác đơn giản hôm nay Cooky sẽ hướng dẫn bạn làm món Cá Diêu Hồng trở nên bắt mắt hơn. Từng miếng cá được tẩm ướp vừa ăn đem đi chiên vàng ươm giòn rụm ăn kèm rau gỏi chua chua cay cay, sốt gỏi thơm nồng chắc chắn sẽ làm các vị khách thưởng thức phải trầm trồ xuýt xoa. Hãy lưu ngay lại cách làm món Cá Diêu Hồng Hóa Rồng tưởng khó mà dễ này thôi nào!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55342, 'Xôi Cá Cơm', 'Xôi là một món ăn được nhiều người yêu thích. Nhưng nếu bạn đã quá quen với xôi gà, xôi mặn hay xôi thập cẩm thì hôm nay hãy cùng Cooky đổi vị với món xôi ăn kèm cá cơm nhé! Phần xôi dẻo, ngọt từ hạt nếp kết hợp với vị mằn mặn ngòn ngọt của cá cơm rim thêm chút hành phi, mỡ hành nữa chắc chắn sẽ khiến bạn mê mẩn.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55343, 'Há Cảo Thủy Tinh', 'Vỏ bánh há cảo thường được làm từ bột tàn mì. Nhưng trong công thức này, Cooky sẽ hướng dẫn bạn làm vỏ bánh há cảo thủy tinh từ bột bắp và bột khoai tây. Chiếc bánh thành phẩm sau khi hấp xong vẫn mềm dẻo, thêm vào đó còn đạt độ trong suốt, nhìn thấy rõ phần nhân bên trong rất bắt mắt. Cùng Cooky thực hiện nha!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55344, 'Ốc Hương Rang Muối Sợi', 'Ốc Hương Rang Muối Sợi món ăn làm mưa làm gió gần đây mà những tín đồ ghiền ốc mà ai cũng muốn thử. Với cách làm không quá phức tạp, hương vị ngọt ngọt mặn mặn áo đều lên từng con ốc hương, phần gia vị dư được kéo thành từng sợi dài  giòn thơm mùi caramel. Chấm cùng sốt muối ớt xanh hoặc nước mắm tắc thì phải gọi là hết sẩy. Nào cùng Cooky đến với món Ốc Hương Rang Muối Sợi để lai rai cùng hội bạn thân thôi nào!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55345, 'Cải Thìa Sốt Dầu Hào Nấm Đông Cô', 'Cải thìa là loại rau chứa nhiều khoáng chất và Vitamin C, A và B tốt cho cơ thể chúng ta. Cải thìa được chế biến thành nhiều loại món ăn khác nhau tùy khẩu vị và sở thích từng người. Dưới đây mình sẽ chia sẻ cách làm cải thìa sốt dầu hào kèm nấm đông cô', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55346, 'Nước Chấm Ốc Hà Nội', 'Ốc là món ăn được rất nhiều người yêu thích đặc biệt tại miền Bắc. Để làm nên một món ốc thơm ngon không chỉ có phần ốc tươi, béo giòn sần sật mà còn là nước chấm đậm đà. Vì vậy, Cooky sẽ hướng dẫn ngay cho bạn cách pha nước chấm ốc Hà Nội siêu thơm ngon và đậm vị.', 4, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55352, 'Cơm Cà Ri', 'Cơm cà ri là món ăn dễ làm, lạ miệng. Đặc biệt có thể dùng bất kỳ bữa nào trong ngày như sáng, trưa, tối đều rất hấp dẫn. Chúng ta cùng vào bếp làm nhé!', 3, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55355, 'Bánh Ngói Hạnh Nhân Keto', 'jBánh ngói hạnh nhân là món ăn vặt cực ngon lại bổ dưỡng cho sức khỏe. Bánh sử dụng bơ lạt, bột hạnh nhân, đường ăn kiêng thích hợp sử dụng cho cả người ăn kiêng, người bị tiểu đường. Với lớp hạnh nhân bùi bùi, kết hợp với vỏ bánh giòn tan có độ ngọt vừa phải thì sẽ đốn ngã tim của bất cứ ai thử qua nó.', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55356, 'Bánh Crepe Cầu Vồng', 'Bánh crepe hay còn gọi là bánh kếp là một loại bánh bắt nguồn từ vùng Bretagne ở tây bắc nước Pháp. Bánh thường có dạng tròn, dẹp, ăn kèm được với rất nhiều loại nhân từ trái cây tươi, si rô lá phong, cho tới thịt xông khói, xúc xích,... Và hôm nay Cooky sẽ hướng dẫn bạn một phiên bản vô cùng nhiều màu sắc của crepe, đó là crepe cầu vồng. Nếu bạn muốn làm những đứa trẻ ở nhà bạn ngạc nhiên thì hãy cùng Cooky vào bếp nào. ', 6, '2022-03-22 00:00:00', 180, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55357, 'Đậu Hủ Xào Chua Ngọt', 'Đậu Hủ Xào Chua Ngọt một món ăn không mấy xa lạ, rất hợp khẩu vị đa số mọi người, với những miếng đậu hủ được chiên vàng giòn, xào cùng các loại rau củ thanh đạm áo một lớp sốt chua chua ngọt ngọt bắt mắt, đặc biệt đây là một món chay sẽ làm phong phú thêm lựa chọn cho những ngày ăn chay hay đơn giản là đổi vị cho bữa cơm gia đình hướng tới sự tiết kiệm và thanh đạm hơn. Hãy bỏ túi công thức món Đậu Hủ Xào Chua Ngọt đơn giản dễ làm này từ Cooky thôi nào.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55358, 'Đậu Hủ Muối Tiêu Rau Răm', 'Đậu Hủ Muối Tiêu Rau Răm là một món ăn đơn giản không cần quá nhiều nguyên liệu và gia vị có thể dùng trong bữa cơm hoặc là một món ăn chơi. Những miếng đậu hủ vàng được hấp nóng hổi mọng nước giữ được vị thơm ngon đặc trưng, kết hợp lớp nhân mặn mặn nồng nồng của muối tiêu quết cùng một lớp sa tế ớt cay xè cùng với một chút chua chua thơm thơm của nước cốt tắc và  rau răm, hứa hẹn sẽ là món đậu hủ làm bạn không thể quên được. Học ngay công thức món Đậu Hủ Muối Tiêu Rau Răm từ Cooky thôi nào!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55359, 'Thịt Ba Rọi Kho', 'Thịt lợn là loại thịt phổ biến nhất ở nước ta, được chế biến theo vô vàn món ăn hấp dẫn, dưới đây tôi sẽ chia sẻ công thức kho thịt ba rọi kiểu miền trung, món ăn này thích hợp cho người không thích vị quá ngọt trong món kho bởi vì theo công thức này sẽ không thắng đường làm nước màu. Vì món thịt kho này không thắng với đường hoặc nước màu nên khi ăn có thể cảm thấy vị ngọt và bùi đặc trưng của thịt nguyên bản. Chúc các bạn thực hiện thành công', 5, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55360, 'Bánh Roti Chuối Thái Lan', 'Bánh Roti nhân chuối là món ăn vặt đường phố nổi tiếng Thái Lan với lớp vỏ bánh bột giòn tan thơm mùi bơ và sữa cắn vào nhân chuối thơm ngon hòa quyện  cùng vị béo thơm của trứng, chấm một ít sữa đặc cùng chocolate nữa thì phải nói là tuyệt vời, hứa hẹn đây là món sẽ làm các tín đồ hảo ngọt phải mê tít. Nào cùng Cooky đến với cách làm món bánh roti nhân chuối Thái Lan với cách làm đơn giản tại nhà mà ai cũng có thể làm được.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55361, 'Yakult Thanh Long Sơ Ri', '<span style="font-family: Lato, sans-serif; font-size: 14px;" data-mce-style="font-family: Lato, sans-serif; font-size: 14px;">Yakult thanh long sơ ri, thức uống vừa " heathy" vừa tốt cho sức khỏe kết hợp giữa yakult giàu men lợi tiêu hoá với nước ép sơ ri 100% tự nhiên và thanh long đỏ thêm chút giòn giòn vui miệng của trân châu trắng, giúp căng tràn khỏe khoắn cho mùa tết nhiều dầu mỡ. </span><br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55362, 'Thạch Sữa Bắp Lá Dứa', 'Chỉ với những nguyên liệu đơn giản cùng một chút tỉ mỉ là bạn có thể nâng cấp món thạch rau câu quen thuộc lên một phiên bản hấp dẫn hơn với bắp và lá dứa. Các lớp thạch xen kẽ nhau không những đẹp mắt mà còn rất ngon miệng. Cùng Cooky thực hiện nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55363, 'Chè Hạt Sen Long Nhãn', 'Hạt sen là nguyên liệu được dùng phổ biến trong nhiều món ăn bởi vị ngon và giá trị dinh dưỡng mà nó mang lại. Kết hợp hạt sen với long nhãn và một số nguyên liệu tốt cho sức khỏe như táo đỏ, kỷ tử, nấm tuyết, bạn sẽ cho một món chè ngon mát và có tác dụng giải nhiệt tuyệt vời. Cùng Cooky thực hiện nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55364, 'Chè Sắn Mochi', 'Chè sắn/khoai mì là một món ăn được nhiều người yêu thích bởi sự kết hợp hoàn hảo giữa vị bùi của sắn, vị ngọt dịu từ nước đường, độ béo của nước cốt dừa thêm chút hương ấm nóng từ gừng tươi. Cùng Cooky nâng cấp món chè này với những viên mochi từ sắn tươi tạo thêm độ dẻo đặc biệt cho món ăn nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55365, 'Bánh Chưng Chiên Nước Ngũ Sắc', 'Bánh chưng món ăn rất quen thuộc với mọi nhà vào dịp tết hôm nay sẽ được hô biến với cách làm đơn giản mà vô cung hấp dẫn với màu sắc tươi mới của các loại rau củ trong nhân kết hợp cùng Phô Mai Con Bò Cười Thơm béo. Lớp vỏ bánh được ráng vàng giòn mà không hề sử dụng dầu ăn hòa cũng mùi thơm của mè rang, ăn kèm dưa món củ kiệu nữa sẽ rất tuyệt vời cho những ngày tết sắp tới. nào hãy cùng bắt tay vào bếp thực hiện ngay món bánh chưng hấp dẫn này thôi nào!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55366, 'Cao Quy Linh 3 Cách Ăn', 'Cao quy linh - một món ăn của người Hoa nhưng đã dần trở nên quen thuộc trong danh sách các món tráng miệng của người Việt. Tuy có vị hơi nhẫn đắng, nhưng món ăn này rất tốt cho sức khỏe bởi thành phần được làm từ các loại thảo dược trong thiên nhiên. Phần thạch đen bóng hơi giống sương sao nhưng mềm, dai và có mùi thơm đặc trưng. Cách làm cao quy linh cũng rất đơn giản. Cùng Cooky thực hiện nhé!', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55367, 'Bánh Giầy Kẹp Chả Lụa', 'Bánh giầy là một loại bánh truyền thống của người Việt Nam nhằm thể hiện lòng tôn kính tổ tiên, đất trời. Được làm từ bột gạo nếp, một trong những nguyên liệu đặc trưng của đất nước nông nghiệp, cùng cách làm đơn giản, bạn còn chờ gì mà không cùng Cooky vào bếp làm một mẻ bánh giầy cho dịp Tết Nguyên Đán sắp tới nào.', 6, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55368, 'Bò Bía Ngọt', 'Thế hệ học sinh Việt Nam hẳn khó ai quên được những cuốn Bò Bía Ngọt và thơm phưng phức bán trước cổng trường. Tưởng rằng cách làm phức tạp nhưng thực ra lại không phải vậy đâu. Những cuốn Bò Bía Ngọt nhỏ nhắn trắng xinh khi cắn vào có vị béo ngậy của nhân dừa hòa quyện cùng những miếng kẹo mạch nha ngọt giòn tan lớp vỏ ngoài dai dai thơm mùi mè rang tạo thành món ăn vặt ăn hoài không biết chán. Hôm nay Cooky sẽ cùng bạn trở lại thời áo trắng với món Bò Bía Ngọt làm xao xuyến bao nhiêu thế hệ học sinh này nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55369, 'Ghẹ Sốt Me', 'Ghẹ rang me món ăn ưa thích của nhiều người xuất hiện từ trong các nhà hàng lớn cho tới các hàng quán vỉa hè. Những con ghẹ được chế biến lớp vỏ đỏ au áo một lớp sốt me chua chua ngọt ngọt, tách ra bên trong từng thớ thịt thơm phức hương vị hải sản chấm cùng nước sốt me đặc sánh chắc chắn sẽ làm bạn siêu lòng. Cùng Cooky thực hiện ngay món hải sản tuyệt vời mà ai cũng thích này thôi nào!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55370, 'Bánh Nếp Chiên', 'Bánh nếp chiên là món ăn vặt thơm ngon, hấp dẫn và chỉ cần thực hiện với các bước không thể đơn giản hơn.', 3, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55373, 'Sandwich Chiên Xù', 'Sandwich chiên xù là món ăn thơm ngon, có lớp vỏ bên ngoài giòn rụm, bên trong phần nhân khoai tây bùi bùi kết hợp cùng với các loại rau củ có vị ngọt tự nhiên, chắc chắn sẽ làm bạn khó cưỡng nếu được một lần ăn thử. Bạn còn chần chờ gì mà không tham khảo ngay công thức bên dưới và vào bếp nấu ngay nhé!', 5, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55374, 'Lườn Cá Hồi Sốt Chua Ngọt', 'Lườn cá hồi sốt chua ngọt là món ăn thơm ngon, bổ dưỡng. Phần cá hồi được chiên giòn rụm, ăn vào bạn sẽ cảm nhận được vị béo ngậy, áo bên ngoài cá là phần sốt chua chua, ngọt ngọt, món ăn này chắc chắn sẽ khiến bạn vét sạch nồi cơm trong tích tắc. Bạn hãy cùng Cooky vào bếp và trổ tài ngay nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55375, 'Bánh Ít Trần Nhân Mặn', 'Bánh ít trần nhân mặn - nhân tôm thịt là món ăn rất được yêu thích. Bánh ít trần có vỏ bánh dẻo dẻo dai bọc bên trong phần tôm thịt đậm đà, bùi béo của đậu xanh, chấm kèm với nước mắm chua chua, ngọt ngọt, tất cả hòa quyện với nhau tạo nên sự độc đáo, lạ miệng và vô cùng hấp dẫn. Bạn hãy cùng Cooky tham khảo ngay công thức bên dưới và vào bếp trổ tài ngay nhé!', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55386, 'Đậu Phụ Sốt Cay Kiểu Hàn', 'Người Việt chúng ta thường có món đậu luộc chấm mắm tôm, đậu sốt cà chua, đậu tẩm hành, nấu canh đậu. Ngoài những món truyền thống đó, chúng ta hoàn toàn có thể biến tấu thành món đậu phụ sốt cay kiểu Hàn. Với nguyên liệu đơn giản, dễ mua ăn lại lạ miệng, đưa cơm.', 4, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55391, 'Khâu Nhục', '<p>Khâu nhục là một món đặc sản nổi tiếng ở Lạng Sơn, thường được dùng phổ biến trong các dịp lễ Tết, cưới hỏi,… Khâu nhục được làm từ nguyên liệu chính là thịt heo, cách làm khâu nhục không quá cầu kì nhưng tương đối lạ và vị cũng lạ, lần đầu thưởng thức có thể chưa thích nhưng càng ăn sẽ càng mê đó nha.</p>', 7, '2022-03-22 00:00:00', 240, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55392, 'Bánh Cuộn Dừa', 'Bánh cuộn dừa một món bánh nổi tiếng của Indonesia với lớp vỏ màu sắc bắt mắt, khi cắn vào sẽ hòa quyện vị ngọt thơm của dừa nạo với một chút thơm thơm của lá dứa. Đây sẽ là món ngọt tráng miệng đỉnh cao mà bạn phải thử. Với cách làm không quá phức tạp, cùng hai công thức vỏ bánh từ lá dứa và bí đỏ sẽ thêm nhiều trải nghiệm về món bánh nổi tiếng này. Nào bắt tay vào làm ngay món này cùng Cooky thôi!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55393, 'Canh Bún Chay', 'Canh bún chay là món ăn thơm ngon, hấp dẫn, phần nước lèo nóng hổi có vị ngọt tự nhiên từ rau củ, ăn kèm bún tươi cùng với rau muống trụng và các loại nấm mang lại cảm giác ngon miệng khó cưỡng. Hãy đổi mới thực đơn hằng ngày của bạn bằng món canh bún chay mà Cooky chia sẽ bên dưới đây nhé!', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55395, 'Thịt Gà Rim Ngũ Vị', 'Nếu bạn đang băn khoăn tối nay ăn gì thì món thịt gà rim ngũ vị là một gợi ý hay cho bạn đấy. Món gà này đậm đà, thơm ngon không kém gì gà rang gừng, gà luộc.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55400, 'Bánh Khoai Tây Bọc Phô Mai Sợi', 'Bánh khoai tây bọc phô mai sợi là một món ăn dễ làm lại ngon miệng. Món ăn phù hợp cho mọi người và nhất là trẻ em.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55404, 'Sữa Chua Úp Ngược', 'Sữa chua là một trong những món ăn vặt phổ biến, giàu chất dinh dưỡng, cả người lớn và trẻ em đều rất thích. Cách làm rất đơn giản nhưng không phải ai cũng làm được. Nếu bạn đã từng thất bại khi làm sữa chua, Cooky sẽ hướng dẫn bạn một cách vô cùng chi tiết để giúp bạn có thể làm ra một hũ sữa chua úp ngược ngon tuyệt vời nào.', 6, '2022-03-22 00:00:00', 450, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55405, 'Bánh Hòn', 'Bánh Hòn là một trong những loại bánh tuổi thơ của con người miền Trung, đặc biệt là Phan Thiết, Ninh Thuận. Bánh Hòn dai dai, được lăn qua cơm dừa bào béo ngậy, chấm cùng muối đậu mặn mặn ngọt ngọt đã đi vào trong tim của nhiều đứa trẻ miền Trung. Nếu bạn muốn có một vé về lại tuổi thơ, hãy cùng Cooky vào bếp và làm một mâm bánh Hòn thật to nào. ', 6, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55406, 'Cơm Hộp 8/3', 'Ngày Quốc Tế Phụ Nữ được tổ chức hàng năm vào ngày 8 tháng 3 nhằm tôn vinh vai trò vĩ đại của người phụ nữ trong gia đình và xã hội. Vậy sao bạn không vào bếp làm một hộp cơm thật dễ thương, ngon lành mà lại giàu chất dinh dưỡng cho người mình thương nào. Hộp cơm này sẽ giúp bạn ghi điểm tuyệt đối trong lòng đối phương đấy. Nhanh nhanh xắn tay áo lên và cùng Cooky vào bếp nào.', 2, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55407, 'Kem Phô Mai Trái Cây', 'Kem là một món tráng miệng phổ biến trên toàn thế giới. Nếu bạn là một người yêu kem, bạn sẽ không thể bỏ qua món kem phô mai trái cây Con Bò Cười này. Kem lạnh lạnh ngọt ngọt kết hợp cùng vì beo béo của phô mai Con Bò Cười, ăn cùng trái cây tươi chua chua sẽ giúp bạn giải tỏa sức nóng của những buổi trưa của miền nhiệt đới này. Còn chờ gì mà không vào bếp để "Đập tan cái nóng mùa hè" nào.', 4, '2022-03-22 00:00:00', 270, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55408, 'Óc Heo Hầm Thuốc Bắc', 'Óc Heo Hầm Thuốc Bắc món ăn bổ dưỡng phù hợp bồi bổ những người vừa ốm dậy hoặc bị thiếu dinh dưỡng cũng như tăng cường sức khỏe phòng chống bệnh tật. Óc heo tươi được chế biến khử mùi tanh, thơm béo ngậy chưng với nước hầm thuốc bắc thơm nồng đặc trưng, không cần quá nhiều gia vị đảm bảo được độ bổ dưỡng và mùi hương tự nhiên từ thuốc bắc. Hãy học ngay cách chế biến đơn giản món ăn cực kì bổ dưỡng Óc Heo Tiềm Thuốc Bắc từ Cooky nhé!', 2, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55411, 'Bánh Cuốn Trứng', 'Bánh cuốn là món ăn được nhiều người việt ưa chuộng vì vị thơm ngon, lại gần gũi. Nay biến tấu thêm trứng gà đã thổi hồn vào món ăn, giúp tăng thêm hương vị vừa mềm, vừa béo vừa thơm và màu sắc bắt mắt hơn, làm cuốn hút nhiều thực khách tò mò với món ăn này. ', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55412, 'Mực Que Chiên Giòn Sốt Cam', 'Mực que chiên giòn sốt cam, món ăn ngon dành tặng cho gia đình nhất là các bé thân yêu. Mực tươi ngọt, giòn tan chấm kèm với xốt cam tươi béo ngậy, chua ngọt tự nhiên, làm món ăn thêm phần hấp dẫn không thể chối từ.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55413, 'Cá Cơm Chay Từ Hoa Chuối', 'Cá cơm chay là món ăn vô cùng dễ thực hiện mang hương vị lạ miệng, cuốn hút đến bất ngờ. Cùng Cooky thực hiện món cá cơm chay giòn rụm từ hoa chuối dưới đây nhé, bảo đảm cả nhà bạn sẽ thích mê!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55414, 'Gà Ủ Muối Hoa Tiêu', 'Gà ủ muối hoa tiêu là món ăn có cách chế biến không quá cầu kỳ mà vẫn giữ trọn hương vị thơm ngon, ngọt nước. Với lớp da ngoài vàng ươm, xớ thịt thì mềm dai, thấm đều gia vị, càng làm cho món ăn thêm đậm đà, hấp dẫn hơn. Chắc chăn món ăn này sẽ khiến bạn mê mẩn ngay từ miếng đầu cho mà xem. ', 4, '2022-03-22 00:00:00', 130, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55416, 'Mandu Hàn Quốc', 'Mandu Hàn Quốc đã dần trở nên quen thuộc với người Việt. Dù là mandu hấp hay chiên đều mang đến một hương vị vô cùng hấp dẫn. Vỏ bánh mỏng dẻo, gói gọn phần nhân bên trong chấm với nước tương đặc trưng pha cùng giấm sẽ là một lựa chọn ngon miệng cho những ngày ngán cơm. Cách thực hiện cũng rất đơn giản, cùng Cooky thực hiện nhé!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55417, 'Lươn Hấp Mướp Hương', 'Lươn hấp mướp hương là một món ăn đơn giản, thực hiện nhanh chóng mà rất đưa cơm. Thịt lươn săn chắc, có vị ngọt đặc trưng khi kết hợp cùng mướp hương. Bạn có thể thêm chút tỉ mẩm tạo hình chiếc thuyền mướp giúp món ăn thêm độc đáo. Thực hiện cùng Cooky nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55418, 'Cà Tím Nhồi Thịt Hấp', 'Cà tím là một loại rau quả phổ biến được nhiều người yêu thích. Để tăng thêm hương vị của món cà, ta có thể nhồi cùng thịt nạc xay và dùng chung với nước xốt dầu hào. Cùng Cooky thực hiện món cà tím nhồi thịt hấp với tạo hình chiếc lồng đèn rất bắt mắt nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55419, 'Madeleine', 'Madeleine là một trong những loại bánh nổi tiếng của ẩm thực nước Pháp. Cái tên Madeleine bắt nguồn từ một nữ đầu bếp có tên là Madeleine Paulmier – người sáng tạo nên một trong những công thức bánh nổi tiếng nhất tại vùng đất Lorraine thơ mộng của Pháp. Bánh có hình vỏ sò xinh xắn nên còn được gọi với cái tên thân thương khác là "bánh vỏ sò". Nếu bạn là một người yêu thích ẩm thực nước Pháp, bạn còn chờ gì mà không cùng Cooky vào bếp làm một mẻ Madeleine nào!', 6, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55420, 'Chocolate Chip Cookies', 'Vào những năm 1930, trong một lần vì hết bột cacao, bà Ruth, chủ của thương hiệu bánh Toll House Inn lại muốn làm cookie chocolate, do đó bà cắt nhỏ những thanh chocolate và cho vào hỗn hợp bột cookie. Ngạc nhiên thay sau khi nướng, chocolate không chảy ra mà vẫn giữ hình dạng những mẫu chip nhỏ. Mọi người rất thích loại bánh này, do đó bà Ruth đã bán lại công thức này cho Nestle, một trong những hãng chocolate lớn nhất lúc bấy giờ để đổi lại quyền sử dụng chocolate của hãng suôt đời. Nếu bạn muốn biết cảm giác của bà Ruth như thế nào khi cho ra lò những chiếc bánh cookie này, thì hãy cùng Cooky vào bếp nào.', 10, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55422, 'Lục Tàu Xá', 'Lục tàu xá hay còn được gọi là lục đậu sa là món chè thơm ngon, bổ dưỡng, được rất nhiều người yêu thích. Món chè có vị ngọt thanh của nước dừa tươi, đường phèn, vị bùi bùi của đậu xanh, hạt sen chín nhừ, vị đăng đắng nhẹ và mùi thơm từ vỏ quýt, thêm chút béo ngậy của nước cốt dừa sẽ khiến cho bạn càng ăn càng mê. Bạn hãy cùng Cooky vào bếp trổ tài ngay nhé!<br>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55423, 'Bánh Nhãn Ngào Đường', 'Bánh nhãn ngào đường là món ăn thơm ngon được rất nhiều người yêu thích. Những viên bánh tròn nhỏ như trái nhãn có màu vàng rất đẹp mắt. Bánh nhãn giòn xốp có vị bùi bùi của trứng và gạo nếp, kết hợp với vị ngọt ngọt của lớp đường áo bên ngoài, tất cả được hòa quyện với nhau tạo nên một món bánh thơm ngon, vô cùng hấp dẫn. Bạn hãy cùng Cooky tham khảo ngay công thức bên dưới, vào bếp và trổ tài ngay nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55424, 'Mì Tương Đen', 'Mì tương đen là món ăn nổi tiếng của Hàn Quốc. Mì tương đen là sự kết hợp độc đáo giữa mì tươi và nước sốt tương đen đậm đà được làm từ thịt và các loại rau củ. Nếu bạn là tín đồ ẩm thực Hàn Quốc thì chắc chắn không thể nào bỏ qua món này. Bạn hãy tham khảo ngay công thức bên dưới và cùng Cooky vào bếp trổ tài ngay nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55428, 'Cháo Éch Kho Hành Tỏi', 'Cháo ếch món ăn rất nhiều người ưa thích, với rất nhiều  hương vị khác nhau. Hôm nay Cooky sẽ mang đến cho bạn một trong số đó, Cháo ếch kho hành tỏi đậm đà rất hợp khẩu vị người Việt Nam. Từng miếng thịt ếch được tẩm ướp vừa ăn kho với hành tỏi thơm lừng nước sôt sánh thơm đậm đà vị mặn ngọt thêm một chút cay cay, ăn kèm với tô cháo nóng thơm mùi lá dứa, tỏi phi hứa hẹn sẽ là món ăn rất hấp dẫn không thể nào bỏ qua. Cùng Cooky đến ngay với món Cháo Ếch Kho Hành Tỏi ngon khó cưỡng này thôi nào.', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55430, 'Lẩu Thả Phan Thiết', 'Lẩu thả một món ăn xuất phát từ các ngư dân vùng biển Phan Thiết. Với cách chế biến độc đáo, bắt mắt, mang lại hương vị thơm ngon cùng cách thưởng thức mới lạ. Phần cá mai được tẩm ướp vừa phải giữ được hương vị đặc trưng ăn kèm nước chấm đặc biệt làm từ chuối sứ chín và đậu phộng cho hương vị thơm bùi, ăn kèm với xoài xanh, dưa leo chua chua cay cay rất khó cưỡng. Còn phần nước lẩu được nấu đậm đà, hành tây và thịt tôm ngọt dịu, màu sắc bắt mắt và vị chua cay nhẹ từ cà chua và sa tế. Chắc chắn món lẩu nổi tiếng mà Cooky mang đến cho bạn ngày hôm nay sẽ là món lẩu rất đặc biệt khó có thể quên.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55437, 'Coconut Macaroon', 'Có phải bạn đang nghĩ mình đang ghi sai tên món không? Không, tên của nó chính xác là Coconut Macaroon. Nó không phải là loại Macaron như mọi người vẫn thấy, đây là một loại bánh quy xuất hiện từ thế kỳ XVIII ở nước Ý. Với lớp vỏ dừa sợi bên ngoài được nướng giòn tan thơm lừng, hòa quyện cùng kết cấu dẻo dẻo, không quá khô bên trong, sẽ càng ngon hơn khi được nhúng trong dòng chocolate đăng đắng sẽ khiến bạn không cầm lòng được. Còn chờ gì mà không cùng Cooky mau mau vào bếp nào.', 5, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55438, 'Bánh Mì Đen', 'Bánh mì đen được làm từ hạt lúa mạch đen, có nguồn gốc xuất xứ từ Đức. Hạt lúa mạch đen có nhiều chất sơ hơn, nhưng lại ít calo hơn so với hạt lúa mì trắng thông thường, vì thế bánh mì đen thích hợp cho người ăn kiêng hoặc tiểu đường. Bên cạnh đó, bằng cách bọc kín lại bỏ tủ đông, bạn có thể làm số lượng lớn và lấy ra ăn dần. Với cách làm đơn giản, bạn còn chờ gì mà không cùng Cooky vào bếp để trải nghiệm một ngày làm thợ bánh mì nào. ', 10, '2022-03-22 00:00:00', 200, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55439, 'Chè Nếp Khoai Môn Lá Dứa', 'Chè nếp món chè quen thuộc với rất nhiều người với các hạt nếp được nấu chín dẻo ngọt nay có thêm khoai môn béo béo bùi bùi, nấu với nước lá dứa cho màu xanh bắt mắt, hương thơm khó cưỡng, ăn kèm nước cốt dừa đảm bảo sẽ làm bạn không thể nào cưỡng nỗi. Hãy đến ngay với công thức món chè nếp khoai môn lá dứa hấp dẫn cùng Cooky nào!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55440, 'Bánh Mì Bò Nướng Campuchia', 'Bánh Mì Bò Nướng Campuchia một món ăn nổi tiếng gần đây. Những miếng bánh mì nóng giòn thơm phức phết ngập một lớp bơ trứng gà béo ngậy ngọt thanh vị đường thốt nốt đặc trưng cảm đất nước Campuchia, ăn cùng từng xiên bò nướng đậm đà thơm mùi sả, ớt và các gia vị khác, gắp kèm theo một ít gỏi đu đủ bóp chua sẽ không hề bị ngán làm bạn chỉ muốn ăn hoài không thôi. Cùng Cooky du lịch tới Campuchia qua món ăn hết sức hấp dẫn Bánh Mì Bò Nướng Campuchia thôi nào!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55441, 'Bún Nước Lèo Sóc Trăng', 'Bún nước lèo là món ăn đặc sản ở Sóc Trăng, là sự kết hợp tinh hoa của dân tộc Kinh - Khmer - Hoa, thể hiện ở mỗi thành phần món ăn là đặc trưng của 3 dân tộc. “Linh hồn” của nồi nước lèo là sự hòa quyện giữa mắm, ngải bún và sả<span style="color: #333333; font-family: Arial, verdana, sans-serif; text-align: justify;" data-mce-style="color: #333333; font-family: Arial, verdana, sans-serif; text-align: justify;">. Các loại rau ăn kèm thông thường gồm: giá, hẹ, rau muống bào, bắp chuối bào, kèm thêm ít rau thơm cho dậy hương vị. Để tô bún thêm phần "tròn trịa" thì phần nguyên liệu ăn kèm có: thịt cá lóc, heo quay và tôm luộc làm cho tô bún thêm phầm hấp dẫn và ngon hết chỗ chê.  </span>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55442, 'Bánh Lá Gai', 'Bánh gai thơm thơm nhân đậu xanh với chút dừa bào sợi, lớp vỏ ngoài đen bóng và thơm mùi lá gai đặc trưng. Bánh có độ ngọt vừa phải, được bọc trong lớp lá chuối đẹp mắt, chắc hẳn ai từng nếm thử sẽ nhớ mãi mùi vị này. ', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55451, 'Bánh Pizza Bằng Chảo', 'Đôi khi thèm pizza mà ngại ra hàng hay không order được thì chúng ta có thể làm theo cách này. Cực dễ làm, đế bánh mềm, thơm ngon.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55453, 'Bánh Mì Sandwich Bơ Chuối', 'Bánh mì sandwich bơ chuối là món ăn vặt vô cùng hấp dẫn, và còn khá dễ làm ngay tại nhà bởi những nguyên liệu đơn giản.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55454, 'Bánh Củ Cải', 'Bánh Củ Cải một món ăn truyền thống nổi tiếng của người Hoa. Với cách làm đơn giản nguồn nguyên liệu dễ tìm và dễ dàng và không tốn nhiều chi phí. Bánh củ cải có thể dùng ăn ngay hoặc chế biến thành nhiều món ngon khác như áp chảo hay chiên trứng. Hương vị rất dễ ăn không hề bị hăng mùi củ cải. Hãy bắt tay làm món bánh hấp dẫn này cùng Cooky nào!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55455, 'Bánh Tako Thái Lan', 'Đất nước Thái Lan nổi tiếng với vô số món ăn đường phố đa dạng và các loại bánh ngọt ở nơi này cũng luôn có một sức hút, hấp dẫn nhất định. Bánh Tako Thái là món bánh nổi tiếng được làm bằng nước cốt dừa béo thơm, thoang thoảng chút hương lá dứa cùng hương vị ngọt ngào, và món này cũng có nhiều cách chế biến đem lại nhiều màu sắc và hương vị sẽ cho bạn một trải nghiệm rất thú vị về các món bánh ngọt của xứ sở chùa vàng. Hãy cùng Cooky khám phá những cách làm đặc biệt của món Bánh Tako Thái thôi nào!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55457, 'Trứng Cuộn Phô Mai Sợi', 'Trứng rán thì quá thông thường, nếu lần nào cũng làm giống nhau sẽ chán và ngấy. Thế nhưng chỉ cần biến tấu một chút là bạn đã có ngay món trứng cuộn phô mai ngon hết ý.', 3, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55458, 'Lẩu Bao Tử Hầm Tiêu', ' Lẩu bao tử hầm tiêu xanh với vị cay ấm và giòn sần sật đem lại cảm giác thú vị và ấm bụng cho cả nhà vào những ngày trời se lạnh hay mưa phùn. Vị cay cay, thơm nức của tiêu xanh kết hợp với bao tử dai giòn sần sật kèm nước lẩu ngọt thanh nóng hổi chắc chắn sẽ khiến gia đình bạn thích thú cho xem. <br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55459, 'Sandwich Phô Mai', 'Sandwich là một loại bánh mì quen thuộc của người người nhà nhà. Từ người lớn tới trẻ nhỏ, ai cũng đã từng ăn ít nhất một lần. Nếu bạn là một tín đồ của sandwich, sao bạn không thử phiên bản nâng cấp "Sandwich Phô Mai". Ổ bánh mì sandwich vỏ cứng chắc, phần ruột mềm mịn, ẩm ướt, hòa quyện cùng vị phô mai beo béo sẽ khiến bạn không ngừng thèm thuồng đó, Còn chờ gì mà không đep tạp dề vào bếp cùng Cooky nào!', 6, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55463, 'Chả Hoa Ngũ Sắc Phô Mai', 'Cùng trổ tài sáng tạo món Chả Hoa Ngũ Sắc Phô Mai thơm ngon đầy dinh dưỡng nào! Kết hợp với phô mai Con Bò Cười mềm mịn cùng vitamin A giúp sáng mắt và Canxi & Vitamin giúp hệ xương chắc khỏe, nhà ta ăn ngon lại thêm bổ dưỡng. Mềm ngon bên ngoài, thơm mịn phô mai kết hợp cùng nhân trứng muối, vô cùng hấp dẫn nha!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55464, 'Chè Củ Sen Đường Phèn', 'Công thức món chè giải thèm siêu đơn giản và tiện lợi cho những nàng quá bận rộn và không-khó-tính. ', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55467, 'Nước Lá Tía Tô', 'Nước lá tía tô tốt cho khớp, hệ tiêu hóa, ngoài ra còn làm giảm thâm sạm nêu uống thường xuyên. Vị ngọt thanh đầu lưỡi, kết hợp hương thơm của lá tía tô và quất sẽ làm cho bạn có một cảm giác mát mẻ, sảng khoái vô cùng.', 5, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55471, 'Thịt Nướng Phô Mai Kiểu Thái', 'Món Thịt nướng phô mai được các bạn nhỏ yêu thích bởi sự hòa quyện hoàn hảo giữa vị ngọt của thịt bò và độ béo ngậy của phô mai. Hãy cùng khám phá cách làm món Thịt nướng phô mai kiểu Thái tuyệt đỉnh tại nhà qua công thức dưới đây nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55472, 'Bánh Mì Nướng Tôm Phô Mai', 'Bánh mì nướng tôm phô mai là món ăn đơn giản, thời gian chế biến nhanh. Bạn có thể chuẩn bị vào bữa sáng để thay đổi khẩu vị cho cả nhà. Bánh mì giòn tan với vị thơm của phô mai Con Bò Cười, vị ngọt của tôm và các loại rau củ là sự kết hợp hoàn hảo, thêm 1 ly sữa hoặc 1 ly nước ép là bữa sáng của bạn đã đủ chất rồi!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55473, 'Mì Xào Hàn Quốc', 'Mì xào, mì trộn là một trong những món ăn được nhiều người yêu thích cho những ngày mưa hay không có nhiều thời gian, lười nấu nướng. Với món mì xào, bạn có thể kết hợp với nhiều loại rau củ, thịt khác nhau để món ăn không nhàm chán và đầy đủ chất dinh dưỡng. Dưới đây là cách làm mì xào Hàn Quốc chuẩn vị với dầu hào mà bạn có thể thêm vào danh sách những món ăn đơn giản, dễ làm tại nhà.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55477, 'Canh Chua Tôm Bông So Đũa', 'Nhắc đến bông so đũa, ta liền nghĩ ngay đến món canh chua tôm nấu cùng loại hoa này - một trong những món ăn đặc trưng của miền đất Nam bộ. Với những nguyên liệu đơn giản, chế biến nhanh gọn, bạn sẽ có ngay một tô canh chua nóng hổi, thơm nức trong tích tắc. Cùng Cooky thực hiện nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55478, 'Bánh Tro', '<span id="docs-internal-guid-f98c1cca-7fff-0a93-1d91-f26ceaa09195"><p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 10pt; font-family: Arial; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">Bánh tro hay bánh gio là một món bánh truyền thống không thể thiếu trong dịp Tết Đoan Ngọ. Nhìn có vẻ phức tạp nhưng món bánh này hoàn toàn có thể làm tại nhà với những nguyên liệu dễ tìm. Bạn có thể sử dụng nước tro tàu để thay cho cách lấy tro từ các loại lá cây. Cùng Cooky thực hiện nhé!</span></p><div><span style="font-size: 10pt; font-family: Arial; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;"><br></span></div></span>', 5, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55479, 'Bắp Xào Nước Súp', 'Bắp xào là một món ăn vặt được nhiều người yêu thích. Cùng Cooky nâng cấp món ăn quen thuộc này lên một phiên bản hấp dẫn hơn với nước súp được hầm từ xương, thơm mùi bơ, ăn kèm cùng nhiều loại topping ngon miệng như thịt, tép và tóp mỡ. Chắc chắn công thức này sẽ không làm bạn thất vọng.', 5, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55480, 'Cải Thảo Cuộn Ngũ Sắc', 'Cải thảo cuộn ngũ sắc như một bông hoa bắt mắt với sự kết hợp của nhiều loại rau củ khác nhau. Món ăn trông cầu kỳ nhưng cách thực hiện rất đơn giản. Hơn hết, nhờ sử dụng phương pháp hấp mà độ ngọt của nguyên liệu được giữ nguyên. Cùng Cooky trổ tài để các thành viên trong gia đình thưởng thức nhé!', 6, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55481, 'Bún Gạo Xào Hải Sản', 'Vào những ngày chán cơm, sao bạn không thử trổ tài bằng món bún gạo hải sản với những nguyên liệu đơn giản và cách làm cũng rất nhanh chóng. Từ những gợi ý của Cooky, bạn sẽ có đĩa bún xào thành phẩm không chỉ bắt mắt mà còn rất ngon miệng với sợi bún tơi dẻo, không bị nát dính. Cùng Cooky thực hiện nhé!<br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55483, 'Bánh Tét Chuối', 'Bánh tét nhân chuối là một biến tấu của người miền Nam từ món bánh tét truyền thống. Bánh có lớp nếp mềm dẻo, thơm mùi nước cốt dừa và lá dứa. Phần chuối bên trong có màu đỏ hồng, tươm mật rất bắt mắt. Bất cứ khi nào, bạn thèm một đòn bánh tét chuối dẻo ngọt đều có thể tự tay thực hiện với cách làm đơn giản từ Cooky nhé! <br data-mce-bogus="1">', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55484, 'Bí Nhồi Chả Nấm Chay', 'Bí xanh có vị ngọt vừa phải, tính mát thích hợp để chế biến nhiều món ăn cho bữa cơm gia đình. Vào những ngày ngán thịt cá, bạn có thể thử công thức bí nhồi chả nấm chay vừa ngon miệng vừa nhiều dinh dưỡng. Cùng Cooky thực hiện nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55485, 'Canh Đu Đủ Đuôi Heo', 'Canh đu đủ đuôi heo thanh ngọt, giàu dinh dưỡng những rất dễ thực hiện với 2 nguyên liệu đơn giản, dễ tìm. Cùng Cooky bắt tay làm món canh này cho gia đình cùng thưởng thức nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55486, 'Bánh Vá', 'Bánh vá là món ăn đặc sản nổi tiếng ở chợ Giồng ở vùng Gò Công, Tiền Giang. Bánh vá còn được gọi là bánh giá hay bánh vá chợ Giồng, bởi vì cái vá là vật dụng để định hình cho món bánh này. Bánh vá vòng óng giòn rụm, với bên trong đủ thứ nhân thịt đậm đà, ăn kèm với các loại rau sống tươi mát, chấm với nước mắm chua ngọt thì còn gì bằng. Bạn hãy cùng Cooky tham khảo ngay công thức bên dưới nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55487, 'Rau Câu Dừa Phô Mai', 'Rau câu dừa phô mai là ăn được nhiều người yêu thích, từ trẻ nhỏ đến người lớn. Sự kết hợp giữa nước dừa tươi ngọt thanh mát cùng với phô mai béo ngậy hòa quyện với nhau giúp tạo nên một món tráng giải nhiệt hoàn hảo cho những ngày trời oi bức. Bạn hãy lưu lại ngay công thức bên dưới của Cooky để làm ngay món ngon này nhé.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55488, 'Há Cảo Phô Mai', 'Há cảo là món ăn được rất nhiều người yêu thích, với loại nhân phổ biến như nhân tôm, thịt. Hôm nay, Con bò cười sẽ bổ sung thêm một loại nhân há cảo mới toanh, đó là há cảo nhân thịt xào cay kết hợp với phô mai béo ngậy. Phần vỏ há cảo chín mềm, giòn và dai nhẹ, phần nhân thịt xào cay cùng phô mai bên trong đậm đà, ăn vào cay cay, bùi bùi, béo béo. Bạn hãy tham khảo ngay công thức bên dưới đây nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55489, 'Bánh Tart Trứng', 'Bánh tart trứng mềm mịn, thơm ngon là món ăn rất được nhiều người yêu thích. Với phần vỏ ngàn lớp giòn rụm cùng với phần nhân mềm và thơm, ăn vào thấy ngọt và béo ngậy. Bạn hãy tham khảo ngay công thức bên dưới để làm ngay món bánh tart trứng cho cả gia đình cùng thưởng thức nhé!', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55490, 'Trà Trái Cây Nhiệt Đới', 'Trà trái cây nhiệt đới là món thức uống giải nhiệt, dễ uống với các loại nguyên liệu tự nhiên rất tốt cho sức khỏe. Với sự kết hợp giữa vị chua, chát nhẹ của trà, vị chua chua, ngọt ngọt từ các loại trái cây nhiệt đới sẽ giúp bổ sung nhiều Vitamin giúp làm đẹp da. Bạn hãy tham khảo ngay công thức bên dưới để pha ngay nhé!', 4, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55491, 'Cá Hồi Ngâm Tương', 'Nếu bạn là "fan cứng" của các món sashimi thì có lẽ không nên bỏ qua món cá hồi ngâm tương. Cá hồi được ngâm với nước tương Maggi đậm đà hương vị, khi ăn bạn sẽ cảm nhận được bị béo ngậy tự nhiên của cá, hương vị đậm đà và thơm ngon của nước tương. Bạn hãy cùng Maggi tham khảo công thức bên dưới và vào bếp trổ tài ngay nhé!', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55492, 'Bánh Tráng Nướng Chảo', ' ', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55493, 'Bánh Sandwich Kẹp Thịt', 'Bánh sandwich kẹp thịt thơm ngon, bổ dưỡng là món ăn thích hợp cho những bữa sáng đầy dinh dưỡng. Bánh sandwich có lớp vỏ giòn giòn, thơm mùi bơ, kẹp ở giữa là các loại rau cải tươi mát, phần thịt mềm mọng, đậm đà hương vị. Bạn hãy cùng Maggi tham khảo ngay công thức bên dưới và vào bếp trổ tài ngay nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55494, 'Xíu Mại Chay', 'Xíu mại chay là món ăn thơm ngon, đơn giản, dễ làm mà vẫn đảm bảo đầy đủ chất dinh dưỡng cho cơ thể. Những viên xíu mại mềm thơm, bùi béo, nước sốt cà chua sóng sánh, đậm đà sẽ khiến cho bạn càng ăn càng mê. Bạn hãy cùng Cooky vào bếp và trổ tài ngay nhé!', 3, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55495, 'Bánh Mì Mè Đen Hàn Quốc', 'Bánh mì mè đen Hàn Quốc là một món ăn vặt rất được ưa chuộng trong giới trẻ hiện nay. Bánh mì giòn bên ngoài, dẻo bên trong, hòa với mùi mè đen rang thơm lừng sẽ làm bạn ăn không ngừng đấy. Bên cạnh đó, bánh mì mè đen lại ít béo, giàu dinh dưỡng, rất có nhiều lợi íchh cho sức khỏe. Còn chờ gì mà không cùng Cooky vào bếp nào!', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55496, 'Đậu Hủ Kho Tương Hột', 'Đậu Hủ Kho Tương Hột một món chay ngon ăn rất thích hợp cho những ngày ăn chay hoặc ăn trong bữa cơm gia đình hàng ngày. Đậu hủ được chiên vàng kho với tương hột nêm nếm đậm đà vừa ăn, thơm mặn vị đặc trưng của đậu tương, rất phù hợp để ăn kèm cơm nóng và rau củ luộc. Nào cùng đến với công thức món Đậu Hủ Kho Tương Hột  của Cooky ngay thôi!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55497, 'Giò Heo Muối Chiên Giòn', 'Giò heo muối chiên giòn món ăn yêu thích của rất nhiều người, thường thấy ở các quán ăn hay nhà hàng. Với lớp da phồng giòn rụm mà phần thịt lại dai ngon vô cùng, thấm đều hương vị của sả ớt và muối ướp, ăn kèm rau thơm uống thêm ly bia thì phải nói là hết sẩy. Vậy hôm nay hãy cùng Cooky khám phá cách làm món ăn có phần hơi phức tạp nhưng kết quả đem lại chắc chắn sẽ trên cả mong đợi.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55498, 'Cánh Gà Chiên Muối Tiêu', 'Cánh Gà Chiên Muối tiêu món ăn được chế biến đặc biệt phần cánh tỏi gà được chế biết độc đáo rất bắt mắt và tiện khi ăn. Với lớp vỏ vàng giòn áo một lớp bơ và muối tiêu béo béo cùng vị nồng nồng vừa phải của ớt chuông, khi cắn vào phần thịt gà mềm tan không khô dai được tẩm ướp ngũ vị hương và muối tiêu thơm phức. Hãy trỗ tài ngay với công thức Cánh Gà Chiên Muối Tiêu dễ làm đến từ Cooky nào!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55499, 'Gỏi Xoài Ếch Chiên Giòn', 'Gỏi Xoài Ếch Chiên Giòn một món ăn hấp dẫn kết hợp giữa thịt ếch chiên vàng giòn bên ngoài mềm thơm bên trong và gỏi xoài chua chua cay cay, cùng đậu phộng rang thơm thơm bùi bùi hứa hẹn là sẽ là món lai rai tới bến. Chần chờ gì mà không thực hiện ngay món này để lai rai dịp cuối tuần với anh em bạn bè nào, bạn  chỉ cần vào bếp công thức đã có Cooky lo!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55500, 'Trứng Bọc Thịt Chiên Xù', 'Trứng bọc thịt chiên xù sẽ là một món ăn mới mẻ bắt mắt và siêu ngon đổi gió cho bữa cơm gia đình bạn. Với lớp vỏ giòn rụm bọc một lớp thịt dai ngon được tẩm ướp đậm đà với tương ớt Vedan cay ngọt đậm vị, cuối cùng đến lớp nhân thơm bùi vị trứng gà, khi thưởng thức sẽ cho bạn một trải nghiệm về hương vị mới lạ thú vị khó thể nào cưỡng lại. Đặc biệt món ăn sẽ ngon hơn bao giờ hết khi chấm cùng tương ớt Vedan và các loại rau. Hãy cùng trổ tài nội trợ với món ăn hấp dẫn này thôi nào!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55501, 'Ba Chỉ Cuộn Sả', 'Thịt Ba Chỉ Cuộn Sả nướng món ăn đầy hấp dẫn và bắt mắt với hương vị dân dã. Vị sả thơm lừng, ớt xanh cay cay, cùng mùi thơm của các hương liệu tự nhiên đặc trưng. Món ăn rất thích hợp cho những buổi tụ họp bạn bè lai rai hay đi giả ngoại. Cùng trổ tài làm món Thịt Ba Chỉ Nướng Sả cùng Cooky siêu ngon bắt mắt này cho mọi người trầm trồ thôi nào!', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55503, 'Bánh Mì Nướng Tôm Phô Mai', 'Bánh mì nướng tôm phô mai là một món ăn đơn giản, dễ làm nhưng cách làm siêu đơn giản. Đây chẳng khác gì phiên bản mini của một chiếc bánh pizza sang chảnh ngoài nhà hàng sang trọng. Bánh mì giòn tan kết hợp với tôm tươi chắc thịt, cùng một lớp bơ tỏi thơm lừng bên dưới và một lớp phô mai béo ngậy kéo sợi bên trên, sẽ làm ngất ngây mọi thành viên trong gia đình của bạn đấy. Còn chờ gì mà không vào bếp với Tupper Ware nào.', 6, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55504, 'Cà Phê Bọt Biển Sầu Riêng', 'Cà phê bọt biển là thức uống đang nổi tiếng gần đây. Thức uống “sành điệu như hàng hiệu” này không chỉ “làm mưa làm gió” khắp xứ sở kim chi mà còn khiến giới trẻ Việt Nam “phát sốt”. Ngoài cách pha truyền thống, Cooky sẽ mang đến cho bạn một món sáng tạo mới, đó là cà phê bọt biển sầu riêng thơm béo. Nếu bạn là tín đồ yêu cà phê, thích tự tay khám phá các món thức uống mới, đừng bỏ qua cách làm cà phê bọt biển sầu riêng ngon khó cưỡng chỉ trong vài phút dưới đây nhé!', 1, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55505, 'Trứng Ngâm Xì Dầu', 'Vị ngọt, mặn hòa quyện món này ăn kèm cơm nóng hoặc mì sẽ khiến bạn ấn tượng.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55506, 'Chè Bí Đỏ Nước Cốt Dừa', 'Chè bí đỏ nước cốt dừa là món ăn vô cùng ngon miệng và dễ thực hiện. Trong công thức này, Cooky sẽ hướng dẫn bạn tạo hình những viên trè hình trái bí để món ăn trông bắt mắt hơn nhé! Cùng Cooky thực hiện nào.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55507, 'Rau Câu Trái Cây Phô Mai', 'Rau câu là một món tráng miệng không thể thiếu trong những ngày nóng bức. Kết hợp thêm với nhân trái cây ngòn ngọt và độ béo ngậy của phô mai càng làm món ăn ngon miệng hơn. Tạo hình thạch bi cũng rất hấp dẫn với trẻ nhỏ. Rau câu viên phô mai trái cây có thể thưởng thức ngay hoặc dùng chung với sữa tươi, sữa dừa hoặc trà sữa đều ngon. Cùng Cooky thực hiện nhé!', 6, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55508, 'Tôm Rim Nước Cốt Dừa', 'Tôm rim nước cốt dừa là một món khá quen thuộc với ẩm thực châu Á. Khi đến với châu Âu, nó được thay đổi một chút để hợp với khẩu vị người địa phương hơn. Nếu bạn muốn có gì đó mới lạ, hãy cùng Latino Bella vào bếp nấu một phần tôm rim nước cốt dừa kiểu Âu nào, món ăn ngon tuyệt vời này sẽ không làm bạn thất vọng đâu. ', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55512, 'Cơm Gà Xối Mỡ Sốt Mắm Tỏi', 'Cơm Gà Xối Mỡ Sốt Mắm Tỏi với từng chiếc đùi gà vàng giòn áo lớp sốt tỏi thơm lừng, cắn vào từng sớ thịt dai ngon không hề sơ cứng dậy lên mùi hương đậm đà của các hương liệu, cùng phần cơm vàng tươi tơi xốp đậm đà không hề thua kém. Chắc chắn bạn sẽ không muốn để sót lại gì trên đĩa cơm. Hãy đến với công thức Cơm Gà Xối Mỡ Sốt Mắm Tỏi không hề thua gì hàng quán mà lại đảm bảo sức khỏe từ Cooky thôi nào.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55513, 'Trân Châu Viên Phô Mai', 'Trân châu viên phô mai là một trong những món topping cho trà sữa được giới trẻ yêu thích nhất hiện nay. Cách làm siêu dễ, màu sắc bắt mắt, và siêu cuốn khi uống cùng trà sữa. Nếu bạn là một người đam mê trân châu phô mai, sao không cùng Con Bò Cười vào bếp để chuẩn bị thật nhiều trân châu cho bữa tiệc trà sữa tiếp theo của mình nào.', 6, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55514, 'Opera Cake', 'Opera là một loại bánh ngọt có nguồn gốc từ Pháp. Bánh Opera bao gồm 7 lớp, được làm từ cốt bánh hạnh nhân, chocolate, syrup cà phê và kem bơ cà phê, tượng trưng cho 7 nốt nhạc. Trước đây, Opera thường xuất hiện trong các bữa tiệc hoặc tiệm bánh lớn vì hình dáng sang trọng, và mùi vị quyến rũ của nó. Nếu bạn muốn một phiên bản homemade của Opera cho riêng mình, vậy thì mau cùng Cooky vào bếp nào!', 6, '2022-03-22 00:00:00', 180, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55515, 'Hải Sản Sốt Hollandaise', 'Hải sản sốt Hollandaise thơm ngon, bổ dưỡng và lạ miệng là món ăn rất thích hợp cho gia đình cùng nhau thưởng thức vào những dịp đặc biệt. Hải sản áp chảo với dầu Olive Latino Bella giúp giữ được hương vị ngọt tự nhiên của thịt, ăn kèm với sốt Hollandaise sánh mịn, vị chua thanh là chuẩn vị nha hàng. Bạn hãy lưu lại ngay công thức bên dưới đây nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55517, 'Mì Ý Gà Nấu Olive', 'Mì ý gà nấu olive là một món mì ý dễ ăn, đơn giản, hấp dẫn, lại đầy đủ chất dinh dưỡng. Sợi mì dai dai chín vừa tới, được đảo đều với oliu, cà chua, hành tây, ớt chuông, bông cải, ức gà áp chảo. Tất cả kết hợp lại tạo thành một bức tranh sống động về miền Nam nước Ý. Nếu bạn là một tín đồ trung thành của ẩm thực Ý, bạn sẽ không thể nào bỏ qua được món mì ý gà nấu olive này đâu. Còn chờ gì mà không cùng Latino Bellla vào bếp nào. ', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55519, 'Chè Thốt Nốt Dừa Dằm', '<span style="color: #333333; font-family: arial; font-size: 10pt; white-space: pre-line;">Chè dừa dằm là món giải khát hạ nhiệt trong những ngày nắng nóng. Cùng Cooky biến tấu món chè này với các nguyên liệu như thốt nốt, hạt đác, các loại thạch để tăng thêm hương vị nhé. Bắt tay thực hiện món chè thốt nốt dừa dằm nào.</span>', 5, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55521, 'Bánh Canh Bột Gạo Nước Cốt Dừa', ' Bánh canh bột gạo nước cốt dừa là một món ăn quen thuộc của người dân sông nước, bánh canh nước cốt dừa mang đậm hương vị tình cảm quê nhà. Cọng bánh canh được làm từ bột gạo mềm, dai được nấu cùng với nước cốt dừa thơm bùi, beo béo xen lẫn vị ngọt từ tôm tươi và thịt heo làm cho món ăn trở nên hài hòa, quyến rũ. Ngày mưa mà nấu nồi bánh canh bột gạo nước cốt dừa thơm phức thì ngon đến nao lòng. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55522, 'Lòng Rán Xóc Tỏi Ớt', 'Lòng rán xóc tỏi ớt có vị dai giòn sần sật, cay cay đậm đà lại thơm thơm vị tỏi ớt, rất thích hợp cho những buổi nhậu hoặc bữa cơm gia đình ấm cúng. Ăn thử một miếng thôi là nghiện ngay vì hương vị lạ miệng, dùng kem với rau củ tươi mát thì chỉ muốn ăn hoài thôi. ', 4, '2022-03-22 00:00:00', 50, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55523, 'Súp Hải Sản Chua Cay', 'Món súp hải sản chua cay là món ăn khá thú vị, bởi tô súp nóng hổi thơm lừng và sánh đặc sẽ khiến bạn ấm bụng hơn đảm bảo cung cấp năng lượng cho bạn hoạt động cả ngày luôn. Với cách chế biến rất dễ dàng, và nguyên liệu dễ tìm, thời gian nấu cũng nhanh nên nó rất phù hợp cho bữa ăn gia đình hoặc các bữa tiệc nhỏ mà bạn không có nhiều thời gian chuẩn bị.<br>', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55524, 'Salad  Phô Mai Tôm Nướng', 'Salad phô mai tôm nướng thích hợp cho những ngày oi bức, với cách làm không quá phức tạp nhưng vẫn mang lại hương vị đặc trưng của món ăn. Sự kết hợp lạ miệng của tôm nướng và trái cây cùng phần sốt kem cam hấp dẫn tạo nên một món salad thơm ngon khó cưỡng. Cùng Con Bò Cười vào bếp và trổ tài thực hiện món salad phô mai tôm nướng bổ dưỡng và mới lạ này nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55525, 'Chè Lam', 'Chè lam là một món đặc sản của tỉnh Thanh Hóa. Trước đây, món ăn thường được làm vào các dịp lễ tết để cúng tổ tiên  Ngày nay chè lam như một món ăn vặt đươc ăn quanh năm. Chè được làm từ bột gạo nếp dẻo dai cùng hương vị thơm lừng của gừng, đường thốt nốt, mật mía, đậu phộng. Chỉ cần một phần chè làm và một ấm chè xanh là đủ để ta nhâm nhi trong một chiều thu mát mẻ. Còn chờ gì mà không cùng Cooky vào bếp làm một phần chè lam nào.', 4, '2022-03-22 00:00:00', 240, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55527, 'Mực Nhồi Chiên Nước Mắm', 'Mực chiên xốt mắm tắc là món ăn tuy là mà quen, với một chút biến tấu là đã có ngay món ăn yêu thích cho cả gia đình, cũng không tốn quá nhiều thời gian của các chị em nội chợ. Mực mềm những vẫn giữ được độ tươi ngon của hải sản, được áo đều lớp xốt mắm tắc vàng óng chua chua, mặn mặn. Phần nhân thịt béo béo, thấm đều gia vị làm cho món ăn thêm phần hấp ngon miệng và hấp dẫn hơn. ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55528, 'Phở Chua', 'Phở chua là món ăn đặc sản Lạng Sơn, không thể thiếu trong các đám cưới ở vùng núi xứ Lạng. Khi ăn phở chua Lạng Sơn sẽ có vị giòn bùi của khoai, lạc, mùi thơm ngậy của thịt xá xíu, vị chua của giấm và cay cay của ớt rất đặc biệt. Món này đã trở thành niềm tự hào, là thứ đặc sản ngon nức tiếng theo chân người xứ Lạng đi khắp nơi.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55529, 'Lẩu Gà Hầm Sả', 'Lẩu gà hầm sả là một món lẩu thơm ngon, đậm đà hương vị và cách chế biến lại đơn giản không quá phức tạp. Thịt gà mềm nhưng vẫn giữ được độ dai vốn có, đậm đà gia vị, thơm thơm mùi sả,  nước dùng thanh, ngọt được hầm từ gà và các loại rau củ. Với vị thơm lừng của món ăn và vị ngọt ngon của nước dùng chắc chắn sẽ khiến bạn hài lòng.', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55530, 'Khoai Lang Ngào Đường Thốt Nốt', 'Khoai lang ngào đường thốt nốt là món ăn vặt không tốn quá nhiều thời gian mà vẫn đảm bảo ngon miệng và dinh dưỡng cho cả nhà. Khoai lang bùi bùi được chiên vàng giòn thơm phúc, áo bên ngoài lớp đường thốt nốt nước cốt dừa béo ngậy không quá ngọt gắt, làm cho món ăn trở nên thú vị và hấp dẫn hơn đối với các bé nhà mình. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55531, 'Khoai Lang Trộn Dừa', '<span style="font-family: -webkit-standard; font-size: medium;">Khoai lang trộn dừa, món ăn vặt được chế biến siêu đơn giản với các nguyên liệu dễ tìm. Món ăn có chút biến tấu từ khoai lang luộc đơn điệu, làm cho món ăn thêm lôi cuốn và hấp dẫn hơn. Khoai lang mềm dẻo, được trộn chung với với cơm dừa ngay giòn béo làm cho món ăn thêm hài hòa và độc đáo hơn. </span>', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55534, 'Chè Sake Bạch Quả', 'Sake dẻo bùi kết hợp cùng bạch quả nhẫn đắng ngập trong nước cốt dừa béo thơm tạo nên một món chè vô cùng ngon miệng. Với những nguyên liệu quen thuộc và trong những ngày thời tiết oi nóng, bạn có thể thử ngay món chè sake bạch quả để thưởng thức. Cùng Cooky thực hiện nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55541, 'Chè Đu Đủ Ngân Nhĩ', 'Chè đu đủ ngân nhĩ ngọt mát nhẹ nhàng, vị ngọt thanh của đu đủ, ngân nhĩ dai giòn được nấu cùng với đường phèn giúp tạo nên hương vị rất mới lạ, bên cạnh đó còn bổ sung cho cơ thể nhiều vitamin và khoáng chất rất tốt cho sức khỏe. Đây là món chè lý tưởng vừa giúp giải nhiệt, vừa giúp bổ sung các chất dinh dưỡng cho cơ thể, bạn chần chừ gì mà không cùng Cooky vào bếp và trổ tài ngay nào!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55542, 'Ba Rọi Rang Sả Ớt', 'Ba rọi rang sả ớt là món ăn thơm ngon, giúp đổi mới cho những bữa cơm gia đình thêm phần mới lạ, hấp dẫn. Phần thịt ra rọi được rang vàng giòn, xào cùng với sả ớt làm dậy lên hương vị rất đặc trưng, thơm nứt mũi, ăn cùng cơm trắng nóng hôi hổi thì không còn gì tuyệt vời hơn. Bạn cùng tham khảo cách làm món ba rọi rang sả ớt mà Cooky chia sẻ ngay sau đây nhé.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55543, 'Cá Kèo Chay Kho Tiêu', 'Việc ăn chay của bạn sẽ trở nên hấp dẫn và ngon miệng hơn với món cá kèo chay kho tiêu, đơn giản, dễ làm. Phần cá bên trong được làm từ đậu hủ béo bùi được bọc lớp rong biển bên ngoài dai dai, phần nước sốt sền sệt, đậm đà, xen lẫn chút cay nồng của tiêu, ăn cùng cơm nóng thì hết sảy. Bạn hãy cùng Cooky tham khảo ngay công thức bên dưới đây nhé!<br /><br />', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55544, 'Ba Chỉ Kho Măng Tươi', 'Ba chỉ kho măng tươi là món ăn quen thuộc trong những bữa cơm gia đình. Phần thịt ba chỉ được nấu chín mềm, phần mỡ béo béo, tan ngay trong miệng, kho cùng với măng tươi giòn giòn, tất cả hòa quyện với nhau tạo nên một hương vị rất đậm đà, ăn cùng với cơm nóng hổi thì còn gì bằng. Bạn hãy cùng Cooky lưu lại ngay công thức bên dưới đây nhé!', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55545, 'Bốn Vị Chả Giò', 'Để tiết kiệm tối đa thời gian nấu nướng của chị em, G-Kitchen cho ra mắt chả giò với bốn hương vị mới lạ như Chả giò thịt đặc biệt, Chả giò thịt nấm tam vị, Chả giò kiểu Hồng Kông và Chả giò thịt hạt sen, cho bạn tha hồ lựa chọn. Chả giò từ G là sự kết hợp hài hòa giữa các nguyên liệu truyền thống kết hợp với thịt heo sạch 3F Plus cùng với lớp da xốp vàng giòn, ít thấm dầu tạo nên hương vị cuốn chả hấp dẫn như nhà làm.', 4, '2022-03-22 00:00:00', 10, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55547, 'Bông Bí Nhồi Chả Cá Chiên Giòn', 'Bông bí không chỉ là một loài hoa có thể ăn được mà còn rất ngon miệng khi được chế biến thành nhiều món khác nhau. Trong số đó phải kể đến bông bí nhồi chả cá thác lác. Bông bí ngòn ngọt, được chiên giòn rụm quyện cùng phần chả cá dai dai, chấm ngập tương ớt là một sự kết hợp hài hòa đầy hương vị. Cách làm cực kỳ đơn giản. Cùng Cooky thực hiện nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55548, 'Đậu Bắp Xào Tỏi Sốt Thịt Bằm', 'Đậu Bắp Xào Tỏi Sốt Thịt Bằm một món ăn rất phù hợp cho bữa cơm gia đình đầm ấm với cách chế biến đơn giản mà hương vị lại thơm ngon rất bắt cơm. Đậu bắp được xào vừa chín giữ được màu sắc và giá trị dinh dưỡng cao áo lớp sốt thịt đậm đà thơm ngon ăn hoài không ngán, món ăn sẽ giúp gia đình bạn bộ sung thêm một lượng rau củ lớn rất có lợi cho sức khỏe. Bắt tay làm ngay món Đậu Bắp Xào Tỏi Sốt Thịt Bằm với công thức từ Cooky ngay hôm nay cho bữa cơm gia đình bạn thôi nào.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55550, 'Cơm Hấp Thập Cẩm Kiểu Nhật', 'Cơm Hấp Thập Cẩm Kiểu Nhật sẽ mang đến cho gia đình bạn một bữa ăn chỉn chu trong những ngày bận rộn. Tất cả những gì bạn cần là 15 phút sơ chế nguyên liệu cho tất cả vào nồi cơm và bấm nút. chỉ sau chưa đầy 30 phút một bữa ăn thơm ngát hương gạo chín hương nước tương Kikkoman nhẹ nhàng đặc trưng, cùng rau củ, và thịt được hấp chín đảm bảo được dưỡng chất. Hãy lưu lại cách làm món Cơm Hấp Kiểu Nhật này dánh cho những ngày bận rộn nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55551, 'Súp Tôm Viên Phô Mai', 'Súp Tôm Viên Phô Mai một món súp với công thức khá quen thuộc nhưng điều đặc biệt trong món ăn này là những viên tôm nhỏ nhắn hồng hồng xinh xắn trong nước súp. Được nặn từ thịt tôm tươi đảm bảo giòn sật và khi cắn vào bạn sẽ bắt gặp ngay lớp nhân phô mai được làm từ Phô Mai Con Bò Cười béo ngậy thơm ngon, hòa quyện cùng nước súp đậm đà rau cũ đảm bảo dưỡng chất cho gia đình đặc biệt rất tốt cho trẻ nhỏ. Hãy thực hiện món súp đầy màu sắc và dinh dưỡng này cho gia đình thôi nào!<br data-mce-bogus="1">', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55554, 'Chè Đậu Ván', 'Chè đậu là món ăn được nhiều người yêu thích với cách chế biến đơn giản và nguyên liệu dễ tìm. Giữa vô số các món chè quen thuộc, chè đậu ván là một gợi ý hấp dẫn mà Cooky sẽ giới thiệu đến bạn. Cách làm món chè này cũng không phức tạp nên dù bạn có vụng về một chút vẫn có thể thực hiện thành công ngày lần đầu thử sức. Cùng Cooky thực hiện nha!', 5, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55555, 'Trà Ổi Hồng Muối Tôm', 'Trà ổi hồng muối tôm là thức uống rất được yêu thích thời gian qua. Hương thơm từ trà lài và vị chua nhẹ của mứt ổi hồng và nước chanh tạo cho thức uống có vị thanh mát dễ chịu cùng màu hồng xinh xắn đặc trưng. Nếu bạn đang tìm một thức uống giải nhiệt ngày hè thì hãy cùng Cooky thực hiện ngay nhé!', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55556, 'Gỏi Ngó Sen Rau Tiến Vua', 'Tiến vua là một loại rau đặc biệt bởi độ giòn sật sật sau khi chế biến. Loại rau này thích hợp nhất là dùng làm gỏi kết hợp cùng ngó sen, rau củ, thịt và hải sản tùy thích. Món ăn thực hiện đơn giản nhưng sẽ mang lại hương vị tươi mát nhất là trong những ngày nắng nóng muốn đổi vị khi ngán cơm. Cùng Cooky bắt tay thực hiện món ăn này nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55557, 'Bánh Đúc Lạc', 'Bánh đúc lạc là món ăn dân dã, quen thuộc của người dân Bắc bộ. Chỉ với 2 nguyên liệu đơn giản là bột gạo và lạc (đậu phộng) cùng một chút thời gian nấu nướng, bạn sẽ có ngay những chiếc bánh đúc núng nính. Nhờ nước vôi trong, bánh có độ trong và độ giòn đặc trưng, quyện cùng vị béo bùi của lạc luộc rất hấp dẫn. Bắt tay thực hiện cùng Cooky nhé!', 5, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55558, 'Gà Xốt Cam', 'Nếu bạn muốn tìm một hương vị mới cho bữa cơm gia đình, gà xốt cam sẽ là một lựa chọn hoàn hảo. Những chiếc đùi gà mọng nước được bao bọc cùng dòng nước xốt đậm đà, cùng vị cam thanh thanh sẽ giúp bạn ăn nhiều mà không bị ngán. Còn chờ gì nữa mà không vào bếp và thực hiện ngay thôi!', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55559, 'Kẹo Gương', 'Kẹo gương là một món kẹo đặc sản tại tỉnh Quảng Ngãi, có hương vị ngọt ngào, béo bùi. Kẹo trong như gương, giòn tan, dễ vỡ nên được gọi là kẹo gương. Món kẹo này từng được sử dụng làm món tráng miệng cho vua chúa ngày xưa. Kẹo gương không khó làm, không cầu kỳ, bạn có thể dễ dàng tại nhà. Còn chờ gì mà không mau mau vào bếp cùng Cooky nào.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55564, 'Bún Gạo Lứt Xào Chay', 'Bún Gạo Lứt Xào Chay một món ăn chay vô cùng vừa miệng, nhiều chất dinh dưỡng từ rau củ, và lành mạnh rất phù hợp cho những người ăn kiên, không hề gây ngán. Những cọng gạo lứt được xào tơi ngon cùng rau củ thơm mùi dầu mè tốt cho sức khoẻ. Chờ gì lưu ngay công thức món ăn lành mạnh rất tốt cho sức khoẻ này từ Cooky thôi! ', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55566, 'Sinh Tố Xoài Cốt Dừa', 'Sinh tố xoài là thức uống chứa nhiều vitamin C được rất nhiều người yêu thích. Thế nhưng, bạn đã biết cách pha một ly sinh tố xoài cốt dừa mới lạ mà chuẩn ngon chưa. Hãy cùng theo dõi công thức dưới đây của Cooky để biết cách thực hiện món sinh tố xoài cốt dừa siêu ngon, chuẩn vị, uống là mê nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55570, 'Củ Cải Ngâm Tương', 'Củ cải trắng ngâm tương là món dưa muối đơn giản, ăn kèm với cháo trắng thì ngon khỏi chê. Bên cạnh đó, củ cải trắng còn là thực phẩm giàu dinh dưỡng, hỗ trợ tốt cho hệ tiêu hóa. Cooky sẽ chia sẻ công thức ngay bên dưới, chỉ cần những nguyên liệu đơn giản, cách làm nhanh gọn là bạn đã có ngay lọ củ cải ngâm tương rồi.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55571, 'Cơm Tấm Sườn Bì Chay', 'Cơm tấm sườn bì chay thơm ngon, nóng hổi với phần thịt chay được tẩm ướp vừa miệng, thêm phần bì dai dai, giòn giòn ăn kèm với mỡ hành béo béo, dưa leo giòn rụm, chan cùng với nước mắm chay sánh kẹo, đậm đà tất cả hòa quyện với nhau tạo nên một hương vị rất đặc biệt. Bạn hãy cùng Cooky vào bếp trổ tài ngay nhé!', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55572, 'Canh Bí Đao Cuộn Tôm Thịt', 'Canh bí đao cuộn tôm thịt là đơn giản, dễ nấu, bên cạnh đó còn giúp giải nhiệt cơ thể nhanh chóng. Vị ngọt thanh của bí đao cuộn với phần nhân tôm thịt đậm đà cộng với vị thanh thanh từ nước dùng, tất cả sẽ hòa quyện lại tạo nên một hương vị rất riêng cho món canh này. Bạn hãy cùng Cooky vào bếp trổ tài ngay nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55573, 'Bánh Sữa Tươi Nướng', 'Bánh sữa tươi nướng là món ăn được biến tấu độc đáo từ sữa tươi, chỉ với những nguyên liệu, cách làm đơn giản, bạn đã có mẻ bánh thơm ngon để chiêu đãi cả nhà rồi đấy. Bánh sữa tươi nướng vàng ruộm bên ngoài, bên trọng mềm mịn và ngọt ngào, cắn vào một miếng bạn sẽ cảm thấy tan chảy và ăn mãi chẳng ngừng. Hãy cùng Cooky tham khảo ngay cách làm bên dưới đây nhé!', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55574, 'Lẩu Bò Nhúng Mẻ', 'Lẩu bò nhúng mẻ có vị chua thanh của mẻ kết hợp với thịt bò mềm tan, rồi cho vào bánh tráng cuốn với bún cùng các loại rau tươi mát, chấm kèm mắm nêm, tất cả hòa quyện vào nhau mang lại cho bạn cảm giác ngon đến khó tả. Trong thời tiết se lạnh, cả gia đình cùng nhau quay quần bên nồi lẩu nóng hổi thì còn gì bằng. Bạn hãy cùng Cooky vào bếp trổ tài ngay nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55576, 'Kẹo Me Cay', 'Kẹo me cay là món ăn vặt được nhiều người yêu thích. Vị chua của me hài hòa với vị cay ngọt từ đường và bột ớt. Công thức này có thêm bột xí muội giúp món kẹo tăng thêm hương vị. Cách làm rất đơn giản, cùng Cooky thực hiện nhé!', 5, '2022-03-22 00:00:00', 90, null, null);
