insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55580, 'Salad Sốt Bơ Đậu Phộng', '<p class="MsoNormal"><p class="MsoNormal"><span lang="VI" style="mso-ansi-language: VI;" data-mce-style="mso-ansi-language: VI;">Salad bò nướng sốtbơ đậu phộng chuẩn bị nhanh chóng trong 5 phút cho bạn bữa sáng hỗ trợ hệ miễndịch. Công thức sốt biến tấu với Thực phảm bảo vệ sức khoẻ Redoxon TripleAction với bộ ba vitamin C, vitamin D và kẽm giúp bữa trưa không những ngon miệngmà còn hỗ trợ tăng sức đề kháng cho cơ thể.</span></p></p>', 2, '2022-03-22 00:00:00', 5, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55581, 'Bingsu Dưa Lưới', '<p class="MsoNormal"><span lang="VI" style="mso-ansi-language: VI;" data-mce-style="mso-ansi-language: VI;">Bingsu dưa lưới là món giải khát lý tưởng cho những bữa xế. Bingsu vừa giúp làm đẹp da, vừa có lợi cho sức khỏe ăn hoài không ngán, bên cạnh đó còn bổ sung đầy đủ vitamin cho cơ thể từ viên sủi Redoxon Triple Action hỗ trợ tăng cường sức đề kháng. Bạn hãy cùng Redoxon tham khảo ngay công thức bên dưới đây nhé!<br><br><o:p></o:p></span></p>', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55582, 'Khoai Tây Viên Phô Mai Chiên', 'Khoai tây chiên viên phô mai chiên, món ăn vặt vô cùng hấp dẫn nhất là các bé trong gia mình. Chỉ cần bẻ đôi viên khoai tây chiên xù ra, nhân phô mai bên trong dai dai, kéo sợi, béo ngậy vô cùng hấp dẫn, chẳng bé nào có thể cưỡng lại được. ', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55583, 'Jelly Yogurt', '<p class="MsoNormal"><span lang="VI" style="mso-ansi-language: VI;">Công thức sữa chua với Jelly được biến tấu từ Thực phảm bảo vệ sức khoẻ Redoxon Triple Action với bộ ba vitamin C, vitamin D và Kẽm hỗ trợ hệ miễn dịch và chống oxy hoá, hỗ trợ tăng sức đề kháng cho cơ thể.<o:p></o:p></span></p>', 2, '2022-03-22 00:00:00', 15, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55585, 'Soda Lê', '<p class="MsoNormal">Công thức soda lêvới đá viên được biến tấu từ Thực phẩm bảo vệ sức khoẻ Redoxon Triple Actionphù hợp giải khát trong giờ thư giãn. Bộ 3 vitamin C, vitamin D và kẽm có trong Redoxon sẽ giúp cho món uống không những giải khát và còn hỗ trợ tăng sức đề kháng.<br></p>', 2, '2022-03-22 00:00:00', 5, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55587, 'Trứng Hấp Nấm Rơm', 'Trứng hấp nấm rơm là một trong những món ăn phổ biến trong bữa cơm gia đình của người Việt Nam. Trứng mềm tan trong miệng cùng vị thơm giòn của nấm, kết hợp cùng thịt bằm là một sự hòa trộn hoàn hảo. Cách làm đơn giản, nhưng trứng hầp nấm rơm sẽ là một trong những sự lựa chọn tuyệt vời nhất cho bữa cơm gia đình đấy. Bạn còn chờ gì mà không mau cùng Cooky vào bếp nào!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55589, 'Kem Sorbet Cam', '<p class="MsoNormal"><span lang="VI" style="mso-ansi-language: VI;">Sorbet cam mát lạnh được biến tấu từ Thực phẩm bảo vệ sức khoẻ Redoxon Triple Action phù hợp dùng tráng miệng sau bữa ăn hoặc nhâm nhi trong lúc thư giãn với với bộ ba vitamin C, vitamin D và kẽm giúp những giây phút thư giãn hay tráng miệng mỗi ngày cũng có thể hỗ trợ tăng sức đề kháng cho cơ thể.<o:p></o:p></span></p>', 2, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55591, 'Bánh Mì Mứt Cam', '<p class="MsoNormal"><span lang="VI" style="mso-ansi-language: VI;">Bánh mì mứt cam được biến tấu từ Thực phẩm bảo vệ sức khoẻ Redoxon Triple Action cho bữa sáng ngon miệng với bộ 3 vitamin C, vitamin D và kẽm không những hỗ trợ hệ miễn dịch mà con tăng sức đề kháng cho cơ thể.<o:p></o:p></span></p>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55592, 'Salad Trái Cây', '<p class="MsoNormal"><span lang="VI" style="mso-ansi-language: VI;">Công thức Salad Trái Cây cực nhanh gọn và dễ làm dành cho những buổi trưa bận rộn, giàu chất xơ từ rau và trái cây. Bộ 3 vitamin C, D, Kẽm từ hực phảm bảo vệ sức khoẻ Redoxon Triple Action cùng với dầu ô liu và các loại hạt cung cấp chất béo tốt, vừa lành mạnh lại giúp hỗ trợ tăng sức đề kháng.<o:p></o:p></span></p>', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55593, 'Mug Cake Dừa', 'Mug Cake Dừa là một loại bánh bạn có thể hoàn thành một cách nhanh chóng chỉ trong vòng 10 phút. Nếu bạn là một người bận rộn và yêu thích đồ ngọt, đây sẽ là một món hoàn hảo dành cho bạn. Tuy cách làm đơn giản nhưng kết cấu bánh bông xốp hòa quyện cùng mùi dừa dịu dàng, ăn kèm cùng nước sốt caramel đậm đà sẽ làm bạn nhớ mãi đấy. Còn chờ gì mà không cùng Cooky mau mau vào bếp nào.', 2, '2022-03-22 00:00:00', 10, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55594, 'Churros Chocolate, Vani Và Matcha', 'Churros là một trong những loại bánh ngọt truyền thống có nguồn gốc từ Tây Ban Nha. Cách làm tương tự với bánh choux nhưng thay vì nướng, bánh được chiên ngập dầu chín vàng, giòn xốp, sau đó được lăn qua lớp đường quế và chấm cùng sốt chocolate đăng đắng. Đồng hành cùng Cooky, bạn sẽ có biết thêm cách làm 3 loại nhân chocolate, matcha, vani ngọt ngào ăn kèm món bánh churros thơm lừng này đấy. Mau mau mang tạp dề lên nào. ', 6, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55595, 'Cheese Cake Chanh Dây', 'Cheese Cake Chanh Dây sẽ là một lựa chọn hoàn hảo để chống lại một mùa hè oi ả đang về. Lớp vụn bánh quy giòn tan, kết hợp cùng lớp phô mai chanh dây chua chua ngọt ngọt và lớp thạch chanh dây dai dai thơm thơm sẽ giúp bạn quên đị được cái nóng xung quanh đấy. Còn chờ gì mà không cùng Cooky vào bếp "giải nhiệt" nào.', 6, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55597, 'Panna Cotta Thạch Trái Cây', 'Panna Cotta Thạch Trái Cây là một loại bánh rực rỡ sắc màu và nhiều tầng lớp hương vị từ các loại trái cây tươi thích hợp cho trẻ nhỏ và những trưa hè oi ả nóng bức. Vị giòn từ đế bánh cracker kết hợp cùng lớp panna cotta mịn màng ngọt ngào được làm từ sữa đặc Ông Thọ và lớp thạch trái cây dai dai lung linh màu sắc sẽ khiến bạn không thể cưỡng lại. Còn chờ gì mà không vào bếp và thực hiện ngay thôi!', 6, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55598, 'Trứng Chiên Hàu', 'Đĩa Trứng Chiên Hàu sôi bốc khói, mùi thơm đặc biệt với những con hàu múp míp nằm trên, hương vị béo ngậy khó cưỡng, nằm bên dưới là một lớp giá đỗ mọng nước giúp bạn không hề cảm thấy ngán, cuối cùng sẽ là lớp bột chiên giòn giòn có mùi thơm xem xém tự như lớp cơm cháy. Đây chắc chắn sẽ là món khó thể nào quên được sau mỗi khi ăn, vậy chờ gì nữa mà không thực hiện ngay món Trứng Chiên Hàu để đãi những người thân yêu cũng như bản thân cùng Cooky thôi nào!', 2, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55600, 'Rau Câu Chân Vịt', 'Rong Chân Vịt một loại nguyên liệu quen thuộc ở các miền biển Việt Nam. Loại rong  này có thể  được dùng chế biến thành nhiều  món ăn khác nhau với đặc biệt với độ thanh mát và khả năng đông lại như rau câu nên món rau câu chân vịt là món phổ biến nhất. Với vị ngọt thanh từ đường phèn mùi thơm của lá dứa và gừng, Cùng độ thanh mát có sẵn của rong chân vịt đây sẽ là món ăn giải nhiệt thanh mát vô cùng hiệu quả. Hãy làm ngay món rau câu đặc trưng miền biển này để giải nhiệt cho những ngày hè oi bức cùng Cooky nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55602, 'Gân Bò Kho Tộ', 'Gân bò một thực phẩm mang nhiều giá trị dinh dưỡng và là món ưa thích của rất nhiều người. Gân bò kho tộ vừa mềm nhưng vẫn giữ được độ giòn đặc trưng, hoà cùng nước sốt gia vị ngon đậm đà, khiến món ăn vừa thơm ngon vừa hấp dẫn. Nhất là trong tiết trời mát, lạnh, được thưởng thức món gân bò kho tộ với cơm nóng thì thật ấm cúng và ngon miệng biết bao. Hãy cùng Cooky lưu lại công thức Gân Bò Kho tộ để bữa cơm nhà thêm phần đa dạng!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55604, 'Sườn Cọng Chiên Xù', 'Sườn heo là loại nguyên liệu nấu ăn quen thuộc với mỗi nhà. Nếu đã cảm thấy ngán với những cách chế biến quan thuộc thường làm, bạn có thể biến tấu món ăn với sườn heo Iberico bằng cách thực hiện món Sườn Cọng Chiên Xù dưới đây nhé, rất ngon mà vẫn đảm bảo dinh dưỡng cho cả nhà! Sườn chiên bên ngoài giòn rụm nhưng bên trong thịt vẫn ẩm và mọng nước. Còn chờ gì mà không vào bếp và thực hành ngay thôi!', 1, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55605, 'Gỏi Xoài Diềm Thăn Heo Iberico', 'Gỏi xoài xanh là món yêu thích của nhiều người, nay được nâng cấp khi kết hợp cùng thịt má heo Iberico. Vị ngọt mềm mọng từ thịt kết hợp cùng vị chua dịu của xoài xanh và hương vị đặc trưng của các loại rau thơm khiến cho món gỏi này rất hấp dẫn, ăn hoài mà không thấy ngán.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55606, 'Dồi Sả', 'Dồi sả là món ăn thơm ngon quen thuộc với các đấng mày râu. Vốn có vị thơm hấp dẫn, phần nhân thịt heo đậm đà, thơm ngon hòa quyện với các gia vị sả, lá chanh, tiêu hạt thơm lừng quyện cùng thịt và mỡ heo béo giòn, dai dai nên rất ngon và lạ miệng. Món ăn có thể ăn kèm với một tô cháo lòng nóng nghi ngút khói hay kèm thêm rau thơm chấm nước mắm ớt thì ngon hết nấc.', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55607, 'Nem Chay Váng Đậu', 'Nem chay váng đậu thanh đạm cho những ngày hè nóng bức,  với cách cuộn nem trong váng đậu dài rồi mới cắt ngắn, sẽ tạo được các miếng nem vát chéo lộ rõ phần nguyên liệu tươi ngon đầy màu sắc thật bắt mắt. Món ăn chay ngon như thế này không chỉ thanh mát trong nguyên liệu. Mà còn thanh tịnh, nhẹ nhõm, bay bổng theo màu sắc, hình ảnh hết sức thẩm mỹ.', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55608, 'Nước Mắm Chay Từ Trái Thơm', '<div style="text-align: justify;"><span face="Arial, Helvetica, sans-serif" style="font-family: Arial, Helvetica, sans-serif;"><span style="font-size: 14px;">Nước mắm chay làm từ thơm có mùi thơm hấp dẫn, vị ngọt, chấm rau luộc hoặc các món chiên rán đều ngon tuyệt. Cách làm nước mắm chay từ quả dứa – bí quyết không chỉ riêng cho người ăn chay trường. Nước mắm chay từ quả dứa còn là loại gia vị được nhiều chị em nội trợ yêu thích vì loại gia vị tự chế biến này rất tốt cho sức khỏe.</span></span></div>', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55609, 'Nước Mía Sầu Riêng', 'Nước mía sầu riêng đã trở thành món đồ uống giải nhiệt mùa hè mà giới trẻ nô nức kéo tới trải nghiệm. Hương vị khác lạ, vị ngọt rất riêng đã khiến nước mía sầu riêng ngày càng có sức hút. Với những tín đồ của sầu riêng thì thử món nước mía sầu riêng này chỉ có nghiện luôn. Thay vì phải xếp hàng mua ngoài quán thì ngay giờ đây, các bạn đã có thể làm nước mía sầu riêng ngay tại nhà, cùng học cách làm từ Cooky nhé!', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55610, 'Bún Mắm Chay', ' Bún mắm chay là món ăn vô cùng hấp dẫn dành cho các tín đồ ăn chay góp phần cho thực đơn của bạn thêm phần phong phú hơn. Với nước dùng ngọt thanh, đậm đà hương vị đặc trưng từ tương đậu và chao, hòa cùng rau củ, nấm dai giòn. Dùng nóng kèm với các loại rau sống tươi mát là rất tuyệt đấy!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55612, 'Đậu Bắp Nhồi Cá Thác Lác Chiên Giòn', 'Đậu bắp nhồi cá thác lác chiên là món ăn có chút biến tấu khá mới lạ mang đến cảm nhận có một không hai với một món ăn chế biến độc đáo này. Nhân cá thác lác giòn dai thơm thơm mùi tiêu và hành tím, đậu bắp giòn ngọt, lớp bột áo bên ngoài thì giòn thơm đậm đà, chấm với tương cà VEDAN chua chua, ngọt ngọt. Chắn chắc đây sẽ là món ăn được yêu thích cho cả gia đình. ', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55613, 'Mì Cay Bạch Tuộc Phô Mai', 'Mì cay bạch tuộc phô mai là món ăn mang lại hương vị rất đặc biệt nhờ sợi mì dai dai, vị cay cay mằn mặn từ bạch tuộc hòa cùng độ béo ngậy của phô mai, đây chắc chắn sẽ là món ăn lý tưởng cho ngày cuối tuần của bạn và gia đình.', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55632, 'Croissant Trứng Muối', 'Bánh Croissant là một trong loại bánh nổi tiếng nhất ở Pháp. Ở Việt Nam, một trong những hãng bánh mì nổi tiếng đã thêm sốt trứng muối vào bên trong và làm mọi người yêu thích nó. Nếu bạn muốn một phiên bản homemade Croissant trứng muối, thì hãy mau vào bếp cùng Cooky nào.', 6, '2022-03-22 00:00:00', 200, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55633, 'Bánh Cookie Bó Hoa', 'Chỉ cần một chút biến tấu, những chiếc cookie giòn rụm, thơm bơ sẽ trở thành những bông hoa bắt mắt, là quà tặng độc đáo và ý nghĩa cho người thân, bạn bè trong những dịp đặc biệt. Lựa chọn loại bơ ngon sẽ giúp chiếc cookie không chỉ đẹp mắt mà hương vị còn rất cuốn hút. Cùng Cooky thực hiện bó hoa bánh cookie nhé!', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55634, 'Salad Hy Lạp', 'Salad Hy Lạp - món ăn quen thuộc với người dân phương tây đã dần trở nên thân thuộc trong ẩm thực Việt bởi vị ngon và giá trị dinh dưỡng mà nó mang lại. Món ăn có nguyên liệu dễ tìm và cách chế biến đơn giản có thể thực hiện trong những bữa ăn hàng ngày. Với công thức này, sự kết hợp của salad cùng sữa chua Hy Lạp Tamar Valley sẽ làm tăng vị ngon đặc biệt cho món ăn. Cùng bắt tay thực hiện nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55637, 'Bánh Tiêu Sầu Riêng', 'Bánh tiêu là món ăn vặt được nhiều người yêu thích. Nhưng với công thức này, chiếc bánh quen thuộc với lớp vỏ ngoài vàng ruộm, giòn xốp, bùi bùi của mè trắng sẽ được nâng cấp lên một phiên bản cực kỳ hấp dẫn với phần nhân sầu riêng béo mịn, thơm lừng. Cùng Cooky thực hiện nhé!', 4, '2022-03-22 00:00:00', 150, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55641, 'Tiramisu Cheesecake Healthy', 'Mê làm bánh nhưng eat clean thì chiếc tiramisu cheesecake này là một gợi ý tuyệt vời đó', 1, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55644, 'Pancake Dâu Tây', 'Bánh pancake là món ăn được rất nhiều chị em yêu thích. Cách làm bánh pancake phủ trái cây với nguyên liệu chính từ bột mì nhưng ăn không ngấy vì có thêm các loại trái cây tươi như dâu tây, việt quất, phúc bồn tử đi kèm. Bữa sáng với chiếc bánh thơm phức kẹp trái cây tươi rưới thêm chút sữa đặc Ông Thọ sánh mịn ngọt ngào nữa thì còn gì bằng!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55646, 'Salad Mực Nướng', 'Salad luôn là lựa chọn lý tưởng cho bữa ăn ngày hè như thế này và bạn vẫn có thể biến tấu món salad thông thường thành món ăn vừa mát lành vừa đầy đủ chất dinh dưỡng cho cơ thể. Hãy cùng làm món salad mực nướng với sốt sữa đặc Ông Thọ ngon sánh mịn để thưởng thức và cảm nhận nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55647, 'Ba Chỉ Và Mực Nướng Sữa Đặc', 'Còn gì tuyệt vời hơn khi cuối tuần rảnh rỗi được sum họp với người thân, bạn bè cùng làm thịt ba chỉ hay mực nướng nhâm nhi? Thịt ướp ngon là phải thấm đều vị, thịt ngọt, mềm và dai vừa. Mỗi người sẽ có một bí quyết ướp thịt riêng cho mình, có thể bằng mật ong, trứng gà, hay thêm ít dầu hào. Tuy nhiên có một bí quyết mà không phải ai cũng biết đó là dùng sữa đặc để làm sốt ướp. Cùng vào bếp học ngay cách làm món ba chỉ và mực nướng sữa đặc thơm ngon cho bữa tiệc cuối tuần nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55653, 'Ruột Heo Khìa Nước Dừa', 'Ruột heo khìa nước dừa là món nhậu ngon, dân dã rất đặc trưng của người dân miền Tây. Miếng lòng dai giòn hòa quyện cùng vị ngọt của nước dừa, nhâm nhi với ly bia thì đảm bảo cả nhà bạn sẽ thích mê. Cách làm không quá khó, cùng theo dõi công thức của Cooky và thực hiện ngay thôi nào!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55656, 'Xôi Đậu Đen', 'Xôi đậu đen món ăn quen thuộc của rất nhiều người nay chế biến thêm phần dễ dàng hơn bao giờ hết với nồi cơm điện gia đình sử dụng hàng ngày. Chỉ cần vài bước chuẩn bị đơn giản sau đó cho vào nồi và ấn nút là bạn đã có ngay một nồi xôi đậu đen thơm phức bổ dưỡng. Hãy lưu ngay các làm Xôi Đậu Đen rất đơn giản này từ Cooky nhé.', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55658, 'Bánh Mì Nướng Bơ Tỏi', 'Chỉ mất 15 phút mỗi sáng đã co được món bánh mì nướng thơm mùi bơ tỏi, ăn vào miệng lan tỏa vị phô mai béo.', 2, '2022-03-22 00:00:00', 10, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55661, 'Soda Cam Hoa Atiso Đỏ', 'Soda Cam Hoa Atiso Đỏ, món nước rất tốt cho sức khỏe với thành phần chủ yếu từ atiso đỏ và nước cam tươi. Vị chua nhẹ từ hoa dâm bụt đỏ kết hợp với vị ngọt thanh của cam tươi và soda mát lạnh, tạo nên một thức uống giải nhiệt mùa hè.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55667, 'Bánh Công Chúa', 'Nếu bạn đang tìm kiếm một món bánh thật xinh xắn để tổ chức một bữa tiệc trà hoặc một buổi sinh nhật thật hoành tráng cho cô con gái yêu của mình, bánh công chúa sẽ là một sự lựa chọn hoàn hảo. Bạn có thể cùng con gái mình vào bếp và cùng nhau thực hiện loại bánh này để tạo ra những kỷ niệm đáng nhớ cho bản thân và con gái. Còn chờ gì mà không cùng Ông Thọ vào bếp nào.', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55674, 'Bún Cá Kiên Giang', 'Đây là món bún đặc sản của Kiên Giang được kết hợp giữa vị ngọt thanh của nước dùng, cá và tôm rim. Đặc biệt có sự góp mặt của rau răm tao nên một hương vị khó quên.', 5, '2022-03-22 00:00:00', 40, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55677, 'Soda Vải', 'Một ly soda vải tươi mát trong cái tiết trời nóng bức của mùa hè giúp cho bạn cảm thấy mái hơn. Soda vải với cách làm đơn giản và bắt trend sẽ giúp bạn dịu ngay cơn khát. Còn chờ gì nữa hãy cùng Cooky thực hiện ngay nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55679, 'Cua Luộc Nước Dừa', 'Cua thì ai cũng thích, chỉ bằng cách luộc đơn giản cũng đem lại món ngon ngây ngất cho các bạn nữ không khéo léo nhưng vẫn thích vào bếp như mình.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55680, 'Lá Lốt Cuộn Thịt Chiên Giòn', 'Lá Lốt Cuộn Thịt Chiên Giòn một món ăn biến tấu mới lạ. Thịt heo, tôm và các loại rau củ được tẩm ướp đậm đà bọc trong một lớp lá lốt thơm nồng giòn rụm, chấm cùng với tương ớt hay nước tương sẽ càng thêm hấp dẫn nhất là trong mùa mưa này. Nhanh tay lưu ngay công thức món ăn hấp dẫn này cùng Cooky nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55692, 'Cá Kho Xơ Mít', 'Cá nục kho xơ mít là món ăn tuy lạ miệng nhưng vô cùng hấp dẫn, khi hoàn thành sẽ có vị đậm đà, thoang thoảng mùi hương đặc trưng của mít. Cá chín nhưng không nhừ mà chắc thịt, thấm đẫm gia vị và có mùi hương của xơ mít len lỏi trong từng thớ thịt. Với món ăn này, bạn nên ăn kèm với cơm trắng khi còn nóng, nước kho có thể để chấm rau củ luộc hoặc chan vào cơm sẽ rất đậm đà.', 4, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55696, 'Cheese Burger', 'Chesse Burger là chiếc bánh burger đặc biệt, với lớp bánh được thay thế bằng phô mai mozzarella từ Newviet có phần vỏ bên ngoài giòn tan, bên trong lại là một lớp Phô mai mozzarella béo ngậy chảy ngập trong miệng. Phần thịt áp chảo chín thơm ăn kèm với các loại rau củ tươi ngon hòa quyện cùng phô mai sẽ mang lại một trải nghiệm khó quên về vị giác. Đến ngay với cách làm món Burger siêu hấp dẫn này thôi nào!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55697, 'Chè Bơ', 'Chè bơ có màu xanh đặc trưng rất bắt mắt. Vị mềm béo mịn của bơ quyện với nước cốt sữa dừa là điểm nhấn đặc biệt của món ăn. Với công thức lần này, hãy cùng Cooky biến tấu món chè bơ cùngcác loại thạch pha lê khác nhau, nâng tầm món chè quen thuộc lên một phiên bản nhiều màu sắc và ngon miệng hơn nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55698, 'Kem Bơ Dừa', 'Kem bơ dừa là món tráng miệng được nhiều người yêu thích. Vị béo ngậy của bơ quyện với độ giòn bùi của topping dừa sấy hòa với cái lạnh của viên kem dừa tan dần trong miệng chính là điểm đặc biệt của món kem này. Nếu trong tủ lạnh đang có sẵn những trái bơ chín, sao bạn không cùng Cooky bắt tay vào thực hiện món ăn này để cả gia đình cùng thưởng thức.', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55703, 'Trứng Chiên Sữa Tươi', 'Một món ăn cực kỳ đơn giản với các nguyên liệu luôn có sẵn trong nhà dành cho bạn nào dở bếp núc nhưng vẫn muốn có bữa ăn đầy dinh dưỡng như mình', 1, '2022-03-22 00:00:00', 10, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55707, 'Bánh Bông Lan Chocolate', 'Bông lan chocolate có thể xem là loại bánh được nhiều người yêu thích nhất vì độ mềm ẩm, hương thơm và vị đắng đặc trưng. Nếu bạn không có lò nướng nhưng vẫn muốn làm chiếc bánh này thì hãy thử ngay công thức bánh bông lan chocolate bằng nồi từ Cooky nhé! Với những nguyên liệu cơ bản cùng một chút kiên nhẫn khi thực hiện, bạn sẽ có chiếc bánh thành phẩm ngon ngoài mong đợi.', 5, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55708, 'Hướng Dẫn Pha Bột Ăn Dặm', 'Từ tháng thứ 6, trẻ bắt đầu bước vào giai đoạn ăn dặm để bổ sung thêm dưỡng chất cho cơ thể bên cạnh việc bú sữa mẹ. Với những người lần đầu làm mẹ, việc chọn lựa thực phẩm cho bé ăn dặm và cách chế biến sao cho phù hợp sẽ còn gặp nhiều lúng túng. Cooky sẽ hướng dẫn các mẹ cách pha bột ăn dặm cực kỳ đơn giản và nhanh chóng giúp bé vẫn còn những bữa ăn ngon đầy dinh dưỡng.', 1, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55709, 'Chuối Sấy Dẻo', 'Chuối sấy dẻo là một món ăn vặt ngon miệng được nhiều người yêu thích. Món ăn này hoàn toàn có thể thực hiện được tại nhà với một chiếc lò nướng. Bằng cách này, bạn vẫn có được những miếng chuối dẻo ngon tươm mật mà không mất nhiều công thực hiện như cách phơi nắng thông thường. Bên cạnh đó còn an tâm về mặt vệ sinh vì chuối không bị tiếp xúc với không khi bên ngoài cũng như côn trùng khi phơi dưới ánh nắng mặt trời. Chuẩn bị một nải chuối và bắt tay thực hiện cùng Cooky ngay nhé!', 4, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55714, 'Penne Xốt Cà Chua Tôm Mực', 'Chỉ mất khoảng 30 phút chuẩn bị nguyên liệu và chế biến là bạn sẽ có ngay dĩa penne xốt cà chua tôm mực ngon như nhà hàng. Sợi mì mềm nhưng không nát quyện với xốt cà chua làm sẵn giúp việc nấu món Âu trở nên đơn giản hơn bao giờ hết. Cùng Cooky thực hiện nha!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55715, 'Chè Khoai Lang Tím Sương Sáo', 'Chè khoai lang tím sương sáo là một trong những món chè được khá nhiều người ưa thích, giúp giải nhiệt ngày hè. Cách làm chè khoai lang tím sương sáo tại nhà cũng khá đơn giản. Chỉ cần vài nguyên liệu cùng ít thời gian là đã có nồi chè thơm ngon. Cùng Cooky trổ tài để các thành viên trong gia đình thưởng thức nhé!', 5, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55716, 'Sữa Chua Trân Châu Trái Cây', 'Từ khi xuất hiện đến nay, trân châu đường đen chưa từng "hạ nhiệt" đối với các "tín đồ" ăn vặt. Những món kết hợp với loại trân châu này luôn tạo nên sức hút đặc biệt. Trong công thức lần này, hãy cùng Cooky kết hợp trân châu đường đen với sữa chua cùng mứt trái cây để cân bằng vị ngọt và tăng thêm phần hấp dẫn. Cách làm đơn giản và bảo đảm an toàn vệ sinh giúp bạn có món tráng miệng ngon mát ngay tại nhà.', 5, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55721, 'Vải Băng Tuyết Elsa', 'Vải băng tuyết Elsa là món ăn ngon và rất hot trong thời gian gần đây được rất nhiều người yêu thích. Trong bài viết hôm nay, Cooky sẽ chia sẻ đến bạn cách làm vải elsa thơm ngon, đơn giản tại nhà. Mời bạn tham khảo ngay nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55723, 'Heo Quay Bằng Nồi Chiên Không Dầu', 'Heo quay một món ăn ngon với lớp da bắt mắt giòn rụm, phần thịt được tẩm ướp thơm phức mềm mọng với cách làm khá phức tạp và cầu kì, nhưng nay trở nên đơn giản hơn bao giờ hết khi đã có trợ thủ đắc lực nồi chiên không dầu. Chỉ vài thao tác đơn giản bạn sẽ có ngay một miếng heo quay ngon giòn không khác gì mua ở bên ngoài, cùng Cooky học ngay cách món heo quay tiện lợi này nhé!', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55724, 'Trà Lài Đác Thơm', 'Trà lài đác thơm là sự kết hợp đầy thú vị giữa trà xanh lài, nước ép dứa và hạt đác rim dẻo dai mang đến một thức uống giải khát tuyệt vời với hương thơm đặc trưng. Bạn hoàn toàn có thể chuẩn tự bị tại nhà với những bước thực hiện rất đơn giản cùng nguyên liệu dễ tìm. Cùng Cooky thực hiện nhé!', 4, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55729, 'Tàu Hủ Kem Trứng Ca Cao', 'Tàu hủ trân châu đường đen món ăn vặt hấp dẫn một thời có lẽ không còn quá xa lạ với giới trẻ. Tiếp tục với xu hướng hot đó là món Tàu hủ kem trứng cacao béo ngậy, thơm phức. Kem trứng béo ngậy, béo ngậy, thêm vị đắng của cacao, thơm ngon theo cách hoàn toàn khác với trân châu đường đen. Cùng vào bếp và biến tấu ngay với công thức đơn giản bên dưới nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55731, 'Mỳ Ý Rau Củ Sốt Bò Bằm', 'Mỳ Ý rau củ sốt bò bằm, món ăn thơm ngon lại nhiều dinh dưỡng. Sợi mỳ mềm, dai được áo bởi nước sốt bò bằm đậm đà, kết hợp thêm các loại rau củ tươi mát, chắc hẳn sẽ là món ăn sáng yêu thích của các thành viên trong gia đình nhất là các bé nhỏ thân yêu. ', 2, '2022-03-22 00:00:00', 45, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55732, 'Trà Lipton Cam Hoa Đậu Biếc', 'Mùa nắng nóng oi bức sẽ được đánh tan bằng ly trà Lipton cam hoa đậu biếc mát lạnh. Với sắc tím của hoa đậu biếc cùng sắc vàng của nước cam tươi và trà Lipton, tạo thành 2 tầng màu vô cùng đẹp mắt. Trà mát lạnh, có vị chua chua ngọt ngọt rất thích hợp để giải khát cho những ngày nóng bức đấy nhé!', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55733, 'Trà Dâu Kem Cheese', 'Một ly trà dâu kem cheese mát lạnh, thật tươi mát sẽ giúp bạn giải tỏa được cơn nóng mùa hè. Vị trà Lipton đậm đà nhưng không hề lấn át vị dâu tươi mát. Kết hợp thêm  lớp kem phô mai béo ngậy, một chút mặn mặn tạo nên hương vị tròn đầy, mát lạnh, sảng khoái của tổ hợp những “cực phẩm" kia khiến bạn nâng niu đến giọt cuối cùng. ', 2, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55734, 'Bơ Sữa Tươi Trân Châu Đường Đen', ' Sau những ngày Sữa Tươi Trân Châu Đường Đen gây nhớ thương mọi người, thì nay một biến tấu mới mẻ lại có phần ngon hơn một bậc – Bơ Sữa Tươi Trân Châu Đường Đen. Món này vừa béo vừa thơm, trân châu ngòn ngọt, và bơ trong ly này dạng như sinh tố xay, quậy đều lên cho hoà quyện với sữa tươi và trân châu là đỉnh nhất.', 2, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55735, 'Bánh Mì Xoắn Ốc Nhân Kem Trứng', 'Bánh mì xoắn ốc là một trong những món ăn tuổi thơ của nhiều người Việt Nam. Bánh mì thơm, mềm, ăn cùng kem trứng mềm mịn ngọt ngào. Bạn có thể nướng bánh bằng lò nướng hoặc nồi chiên không dầu. Nếu nhà bạn có trẻ nhỏ, đây sẽ là một món ăn xế hoàn hảo mà không đứa trẻ nào có thể từ chối đấy. Còn chờ gì mà không cùng Cooky vào bếp và nướng những "con ốc" thật ngọt ngào nào.', 150, '2022-03-22 00:00:00', 150, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55739, 'Chả Trứng Nấm Rơm', 'Trứng là một loại thực phẩm đầy dinh dưỡng và có thể biến tấu thành nhiều món ăn khác nhau. Trong công thức này, hãy cùng Tupperware kết hợp trứng với nấm rơm và thịt heo xay để làm món chả trứng nấm rơm vô cùng hấp dẫn. Không quá cầu kỳ trong cách chế biến mà bạn vẫn có được miếng chả trứng mềm ẩm đưa cơm. Bắt tay thực hiện nhé!', 5, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55745, 'Bánh Red Velvet', 'Bánh Red Velvet hay còn gọi là bánh nhung đỏ là một loại bánh chocolate có nhiều lớp màu đỏ tươi ăn kem cùng kem phô mai béo ngậy có nguồn gốc từ Mỹ. Khi mới ra đời, màu đỏ của bánh được tiết ra từ phản ứng hóa học giữa axit trong chanh, giấm với anthocyanin trong bột cacao được sản xuất theo kiểu cũ. Ngày nay, bột cacao được sản xuất theo phương pháp mới, do đó để có được màu đỏ, người ta thêm củ dền hoặc các loại màu thực phẩm. Màu đỏ tươi từ cốt bánh và màu trắng sáng từ kem phô mai gợi lên sự sang trọng, quý phái, thích hợp cho các dịp sinh nhật hoặc lễ lớn. Và vô tình nó cũng trùng khớp với biểu tượng của Cooky chúng tôi. Còn chờ gì mà không vào bếp và làm một cái bánh Red Velvet thật to mừng sinh nhật cùng Cooky nào.', 10, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55747, 'Chè Khoai Lang Bột Báng Nước Cốt Dừa', '<span style="color: #262626; font-family: "Noto Sans"; font-size: 16px;">Một ngày mùa hè nóng nực, muốn được đãi cả nhà món chè nhưng bạn phân vân không biết nên ăn loại chè gì đây. Những loại chè đá thì ăn hoài cũng ngán. Hôm nay Cooky chia sẻ với các bạn món chè khoai lang bột báng nước cốt dừa ngon tuyệt. Đừng nghĩ nghe đã thấy ngán. Hãy thử đi rồi bạn sẽ ngạc nhiên cho mà xem.</span>', 30, '2022-03-22 00:00:00', 30, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55748, 'Bánh Trung Thu Cầu Vồng', 'Bên cạnh các loại bánh nướng, bánh dẻo truyền thống, nếu bạn muốn một chút mới lạ trong mùa trung thu sắp tới, bạn có thể thử làm bánh trung thu cầu vồng. Vỏ bánh nhiều lớp giòn rụm, nhân đậu xanh mềm mịn, ngọt thanh, sẽ ngon hơn khi bạn ăn bánh cùng một ly trà nóng. Còn chờ gì mà không cùng Tupperware vào bếp và chuẩn bị cho mùa trung thu sắp tới nào!', 10, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55750, 'Chạo Tôm Nướng Mía', 'Chạo tôm nướng mía là một món ăn vô cùng "bắt miệng". Bạn có thể dùng với cơm trắng, bún tươi hay cuốn bánh tráng đều ngon. Chạo hấp dẫn nhất là khi được nướng trên than hồng nhưng bạn hoàn toàn có thể chế biến nhanh gọn bằng nồi chiên không dầu. Cách làm đơn giản và bí kíp để có những chiếc chạo dai ngon, đậm vị sẽ được Cooky bật mí ngay sau đây nhé!', 5, '2022-03-22 00:00:00', 120, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55751, 'Chả Ốc Lá Lốt', 'Chả ốc lá lốt là một trong nhũng món ăn dân dã khá lạ miệng, bạn có thể ăn kèm bún hoặc cuốn rau sống, chấm ngập nước mắm ngọt sẽ thỏa mãn bất cứ con tim yêu mến ẩm thực các vùng miền. Cách làm đơn giản, bạn có thể nướng than hoặc bằng nồi chiên không dầu, thậm chí là chiên với ít dầu trên bếp ga. Còn chờ gì mà không vào bếp cùng Cooky làm một mâm chả ốc lá lốt thật to cho cả nhà nào.', 4, '2022-03-22 00:00:00', 90, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55752, 'Soda Chanh Yakult Hoa Đậu Biếc', 'Soda chanh yakult hoa đậu biếc thu hút ánh nhìn của mọi người nhờ sắc xanh tím đẹp mắt từ hoa đậu biếc, vị chua chua ngọt ngọt từ chanh và yakult hòa quyện cùng cùng vị đặc trưng của soda. Tất cả tạo nên một thức uống thơm ngon, mát lạnh, cực kỳ lý tưởng cho những ngày oi bức.', 1, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55753, 'Soda Xoài Yakult', 'Vào những ngày hè nóng bức thế này, làm một ly soda giải khát luôn là một ý tưởng tuyệt vời. Ngoài việc thưởng thức thông thường, soda còn có thể mix cùng các loại trái cây để tạo ra những món đồ uống đa dạng. Cùng thực hiện công thức Soda xoài yakult ngon và đẹp mắt theo công thức dưới đây của Cooky nhé. Trong khi xoài có vị ngọt lịm còn soda lại có sự thanh mát, sự kết hợp giữa hai thành phần này cộng thêm ít nước yakult, chắc chắn sẽ tạo ra hương vị rất tuyệt vời.', 1, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55754, 'Soda Thanh Long Đỏ Yakult', 'Vào những ngày hè nóng bức thế này, làm một ly soda giải khát luôn là một ý tưởng tuyệt vời. Ngoài việc thưởng thức thông thường, soda còn có thể mix cùng các loại trái cây để tạo ra những món đồ uống đa dạng. Hãy cùng note lại những công thức Soda thanh long đó yakult đẹp mắt dưới đây để "chill” cùng bạn bè dịp cuối tuần nha!', 1, '2022-03-22 00:00:00', 20, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55756, 'Bánh Sữa Chua Xoài', 'Bánh sữa chua xoài là món ăn ngon tráng miệng rất tuyệt vời. Không cần dùng lò nướng, bạn có thể triển ngay món bánh sữa chua xoài thơm ngon khó cưỡng này cho cả gia đình dùng thử rồi nhé. Món bánh mát lạnh, vị chua ngọt, béo ngon chắc chắn khiến bạn hài lòng.', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55782, 'Nghêu Hấp Thái', '<p>Món nghêu hấp thái với những con nghêu ngọt béo hòa quyện cùng vị chua, cay đặc trưng của món thái cùng mùi thơm của xả, lá chanh thật không thể cưỡng lại. Hãy cùng hằng vào bếp để làm nhé.</p>
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55787, 'Lẩu Gà Lá Giang', 'Lẩu gà lá giang là một món ăn ngon, cách chế biến vô cùng mới lạ và hấp dẫn. Lá Giang có vị chua, dễ ăn, thanh mát là 1 món ăn bạn không thể bỏ qua nhé', 4, '2022-03-22 00:00:00', 60, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55788, 'Bầu Luộc Chấm Chao', 'Ăn chay luôn luôn tốt cho sức khỏe, hôm nay hãy cùng hằng làm món Bầu luộc chấm chao nhé, đảm bảo sẽ vừa lòng các bạn ngay.', 2, '2022-03-22 00:00:00', 15, null, null);
insert into whatseat.recipes (RecipeId, Name, Description, Serving, CreatedOn, TotalTime, RecipeTypeId, ThumbnailUrl) values (55797, 'Pad Thai', '<p>Cùng chút biến tấu về hương vị, Pad Thai sử dụng nước sốt thái được pha hài hòa tự nhiên cùng đường thốt nốt khiến món Pad Thai ngon hơn bao giờ hết.</p>
